import React, { useEffect, useState } from "react";
import { jsPDF } from "jspdf";

/*
  Vols.js
  - identical layout & drawer behavior to Caisse.js
  - left drawer: Ajouter / Modifier / Supprimer
  - right drawer: Impression / Fiche vol
  - CRUD table + modal
  - FR / AR locale + pywebview backend
*/

const TABLE_FIELDS = [
  "flight_number",
  "compagnie",
  "depart_aeroport",
  "arrivee_aeroport",
  "date_depart",
  "heure_depart",
  "duree",
  "classe",
  "prix",
  "places_disponibles",
  "statut"
];

const translations = {
  fr: {
    title: "Liste des Vols",
    searchPlaceholder: "Rechercher vol, compagnie, a√©roport...",
    selectRowAlert: "Veuillez s√©lectionner une ligne vol.",
    addVol: "Ajouter Nouveau Vol",
    editVol: "Modifier Vol",
    confirmDelete: (name) => `Supprimer le vol ${name} ?`,
    save: "Enregistrer",
    cancel: "Annuler",
    addButton: "‚ûï Ajouter",
    editButton: "‚úèÔ∏è Modifier",
    deleteButton: "üóëÔ∏è Supprimer",
    printButton: "üñ®Ô∏è Impression",
    ficheButton: "üßæ Fiche vol",
    errorLoad: "Aucun vol trouv√©",
    errorSave: "Erreur lors de l'enregistrement !",
    errorDelete: "Erreur lors de la suppression !",
    tableHeaders: [
      "#",
      "N¬∞ Vol",
      "Compagnie",
      "D√©part",
      "Arriv√©e",
      "Date",
      "Heure",
      "Dur√©e",
      "Classe",
      "Prix",
      "Places",
      "Statut"
    ],
    modalLabels: {
      flight_number: "N¬∞ Vol",
      compagnie: "Compagnie",
      depart_aeroport: "A√©roport d√©part",
      arrivee_aeroport: "A√©roport arriv√©e",
      date_depart: "Date d√©part",
      heure_depart: "Heure d√©part",
      duree: "Dur√©e",
      classe: "Classe",
      prix: "Prix",
      places_disponibles: "Places disponibles",
      statut: "Statut (OK/Annul√©)"
    }
  },
  ar: {
    title: "ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿ±ÿ≠ŸÑÿßÿ™",
    searchPlaceholder: "ÿ®ÿ≠ÿ´ ÿπŸÜ ÿßŸÑÿ±ÿ≠ŸÑÿ©ÿå ÿßŸÑÿ¥ÿ±ŸÉÿ©ÿå ÿßŸÑŸÖÿ∑ÿßÿ±...",
    selectRowAlert: "ÿßŸÑÿ±ÿ¨ÿßÿ° ÿ™ÿ≠ÿØŸäÿØ ÿµŸÅ ÿßŸÑÿ±ÿ≠ŸÑÿ©.",
    addVol: "ÿ•ÿ∂ÿßŸÅÿ© ÿ±ÿ≠ŸÑÿ© ÿ¨ÿØŸäÿØÿ©",
    editVol: "ÿ™ÿπÿØŸäŸÑ ÿßŸÑÿ±ÿ≠ŸÑÿ©",
    confirmDelete: (name) => `ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ≠ÿ∞ŸÅ ÿßŸÑÿ±ÿ≠ŸÑÿ© ${name}ÿü`,
    save: "ÿ≠ŸÅÿ∏",
    cancel: "ÿ•ŸÑÿ∫ÿßÿ°",
    addButton: "‚ûï ÿ•ÿ∂ÿßŸÅÿ©",
    editButton: "‚úèÔ∏è ÿ™ÿπÿØŸäŸÑ",
    deleteButton: "üóëÔ∏è ÿ≠ÿ∞ŸÅ",
    printButton: "üñ®Ô∏è ÿ∑ÿ®ÿßÿπÿ©",
    ficheButton: "üßæ ÿ®ÿ∑ÿßŸÇÿ© ÿßŸÑÿ±ÿ≠ŸÑÿ©",
    errorLoad: "ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ±ÿ≠ŸÑÿßÿ™",
    errorSave: "ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿ≠ŸÅÿ∏!",
    errorDelete: "ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿ≠ÿ∞ŸÅ!",
    tableHeaders: [
      "#",
      "ÿ±ŸÇŸÖ ÿßŸÑÿ±ÿ≠ŸÑÿ©",
      "ÿßŸÑÿ¥ÿ±ŸÉÿ©",
      "ŸÖÿ∑ÿßÿ± ÿßŸÑŸÖÿ∫ÿßÿØÿ±ÿ©",
      "ŸÖÿ∑ÿßÿ± ÿßŸÑŸàÿµŸàŸÑ",
      "ÿßŸÑÿ™ÿßÿ±ŸäÿÆ",
