      depart_aeroport: "ŸÖÿ∑ÿßÿ± ÿßŸÑŸÖÿ∫ÿßÿØÿ±ÿ©",
      arrivee_aeroport: "ŸÖÿ∑ÿßÿ± ÿßŸÑŸàÿµŸàŸÑ",
      date_depart: "ÿ™ÿßÿ±ŸäÿÆ ÿßŸÑŸÖÿ∫ÿßÿØÿ±ÿ©",
      heure_depart: "ŸàŸÇÿ™ ÿßŸÑŸÖÿ∫ÿßÿØÿ±ÿ©",
      duree: "ÿßŸÑŸÖÿØÿ©",
      classe: "ÿßŸÑÿØÿ±ÿ¨ÿ©",
      prix: "ÿßŸÑÿ≥ÿπÿ±",
      places_disponibles: "ÿßŸÑŸÖŸÇÿßÿπÿØ ÿßŸÑŸÖÿ™ÿßÿ≠ÿ©",
      statut: "ÿßŸÑÿ≠ÿßŸÑÿ© (OK/ŸÖŸÑÿ∫ÿßÿ©)"
    }
  }
};

function formatDateToFR(dateString) {
  if (!dateString) return "";
  if (/^\d{4}-\d{2}-\d{2}$/.test(dateString)) {
    const [yyyy, mm, dd] = dateString.split("-");
    return `${dd}/${mm}/${yyyy}`;
  }
  return dateString;
}
function formatDateToISO(dateString) {
  if (!dateString) return "";
  if (/^\d{2}\/\d{2}\/\d{4}$/.test(dateString)) {
    const [dd, mm, yyyy] = dateString.split("/");
    return `${yyyy}-${mm}-${dd}`;
  }
  return dateString;
}

async function generateVolPDF(vol, locale) {
  const doc = new jsPDF();
  let y = 12;
  doc.setFontSize(18);
  doc.text(translations[locale].ficheButton, 12, y);
  y += 10;
  doc.setFontSize(12);
  const L = translations[locale].modalLabels;
  for (const field of TABLE_FIELDS) {
    const label = L[field] || field;
    const value = vol[field] ?? "";
    doc.text(`${label}: ${value}`, 12, y);
    y += 8;
  }
  const pdfBlob = doc.output("blob");
  const arrayBuffer = await pdfBlob.arrayBuffer();
  const uint8Array = new Uint8Array(arrayBuffer);
  let binary = "";
  for (let i = 0; i < uint8Array.length; i++)
    binary += String.fromCharCode(uint8Array[i]);
  const base64PDF = window.btoa(binary);
  if (window.pywebview?.api?.open_pdf_base64)
    await window.pywebview.api.open_pdf_base64(base64PDF);
  else alert("PDF preview not supported.");
}

function VolsPage({ locale = "fr" }) {
  const [vols, setVols] = useState([]);
  const [selectedIndex, setSelectedIndex] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [modalMode, setModalMode] = useState("add");
  const [volForm, setVolForm] = useState({
    id: null,
    flight_number: "",
    compagnie: "",
    depart_aeroport: "",
    arrivee_aeroport: "",
    date_depart: "",
    heure_depart: "",
    duree: "",
    classe: "",
    prix: "",
    places_disponibles: "",
    statut: "OK"
  });

  const isRtl = locale === "ar";
  const directionStyle = isRtl ? { direction: "rtl", textAlign: "right" } : {};

  // üß≠ Drawer hover CSS (inside component)
  useEffect(() => {
    const style = document.createElement("style");
    style.id = "drawer-hover-style";
    style.innerHTML = `
      .drawer-left, .drawer-right {
        position: fixed;
        top: 50%;
        transform: translateY(-50%);
        z-index: 10;
        display: flex;
        flex-direction: column;
        gap: 10px;
        padding: 0.5rem;
        background: rgba(255,255,255,0.9);
        border-radius: 10px;
        box-shadow: 0 0 10px rgba(0,0,0,0.1);
        transition: all 0.4s ease;
        opacity: 0.3;
      }
      .drawer-left { left: -180px; }
      .drawer-right { right: -180px; }
      .drawer-left:hover { left: 15px; opacity: 1; }
      .drawer-right:hover { right: 15px; opacity: 1; }
      .drawer-left button,
      .drawer-right button {
        background: #f4f7ff;
        border: none;
        border-radius: 6px;
        padding: 0.6rem 1.1rem;
        cursor: pointer;
        font-size: 1rem;
        font-weight: 600;
        color: #2a3f79;
        display: flex;
        align-items: center;
        gap: 6px;
        transition: all 0.3s;
      }
      .drawer-left button:hover,
      .drawer-right button:hover {
        background: #3477eb;
        color: white;
      }
    `;
    document.head.appendChild(style);
    return () => document.head.removeChild(style);
  }, []);

  const fetchVols = async () => {
    try {
      const res = await window.pywebview.api.get_vols();
      setVols(res || []);
    } catch {
      setVols([]);
    }
  };

  useEffect(() => { fetchVols(); }, []);

  const filteredVols = vols.filter(v => {
    const q = searchQuery.trim().toLowerCase();
    return (
      v.flight_number?.toLowerCase().includes(q) ||
      v.compagnie?.toLowerCase().includes(q)
    );
  });

  const handleAjouter = () => {
    setModalMode("add");
    setVolForm({
      id: null,
      flight_number: "",
      compagnie: "",
      depart_aeroport: "",
      arrivee_aeroport: "",
      date_depart: "",
      heure_depart: "",
      duree: "",
      classe: "",
      prix: "",
      places_disponibles: "",
      statut: "OK"
    });
    setShowModal(true);
  };

  const handleModifier = () => {
    if (selectedIndex === null)
      return alert(translations[locale].selectRowAlert);
    setModalMode("edit");
    setVolForm(filteredVols[selectedIndex]);
    setShowModal(true);
  };

  const handleSupprimer = async () => {
    if (selectedIndex === null)
      return alert(translations[locale].selectRowAlert);
    const vol = filteredVols[selectedIndex];
    if (!window.confirm(translations[locale].confirmDelete(vol.flight_number)))
      return;
    await window.pywebview.api.delete_vol(vol.id);
    fetchVols();
  };

  const handleSave = async (e) => {
    e.preventDefault();
    const payload = { ...volForm, date_depart: formatDateToISO(volForm.date_depart) };
    if (modalMode === "add") await window.pywebview.api.add_vol(payload);
    else await window.pywebview.api.update_vol(payload);
    setShowModal(false);
    fetchVols();
  };

  const handleImpression = () => window.print();
  const handleFiche = () => {
    if (selectedIndex === null)
      return alert(translations[locale].selectRowAlert);
    generateVolPDF(filteredVols[selectedIndex], locale);
  };

  return (
    <div style={{ padding: "1.25rem", ...directionStyle }}>
      <h2 style={{ textAlign: "center" }}>{translations[locale].title}</h2>

      <div style={{ display: "flex", marginBottom: "1rem" }}>
        <input
          type="text"
          placeholder={translations[locale].searchPlaceholder}
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          style={{
            width: "100%",
            padding: "0.5rem",
            borderRadius: "5px",
            border: "1px solid #ccc"
          }}
        />
      </div>

      <table style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr>
            {translations[locale].tableHeaders.map((h, i) => (
              <th
                key={i}
                style={{
                  border: "1px solid #ddd",
                  padding: "8px",
                  background: "#f4f6ff"
                }}
              >
                {h}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {filteredVols.map((v, idx) => (
            <tr
              key={v.id}
              style={{
                background:
                  idx === selectedIndex ? "#cce5ff" : idx % 2 ? "#fff" : "#f9f9f9",
                cursor: "pointer"
              }}
              onClick={() => setSelectedIndex(idx)}
            >
              <td>{idx + 1}</td>
              {TABLE_FIELDS.map((f) => (
                <td key={f} style={{ border: "1px solid #ddd", padding: "8px" }}>
                  {f === "date_depart" ? formatDateToFR(v[f]) : v[f]}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>

      {/* üß≠ Left Drawer */}
      <div className="drawer-left">
        <button onClick={handleAjouter}>{translations[locale].addButton}</button>
        <button onClick={handleModifier}>{translations[locale].editButton}</button>
        <button onClick={handleSupprimer}>{translations[locale].deleteButton}</button>
      </div>

      {/* üß≠ Right Drawer */}
      <div className="drawer-right">
        <button onClick={handleImpression}>{translations[locale].printButton}</button>
        <button onClick={handleFiche}>{translations[locale].ficheButton}</button>
      </div>

      {/* ü™ü Modal */}
      {showModal && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background: "rgba(0,0,0,0.5)",
            display: "flex",
            alignItems: "center",
            justifyContent: "center"
          }}
        >
          <div
            style={{
              background: "white",
              padding: "2rem",
              borderRadius: "10px",
              width: "90%",
              maxWidth: "600px",
              ...directionStyle
            }}
          >
            <h3>
              {modalMode === "add"
                ? translations[locale].addVol
                : translations[locale].editVol}
            </h3>
            <form
              onSubmit={handleSave}
              style={{ display: "flex", flexDirection: "column", gap: "1rem" }}
            >
              {Object.keys(translations[locale].modalLabels).map((key) => (
                <label key={key}>
                  {translations[locale].modalLabels[key]}:
                  <input
                    value={volForm[key] || ""}
                    onChange={(e) =>
                      setVolForm({ ...volForm, [key]: e.target.value })
                    }
                    style={{
                      width: "100%",
                      padding: "0.5rem",
                      borderRadius: "5px",
                      border: "1px solid #ccc"
                    }}
                  />
                </label>
              ))}
              <div
                style={{ display: "flex", justifyContent: "flex-end", gap: "1rem" }}
              >
                <button
                  type="submit"
                  style={{
                    background: "#3477eb",
                    color: "white",
                    border: "none",
                    padding: "0.6rem 1rem",
                    borderRadius: "5px"
                  }}
                >
                  {translations[locale].save}
                </button>
                <button
                  type="button"
                  onClick={() => setShowModal(false)}
                  style={{
                    background: "#999",
                    color: "white",
                    border: "none",
                    padding: "0.6rem 1rem",
                    borderRadius: "5px"
                  }}
                >
                  {translations[locale].cancel}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default VolsPage;
