import React, { useState } from "react";
import ClientPage from "./client";
import GroupPage from "./group"; // Ensure this exists

// Define translations in French and Arabic including updated pages
const translations = {
  fr: {
    loginTitle: "Connexion Agence de Voyage",
    usernamePlaceholder: "Nom d'utilisateur",
    passwordPlaceholder: "Mot de passe",
    loginButton: "Se connecter",
    dashboardTitle: "Tableau de bord",
    retourDashboard: "‚Üê Retour au dashboard",
    // Updated pages:
    pages: [
      { label: "Clients", page: "client" },
      { label: "Groupes", page: "groupe" },
      { label: "Caisse", page: "caisse" },
      { label: "Personnel", page: "personel" },
      { label: "Vols & H√¥tels", page: "vols_hotels" },
      { label: "Param√®tres", page: "parametres" }
    ],
    loginError: "Nom d'utilisateur ou mot de passe incorrect.",
    backendError: "Erreur de connexion au backend."
  },
  ar: {
    loginTitle: "ÿ™ÿ≥ÿ¨ŸäŸÑ ÿØÿÆŸàŸÑ ŸàŸÉÿßŸÑÿ© ÿßŸÑÿ≥ŸÅÿ±",
    usernamePlaceholder: "ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ",
    passwordPlaceholder: "ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±",
    loginButton: "ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ",
    dashboardTitle: "ŸÑŸàÿ≠ÿ© ÿßŸÑŸÖÿπŸÑŸàŸÖÿßÿ™",
    retourDashboard: "‚Üê ÿßŸÑÿπŸàÿØÿ© ŸÑŸÑŸàÿ≠ÿ©",
    // Updated pages (Arabic translations; adjust text as needed)
    pages: [
      { label: "ÿßŸÑÿπŸÖŸÑÿßÿ°", page: "client" },
      { label: "ÿßŸÑŸÖÿ¨ŸÖŸàÿπÿßÿ™", page: "groupe" },
      { label: "ÿßŸÑÿÆÿ≤ŸäŸÜÿ©", page: "caisse" },
      { label: "ÿßŸÑŸÖŸàÿ∏ŸÅŸàŸÜ", page: "personel" },
      { label: "ÿ±ÿ≠ŸÑÿßÿ™ Ÿàÿ•ŸÇÿßŸÖÿßÿ™", page: "vols_hotels" },
      { label: "ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™", page: "parametres" }
    ],
    loginError: "ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿ£Ÿà ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ± ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠ÿ©.",
    backendError: "ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆŸÑŸÅŸäÿ©."
  }
};

function Tabs({ currentPage, onNavigate, locale, setLocale, pages }) {
  // Define icons for each page based on the page key.
  const icons = {
    client: "üë•",
    groupe: "üß≥",
    caisse: "üßæ",
    personel: "üëî",
    vols_hotels: "üõ´",
    parametres: "‚öôÔ∏è"
  };
  return (
    <div style={styles.tabs}>
      {pages.map(({ label, page }) => (
        <button
          key={page}
          className="button-hover tab-button"
          style={{
            ...styles.tab,
            ...(currentPage === page ? styles.activeTab : {})
          }}
          onClick={() => onNavigate(page)}
        >
          <span style={styles.icon}>{icons[page]}</span>
          <span style={styles.tabLabel}>{label}</span>
        </button>
      ))}
      <div style={styles.languageSwitch}>
        <button
          onClick={() => setLocale("fr")}
          className="button-hover"
          style={{
            ...styles.langButton,
            background: locale === "fr" ? "#6ea6ff" : "#1f2a3a",
            color: locale === "fr" ? "#072447" : "#fff"
          }}
        >
          FR
        </button>
        <button
          onClick={() => setLocale("ar")}
          className="button-hover"
          style={{
            ...styles.langButton,
            marginLeft: "0.5rem",
            background: locale === "ar" ? "#6ea6ff" : "#1f2a3a",
            color: locale === "ar" ? "#072447" : "#fff"
          }}
        >
          AR
        </button>
      </div>
    </div>
  );
}

function Dashboard({ onNavigate, locale }) {
  const pages = translations[locale].pages;
  const emojiForPage = (page) =>
    page === "client"
      ? "üë•"
      : page === "groupe"
      ? "üß≥"
      : page === "caisse"
      ? "üßæ"
      : page === "personel"
      ? "üëî"
      : page === "vols_hotels"
      ? "üõ´"
      : page === "parametres"
      ? "‚öôÔ∏è"
      : "";
  return (
    <div style={styles.dashboardContainer}>
      <h2>{translations[locale].dashboardTitle}</h2>
      <div style={styles.grid}>
        {pages.map(({ label, page }) => (
          <button
            key={page}
            className="dashboard-button button-hover"
            style={styles.tile}
            onClick={() => onNavigate(page)}
          >
            <span style={styles.tileIcon}>{emojiForPage(page)}</span>
            <span style={styles.tileLabel}>{label}</span>
          </button>
        ))}
      </div>
      {/* Footer Bar */}
      <div style={styles.footerBar}>
        <div style={styles.footerLeft}>¬© 2025 R-TECH Inc.</div>
        <div style={styles.footerCenter}>Contactez nous:</div>
        <div style={styles.footerRight}>
          {/* Social logos (only icons shown) */}
          <button
            className="button-hover"
            style={styles.socialButton}
            onClick={() =>
              window.open("https://www.facebook.com/yourpage", "_blank")
            }
            aria-label="facebook"
          >
            <img
              src="https://upload.wikimedia.org/wikipedia/commons/5/51/Facebook_f_logo_%282019%29.svg"
              alt="Facebook"
              style={styles.socialIcon}
            />
          </button>
          <button
            className="button-hover"
            style={styles.socialButton}
            onClick={() =>
              window.open("https://www.instagram.com/yourpage", "_blank")
            }
            aria-label="instagram"
          >
            <img
              src="https://upload.wikimedia.org/wikipedia/commons/e/e7/Instagram_logo_2016.svg"
              alt="Instagram"
              style={styles.socialIcon}
            />
          </button>
          <button
            className="button-hover"
            style={styles.socialButton}
            onClick={() =>
              window.open("https://www.tiktok.com/@yourpage", "_blank")
            }
            aria-label="tiktok"
          >
            <img
              src="https://upload.wikimedia.org/wikipedia/en/a/a9/TikTok_logo.svg"
              alt="TikTok"
              style={styles.socialIcon}
            />
          </button>
          <button
            className="button-hover"
            style={styles.socialButton}
            onClick={() =>
              window.open("https://www.youtube.com/yourchannel", "_blank")
            }
            aria-label="youtube"
          >
            <img
              src="https://upload.wikimedia.org/wikipedia/commons/b/b8/YouTube_Logo_2017.svg"
              alt="YouTube"
              style={styles.socialIcon}
            />
          </button>
        </div>
      </div>
    </div>
  );
}

function App() {
  const [locale, setLocale] = useState("fr");
  const [loggedIn, setLoggedIn] = useState(false);
  const [currentPage, setCurrentPage] = useState("dashboard");
  const [loginData, setLoginData] = useState({ username: "", password: "" });
  const [error, setError] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const exists = await window.pywebview.api.check_user_login({
        username: loginData.username,
        password: loginData.password
      });
      if (exists) {
        setLoggedIn(true);
        setError("");
        setCurrentPage("dashboard");
      } else {
        setError(translations[locale].loginError);
      }
    } catch (err) {
      setError(translations[locale].backendError);
    }
  };

  let pageContent;
  if (!loggedIn) {
    pageContent = (
      <div style={styles.loginContainer}>
        <div style={styles.loginCard}>
          <h2>{translations[locale].loginTitle}</h2>
          <form onSubmit={handleLogin} style={styles.form}>
            <input
              type="text"
              placeholder={translations[locale].usernamePlaceholder}
              value={loginData.username}
              onChange={(e) =>
                setLoginData({ ...loginData, username: e.target.value })
              }
              style={styles.input}
            />
            <input
              type="password"
              placeholder={translations[locale].passwordPlaceholder}
              value={loginData.password}
              onChange={(e) =>
                setLoginData({ ...loginData, password: e.target.value })
              }
              style={styles.input}
            />
            <button type="submit" className="button-hover" style={styles.button}>
              {translations[locale].loginButton}
            </button>
            {error && <p style={styles.error}>{error}</p>}
          </form>
        </div>
      </div>
    );
  } else {
    switch (currentPage) {
      case "client":
        pageContent = <ClientPage locale={locale} />;
        break;
      case "groupe":
        pageContent = <GroupPage locale={locale} />;
        break;
      case "caisse":
        pageContent = <div style={styles.page}>Caisse page</div>;
        break;
      case "personel":
        pageContent = <div style={styles.page}>Personnel page</div>;
        break;
      case "vols_hotels":
        pageContent = <div style={styles.page}>üõ´ Vols & H√¥tels page</div>;
        break;
      case "parametres":
        pageContent = <div style={styles.page}>Param√®tres page</div>;
        break;
      case "dashboard":
      default:
        pageContent = <Dashboard onNavigate={setCurrentPage} locale={locale} />;
        break;
    }
  }

  return (
    <div style={styles.appContainer}>
      {/* Global styles for hover effects and tab color shift */}
      <style>{`
        .button-hover {
          transition: background 0.28s ease, box-shadow 0.28s ease, transform 0.18s ease, color 0.18s ease;
          cursor: pointer;
        }
        .button-hover:hover {
          box-shadow: 0px 10px 28px rgba(0,0,0,0.22);
          transform: translateY(-4px);
        }

        /* Tab hover color-shift (soft gradient shift) */
        .tab-button {
          transition: background 0.32s ease, color 0.32s ease, box-shadow 0.32s ease;
        }
        .tab-button:hover {
          background: linear-gradient(90deg, rgba(110,166,255,0.16), rgba(235,52,52,0.12));
          color: #fff;
          box-shadow: 0 6px 18px rgba(0,0,0,0.18);
        }

        /* Dashboard tile hover accent: subtle color shift and brighten */
        .dashboard-button:hover {
          background: linear-gradient(90deg, rgba(255,255,255,0.05), rgba(255,255,255,0.02));
          filter: brightness(1.06);
        }
      `}</style>

      {/* Static gradient background (no animation) */}
      <div style={styles.bgAnimation} />

      {loggedIn && (
        <Tabs
          currentPage={currentPage}
          onNavigate={setCurrentPage}
          locale={locale}
          setLocale={setLocale}
          pages={translations[locale].pages}
        />
      )}
      {loggedIn && currentPage !== "dashboard" && (
        <button
          className="button-hover"
          style={styles.backButton}
          onClick={() => setCurrentPage("dashboard")}
        >
          {translations[locale].retourDashboard}
        </button>
      )}
      {pageContent}
    </div>
  );
}

