      alert((locale === "ar" ? "ÙØ´Ù„ Ø¬Ù„Ø¨ Ù‚Ø§Ø¦Ù…Ø© Ø§Ù„ØªØ­Ø¯ÙŠØ¯" : "Failed to fetch picker list"));
