import React, { useState } from "react";
import ClientPage from "./client";
import "./styles/dashboard.css"; // Import the CSS file that handles background animations and hover effects

// Define translations in French and Arabic
const translations = {
  fr: {
    loginTitle: "Connexion Agence de Voyage",
    usernamePlaceholder: "Nom d'utilisateur",
    passwordPlaceholder: "Mot de passe",
    loginButton: "Se connecter",
    dashboardTitle: "Tableau de bord",
    retourDashboard: "‚Üê Retour au dashboard",
    pages: [
      { label: "Clients", page: "client" },
      { label: "Booking", page: "booking" },
      { label: "Paiements", page: "payments" },
      { label: "Rapports", page: "reports" },
      { label: "Param√®tres", page: "settings" }
    ],
    loginError: "Nom d'utilisateur ou mot de passe incorrect.",
    backendError: "Erreur de connexion au backend."
  },
  ar: {
    loginTitle: "ÿ™ÿ≥ÿ¨ŸäŸÑ ÿØÿÆŸàŸÑ ŸàŸÉÿßŸÑÿ© ÿßŸÑÿ≥ŸÅÿ±",
    usernamePlaceholder: "ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ",
    passwordPlaceholder: "ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±",
    loginButton: "ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ",
    dashboardTitle: "ŸÑŸàÿ≠ÿ© ÿßŸÑŸÖÿπŸÑŸàŸÖÿßÿ™",
    retourDashboard: "‚Üê ÿßŸÑÿπŸàÿØÿ© ŸÑŸÑŸàÿ≠ÿ©",
    pages: [
      { label: "ÿßŸÑÿπŸÖŸÑÿßÿ°", page: "client" },
      { label: "ÿßŸÑÿ≠ÿ¨Ÿàÿ≤ÿßÿ™", page: "booking" },
      { label: "ÿßŸÑŸÖÿØŸÅŸàÿπÿßÿ™", page: "payments" },
      { label: "ÿßŸÑÿ™ŸÇÿßÿ±Ÿäÿ±", page: "reports" },
      { label: "ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™", page: "settings" }
    ],
    loginError: "ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿ£Ÿà ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ± ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠ÿ©.",
    backendError: "ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆŸÑŸÅŸäÿ©."
  }
};

function Tabs({ currentPage, onNavigate, locale, setLocale, pages }) {
  // Using emojis as icons for each dashboard page
  const icons = {
    client: "üë•",
    booking: "üìÖ",
    payments: "üí∞",
    reports: "üìä",
    settings: "‚öôÔ∏è"
  };
  return (
    <div className="tabs">
      {pages.map(({ label, page }) => (
        <button
          key={page}
          className={`button-hover tab ${currentPage === page ? "activeTab" : ""}`}
          onClick={() => onNavigate(page)}
        >
          <span className="icon">{icons[page]}</span> {label}
        </button>
      ))}
      <div className="languageSwitch">
        <button
          onClick={() => setLocale("fr")}
          className={`button-hover langButton ${locale === "fr" ? "activeLang" : ""}`}
        >
          FR
        </button>
        <button
          onClick={() => setLocale("ar")}
          className={`button-hover langButton ${locale === "ar" ? "activeLang" : ""}`}
        >
          AR
        </button>
      </div>
    </div>
  );
}

function Dashboard({ onNavigate, locale }) {
  return (
    <div className="dashboardContainer">
      <h2>{translations[locale].dashboardTitle}</h2>
      <div className="grid">
        {translations[locale].pages.map(({ label, page }) => (
          <button
            key={page}
            className="dashboard-button button-hover tile"
            onClick={() => onNavigate(page)}
          >
            {page === "client"
              ? "üë•"
              : page === "booking"
              ? "üìÖ"
              : page === "payments"
              ? "üí∞"
              : page === "reports"
              ? "üìä"
              : page === "settings"
              ? "‚öôÔ∏è"
              : ""}
            &nbsp;{label}
          </button>
        ))}
      </div>
    </div>
  );
}

function App() {
  const [locale, setLocale] = useState("fr");
  const [loggedIn, setLoggedIn] = useState(false);
  const [currentPage, setCurrentPage] = useState("dashboard");
  const [loginData, setLoginData] = useState({ username: "", password: "" });
  const [error, setError] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const exists = await window.pywebview.api.check_user_login({
        username: loginData.username,
        password: loginData.password
      });
      if (exists) {
        setLoggedIn(true);
        setError("");
        setCurrentPage("dashboard");
      } else {
        setError(translations[locale].loginError);
      }
    } catch (err) {
      setError(translations[locale].backendError);
    }
  };

  let pageContent;
  if (!loggedIn) {
    pageContent = (
      <div className="loginContainer">
        <div className="loginCard">
          <h2>{translations[locale].loginTitle}</h2>
          <form onSubmit={handleLogin} className="form">
            <input
              type="text"
              placeholder={translations[locale].usernamePlaceholder}
              value={loginData.username}
              onChange={(e) =>
                setLoginData({ ...loginData, username: e.target.value })
              }
              className="input"
            />
            <input
              type="password"
              placeholder={translations[locale].passwordPlaceholder}
              value={loginData.password}
              onChange={(e) =>
                setLoginData({ ...loginData, password: e.target.value })
              }
              className="input"
            />
            <button type="submit" className="button-hover button">
              {translations[locale].loginButton}
            </button>
            {error && <p className="error">{error}</p>}
          </form>
        </div>
      </div>
    );
  } else {
    switch (currentPage) {
      case "client":
        pageContent = <ClientPage locale={locale} />;
        break;
      case "booking":
        pageContent = <div className="page">üìÖ Booking Page</div>;
        break;
      case "payments":
        pageContent = <div className="page">üí∞ Payments Page</div>;
        break;
      case "reports":
        pageContent = <div className="page">üìä Reports Page</div>;
        break;
      case "settings":
        pageContent = <div className="page">‚öôÔ∏è Settings Page</div>;
        break;
      case "dashboard":
      default:
        pageContent = <Dashboard onNavigate={setCurrentPage} locale={locale} />;
        break;
    }
  }

  return (
    <div className="appContainer">
      {/* Animated background lines (from dashboard.css) */}
      <div className="dashboard-background">
        <div className="line line1"></div>
        <div className="line line2"></div>
      </div>
      {loggedIn && (
        <Tabs
          currentPage={currentPage}
          onNavigate={setCurrentPage}
          locale={locale}
          setLocale={setLocale}
          pages={translations[locale].pages}
        />
      )}
      {loggedIn && currentPage !== "dashboard" && (
        <button
          className="button-hover backButton"
          onClick={() => setCurrentPage("dashboard")}
        >
          {translations[locale].retourDashboard}
        </button>
      )}
      {pageContent}
    </div>
  );
}

export default App;
