    if (selectedIndex !== null && filteredClients[selectedIndex]) {
      const selected = filteredClients[selectedIndex];
      setModalMode("edit");
      // Load canonical credit into form for editing (fallback to unformat formatted)
      const canonicalCredit = selected.credit !== undefined && selected.credit !== null && selected.credit !== ""
