import React, { useEffect, useState } from "react";

/*
  hotels.js
  - Updated to use the same layout/controls as caisse and personel pages:
    * Floating drawer (left bottom) with Ajouter / Modifier / Supprimer buttons
    * Floating drawer (right bottom) with Impression button
    * Centered modal overlay for add/edit
    * Search + table
    * Backend integration with conventional function names
    * Locale-aware (fr/ar) and RTL support
*/

function toISODate(input) {
  if (!input) return "";
  if (input instanceof Date) {
    const yyyy = input.getFullYear();
    const mm = String(input.getMonth() + 1).padStart(2, "0");
    const dd = String(input.getDate()).padStart(2, "0");
    return `${yyyy}-${mm}-${dd}`;
  }
  if (/^\d{4}-\d{2}-\d{2}$/.test(input)) return input;
  if (/^\d{2}\/\d{2}\/\d{4}$/.test(input)) {
    const [dd, mm, yyyy] = input.split("/");
    return `${yyyy}-${mm}-${dd}`;
  }
  return input;
}

export default function HotelsPage({ locale = "fr" }) {
  const isRtl = locale === "ar";

  const [hotels, setHotels] = useState([]);
  const [loading, setLoading] = useState(false);
  const [search, setSearch] = useState("");
  const [selectedIndex, setSelectedIndex] = useState(null);

  const [showModal, setShowModal] = useState(false);
  const [modalMode, setModalMode] = useState("add"); // add | edit
  const [form, setForm] = useState({
    id: null,
    name: "",
    city: "",
    checkin: "",
    checkout: "",
    rooms: "",
    pricePerNight: "",
    statut: "Available"
  });
  const [error, setError] = useState("");

  const texts = {
    fr: {
      title: "H√¥tels",
      add: "Ajouter",
      edit: "Modifier",
      delete: "Supprimer",
      print: "Impression",
      searchPlaceholder: "Rechercher h√¥tel...",
      noItems: "Aucun h√¥tel",
      save: "Enregistrer",
      cancel: "Annuler",
      required: "Veuillez remplir tous les champs requis.",
      selectRow: "Veuillez s√©lectionner une ligne."
    },
    ar: {
      title: "ÿßŸÑŸÅŸÜÿßÿØŸÇ",
      add: "ÿ•ÿ∂ÿßŸÅÿ©",
      edit: "ÿ™ÿπÿØŸäŸÑ",
      delete: "ÿ≠ÿ∞ŸÅ",
      print: "ÿ∑ÿ®ÿßÿπÿ©",
      searchPlaceholder: "ÿ®ÿ≠ÿ´ ÿπŸÜ ŸÅŸÜÿØŸÇ...",
      noItems: "ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÅŸÜÿßÿØŸÇ",
      save: "ÿ≠ŸÅÿ∏",
      cancel: "ÿ•ŸÑÿ∫ÿßÿ°",
      required: "Ÿäÿ±ÿ¨Ÿâ ÿ™ÿπÿ®ÿ¶ÿ© ÿßŸÑÿ≠ŸÇŸàŸÑ ÿßŸÑŸÖÿ∑ŸÑŸàÿ®ÿ©.",
      selectRow: "Ÿäÿ±ÿ¨Ÿâ ÿ™ÿ≠ÿØŸäÿØ ÿ≥ÿ∑ÿ±."
    }
  }[locale];

  // --- backend ---
  async function loadHotels() {
    setLoading(true);
    try {
      const loader = window.pywebview?.api?.load_hotels || window.pywebview?.api?.loadHotels || window.pywebview?.api?.get_hotels;
      if (typeof loader === "function") {
        const res = await loader();
        setHotels(Array.isArray(res) ? res : []);
      } else {
        setHotels([]);
      }
    } catch (e) {
      console.error("loadHotels error", e);
      setHotels([]);
      alert(locale === "ar" ? "ŸÅÿ¥ŸÑ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÅŸÜÿßÿØŸÇ" : "Failed to load hotels");
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => { loadHotels(); }, [locale]);

  const filtered = hotels.filter(h => {
    const q = search.trim().toLowerCase();
    if (!q) return true;
    return (
      (h.name || "").toLowerCase().includes(q) ||
      (h.city || "").toLowerCase().includes(q)
    );
  });

  function openAdd() {
    setModalMode("add");
    const now = toISODate(new Date());
    setForm({
      id: null,
      name: "",
      city: "",
      checkin: now,
      checkout: now,
      rooms: "",
      pricePerNight: "",
      statut: "Available"
    });
    setError("");
    setShowModal(true);
  }

  function openEdit() {
    if (selectedIndex === null || filtered[selectedIndex] === undefined) {
      alert(texts.selectRow);
      return;
    }
    const h = filtered[selectedIndex];
    setModalMode("edit");
    setForm({
      id: h.id ?? null,
      name: h.name || "",
      city: h.city || "",
      checkin: h.checkin ? toISODate(h.checkin) : "",
      checkout: h.checkout ? toISODate(h.checkout) : "",
      rooms: h.rooms !== undefined ? String(h.rooms) : "",
      pricePerNight: h.pricePerNight !== undefined ? String(h.pricePerNight) : "",
      statut: h.statut || "Available"
    });
    setError("");
    setShowModal(true);
  }

  async function handleDelete() {
    if (selectedIndex === null || filtered[selectedIndex] === undefined) {
      alert(texts.selectRow);
      return;
    }
    const h = filtered[selectedIndex];
    if (!window.confirm((locale === "ar" ? "ÿ≠ÿ∞ŸÅ ÿßŸÑŸÅŸÜÿØŸÇ " : "Delete hotel ") + (h.name || "") + " ?")) return;
    try {
      const fn = window.pywebview?.api?.delete_hotel || window.pywebview?.api?.deleteHotel;
      if (typeof fn === "function") {
        await fn(h.id);
        await loadHotels();
        setSelectedIndex(null);
      } else {
        setHotels(prev => prev.filter(x => x.id !== h.id));
        setSelectedIndex(null);
        alert(locale === "ar" ? "ÿ™ŸÖ ÿßŸÑÿ≠ÿ∞ŸÅ ŸÖÿ≠ŸÑŸäŸãÿß." : "Deleted locally (no backend).");
      }
    } catch (e) {
      console.error("delete hotel failed", e);
      alert(locale === "ar" ? "ŸÅÿ¥ŸÑ ÿßŸÑÿ≠ÿ∞ŸÅ" : "Delete failed");
    }
  }

  async function handleSave(e) {
    e?.preventDefault?.();
    setError("");
    if (!form.name || !form.city || form.rooms === "" || form.pricePerNight === "") {
      setError(texts.required);
      return;
    }

    const payload = {
      name: form.name,
      city: form.city,
      checkin: toISODate(form.checkin),
      checkout: toISODate(form.checkout),
      rooms: Number(form.rooms),
      pricePerNight: Number(form.pricePerNight),
      statut: form.statut
    };

    try {
      if (modalMode === "add") {
        const fn = window.pywebview?.api?.add_hotel || window.pywebview?.api?.addHotel;
        if (typeof fn === "function") {
          await fn(payload);
          await loadHotels();
        } else {
          const tmpId = Date.now();
          setHotels(prev => [{ ...payload, id: tmpId }, ...prev]);
        }
      } else {
        if (!form.id) {
          setError("Missing id");
          return;
        }
        const fn = window.pywebview?.api?.update_hotel || window.pywebview?.api?.updateHotel;
        if (typeof fn === "function") {
          await fn(form.id, payload);
          await loadHotels();
        } else {
          setHotels(prev => prev.map(x => (x.id === form.id ? { ...x, ...payload } : x)));
        }
      }
      setShowModal(false);
    } catch (err) {
      console.error("save hotel error", err);
      setError(locale === "ar" ? "ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿ≠ŸÅÿ∏" : "Save error");
    }
  }

  function handlePrint() { window.print(); }

  return (
    <div style={{ padding: 20, ...(isRtl ? { direction: "rtl" } : { direction: "ltr" }) }}>
      <h2 style={{ textAlign: "center" }}>{texts.title}</h2>

      <div style={{ display: "flex", gap: 12, marginBottom: 12, alignItems: "center" }}>
        <div style={{ flex: 1, maxWidth: 420, display: "flex", alignItems: "center", gap: 8 }}>
          <span>üîç</span>
          <input placeholder={texts.searchPlaceholder} value={search} onChange={e => { setSearch(e.target.value); setSelectedIndex(null); }} style={{ width: "100%", padding: 8, borderRadius: 6, border: "1px solid #ccc" }} />
        </div>

        <div style={{ marginLeft: "auto", display: "flex", gap: 8 }}>
          <div style={styles.summaryBadge}>{locale === "ar" ? "ÿπÿØÿØ ÿßŸÑŸÅŸÜÿßÿØŸÇ" : "Hotels"}: <strong style={{ marginLeft: 6 }}>{filtered.length}</strong></div>
        </div>
      </div>

      <div style={styles.tableWrap}>
        <table style={styles.table}>
          <thead>
            <tr style={styles.tableHeaderRow}>
              <th style={{ ...styles.tableHeader, width: 36 }}>#</th>
              <th style={styles.tableHeader}>{locale === "ar" ? "ÿßŸÑŸÅŸÜÿØŸÇ" : "H√¥tel"}</th>
              <th style={styles.tableHeader}>{locale === "ar" ? "ÿßŸÑŸÖÿØŸäŸÜÿ©" : "Ville"}</th>
              <th style={styles.tableHeader}>{locale === "ar" ? "ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑŸàÿµŸàŸÑ" : "Check-in"}</th>
              <th style={styles.tableHeader}>{locale === "ar" ? "ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑŸÖÿ∫ÿßÿØÿ±ÿ©" : "Check-out"}</th>
              <th style={styles.tableHeader}>{locale === "ar" ? "ÿßŸÑÿ∫ÿ±ŸÅ" : "Chambres"}</th>
              <th style={styles.tableHeader}>{locale === "ar" ? "ÿßŸÑÿ≥ÿπÿ±/ŸÑŸäŸÑÿ©" : "Prix/nuit"}</th>
              <th style={styles.tableHeader}>{locale === "ar" ? "ÿßŸÑÿ≠ÿßŸÑÿ©" : "Statut"}</th>
            </tr>
          </thead>
          <tbody>
            {loading ? (
              <tr><td colSpan={8} style={{ padding: 20, textAlign: "center" }}>{locale === "ar" ? "ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ..." : "Loading..."}</td></tr>
            ) : filtered.length === 0 ? (
              <tr><td colSpan={8} style={{ padding: 20, textAlign: "center" }}>{texts.noItems}</td></tr>
            ) : (
              filtered.map((h, i) => (
                <tr key={h.id ?? i} onClick={() => setSelectedIndex(i)} style={{ background: i === selectedIndex ? "#cce5ff" : (i % 2 === 0 ? "#fff" : "#f4f8ff"), cursor: "pointer" }}>
                  <td style={styles.tableCell}>{i + 1}</td>
                  <td style={styles.tableCell}>{h.name}</td>
                  <td style={styles.tableCell}>{h.city}</td>
                  <td style={styles.tableCell}>{h.checkin}</td>
                  <td style={styles.tableCell}>{h.checkout}</td>
                  <td style={styles.tableCell}>{h.rooms}</td>
                  <td style={styles.tableCell}>{h.pricePerNight}</td>
                  <td style={styles.tableCell}>{h.statut}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      {/* Left drawer (add/edit/delete) */}
      <div style={{ ...styles.drawer, left: 20, right: "unset", bottom: 28 }}>
        <button style={styles.drawerButton} onClick={openAdd}>{texts.add}</button>
        <button style={styles.drawerButton} onClick={openEdit}>{texts.edit}</button>
        <button style={styles.drawerButton} onClick={handleDelete}>{texts.delete}</button>
      </div>

      {/* Right drawer (print) */}
      <div style={{ ...styles.drawer, right: 20, left: "unset", bottom: 28 }}>
        <button style={{ ...styles.drawerButton, background: "#3477eb", color: "#fff" }} onClick={handlePrint}>{texts.print}</button>
      </div>

      {/* Modal */}
      {showModal && (
        <div style={styles.modalOverlay}>
          <div style={{ ...styles.modal, ...(isRtl ? { direction: "rtl", textAlign: "right" } : {}) }}>
            <h3>{modalMode === "add" ? texts.add : texts.edit}</h3>
            <form onSubmit={handleSave} style={styles.form}>
              <div style={styles.row}>
                <label style={styles.label}>
                  H√¥tel
                  <input value={form.name} onChange={e => setForm(s => ({ ...s, name: e.target.value }))} style={styles.input} />
                </label>
                <label style={styles.label}>
                  Ville
                  <input value={form.city} onChange={e => setForm(s => ({ ...s, city: e.target.value }))} style={styles.input} />
                </label>
              </div>

              <div style={styles.row}>
                <label style={styles.label}>
                  Check-in
                  <input type="date" value={form.checkin} onChange={e => setForm(s => ({ ...s, checkin: e.target.value }))} style={styles.input} />
                </label>
                <label style={styles.label}>
                  Check-out
                  <input type="date" value={form.checkout} onChange={e => setForm(s => ({ ...s, checkout: e.target.value }))} style={styles.input} />
                </label>
              </div>

              <div style={styles.row}>
                <label style={styles.label}>
                  Chambres
                  <input value={form.rooms} onChange={e => setForm(s => ({ ...s, rooms: e.target.value }))} style={styles.input} type="number" />
                </label>
                <label style={styles.label}>
                  Prix / nuit
                  <input value={form.pricePerNight} onChange={e => setForm(s => ({ ...s, pricePerNight: e.target.value }))} style={styles.input} type="number" step="0.01" />
                </label>
              </div>

              <label style={styles.label}>
                Statut
                <select value={form.statut} onChange={e => setForm(s => ({ ...s, statut: e.target.value }))} style={styles.input}>
                  <option>Available</option>
                  <option>Full</option>
                  <option>Maintenance</option>
                </select>
              </label>

              {error && <div style={{ color: "red" }}>{error}</div>}

              <div style={{ display: "flex", justifyContent: "flex-end", gap: 8, marginTop: 12 }}>
                <button type="submit" style={styles.saveButton}>{texts.save}</button>
                <button type="button" style={styles.cancelButton} onClick={() => setShowModal(false)}>{texts.cancel}</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

const styles = {
  tableWrap: {
    maxHeight: "60vh",
    overflowY: "auto",
    border: "1px solid #e0e0e0",
    borderRadius: 6,
    background: "#fff"
  },
  table: { width: "100%", borderCollapse: "separate", tableLayout: "fixed", borderSpacing: 0 },
  tableHeaderRow: { background: "#f1f6ff" },
  tableHeader: { textAlign: "left", padding: "0.6rem", borderBottom: "1px solid #e2e8f0", fontWeight: 700 },
  tableCell: { padding: "0.45rem 0.6rem", borderBottom: "1px solid #f0f4fb", verticalAlign: "middle", wordBreak: "break-word" },

  drawer: {
    position: "fixed",
    background: "transparent",
    padding: "1rem",
    borderRadius: "8px",
    boxShadow: "0 2px 8px rgba(0,0,0,0.15)",
    display: "flex",
    gap: "0.5rem",
    zIndex: 8,
    alignItems: "center"
  },
  drawerButton: {
    display: "flex",
    alignItems: "center",
    gap: "0.3rem",
    padding: "0.5rem 1rem",
    fontSize: "1rem",
    color: "#3477eb",
    background: "#eaf3ff",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
    fontWeight: 700
  },

  summaryBadge: {
    padding: "0.4rem 0.6rem",
    background: "rgba(255,255,255,0.9)",
    borderRadius: 8,
    border: "1px solid #e6eefb",
    display: "flex",
    alignItems: "center",
    gap: 8,
    fontWeight: 700,
    color: "#14355f"
  },

  modalOverlay: { position: "fixed", inset: 0, background: "rgba(0,0,0,0.45)", display: "flex", alignItems: "center", justifyContent: "center", zIndex: 999 },
  modal: { background: "#fff", padding: 20, borderRadius: 10, width: "92%", maxWidth: 820, boxShadow: "0 8px 30px rgba(0,0,0,0.2)" },
  form: { display: "flex", flexDirection: "column", gap: 12 },
  row: { display: "flex", gap: 12 },
  label: { flex: 1, display: "flex", flexDirection: "column", fontSize: 14 },
  input: { marginTop: 6, padding: "0.5rem", borderRadius: 6, border: "1px solid #ccc" },

  saveButton: { padding: "0.55rem 1rem", background: "#3477eb", color: "#fff", border: "none", borderRadius: 6, cursor: "pointer", fontWeight: 700 },
  cancelButton: { padding: "0.45rem 0.9rem", background: "#f1f4f8", color: "#333", border: "none", borderRadius: 6, cursor: "pointer", fontWeight: 700 }
};
