    : styles.table;

  const fetchClients = async () => {
    setIsLoading(true);
    try {
      const result = await window.pywebview.api.get_clients();
      setClients(result || []);
    } catch (err) {
      setClients([]);
    }
    setIsLoading(false);
  };

  useEffect(() => {
    fetchClients();
    // eslint-disable-next-line
