import React, { useEffect, useState } from "react";

/*
  vols.js

  Reworked to MATCH caisse.js structure and style:
  - "Ajouter", "Modifier", "Supprimer" buttons go in a floating drawer, with icons (SVG, as in caisse)
  - Print (Impression) button on right floating drawer, blue when active
  - Search bar and results count styled like caisse
  - Table styled like caisse, including row selection
  - Modal add/edit overlays
  - RTL/fr/ar localization maintained
  - Backend integration/fallback preserved
*/

function formatNumberSpaceDot(value) {
  if (value === null || value === undefined || value === "") return "";
  const cleaned = String(value).replace(/\s+/g, "").replace(/,/g, ".");
  const n = Number(cleaned);
  if (!Number.isFinite(n)) return String(value);
  const parts = n.toFixed(2).split(".");
  parts[0] = parts[0].replace(/\B(?=(\d{3})+(?!\d))/g, " ");
  return parts.join(".");
}
function formatDateToDisplay(iso) {
  if (!iso) return "";
  const m = iso.match(/^(\d{4})-(\d{2})-(\d{2})/);
  if (m) return `${m[3]}/${m[2]}/${m[1]}`;
  return iso;
}
function toISODate(input) {
  if (!input) return "";
  if (input instanceof Date) {
    const yyyy = input.getFullYear();
    const mm = String(input.getMonth() + 1).padStart(2, "0");
    const dd = String(input.getDate()).padStart(2, "0");
    return `${yyyy}-${mm}-${dd}`;
  }
  if (/^\d{2}\/\d{2}\/\d{4}$/.test(input)) {
    const [dd, mm, yyyy] = input.split("/");
    return `${yyyy}-${mm}-${dd}`;
  }
  if (/^\d{4}-\d{2}-\d{2}$/.test(input)) return input;
  return input;
}

export default function VolsPage({ locale = "fr" }) {
  const isRtl = locale === "ar";
  const [vols, setVols] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedIndex, setSelectedIndex] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [modalMode, setModalMode] = useState("add"); // add | edit
  const [form, setForm] = useState({
    id: null,
    volNumber: "",
    dateDepart: "",
    origine: "",
    destination: "",
    places: "",
    prix: "",
    statut: "Scheduled"
  });
  const [error, setError] = useState("");

  const TABLE_FIELDS = ["volNumber", "dateDepart", "origine", "destination", "places", "prix", "statut"];
  const headersFr = ["#", "Vol #", "Date d√©part", "Origine", "Destination", "Places", "Prix", "Statut"];
  const headersAr = ["#", "ÿ±ŸÇŸÖ ÿßŸÑÿ±ÿ≠ŸÑÿ©", "ÿ™ÿßÿ±ŸäÿÆ ÿßŸÑŸÖÿ∫ÿßÿØÿ±ÿ©", "ÿßŸÑŸÖŸÜÿ®ÿπ", "ÿßŸÑŸàÿ¨Ÿáÿ©", "ÿßŸÑÿ£ŸÖÿßŸÉŸÜ", "ÿßŸÑÿ≥ÿπÿ±", "ÿßŸÑÿ≠ÿßŸÑÿ©"];
  const headers = locale === "ar" ? headersAr : headersFr;

  const texts = {
    fr: {
      title: "Vols",
      add: "Ajouter",
      edit: "Modifier",
      delete: "Supprimer",
      print: "Impression",
      searchPlaceholder: "Rechercher...",
      noItems: "Aucun vol",
      save: "Enregistrer",
      cancel: "Annuler",
      selectRow: "Veuillez s√©lectionner une ligne.",
      count: "Vols"
    },
    ar: {
      title: "ÿßŸÑÿ±ÿ≠ŸÑÿßÿ™",
      add: "ÿ•ÿ∂ÿßŸÅÿ©",
      edit: "ÿ™ÿπÿØŸäŸÑ",
      delete: "ÿ≠ÿ∞ŸÅ",
      print: "ÿ∑ÿ®ÿßÿπÿ©",
      searchPlaceholder: "ÿ®ÿ≠ÿ´...",
      noItems: "ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ±ÿ≠ŸÑÿßÿ™",
      save: "ÿ≠ŸÅÿ∏",
      cancel: "ÿ•ŸÑÿ∫ÿßÿ°",
      selectRow: "Ÿäÿ±ÿ¨Ÿâ ÿ™ÿ≠ÿØŸäÿØ ÿ≥ÿ∑ÿ±.",
      count: "ÿπÿØÿØ ÿßŸÑÿ±ÿ≠ŸÑÿßÿ™"
    }
  }[locale];

  // --- Backend integration ---
  async function fetchVols() {
    setLoading(true);
    try {
      const loader = window.pywebview?.api?.load_vols || window.pywebview?.api?.loadVols || window.pywebview?.api?.get_vols;
      if (typeof loader === "function") {
        const res = await loader();
        setVols(Array.isArray(res) ? res : []);
      } else {
        setVols([]);
      }
    } catch (e) {
      console.error("Failed to load vols", e);
      setVols([]);
      alert(locale === "ar" ? "ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ±ÿ≠ŸÑÿßÿ™" : "Failed to load flights");
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => { fetchVols(); }, [locale]);

  const filtered = vols.filter(v => {
    const q = searchQuery.trim().toLowerCase();
    if (!q) return true;
    return (
      String(v.volNumber || "").toLowerCase().includes(q) ||
      String(v.origine || "").toLowerCase().includes(q) ||
      String(v.destination || "").toLowerCase().includes(q)
    );
  });

  function openAdd() {
    setModalMode("add");
    const now = toISODate(new Date());
    setForm({
      id: null,
      volNumber: "",
      dateDepart: now,
      origine: "",
      destination: "",
      places: "",
      prix: "",
      statut: "Scheduled"
    });
    setError("");
    setShowModal(true);
  }

  function openEdit() {
    if (selectedIndex === null || filtered[selectedIndex] === undefined) {
      alert(texts.selectRow);
      return;
    }
    const v = filtered[selectedIndex];
    setModalMode("edit");
    setForm({
      id: v.id ?? null,
      volNumber: v.volNumber || "",
      dateDepart: v.dateDepart ? toISODate(v.dateDepart) : "",
      origine: v.origine || "",
      destination: v.destination || "",
      places: v.places !== undefined ? String(v.places) : "",
      prix: v.prix !== undefined ? String(v.prix) : "",
      statut: v.statut || "Scheduled"
    });
    setError("");
    setShowModal(true);
  }

  async function handleDelete() {
    if (selectedIndex === null || filtered[selectedIndex] === undefined) {
      alert(texts.selectRow);
      return;
    }
    const v = filtered[selectedIndex];
    if (!window.confirm((locale === "ar" ? "ÿ≠ÿ∞ŸÅ ÿßŸÑÿ±ÿ≠ŸÑÿ© " : "Delete flight ") + (v.volNumber || "") + " ?")) return;
    try {
      const fn = window.pywebview?.api?.delete_vol || window.pywebview?.api?.deleteVol;
      if (typeof fn === "function") {
        await fn(v.id);
        await fetchVols();
        setSelectedIndex(null);
      } else {
        setVols(prev => prev.filter(x => x.id !== v.id));
        setSelectedIndex(null);
        alert(locale === "ar" ? "ÿ™ŸÖ ÿßŸÑÿ≠ÿ∞ŸÅ ŸÖÿ≠ŸÑŸäŸãÿß." : "Deleted locally (no backend).");
      }
    } catch (e) {
      console.error("delete vol failed", e);
      alert(locale === "ar" ? "ŸÅÿ¥ŸÑ ÿßŸÑÿ≠ÿ∞ŸÅ" : "Delete failed");
    }
  }

  async function handleSave(e) {
    e?.preventDefault?.();
    setError("");
    if (!form.volNumber || !form.dateDepart || !form.origine || !form.destination || form.places === "" || form.prix === "") {
      setError(locale === "ar" ? "Ÿäÿ±ÿ¨Ÿâ ÿ™ÿπÿ®ÿ¶ÿ© ÿßŸÑÿ≠ŸÇŸàŸÑ ÿßŸÑŸÖÿ∑ŸÑŸàÿ®ÿ©." : "Please fill required fields.");
      return;
    }

    const payload = {
      volNumber: form.volNumber,
      dateDepart: toISODate(form.dateDepart),
      origine: form.origine,
      destination: form.destination,
      places: Number(form.places),
      prix: Number(form.prix),
      statut: form.statut
    };

    try {
      if (modalMode === "add") {
        const fn = window.pywebview?.api?.add_vol || window.pywebview?.api?.addVol;
        if (typeof fn === "function") {
          await fn(payload);
          await fetchVols();
        } else {
          const tmpId = Date.now();
          setVols(prev => [{ ...payload, id: tmpId }, ...prev]);
        }
      } else {
        if (!form.id) {
          setError("Missing id");
          return;
        }
        const fn = window.pywebview?.api?.update_vol || window.pywebview?.api?.updateVol;
        if (typeof fn === "function") {
          await fn(form.id, payload);
          await fetchVols();
        } else {
          setVols(prev => prev.map(x => (x.id === form.id ? { ...x, ...payload } : x)));
        }
      }
      setShowModal(false);
    } catch (err) {
      console.error("save vol error", err);
      setError(locale === "ar" ? "ŸÅÿ¥ŸÑ ÿßŸÑÿ≠ŸÅÿ∏" : "Save error");
    }
  }

  function handlePrint() { window.print(); }

  function renderCell(row, field, idx) {
    const isLast = idx === TABLE_FIELDS.length - 1;
    const baseStyle = { ...styles.tableCell, borderRight: isLast ? "none" : "1px solid #e6eefb" };
    if (field === "dateDepart") return <td key={field} style={baseStyle}>{formatDateToDisplay(row[field]) || "-"}</td>;
    if (field === "prix" || field === "places") return <td key={field} style={baseStyle}>{formatNumberSpaceDot(row[field]) || "-"}</td>;
    return <td key={field} style={baseStyle}>{row[field] ?? "-"}</td>;
  }

  return (
    <div style={{ ...styles.container, ...(isRtl ? { direction: "rtl" } : { direction: "ltr" }) }}>
      <h2 style={{ textAlign: "center" }}>{texts.title}</h2>

      {/* Search bar and count */}
      <div style={{ display: "flex", gap: 12, marginBottom: 12, alignItems: "center" }}>
        <div style={{ ...styles.searchWrap, maxWidth: 420 }}>
          <span style={{ marginRight: 8 }}>üîç</span>
          <input
            placeholder={texts.searchPlaceholder}
            value={searchQuery}
            onChange={e => { setSearchQuery(e.target.value); setSelectedIndex(null); }}
            style={styles.searchInput}
          />
        </div>
        <div style={{ marginLeft: "auto", display: "flex", gap: 8, alignItems: "center" }}>
          <div style={styles.summaryBadge}>{texts.count}: <strong style={{ marginLeft: 6 }}>{filtered.length}</strong></div>
        </div>
      </div>

      <div style={styles.tableWrap}>
        <table style={styles.table}>
          <thead>
            <tr style={styles.tableHeaderRow}>
              <th style={{ ...styles.tableHeader, borderRight: "1px solid #e6eefb", width: 36 }}>#</th>
              {TABLE_FIELDS.map((f, idx) => <th key={f} style={{ ...styles.tableHeader, borderRight: idx === TABLE_FIELDS.length - 1 ? "none" : "1px solid #e6eefb" }}>{headers[idx + 1]}</th>)}
            </tr>
          </thead>
          <tbody>
            {loading ? <tr><td colSpan={1 + TABLE_FIELDS.length} style={{ padding: 20, textAlign: "center" }}>{texts.loading}</td></tr>
              : filtered.length === 0 ? <tr><td colSpan={1 + TABLE_FIELDS.length} style={{ padding: 20, textAlign: "center" }}>{texts.noItems}</td></tr>
              : filtered.map((row, i) => (
                <tr key={row.id ?? i} onClick={() => setSelectedIndex(i)} style={{ ...styles.tableRow, background: i === selectedIndex ? "#cce5ff" : i % 2 === 0 ? "#fff" : "#f4f8ff", cursor: "pointer" }}>
                  <td style={{ ...styles.tableCell, width: 36 }}>{i + 1}</td>
                  {TABLE_FIELDS.map((f, idx) => renderCell(row, f, idx))}
                </tr>
              ))
            }
          </tbody>
        </table>
      </div>

      {/* Left drawer (add/edit/delete) with icons */}
      <div style={{ ...styles.drawer, left: 20, right: "unset", bottom: 28 }}>
        <button style={styles.drawerButton} onClick={openAdd}>
          <span style={styles.iconSmall}><svg width="18" height="18" fill="currentColor" viewBox="0 0 24 24" aria-hidden="true"><path d="M19 11H13V5a1 1 0 0 0-2 0v6H5a1 1 0 0 0 0 2h6v6a1 1 0 0 0 2 0v-6h6a1 1 0 0 0 0-2z" /></svg></span>
          {texts.add}
        </button>
        <button style={styles.drawerButton} onClick={openEdit}>
          <span style={styles.iconSmall}><svg width="18" height="18" fill="currentColor" viewBox="0 0 24 24" aria-hidden="true"><path d="M3 17.25V21h3.75L17.81 9.94l-3.75-3.75L3 17.25zM20.71 7.04a1 1 0 0 0 0-1.41l-2.34-2.34a1 1 0 0 0-1.41 0l-1.83 1.83 3.75 3.75 1.83-1.83z"/></svg></span>
          {texts.edit}
        </button>
        <button style={styles.drawerButton} onClick={handleDelete}>
          <span style={styles.iconSmall}><svg width="18" height="18" fill="currentColor" viewBox="0 0 24 24" aria-hidden="true"><path d="M6 7h12v13a2 2 0 0 1-2 2H8a2 2 0 0 1-2-2V7zm3-4h6l1 1h4v2H2V4h4l1-1z"/></svg></span>
          {texts.delete}
        </button>
      </div>

      {/* Right drawer (print) */}
      <div style={{ ...styles.drawer, right: 20, left: "unset", bottom: 28 }}>
        <button style={{ ...styles.drawerButton, background: "#3477eb", color: "#fff" }} onClick={handlePrint}>{texts.print}</button>
      </div>

      {/* Add/Edit Modal */}
      {showModal && (
        <div style={styles.modalOverlay}>
          <div style={{ ...styles.modal, ...(isRtl ? { direction: "rtl", textAlign: "right" } : {}) }}>
            <h3>{modalMode === "add" ? texts.add : texts.edit}</h3>
            <form onSubmit={handleSave} style={styles.form}>
              <div style={styles.row}>
                <label style={styles.label}>
                  Vol #
                  <input value={form.volNumber} onChange={e => setForm(s => ({ ...s, volNumber: e.target.value }))} style={styles.input} />
                </label>
                <label style={styles.label}>
                  {locale === "ar" ? "ÿ™ÿßÿ±ŸäÿÆ ÿßŸÑŸÖÿ∫ÿßÿØÿ±ÿ©" : "Date d√©part"}
                  <input type="date" value={form.dateDepart} onChange={e => setForm(s => ({ ...s, dateDepart: e.target.value }))} style={styles.input} />
                </label>
              </div>
              <div style={styles.row}>
                <label style={styles.label}>
                  {locale === "ar" ? "ÿßŸÑŸÖŸÜÿ®ÿπ" : "Origine"}
                  <input value={form.origine} onChange={e => setForm(s => ({ ...s, origine: e.target.value }))} style={styles.input} />
                </label>
                <label style={styles.label}>
                  {locale === "ar" ? "ÿßŸÑŸàÿ¨Ÿáÿ©" : "Destination"}
                  <input value={form.destination} onChange={e => setForm(s => ({ ...s, destination: e.target.value }))} style={styles.input} />
                </label>
              </div>
              <div style={styles.row}>
                <label style={styles.label}>
                  {locale === "ar" ? "ÿßŸÑÿ£ŸÖÿßŸÉŸÜ" : "Places"}
                  <input value={form.places} onChange={e => setForm(s => ({ ...s, places: e.target.value }))} style={styles.input} type="number" />
                </label>
                <label style={styles.label}>
                  {locale === "ar" ? "ÿßŸÑÿ≥ÿπÿ±" : "Prix"}
                  <input value={form.prix} onChange={e => setForm(s => ({ ...s, prix: e.target.value }))} style={styles.input} type="number" step="0.01" />
                </label>
              </div>
              <label style={styles.label}>
                {locale === "ar" ? "ÿßŸÑÿ≠ÿßŸÑÿ©" : "Statut"}
                <select value={form.statut} onChange={e => setForm(s => ({ ...s, statut: e.target.value }))} style={styles.input}>
                  <option>Scheduled</option>
                  <option>Delayed</option>
                  <option>Cancelled</option>
                  <option>Departed</option>
                </select>
              </label>
              {error && <div style={{ color: "red" }}>{error}</div>}
              <div style={{ display: "flex", justifyContent: "flex-end", gap: 8, marginTop: 12 }}>
                <button type="submit" style={styles.saveButton}>{texts.save}</button>
                <button type="button" style={styles.cancelButton} onClick={() => setShowModal(false)}>{texts.cancel}</button>
              </div>
            </form>
          </div>
        </div>
      )}

    </div>
  );
}

const styles = {
  container: { padding: "1.25rem", position: "relative", minHeight: "100vh", boxSizing: "border-box" },
  searchWrap: { flex: 1, maxWidth: 420, display: "flex", alignItems: "center", gap: 8 },
  searchInput: { width: "100%", padding: "0.5rem", borderRadius: 6, border: "1px solid #ccc", fontSize: "1rem" },
  tableWrap: { maxHeight: "60vh", overflowY: "auto", border: "1px solid #e0e0e0", borderRadius: 6, background: "#fff" },
  table: { width: "100%", borderCollapse: "separate", tableLayout: "fixed", borderSpacing: 0 },
  tableHeaderRow: { background: "#f1f6ff" },
  tableHeader: { textAlign: "center", padding: "0.6rem", borderBottom: "1px solid #e2e8f0", background: "#f1f6ff", fontWeight: 700 },
  tableRow: { cursor: "pointer" },
  tableCell: { padding: "0.45rem 0.6rem", borderBottom: "1px solid #f0f4fb", verticalAlign: "middle", wordBreak: "break-word" },

  drawer: { position: "fixed", background: "transparent", padding: "1rem", borderRadius: "8px", boxShadow: "0 2px 8px rgba(0,0,0,0.15)", display: "flex", flexDirection: "row", gap: "0.5rem", zIndex: 8, minWidth: 0, width: "auto", alignItems: "center" },
  drawerButton: { display: "flex", alignItems: "center", gap: "0.3rem", padding: "0.5rem 1rem", fontSize: "1rem", color: "#3477eb", background: "#eaf3ff", border: "none", borderRadius: "4px", cursor: "pointer", fontWeight: 700 },
  iconSmall: { width: 18, height: 18, display: "inline-flex", alignItems: "center", marginRight: 8 },
  summaryBadge: { padding: "0.4rem 0.6rem", background: "rgba(255,255,255,0.9)", borderRadius: 8, border: "1px solid #e6eefb", display: "flex", alignItems: "center", gap: 8, fontWeight: 700, color: "#14355f" },

  modalOverlay: { position: "fixed", inset: 0, background: "rgba(0,0,0,0.45)", display: "flex", alignItems: "center", justifyContent: "center", zIndex: 999 },
  modal: { background: "#fff", padding: 20, borderRadius: 10, width: "92%", maxWidth: 820, boxShadow: "0 8px 30px rgba(0,0,0,0.2)" },
  form: { display: "flex", flexDirection: "column", gap: 12 },
  row: { display: "flex", gap: 12 },
  label: { flex: 1, display: "flex", flexDirection: "column", fontSize: 14, color: "#333" },
  input: { marginTop: 6, padding: "0.5rem", borderRadius: 6, border: "1px solid #ccc", fontSize: 14 },
  saveButton: { padding: "0.55rem 1rem", background: "#3477eb", color: "#fff", border: "none", borderRadius: 6, cursor: "pointer", fontWeight: 700 },
  cancelButton: { padding: "0.45rem 0.9rem", background: "#f1f4f8", color: "#333", border: "none", borderRadius: 6, cursor: "pointer", fontWeight: 700 }
};
