,{width:"88%",maxWidth:860},isRtl?{direction:"rtl",textAlign:"right"}:{}),children:[/*#__PURE__*/_jsx("h3",{children:pickerType==="client"?locale==="ar"?"Ø§Ø®ØªØ± Ø§Ù„Ø¹Ù…ÙŠÙ„":"Select client":locale==="ar"?"Ø§Ø®ØªØ± Ø§Ù„Ù…ÙˆØ¸Ù":"Select personnel"}),/*#__PURE__*/_jsx("div",{style:{marginTop:8,marginBottom:8},children:/*#__PURE__*/_jsx("input",{placeholder:locale==="ar"?"Ø¨Ø­Ø«...":"Search...",value:pickerSearch,onChange:e=>setPickerSearch(e.target.value),style:styles.searchInput})}),/*#__PURE__*/_jsxs("div",{style:{maxHeight:360,overflowY:"auto",border:"1px solid #eef3fb",borderRadius:6},children:[pickerList.filter(item=>{const q=pickerSearch.trim().toLowerCase();if(!q)return true;const name="".concat(item.nom||""," ").concat(item.prenom||"").toLowerCase();const passport=(item.passport_number||item.passport||"").toLowerCase();const telephone=(item.telephone||"").toLowerCase();const credit=String(item.credit||item.credit_formatted||"");return name.includes(q)||passport.includes(q)||telephone.includes(q)||credit.includes(q);}).map(item=>/*#__PURE__*/_jsxs("div",{style:{padding:10,borderBottom:"1px solid #f2f6ff",display:"flex",justifyContent:"space-between",alignItems:"center"},children:[/*#__PURE__*/_jsxs("div",{children:[/*#__PURE__*/_jsx("div",{style:{fontWeight:700},children:(item.nom||"")+(item.prenom?" "+item.prenom:"")}),pickerType==="client"?/*#__PURE__*/_jsxs("div",{style:{fontSize:12,color:"#666"},children:[item.passport_number||item.passport||""," \u2022 ",formatNumberSpaceDot(item.credit||item.credit_formatted||"")]}):/*#__PURE__*/_jsxs("div",{style:{fontSize:12,color:"#666"},children:[item.poste||""," \u2022 ",item.telephone||""]})]}),/*#__PURE__*/_jsx("div",{children:/*#__PURE__*/_jsx("button",{style:styles.drawerButton,onClick:()=>pickItem(item),children:locale==="ar"?"Ø§Ø®ØªÙŠØ§Ø±":"Choose"})})]},item.id)),pickerList.length===0&&/*#__PURE__*/_jsx("div",{style:{padding:20,textAlign:"center",color:"#666"},children:locale==="ar"?"Ù„Ø§ ØªÙˆØ¬Ø¯ Ø¨ÙŠØ§Ù†Ø§Øª":"No items found"})]}),/*#__PURE__*/_jsx("div",{style:{display:"flex",justifyContent:"flex-end",marginTop:12},children:/*#__PURE__*/_jsx("button",{style:styles.cancelSmall,onClick:()=>setShowPicker(false),children:locale==="ar"?"Ø¥Ù„ØºØ§Ø¡":"Annuler"})})]})})]});}const styles={container:{padding:"1.25rem",position:"relative",minHeight:"100vh",boxSizing:"border-box"},searchWrap:{flex:1,maxWidth:420,display:"flex",alignItems:"center",gap:8},searchInput:{width:"100%",padding:"0.5rem",borderRadius:6,border:"1px solid #ccc",fontSize:"1rem"},tableWrap:{maxHeight:"60vh",overflowY:"auto",border:"1px solid #e0e0e0",borderRadius:6,background:"#fff"},table:{width:"100%",borderCollapse:"separate",tableLayout:"fixed",borderSpacing:0},tableHeaderRow:{background:"#f1f6ff"},tableHeader:{textAlign:"center",padding:"0.6rem",borderBottom:"1px solid #e2e8f0",background:"#f1f6ff",fontWeight:700},tableRow:{cursor:"pointer"},tableCell:{padding:"0.45rem 0.6rem",borderBottom:"1px solid #f0f4fb",verticalAlign:"middle",wordBreak:"break-word"},drawer:{position:"fixed",background:"transparent",padding:"1rem",borderRadius:"8px",boxShadow:"0 2px 8px rgba(0,0,0,0.15)",display:"flex",flexDirection:"row",gap:"0.5rem",zIndex:8,minWidth:0,width:"auto",alignItems:"center"},drawerButton:{display:"flex",alignItems:"center",gap:"0.3rem",padding:"0.5rem 1rem",fontSize:"1rem",color:"#3477eb",background:"#eaf3ff",border:"none",borderRadius:"4px",cursor:"pointer"},summaryBadge:{padding:"0.4rem 0.6rem",background:"rgba(255,255,255,0.9)",borderRadius:8,border:"1px solid #e6eefb",display:"flex",alignItems:"center",gap:8,fontWeight:700,color:"#14355f"},modalOverlay:{position:"fixed",inset:0,background:"rgba(0,0,0,0.45)",display:"flex",alignItems:"center",justifyContent:"center",zIndex:999},modal:{background:"#fff",padding:20,borderRadius:10,width:"92%",maxWidth:820,boxShadow:"0 8px 30px rgba(0,0,0,0.2)"},form:{display:"flex",flexDirection:"column",gap:12},row:{display:"flex",gap:12},label:{flex:1,display:"flex",flexDirection:"column",fontSize:14,color:"#333"},input:{marginTop:6,padding:"0.5rem",borderRadius:6,border:"1px solid #ccc",fontSize:14},saveButton:{padding:"0.55rem 1rem",background:"#3477eb",color:"#fff",border:"none",borderRadius:6,cursor:"pointer",fontWeight:700},cancelButton:{padding:"0.45rem 0.9rem",background:"#f1f4f8",color:"#333",border:"none",borderRadius:6,cursor:"pointer",fontWeight:700},ltr:{direction:"ltr",unicodeBidi:"isolate"},// Add choice panel
addChoicePanel:{background:"#fff",padding:20,borderRadius:12,width:"84%",maxWidth:720,boxShadow:"0 12px 40px rgba(0,0,0,0.35)",textAlign:"center"},addChoiceButtons:{display:"flex",gap:18,justifyContent:"center",marginTop:12,flexWrap:"wrap"},bigButton:{width:260,height:160,borderRadius:12,border:"none",color:"#fff",display:"flex",flexDirection:"column",alignItems:"center",justifyContent:"center",gap:12,fontSize:16,cursor:"pointer",boxShadow:"0 10px 30px rgba(11,30,60,0.12)"},encaissementButton:{background:"linear-gradient(180deg,#26a65b,#1e8f47)"},depenseButton:{background:"linear-gradient(180deg,#e74c3c,#c83a2b)"},bigIcon:{width:56,height:56,borderRadius:28,background:"rgba(255,255,255,0.15)",display:"flex",alignItems:"center",justifyContent:"center",fontSize:28,fontWeight:900},cancelSmall:{padding:"0.45rem 0.9rem",background:"#f1f4f8",border:"none",borderRadius:8,cursor:"pointer"}};

sourcesContent:
// Updated caisse.js - backend integration (save / update / delete)
// Uses backend API functions exposed on window.pywebview.api:
// - load_caisse()
// - add_caisse_entry(payload)
// - update_caisse_entry(entry_id, payload)
// - delete_caisse_entry(entry_id)
// - load_all_clients()
// - load_all_personnel()
//
// Also keeps all UI features: Encaissement/DÃ©pense choice, nature lists,
// client/personnel pickers with search, print, edit/delete.

import React, { useEffect, useState } from "react";

function formatNumberSpaceDot(value) {
  if (value === null || value === undefined || value === "") return "";
  const cleaned = String(value).replace(/\s+/g, "").replace(",", ".");
  const n = Number(cleaned);
  if (!Number.isFinite(n)) return String(value);
  const parts = n.toFixed(2).split(".");
  parts[0] = parts[0].replace(/\B(?=(\d{3})+(?!\d))/g, " ");
  return parts.join(".");
}
function parseNumber(value) {
  if (value === null || value === undefined || value === "") return 0;
  const s = String(value).replace(/\s+/g, "").replace(",", ".");
  const n = Number(s);
  return Number.isFinite(n) ? n : 0;
}
function formatDateToDisplay(iso) {
  if (!iso) return "";
  const m = iso.match(/^(\d{4})-(\d{2})-(\d{2})/);
  if (m) return `${m[3]}/${m[2]}/${m[1]}`;
  return iso;
}
function toISODate(input) {
  if (!input) return "";
  if (input instanceof Date) {
    const yyyy = input.getFullYear();
    const mm = String(input.getMonth() + 1).padStart(2, "0");
    const dd = String(input.getDate()).padStart(2, "0");
    return `${yyyy}-${mm}-${dd}`;
  }
  if (/^\d{2}\/\d{2}\/\d{4}$/.test(input)) {
    const [dd, mm, yyyy] = input.split("/");
    return `${yyyy}-${mm}-${dd}`;
  }
  if (/^\d{4}-\d{2}-\d{2}$/.test(input)) return input;
  return input;
}

export default function CaissePage({ locale = "fr" }) {
  const isRtl = locale === "ar";
  const [entries, setEntries] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedIndex, setSelectedIndex] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [showAddChoice, setShowAddChoice] = useState(false);
  const [modalMode, setModalMode] = useState("add"); // add | edit
  const [showPicker, setShowPicker] = useState(false); // show client/person picker
  const [pickerType, setPickerType] = useState(null); // "client" or "personel"
  const [pickerList, setPickerList] = useState([]);
  const [pickerSearch, setPickerSearch] = useState("");
  const [form, setForm] = useState({
    id: null,
    date: "",
    heure: "",
    operation: "",
    nature: "",
    montant: "",
    solde: "",
    target_type: null, // "client" | "personel"
    target_id: null,
    target_label: null
  });
  const [error, setError] = useState("");

  const TABLE_FIELDS = ["date", "heure", "operation", "nature", "montant", "solde"];
  const headersFr = ["#", "Date", "Heure", "OpÃ©ration", "Nature", "Montant", "Solde"];
  const headersAr = ["#", "Ø§Ù„ØªØ§Ø±ÙŠØ®", "Ø§Ù„Ø³Ø§Ø¹Ø©", "Ø§Ù„Ø¹Ù…Ù„ÙŠØ©", "Ø§Ù„Ù†ÙˆØ¹", "Ø§Ù„Ù…Ø¨Ù„Øº", "Ø§Ù„Ø±ØµÙŠØ¯"];
  const headers = locale === "ar" ? headersAr : headersFr;

  // Depense now contains salary items
  const natureOptions = {
    encaissement: ["Paiement client", "Autre"],
    depense: ["Paiement vol", "Paiement hotel", "Charge bureautique", "Paiement salaire", "Avance sur salaire", "Autre charge"]
  };

  const directionStyle = isRtl ? { direction: "rtl" } : { direction: "ltr" };

  // --- Backend integration functions ---

  async function fetchEntries() {
    setLoading(true);
    try {
      if (window.pywebview && window.pywebview.api && typeof window.pywebview.api.load_caisse === "function") {
        const res = await window.pywebview.api.load_caisse();
        const normalized = (res || []).map(r => ({
          ...r,
          montant: r.montant !== undefined && r.montant !== null ? String(r.montant) : "",
          solde: r.solde !== undefined && r.solde !== null ? String(r.solde) : "",
          date: r.date || r.date_iso || ""
        }));
        setEntries(normalized);
      } else {
        // fallback: empty
        setEntries([]);
      }
    } catch (e) {
      console.error("Failed to load caisse entries", e);
      setEntries([]);
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => { fetchEntries(); }, [locale]);

  const filtered = entries.filter((e) => {
    const q = searchQuery.trim().toLowerCase();
    if (!q) return true;
    return (
      (e.date && e.date.toLowerCase().includes(q)) ||
      (e.heure && e.heure.toLowerCase().includes(q)) ||
      (e.operation && String(e.operation).toLowerCase().includes(q)) ||
      (e.nature && String(e.nature).toLowerCase().includes(q)) ||
      (String(e.montant || "").toLowerCase().includes(q)) ||
      (String(e.solde || "").toLowerCase().includes(q))
    );
  });

  function openAddChoice() { setShowAddChoice(true); }

  function chooseAdd(type) {
    setShowAddChoice(false);
    setModalMode("add");
    const now = new Date();
    const isoDate = toISODate(now);
    const hh = String(now.getHours()).padStart(2, "0");
    const mm = String(now.getMinutes()).padStart(2, "0");
    const operationLabel = type === "encaissement" ? "Encaissement" : "DÃ©pense";
    const natureDefault = type === "encaissement" ? natureOptions.encaissement[0] : natureOptions.depense[0];
    setForm({
      id: null,
      date: isoDate,
      heure: `${hh}:${mm}`,
      operation: operationLabel,
      nature: natureDefault,
      montant: "",
      solde: "",
      target_type: null,
      target_id: null,
      target_label: null
    });
    setError("");
    setShowModal(true);
  }

  function openEdit() {
    if (selectedIndex === null || filtered[selectedIndex] === undefined) {
      alert(locale === "ar" ? "Ø§Ù„Ø±Ø¬Ø§Ø¡ ØªØ­Ø¯ÙŠØ¯ Ø³Ø·Ø±." : "Please select a row.");
      return;
    }
    const row = filtered[selectedIndex];
    setModalMode("edit");
    setForm({
      id: row.id ?? null,
      date: row.date ? toISODate(row.date) : "",
      heure: row.heure || "",
      operation: row.operation || "",
      nature: row.nature || "",
      montant: row.montant !== undefined && row.montant !== null ? String(row.montant) : "",
      solde: row.solde !== undefined && row.solde !== null ? String(row.solde) : "",
      target_type: row.target_type || null,
      target_id: row.target_id || null,
      target_label: row.target_label || null
    });
    setError("");
    setShowModal(true);
  }

  async function handleDelete() {
    if (selectedIndex === null || filtered[selectedIndex] === undefined) {
      alert(locale === "ar" ? "Ø§Ù„Ø±Ø¬Ø§Ø¡ ØªØ­Ø¯ÙŠØ¯ Ø³Ø·Ø±." : "Please select a row.");
      return;
    }
    const row = filtered[selectedIndex];
    const name = `${row.date || ""} ${row.operation || ""}`;
    if (!window.confirm((locale === "ar" ? "Ø­Ø°Ù Ø§Ù„Ø³Ø¬Ù„ " : "Delete entry ") + name + "?")) return;
    try {
      if (window.pywebview && window.pywebview.api && typeof window.pywebview.api.delete_caisse_entry === "function") {
        await window.pywebview.api.delete_caisse_entry(row.id);
        await fetchEntries();
        setSelectedIndex(null);
      } else {
        // fallback: local removal
        setEntries(prev => prev.filter(x => x.id !== row.id));
        setSelectedIndex(null);
      }
    } catch (e) {
      console.error("delete failed", e);
      alert(locale === "ar" ? "ÙØ´Ù„ Ø§Ù„Ø­Ø°Ù" : "Delete failed");
    }
  }

  async function handleSave(e) {
    e?.preventDefault?.();
    setError("");
    if (!form.date || !form.heure || !form.operation || !form.nature || form.montant === "") {
      setError(locale === "ar" ? "ØªØ£ÙƒØ¯ Ù…Ù† Ù…Ù„Ø¡ Ø§Ù„Ø­Ù‚ÙˆÙ„ Ø§Ù„Ù…Ø·Ù„ÙˆØ¨Ø©" : "Please fill required fields");
      return;
    }

    const lower = String(form.nature || "").toLowerCase();
    // validate target selection if required
    if (lower.includes("paiement client")) {
      if (!form.target_type || form.target_type !== "client" || !form.target_id) {
        setError(locale === "ar" ? "Ø§Ø®ØªØ± Ø§Ù„Ø¹Ù…ÙŠÙ„" : "Please choose a client");
        return;
      }
    }
    if (lower.includes("paiement salaire") || lower.includes("avance sur salaire")) {
      if (!form.target_type || form.target_type !== "personel" || !form.target_id) {
        setError(locale === "ar" ? "Ø§Ø®ØªØ± Ø§Ù„Ù…ÙˆØ¸Ù" : "Please choose a personnel");
        return;
      }
    }

    const payload = {
      date: toISODate(form.date),
      heure: form.heure,
      operation: form.operation,
      nature: form.nature,
      montant: parseNumber(form.montant),
      solde: form.solde === "" ? null : parseNumber(form.solde),
      target_type: form.target_type,
      target_id: form.target_id,
      target_label: form.target_label,
      created_at: new Date().toISOString()
    };

    try {
      if (modalMode === "add") {
        if (window.pywebview && window.pywebview.api && typeof window.pywebview.api.add_caisse_entry === "function") {
          await window.pywebview.api.add_caisse_entry(payload);
          await fetchEntries();
        } else {
          // fallback local insert with temp id
          const tmpId = Date.now();
          setEntries(prev => [{ ...payload, id: tmpId }, ...prev]);
        }
      } else {
        // update
        if (!form.id) {
          setError("Missing id for update");
          return;
        }
        if (window.pywebview && window.pywebview.api && typeof window.pywebview.api.update_caisse_entry === "function") {
          await window.pywebview.api.update_caisse_entry(form.id, payload);
          await fetchEntries();
        } else {
          setEntries(prev => prev.map(x => (x.id === form.id ? { ...x, ...payload } : x)));
        }
      }
      setShowModal(false);
    } catch (err) {
      console.error("save error", err);
      setError(locale === "ar" ? "Ø®Ø·Ø£ Ø£Ø«Ù†Ø§Ø¡ Ø§Ù„Ø­ÙØ¸" : "Save error");
    }
  }

  function handlePrint() { window.print(); }

  // Picker: open list of clients or personnel using backend helpers
  async function openPickerFor(natureValue) {
    const lower = String(natureValue || "").toLowerCase();
    let typeToOpen = null;
    if (lower.includes("paiement client")) typeToOpen = "client";
    else if (lower.includes("paiement salaire") || lower.includes("avance sur salaire")) typeToOpen = "personel";
    if (!typeToOpen) return;
    setPickerType(typeToOpen);
    setPickerSearch("");
    setShowPicker(true);
    try {
      if (typeToOpen === "client") {
        if (window.pywebview && window.pywebview.api && typeof window.pywebview.api.load_all_clients === "function") {
          const res = await window.pywebview.api.load_all_clients();
          setPickerList(Array.isArray(res) ? res : []);
        } else {
          setPickerList([]);
        }
      } else {
        if (window.pywebview && window.pywebview.api && typeof window.pywebview.api.load_all_personnel === "function") {
          const res = await window.pywebview.api.load_all_personnel();
          setPickerList(Array.isArray(res) ? res : []);
        } else {
          setPickerList([]);
        }
      }
    } catch (e) {
      console.error("picker fetch failed", e);
      setPickerList([]);
    }
  }

  // effect: auto-open picker when nature changes to a type requiring it
  useEffect(() => {
    const lower = String(form.nature || "").toLowerCase();
    if (!form.nature) return;
    if (lower.includes("paiement client") || lower.includes("paiement salaire") || lower.includes("avance sur salaire")) {
      openPickerFor(form.nature);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [form.nature]);

  function pickItem(item) {
    if (!item) return;
    if (pickerType === "client") {
      const label = `${item.nom || ""}${item.prenom ? " " + item.prenom : ""}`;
      setForm(prev => ({ ...prev, target_type: "client", target_id: item.id, target_label: label }));
    } else if (pickerType === "personel") {
      const label = `${item.nom || ""}${item.prenom ? " " + item.prenom : ""}`;
      setForm(prev => ({ ...prev, target_type: "personel", target_id: item.id, target_label: label }));
    }
    setShowPicker(false);
  }

  function renderCell(row, field, idx) {
    const isLast = idx === TABLE_FIELDS.length - 1;
    const baseStyle = { ...styles.tableCell, borderRight: isLast ? "none" : "1px solid #e6eefb" };
    if (field === "date") return <td key={field} style={baseStyle}>{formatDateToDisplay(row.date) || "-"}</td>;
    if (field === "montant" || field === "solde") return <td key={field} style={baseStyle}><span style={styles.ltr}>{formatNumberSpaceDot(row[field])}</span></td>;
    if (field === "nature") return <td key={field} style={baseStyle}>{row[field]}{row.target_label ? ` â€” ${row.target_label}` : ""}</td>;
    return <td key={field} style={baseStyle}>{row[field] ?? "-"}</td>;
  }

  return (
    <div style={{ ...styles.container, ...directionStyle }}>
      <h2 style={{ textAlign: "center" }}>{locale === "ar" ? "Ø§Ù„Ø®Ø²ÙŠÙ†Ø©" : "Caisse"}</h2>

      <div style={{ display: "flex", gap: 12, marginBottom: 12, alignItems: "center" }}>
        <div style={{ ...styles.searchWrap, maxWidth: 420 }}>
          <span style={{ marginRight: 8 }}>ğŸ”</span>
          <input placeholder={locale === "ar" ? "Ø¨Ø­Ø«..." : "Search..."} value={searchQuery} onChange={e => { setSearchQuery(e.target.value); setSelectedIndex(null); }} style={styles.searchInput} />
        </div>

        <div style={{ marginLeft: "auto", display: "flex", gap: 8 }}>
          <div style={styles.summaryBadge}>{locale === "ar" ? "Ø§Ù„Ø³Ø¬Ù„Ø§Øª" : "Entries"}: <strong style={{ marginLeft: 6 }}>{filtered.length}</strong></div>
        </div>
      </div>

      <div style={styles.tableWrap}>
        <table style={styles.table}>
          <thead>
            <tr style={styles.tableHeaderRow}>
              <th style={{ ...styles.tableHeader, borderRight: "1px solid #e6eefb", width: 36 }}>#</th>
              {TABLE_FIELDS.map((f, idx) => <th key={f} style={{ ...styles.tableHeader, borderRight: idx === TABLE_FIELDS.length - 1 ? "none" : "1px solid #e6eefb" }}>{headers[idx + 1]}</th>)}
            </tr>
          </thead>
          <tbody>
            {loading ? <tr><td colSpan={1 + TABLE_FIELDS.length} style={{ padding: 20, textAlign: "center" }}>{locale === "ar" ? "Ø¬Ø§Ø±ÙŠ Ø§Ù„ØªØ­Ù…ÙŠÙ„..." : "Loading..."}</td></tr>
              : filtered.length === 0 ? <tr><td colSpan={1 + TABLE_FIELDS.length} style={{ padding: 20, textAlign: "center" }}>{locale === "ar" ? "Ù„Ø§ ØªÙˆØ¬Ø¯ Ø³Ø¬Ù„Ø§Øª" : "No entries"}</td></tr>
              : filtered.map((row, i) => (
                <tr key={row.id ?? i} onClick={() => setSelectedIndex(i)} style={{ ...styles.tableRow, background: i === selectedIndex ? "#cce5ff" : i % 2 === 0 ? "#fff" : "#f4f8ff", cursor: "pointer" }}>
                  <td style={{ ...styles.tableCell, width: 36 }}>{i + 1}</td>
                  {TABLE_FIELDS.map((f, idx) => renderCell(row, f, idx))}
                </tr>
              ))
            }
          </tbody>
        </table>
      </div>

      {/* Drawers */}
      <div style={{ ...styles.drawer, left: 20, right: "unset", bottom: 28 }}>
        <button style={styles.drawerButton} onClick={openAddChoice}>{locale === "ar" ? "â• Ø¥Ø¶Ø§ÙØ©" : "â• Ajouter"}</button>
        <button style={styles.drawerButton} onClick={openEdit}>{locale === "ar" ? "âœï¸ ØªØ¹Ø¯ÙŠÙ„" : "âœï¸ Modifier"}</button>
        <button style={styles.drawerButton} onClick={handleDelete}>{locale === "ar" ? "ğŸ—‘ï¸ Ø­Ø°Ù" : "ğŸ—‘ï¸ Supprimer"}</button>
      </div>

      <div style={{ ...styles.drawer, right: 20, left: "unset", bottom: 28 }}>
        <button style={{ ...styles.drawerButton, background: "#3477eb", color: "#fff" }} onClick={handlePrint}>{locale === "ar" ? "ğŸ–¨ï¸ Ø·Ø¨Ø§Ø¹Ø©" : "ğŸ–¨ï¸ Impression"}</button>
      </div>

      {/* Add choice overlay */}
      {showAddChoice && (
        <div style={styles.modalOverlay}>
          <div style={{ ...styles.addChoicePanel, ...(isRtl ? { direction: "rtl", textAlign: "right" } : {}) }}>
            <h3 style={{ marginTop: 0 }}>{locale === "ar" ? "Ø§Ø®ØªØ± Ù†ÙˆØ¹ Ø§Ù„Ø¥Ø¶Ø§ÙØ©" : "Choisir le type"}</h3>
            <div style={styles.addChoiceButtons}>
              <button style={{ ...styles.bigButton, ...styles.encaissementButton }} onClick={() => chooseAdd("encaissement")}>
                <div style={styles.bigIcon}>ï¼‹</div>
                <div style={{ fontSize: 18, fontWeight: 800 }}>{locale === "ar" ? "ØªØ­ØµÙŠÙ„" : "Encaissement"}</div>
              </button>
              <button style={{ ...styles.bigButton, ...styles.depenseButton }} onClick={() => chooseAdd("depense")}>
                <div style={styles.bigIcon}>âˆ’</div>
                <div style={{ fontSize: 18, fontWeight: 800 }}>{locale === "ar" ? "Ù…ØµØ±ÙˆÙ" : "DÃ©pense"}</div>
              </button>
            </div>
            <div style={{ display: "flex", justifyContent: "flex-end", marginTop: 12 }}>
              <button style={styles.cancelSmall} onClick={() => setShowAddChoice(false)}>{locale === "ar" ? "Ø¥Ù„ØºØ§Ø¡" : "Annuler"}</button>
            </div>
          </div>
        </div>
      )}

      {/* Add/Edit modal */}
      {showModal && (
        <div style={styles.modalOverlay}>
          <div style={{ ...styles.modal, ...(isRtl ? { direction: "rtl", textAlign: "right" } : {}) }}>
            <h3>{modalMode === "add" ? (locale === "ar" ? "Ø¥Ø¶Ø§ÙØ© Ø³Ø¬Ù„" : "Ajouter entrÃ©e") : (locale === "ar" ? "ØªØ¹Ø¯ÙŠÙ„ Ø³Ø¬Ù„" : "Modifier entrÃ©e")}</h3>

            <form onSubmit={handleSave} style={styles.form}>
              <div style={styles.row}>
                <label style={styles.label}>
                  {locale === "ar" ? "Ø§Ù„ØªØ§Ø±ÙŠØ®" : "Date"}
                  <input type="date" value={form.date ? toISODate(form.date) : ""} onChange={e => setForm(s => ({ ...s, date: e.target.value }))} style={styles.input} required />
                </label>
                <label style={styles.label}>
                  {locale === "ar" ? "Ø§Ù„Ø³Ø§Ø¹Ø©" : "Heure"}
                  <input type="time" value={form.heure || ""} onChange={e => setForm(s => ({ ...s, heure: e.target.value }))} style={styles.input} required />
                </label>
              </div>

              <div style={styles.row}>
                <label style={styles.label}>
                  {locale === "ar" ? "Ø§Ù„Ø¹Ù…Ù„ÙŠØ©" : "OpÃ©ration"}
                  <input value={form.operation || ""} onChange={e => setForm(s => ({ ...s, operation: e.target.value }))} style={styles.input} required />
                </label>

                <label style={styles.label}>
                  {locale === "ar" ? "Ø§Ù„Ù†ÙˆØ¹" : "Nature"}
                  <select value={form.nature || ""} onChange={e => setForm(s => ({ ...s, nature: e.target.value }))} style={styles.input} required>
                    { (String(form.operation || "").toLowerCase().includes("enc") || String(form.operation || "").toLowerCase().includes("encaissement")) 
                      ? natureOptions.encaissement.map(opt => <option key={opt} value={opt}>{opt}</option>)
                      : natureOptions.depense.map(opt => <option key={opt} value={opt}>{opt}</option>)
                    }
                  </select>
                </label>
              </div>

              {(String(form.nature || "").toLowerCase().includes("paiement client") ||
                String(form.nature || "").toLowerCase().includes("paiement salaire") ||
                String(form.nature || "").toLowerCase().includes("avance sur salaire")) && (
                <div style={{ marginTop: 6, display: "flex", gap: 12, alignItems: "center" }}>
                  <div style={{ flex: 1 }}>
                    <div style={{ fontSize: 13, color: "#666" }}>{locale === "ar" ? "Ø§Ù„Ù…Ø­Ø¯Ù‘ÙØ¯" : "Selected"}</div>
                    <div style={{ fontWeight: 700 }}>{form.target_label || (locale === "ar" ? "Ù„Ù… ÙŠØªÙ… Ø§Ù„ØªØ­Ø¯ÙŠØ¯" : "None selected")}</div>
                  </div>
                  <div>
                    <button type="button" style={styles.drawerButton} onClick={() => openPickerFor(form.nature)}>{locale === "ar" ? "Ø§Ø®ØªÙŠØ§Ø±" : "Choose"}</button>
                  </div>
                </div>
              )}

              <div style={styles.row}>
                <label style={styles.label}>
                  {locale === "ar" ? "Ø§Ù„Ù…Ø¨Ù„Øº" : "Montant"}
                  <input value={form.montant || ""} onChange={e => setForm(s => ({ ...s, montant: e.target.value }))} style={styles.input} type="number" step="0.01" required />
                </label>
                <label style={styles.label}>
                  {locale === "ar" ? "Ø§Ù„Ø±ØµÙŠØ¯ (Ø§Ø®ØªÙŠØ§Ø±ÙŠ)" : "Solde (optionnel)"}
                  <input value={form.solde || ""} onChange={e => setForm(s => ({ ...s, solde: e.target.value }))} style={styles.input} type="number" step="0.01" />
                </label>
              </div>

              {error && <div style={{ color: "red" }}>{error}</div>}

              <div style={{ display: "flex", justifyContent: "flex-end", gap: 8, marginTop: 12 }}>
                <button type="submit" style={styles.saveButton}>{locale === "ar" ? "Ø­ÙØ¸" : "Enregistrer"}</button>
                <button type="button" style={styles.cancelButton} onClick={() => setShowModal(false)}>{locale === "ar" ? "Ø¥Ù„ØºØ§Ø¡" : "Annuler"}</button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Picker modal (client or personnel) */}
      {showPicker && (
        <div style={styles.modalOverlay}>
          <div style={{ ...styles.modal, width: "88%", maxWidth: 860, ...(isRtl ? { direction: "rtl", textAlign: "right" } : {}) }}>
            <h3>{pickerType === "client" ? (locale === "ar" ? "Ø§Ø®ØªØ± Ø§Ù„Ø¹Ù…ÙŠÙ„" : "Select client") : (locale === "ar" ? "Ø§Ø®ØªØ± Ø§Ù„Ù…ÙˆØ¸Ù" : "Select personnel")}</h3>
            <div style={{ marginTop: 8, marginBottom: 8 }}>
              <input placeholder={locale === "ar" ? "Ø¨Ø­Ø«..." : "Search..."} value={pickerSearch} onChange={e => setPickerSearch(e.target.value)} style={styles.searchInput} />
            </div>
            <div style={{ maxHeight: 360, overflowY: "auto", border: "1px solid #eef3fb", borderRadius: 6 }}>
              {pickerList
                .filter(item => {
                  const q = pickerSearch.trim().toLowerCase();
                  if (!q) return true;
                  const name = `${item.nom || ""} ${(item.prenom || "")}`.toLowerCase();
                  const passport = (item.passport_number || item.passport || "").toLowerCase();
                  const telephone = (item.telephone || "").toLowerCase();
                  const credit = String(item.credit || item.credit_formatted || "");
                  return name.includes(q) || passport.includes(q) || telephone.includes(q) || credit.includes(q);
                })
                .map(item => (
                <div key={item.id} style={{ padding: 10, borderBottom: "1px solid #f2f6ff", display: "flex", justifyContent: "space-between", alignItems: "center" }}>
                  <div>
                    <div style={{ fontWeight: 700 }}>{(item.nom || "") + (item.prenom ? " " + item.prenom : "")}</div>
                    {pickerType === "client" ? (
                      <div style={{ fontSize: 12, color: "#666" }}>{(item.passport_number || item.passport || "")} â€¢ {formatNumberSpaceDot(item.credit || item.credit_formatted || "")}</div>
                    ) : (
                      <div style={{ fontSize: 12, color: "#666" }}>{item.poste || ""} â€¢ {item.telephone || ""}</div>
                    )}
                  </div>
                  <div>
                    <button style={styles.drawerButton} onClick={() => pickItem(item)}>{locale === "ar" ? "Ø§Ø®ØªÙŠØ§Ø±" : "Choose"}</button>
                  </div>
                </div>
              ))}
              {pickerList.length === 0 && <div style={{ padding: 20, textAlign: "center", color: "#666" }}>{locale === "ar" ? "Ù„Ø§ ØªÙˆØ¬Ø¯ Ø¨ÙŠØ§Ù†Ø§Øª" : "No items found"}</div>}
            </div>
            <div style={{ display: "flex", justifyContent: "flex-end", marginTop: 12 }}>
              <button style={styles.cancelSmall} onClick={() => setShowPicker(false)}>{locale === "ar" ? "Ø¥Ù„ØºØ§Ø¡" : "Annuler"}</button>
            </div>
          </div>
        </div>
      )}

    </div>
  );
}

const styles = {
