import React, { useEffect, useState } from "react";
import ReactDOM from "react-dom";

/**
 * Pointage UI.
 *
 * Usage:
 * - Supports two activation methods:
 *   1) Call exported openPointage(detail) where detail can include:
 *        { locale: "fr"|"ar", translations: {...}, personnel: {id, nom, prenom, salaire, avance, ...} }
 *      If personnel is provided, the dialog opens for that person directly.
 *   2) Listen to the "openPointage" window event (personel.js dispatches this). The event should
 *      include detail: { locale, translations, personnel? }.
 *
 * Behavior:
 * - Loads personnel list from backend (window.pywebview.api.get_personnel) if no personnel was passed.
 * - Allows selecting month/year.
 * - Shows the list of working days in the month (all days except Fridays).
 * - By default all working days are checked (present). You can uncheck days to mark absences.
 * - Calculates salary for the month:
 *     salary_per_day = monthly_salary / total_working_days
 *     salary_due = salary_per_day * days_present - avance (advance)
 * - "Save pointage" attempts to call window.pywebview.api.save_pointage(payload).
 *   Payload structure (suggested): { personnel_id, year, month, days_present, days_total, salary_per_day, salary_due, details: {dates_present: []} }
 *
 * Notes:
 * - This is a self-mounting UI: openPointage(...) will render the modal into document.body.
 * - It is resilient if backend APIs are missing (it will show errors).
 */

function monthName(monthIndex, locale = "fr") {
  const monthsFr = [
    "Janvier","Février","Mars","Avril","Mai","Juin",
    "Juillet","Août","Septembre","Octobre","Novembre","Décembre"
  ];
  const monthsAr = [
    "يناير","فيفري","مارس","أبريل","ماي","جوان",
    "جويلية","أوت","سبتمبر","أكتوبر","نوفمبر","ديسمبر"
  ];
  return (locale === "ar" ? monthsAr : monthsFr)[monthIndex];
}

function getWorkingDays(year, month) {
  // return array of Date objects for all days in month except Fridays (day === 5)
  const days = [];
  const date = new Date(year, month, 1);
  while (date.getMonth() === month) {
    if (date.getDay() !== 5) { // Friday = 5 (Sunday=0)
      days.push(new Date(date));
    }
    date.setDate(date.getDate() + 1);
  }
  return days;
}

function formatDateISO(d) {
  if (!d) return "";
  const yyyy = d.getFullYear();
  const mm = String(d.getMonth() + 1).padStart(2, "0");
  const dd = String(d.getDate()).padStart(2, "0");
  return `${yyyy}-${mm}-${dd}`;
}

function formatCurrencyFR(value) {
  if (value === "" || value === null || value === undefined) return "";
  const num = Number(String(value).replace(/\s+/g, "").replace(",", "."));
  if (Number.isNaN(num)) return String(value);
  return num.toLocaleString("fr-FR", { minimumFractionDigits: 2, maximumFractionDigits: 2 }).replace(/\u202f/g, " ");
}

function PointageModal({ initialDetail, onClose }) {
  const locale = initialDetail?.locale || "fr";
  const T = initialDetail?.translations?.[locale] || {};
  const [personnelList, setPersonnelList] = useState([]);
  const [loadingPersonnel, setLoadingPersonnel] = useState(false);
  const [selectedPersonnel, setSelectedPersonnel] = useState(initialDetail?.personnel ?? null);

  const now = new Date();
  const [year, setYear] = useState(now.getFullYear());
  const [month, setMonth] = useState(now.getMonth()); // 0-11

  const [workingDays, setWorkingDays] = useState([]); // array of Date
  const [presentMap, setPresentMap] = useState({}); // isoDate => boolean
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    // build workingDays for selected month/year
    const days = getWorkingDays(year, month);
    setWorkingDays(days);
    // initialize presentMap: default true for all working days
    const map = {};
    days.forEach(d => { map[formatDateISO(d)] = true; });
    setPresentMap(map);
  }, [year, month]);

  useEffect(() => {
    // load personnel only if not pre-selected
    if (!selectedPersonnel) {
      let mounted = true;
      setLoadingPersonnel(true);
      window.pywebview?.api?.get_personnel?.().then(res => {
        if (!mounted) return;
        setPersonnelList(res || []);
      }).catch((e) => {
        console.error("Failed loading personnel", e);
        setPersonnelList([]);
      }).finally(() => setLoadingPersonnel(false));
      return () => { mounted = false; };
    }
  }, [selectedPersonnel]);

  const totalWorkingDays = workingDays.length;
  const daysPresent = Object.values(presentMap).filter(Boolean).length;

  const monthlySalary = Number(String(selectedPersonnel?.salaire ?? "0").replace(/\s+/g, "").replace(",", ".") || 0);
  const avance = Number(String(selectedPersonnel?.avance ?? "0").replace(/\s+/g, "").replace(",", ".") || 0);

  const salaryPerDay = totalWorkingDays > 0 ? (monthlySalary / totalWorkingDays) : 0;
  const salaryDue = (salaryPerDay * daysPresent) - avance;

  const toggleDay = (iso) => {
    setPresentMap(prev => ({ ...prev, [iso]: !prev[iso] }));
  };

  const handleSavePointage = async () => {
    if (!selectedPersonnel) {
      setError(locale === "fr" ? "Sélectionnez un membre du personnel." : "الرجاء تحديد موظف.");
      return;
    }
    setError("");
    setSaving(true);
    const payload = {
      personnel_id: selectedPersonnel.id,
      year,
      month: month + 1, // send 1-12
      days_total: totalWorkingDays,
      days_present: daysPresent,
      salary_per_day: salaryPerDay,
      salary_due: salaryDue,
      dates_present: Object.entries(presentMap).filter(([k,v]) => v).map(([k]) => k)
    };
    try {
      if (window.pywebview && window.pywebview.api && window.pywebview.api.save_pointage) {
        await window.pywebview.api.save_pointage(payload);
      } else {
        // fallback: store in localStorage for demo
        const key = `pointage_${selectedPersonnel.id}_${year}_${month+1}`;
        localStorage.setItem(key, JSON.stringify(payload));
      }
      // optionally show a toast
      setSaving(false);
      onClose?.();
    } catch (e) {
      console.error("save_pointage failed", e);
      setError(locale === "fr" ? "Échec de l'enregistrement." : "فشل الحفظ.");
      setSaving(false);
    }
  };

  const handleSelectPersonnel = (id) => {
    const p = personnelList.find(x => x.id === id);
    setSelectedPersonnel(p || null);
  };

  return (
    <div style={styles.overlay}>
      <div style={{ ...styles.modal, direction: locale === "ar" ? "rtl" : "ltr", textAlign: locale === "ar" ? "right" : "left" }}>
        <div style={styles.header}>
          <h3 style={{ margin: 0 }}>{locale === "fr" ? "Pointage / Calcul Salaire" : "تسجيل الحضور / حساب الراتب"}</h3>
          <button onClick={onClose} style={styles.closeButton}>✖</button>
        </div>

        <div style={styles.row}>
          <div style={{ flex: 1 }}>
            <label style={styles.label}>{locale === "fr" ? "Personnel" : "الموظف"}</label>
            {selectedPersonnel ? (
              <div style={styles.personnelCard}>
                <div>
                  <strong>{selectedPersonnel.nom} {selectedPersonnel.prenom}</strong>
                  <div style={{ fontSize: 12, color: "#666" }}>{selectedPersonnel.poste ?? ""}</div>
                </div>
                <div style={{ textAlign: "right" }}>
                  <div style={{ fontSize: 12 }}>{locale === "fr" ? "Salaire mensuel" : "الراتب الشهري"}</div>
                  <div style={{ fontWeight: 700 }}>{formatCurrencyFR(selectedPersonnel.salaire || 0)} DZD</div>
                </div>
              </div>
            ) : (
              <>
                {loadingPersonnel ? (
                  <div>Loading...</div>
                ) : (
                  <select style={styles.input} onChange={(e) => handleSelectPersonnel(Number(e.target.value))} defaultValue="">
                    <option value="">{locale === "fr" ? "Sélectionner..." : "اختر..."}</option>
                    {personnelList.map(p => (
                      <option value={p.id} key={p.id}>
                        {p.nom} {p.prenom} {p.poste ? ` - ${p.poste}` : ""}
                      </option>
                    ))}
                  </select>
                )}
              </>
            )}
          </div>

          <div style={{ width: 220 }}>
            <label style={styles.label}>{locale === "fr" ? "Mois / Année" : "الشهر / السنة"}</label>
            <div style={{ display: "flex", gap: 8 }}>
              <select value={month} onChange={(e) => setMonth(Number(e.target.value))} style={styles.input}>
                {Array.from({ length: 12 }).map((_, i) => (
                  <option key={i} value={i}>{monthName(i, locale)}</option>
                ))}
              </select>
              <input type="number" value={year} onChange={(e) => setYear(Number(e.target.value))} style={{ ...styles.input, width: 100 }} />
            </div>
            <div style={{ marginTop: 8, fontSize: 12, color: "#666" }}>
              {locale === "fr" ? `${totalWorkingDays} jours ouvrables (vendredis exclus)` : `${totalWorkingDays} أيام عمل (باستثناء الجمعة)`}
            </div>
          </div>
        </div>

        <div style={{ marginTop: 12 }}>
          <div style={{ fontWeight: 700, marginBottom: 8 }}>{locale === "fr" ? "Jours de travail (décocher pour absence)" : "أيام العمل (قم بإلغاء التحديد للغياب)"}</div>
          <div style={styles.daysGrid}>
            {workingDays.map(d => {
              const iso = formatDateISO(d);
              const label = `${String(d.getDate()).padStart(2,"0")}/${String(d.getMonth()+1).padStart(2,"0")}`;
              return (
                <label key={iso} style={styles.dayItem}>
                  <input type="checkbox" checked={!!presentMap[iso]} onChange={() => toggleDay(iso)} />
                  <span style={{ marginLeft: 8 }}>{label} ({d.toLocaleDateString(locale === "fr" ? "fr-FR" : "ar", { weekday: 'short' })})</span>
                </label>
              );
            })}
          </div>
        </div>

        <div style={{ marginTop: 12, display: "flex", gap: 12, alignItems: "center", justifyContent: "space-between" }}>
          <div>
            <div style={{ fontSize: 14, color: "#666" }}>{locale === "fr" ? "Présences" : "عدد الحضور" }</div>
            <div style={{ fontWeight: 700, fontSize: 18 }}>{daysPresent} / {totalWorkingDays}</div>
          </div>

          <div>
            <div style={{ fontSize: 13, color: "#666", textAlign: "right" }}>{locale === "fr" ? "Salaire par jour" : "الراتب اليومي"}</div>
            <div style={{ fontWeight: 700, textAlign: "right" }}>{formatCurrencyFR(salaryPerDay)} DZD</div>
            <div style={{ height: 6 }}></div>
            <div style={{ fontSize: 13, color: "#666", textAlign: "right" }}>{locale === "fr" ? "Salaire dû" : "المبلغ المستحق"}</div>
            <div style={{ fontWeight: 900, fontSize: 18, color: salaryDue < 0 ? "#c00" : "#14355f", textAlign: "right" }}>
              {formatCurrencyFR(salaryDue)} DZD
            </div>
            <div style={{ fontSize: 11, color: "#666", textAlign: "right" }}>{locale === "fr" ? `Avance: ${formatCurrencyFR(avance)} DZD` : `السلفة: ${formatCurrencyFR(avance)} DZD`}</div>
          </div>
        </div>

        {error && <div style={{ marginTop: 8, color: "red" }}>{error}</div>}

        <div style={{ marginTop: 14, display: "flex", gap: 8, justifyContent: "flex-end" }}>
          <button onClick={onClose} style={styles.btnSecondary}>{locale === "fr" ? "Fermer" : "إغلاق"}</button>
          <button onClick={handleSavePointage} style={styles.btnPrimary} disabled={saving}>
            {saving ? (locale === "fr" ? "Enregistrement..." : "جاري الحفظ...") : (locale === "fr" ? "Enregistrer le pointage" : "حفظ التسجيل")}
          </button>
        </div>
      </div>
    </div>
  );
}

/**
 * Programmatic opener: renders modal into document.body.
 * detail: { locale, translations, personnel? }
 */
export async function openPointage(detail = {}) {
  // create container
  const containerId = "pointage-modal-root";
  let container = document.getElementById(containerId);
  if (!container) {
    container = document.createElement("div");
    container.id = containerId;
    document.body.appendChild(container);
  }

  function cleanup() {
    try {
      ReactDOM.unmountComponentAtNode(container);
      container.remove();
    } catch (e) {
      // ignore
    }
  }

  const onClose = () => cleanup();

  ReactDOM.render(<PointageModal initialDetail={detail} onClose={onClose} />, container);
}

// Also register a window event listener so other parts of the app can dispatch an event
if (typeof window !== "undefined") {
  window.addEventListener("openPointage", (e) => {
    const detail = (e && e.detail) ? e.detail : {};
    // render UI
    openPointage(detail);
  });
}

const styles = {
  overlay: {
    position: "fixed",
    inset: 0,
    background: "rgba(0,0,0,0.45)",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    zIndex: 5000
  },
  modal: {
    width: "92%",
    maxWidth: 980,
    background: "#fff",
    borderRadius: 10,
    padding: 16,
    boxShadow: "0 12px 40px rgba(0,0,0,0.35)"
  },
  header: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    marginBottom: 8
  },
  closeButton: {
    border: "none",
    background: "transparent",
    fontSize: 18,
    cursor: "pointer"
  },
  row: {
    display: "flex",
    gap: 12
  },
  label: {
    fontSize: 13,
    color: "#333",
    marginBottom: 6
  },
  input: {
    padding: "0.5rem",
    borderRadius: 6,
    border: "1px solid #ccc",
    fontSize: 14,
    width: "100%"
  },
  personnelCard: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "8px 10px",
    border: "1px solid #e9eef9",
    borderRadius: 8,
    background: "#fbfdff"
  },
  daysGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit,minmax(180px,1fr))",
    gap: 8,
    maxHeight: 240,
    overflowY: "auto",
    padding: 6,
    border: "1px solid #eef3fb",
    borderRadius: 6,
    background: "#fff"
  },
  dayItem: {
    display: "flex",
    alignItems: "center",
    gap: 8,
    padding: "6px 8px",
    borderRadius: 6,
    background: "#fbfbff",
    border: "1px solid #f0f6ff",
    fontSize: 13
  },
  btnPrimary: {
    padding: "0.6rem 1rem",
    background: "#3477eb",
    color: "#fff",
    border: "none",
    borderRadius: 6,
    cursor: "pointer",
    fontWeight: 700
  },
  btnSecondary: {
    padding: "0.5rem 0.9rem",
    background: "#f4f6fb",
    border: "none",
    borderRadius: 6,
    cursor: "pointer"
  },
  spinner: {
    width: 48,
    height: 48,
    borderRadius: 24,
    border: "6px solid #eee",
    borderTop: "6px solid #3477eb",
    animation: "spin 1s linear infinite"
  }
};

export default PointageModal;
