function formatDateToFR(dateString) {
  if (!dateString) return "";
  if (/^\d{4}-\d{2}-\d{2}$/.test(dateString)) {
    const [yyyy, mm, dd] = dateString.split("-");
    return `${dd}/${mm}/${yyyy}`;
  } else if (/^\d{2}-\d{2}-\d{4}$/.test(dateString)) {
    const [dd, mm, yyyy] = dateString.split("-");
    return `${dd}/${mm}/${yyyy}`;
  } else if (/^\d{2}\/\d{2}\/\d{4}$/.test(dateString)) {
    return dateString;
  }
  return dateString;
}

function formatDateToISO(dateString) {
  if (!dateString) return "";
  let dd, mm, yyyy;
  if (/^\d{2}\/\d{2}\/\d{4}$/.test(dateString)) {
    [dd, mm, yyyy] = dateString.split("/");
  } else if (/^\d{2}-\d{2}-\d{4}$/.test(dateString)) {
    [dd, mm, yyyy] = dateString.split("-");
  } else if (/^\d{4}-\d{2}-\d{2}$/.test(dateString)) {
    return dateString;
  }
  if (yyyy && mm && dd) {
    return `${yyyy}-${mm}-${dd}`;
  }
  return dateString;
}

function isArabic(str) {
  return /^[\u0600-\u06FF\s]+$/.test(str);
}

// PDF generator for Fiche client
function generateClientPDF(client, locale, translations) {
  // This implementation uses jsPDF (https://github.com/parallax/jsPDF)
  // Make sure to add jsPDF to your project: npm install jspdf
  // Or include CDN in index.html: <script src="https://cdnjs.cloudflare.com/ajax/libs/jspdf/2.5.1/jspdf.umd.min.js"></script>
  import("jspdf").then(jsPDFModule => {
    const jsPDF = jsPDFModule.default;
    const doc = new jsPDF();
    let y = 12;
    doc.setFontSize(18);
    doc.text(translations[locale].ficheButton, 12, y);
    y += 10;
    doc.setFontSize(12);

    const printField = (label, value) => {
      doc.text(`${label}: ${value ?? ""}`, 12, y);
      y += 8;
    };

    const L = translations[locale].modalLabels;
    printField(L.nom, client.nom);
    printField(L.prenom, client.prenom);
    printField(L.nom_ar, client.nom_ar);
    printField(L.prenom_ar, client.prenom_ar);
    printField(L.prenom_pere, client.prenom_pere);
    printField(L.prenom_pere_ar, client.prenom_pere_ar);
    printField(L.nom_mere, client.nom_mere);
    printField(L.prenom_mere, client.prenom_mere);
    printField(L.nom_mere_ar, client.nom_mere_ar);
    printField(L.prenom_mere_ar, client.prenom_mere_ar);
    printField(L.date_de_naissance, client.date_de_naissance);
    printField(L.lieu_de_naissance, client.lieu_de_naissance);
    printField(L.nationalite, client.nationalite);
    printField(L.passport_number, client.passport_number);
    printField(L.date_delivrance, client.date_delivrance);
    printField(L.date_expiration, client.date_expiration);
    printField(L.addresse, client.addresse);
    printField(L.telephone, client.telephone);
    printField(L.credit, client.credit);
    // Photo: render as image if available and base64
    if (client.photo && client.photo.startsWith("data:")) {
      try {
        doc.addImage(client.photo, "JPEG", 140, 12, 40, 32);
      } catch (err) {
        // ignore image errors
      }
    }
    doc.save(`${client.nom}_${client.prenom}_fiche.pdf`);
  });
}

function ClientPage({ locale = "fr" }) {
  const [clients, setClients] = useState([]);
  const [selectedIndex, setSelectedIndex] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [modalMode, setModalMode] = useState("add");
  const [clientForm, setClientForm] = useState({
    id: null,
    nom: "",
    prenom: "",
    nom_ar: "",
    prenom_ar: "",
    prenom_pere: "",
    prenom_pere_ar: "",
    nom_mere: "",
    prenom_mere: "",
    nom_mere_ar: "",
    prenom_mere_ar: "",
    date_de_naissance: "",
    lieu_de_naissance: "",
    nationalite: "AlgÃ©rienne",
    passport_number: "",
    date_delivrance: "",
    date_expiration: "",
    addresse: "",
    telephone: "",
    credit: "",
    photo: ""
  });
  const [arErrors, setArErrors] = useState({});
  const [isDeleting, setIsDeleting] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const isRtl = locale === "ar";
  const displayedHeaders = isRtl
    ? [...translations[locale].tableHeaders].reverse()
    : translations[locale].tableHeaders;
  const tableFieldsOrder = isRtl
    ? [...TABLE_FIELDS].reverse()
    : [...TABLE_FIELDS];
  const directionStyle = isRtl
    ? { direction: "rtl" }
    : { direction: "ltr" };
  const tableStyle = isRtl
    ? { ...styles.table, direction: "rtl" }
    : styles.table;

  const fetchClients = async () => {
    setIsLoading(true);
    try {
      const result = await window.pywebview.api.get_clients();
      setClients(result || []);
    } catch (err) {
      setClients([]);
    }
    setIsLoading(false);
  };

  useEffect(() => {
    fetchClients();
    // eslint-disable-next-line
  }, [locale]);

  const filteredClients = clients.filter((c) => {
    const q = searchQuery.trim().toLowerCase();
    if (!q) return true;
    return (
      (c.nom || "").toLowerCase().includes(q) ||
      (c.prenom || "").toLowerCase().includes(q) ||
      (c.date_de_naissance || "").toLowerCase().includes(q) ||
      (c.lieu_de_naissance || "").toLowerCase().includes(q) ||
      (c.nationalite || "").toLowerCase().includes(q) ||
      (c.passport_number || "").toString().toLowerCase().includes(q) ||
      (c.telephone || "").toLowerCase().includes(q)
    );
  });

  const emptyForm = {
    id: null,
    nom: "",
    prenom: "",
    nom_ar: "",
    prenom_ar: "",
    prenom_pere: "",
    prenom_pere_ar: "",
    nom_mere: "",
    prenom_mere: "",
    nom_mere_ar: "",
    prenom_mere_ar: "",
    date_de_naissance: "",
    lieu_de_naissance: "",
    nationalite: "AlgÃ©rienne",
    passport_number: "",
    date_delivrance: "",
    date_expiration: "",
    addresse: "",
    telephone: "",
    credit: "",
    photo: ""
  };

  const handleAjouter = () => {
    setModalMode("add");
    setClientForm({ ...emptyForm });
    setArErrors({});
    setShowModal(true);
  };

  const handleModifier = () => {
    if (selectedIndex !== null && filteredClients[selectedIndex]) {
      setModalMode("edit");
      setClientForm({
        ...filteredClients[selectedIndex],
        date_de_naissance: formatDateToFR(filteredClients[selectedIndex].date_de_naissance),
        date_delivrance: formatDateToFR(filteredClients[selectedIndex].date_delivrance),
        date_expiration: formatDateToFR(filteredClients[selectedIndex].date_expiration)
      });
      setArErrors({});
      setShowModal(true);
    } else {
      alert(translations[locale].selectRowAlert);
    }
  };

  const handleSupprimer = async () => {
    if (selectedIndex === null || !filteredClients[selectedIndex]) {
      alert(translations[locale].selectRowAlert);
      return;
    }
    if (isDeleting) return;
    const clientId = filteredClients[selectedIndex].id;
    const clientName =
      filteredClients[selectedIndex].nom ||
      filteredClients[selectedIndex].prenom ||
      filteredClients[selectedIndex].passport_number ||
      "";
    const confirmDelete = window.confirm(
      translations[locale].confirmDelete(clientName)
    );
    if (!confirmDelete) return;
    try {
      setIsDeleting(true);
      await window.pywebview.api.delete_client(clientId);
      setSelectedIndex(null);
      fetchClients();
    } catch (err) {
      alert(translations[locale].errorDelete);
    } finally {
      setIsDeleting(false);
    }
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setArErrors({});
    setClientForm({ ...emptyForm });
  };

  const handleArInput = (field, value) => {
    let err = "";
    if (value && !isArabic(value)) {
      err = translations[locale].saisieArabe;
    }
    setClientForm((prev) => ({ ...prev, [field]: value }));
    setArErrors((prev) => ({ ...prev, [field]: err }));
  };

  const handleSaveClient = async (e) => {
    e.preventDefault();
    let newErrors = {};
    for (const f of Object.keys(clientForm)) {
      if (f.endsWith("_ar") && clientForm[f] && !isArabic(clientForm[f])) {
        newErrors[f] = translations[locale].saisieArabe;
      }
    }
    setArErrors(newErrors);
    if (Object.values(newErrors).some(Boolean)) return;

    try {
      const payload = {
        ...clientForm,
        date_de_naissance: formatDateToISO(clientForm.date_de_naissance),
        date_delivrance: formatDateToISO(clientForm.date_delivrance),
        date_expiration: formatDateToISO(clientForm.date_expiration)
      };
      if (modalMode === "add") {
        await window.pywebview.api.add_client(payload);
      } else {
        await window.pywebview.api.update_client(payload);
      }
      handleCloseModal();
      fetchClients();
    } catch (err) {
      alert(translations[locale].errorSave);
    }
  };

  const handlePhotoChange = (file) => {
    if (!file) {
      setClientForm((s) => ({ ...s, photo: "" }));
      return;
    }
    const reader = new FileReader();
    reader.onload = () => {
      setClientForm((s) => ({ ...s, photo: reader.result }));
    };
    reader.readAsDataURL(file);
  };

  const photoSrc = (val) => {
    if (!val) return null;
    if (typeof val !== "string") return null;
    if (val.startsWith("data:")) return val;
    return `data:image/png;base64,${val}`;
  };

  const tableContainerStyle = {
    maxHeight: "60vh",
    overflowY: "auto",
    border: "1px solid #e0e0e0",
    borderRadius: "6px"
  };

  const spinnerStyle = {
    margin: "60px auto",
    border: "8px solid #eee",
    borderTop: "8px solid #3477eb",
    borderRadius: "50%",
    width: 48,
    height: 48,
    animation: "spin 1s linear infinite"
  };

  if (typeof document !== "undefined" && !document.getElementById("client-spinner-style")) {
    const style = document.createElement("style");
    style.id = "client-spinner-style";
    style.innerHTML = `
      @keyframes spin { to { transform: rotate(360deg); } }
      .tableHeaderSticky { position: sticky; top: 0; background: #e8f0fe; z-index: 2; }
    `;
    document.head.appendChild(style);
  }

  function renderTableCell(client, field) {
    if (field === "photo") {
      const src = photoSrc(client[field]);
      return (
        <td
          key={`${client.id}-${field}`}
          style={{
            ...styles.tableCell,
            padding: 0,
            textAlign: "center",
            minWidth: 50,
            maxWidth: 70,
            width: 60,
            verticalAlign: "middle",
            background: "#fff"
          }}
        >
          {src ? (
            <img
              src={src}
              alt="photo"
              style={{
                width: 50,
                height: 36,
                objectFit: "cover",
                borderRadius: 6,
                display: "block",
                margin: "0 auto"
              }}
            />
          ) : (
            "-"
          )}
        </td>
      );
    }
    if (
      field === "date_de_naissance" ||
      field === "date_delivrance" ||
      field === "date_expiration"
    ) {
      return (
        <td key={`${client.id}-${field}`} style={styles.tableCell}>
          {formatDateToFR(client[field])}
        </td>
      );
    }
    return (
      <td key={`${client.id}-${field}`} style={styles.tableCell}>
        {client[field] ?? "-"}
      </td>
    );
  }

  // For modal: 4 fields per line, photo on its own line
  const modalRows = [
    ["nom", "prenom", "nom_ar", "prenom_ar"],
    ["prenom_pere", "prenom_pere_ar", "nom_mere", "prenom_mere"],
    ["nom_mere_ar", "prenom_mere_ar", "date_de_naissance", "lieu_de_naissance"],
    ["nationalite", "passport_number", "date_delivrance", "date_expiration"],
    ["addresse", "telephone", "credit"]
  ];

  // Print handler
  const handleImpression = () => {
    window.print();
  };

  // Fiche client handler: generate PDF of client info
  const handleFicheClient = () => {
    if (selectedIndex === null || !filteredClients[selectedIndex]) {
      alert(translations[locale].selectRowAlert);
      return;
    }
    generateClientPDF(filteredClients[selectedIndex], locale, translations);
  };

  // Two drawers: left and right, slightly lower (bottom: 28px)
  const drawerLeft = (
    <div
      style={{
        ...styles.drawer,
        left: 20,
        right: "unset",
        bottom: 28,
        justifyContent: "flex-start",
        width: "auto",
        minWidth: 0,
      }}
    >
      <button style={styles.drawerButton} onClick={handleAjouter}>
        {translations[locale].addButton}
      </button>
      <button style={styles.drawerButton} onClick={handleModifier}>
        {translations[locale].editButton}
      </button>
      <button style={styles.drawerButton} onClick={handleSupprimer}>
        {translations[locale].deleteButton}
      </button>
    </div>
  );
  const drawerRight = (
    <div
      style={{
        ...styles.drawer,
        right: 20,
        left: "unset",
        bottom: 28,
        justifyContent: "flex-end",
        width: "auto",
        minWidth: 0,
      }}
    >
      <button style={{ ...styles.drawerButton, background: "#1b9e5c", color: "#fff" }} onClick={handleFicheClient}>
        {translations[locale].ficheButton}
      </button>
      <button style={{ ...styles.drawerButton, background: "#3477eb", color: "#fff" }} onClick={handleImpression}>
        {translations[locale].printButton}
      </button>
    </div>
  );

  return (
    <div style={{ ...styles.pageContainer, ...directionStyle }}>
      <h2 style={{ textAlign: "center" }}>{translations[locale].title}</h2>
      {/* Search bar, reduced width */}
      <div style={{ ...styles.searchContainer, maxWidth: 340, flexDirection: isRtl ? "row-reverse" : "row" }}>
        <span style={styles.searchIcon}>ğŸ”</span>
        <input
          type="text"
          placeholder={translations[locale].searchPlaceholder}
          value={searchQuery}
          onChange={(e) => {
            setSearchQuery(e.target.value);
            setSelectedIndex(null);
          }}
          style={styles.searchInput}
        />
      </div>
      {isLoading ? (
        <div style={{ display: "flex", justifyContent: "center" }}>
          <div style={spinnerStyle}></div>
        </div>
      ) : (
        <div style={tableContainerStyle}>
          <table style={tableStyle}>
            <thead>
              <tr style={styles.tableHeaderRow}>
                {isRtl
                  ? [...displayedHeaders].reverse().map((h, i) => (
                      <th
                        key={i}
                        style={{
                          ...styles.tableHeader,
                          ...(h === "#" ? { width: 36, minWidth: 24, maxWidth: 40 } : {}),
                          ...(h === "Photo" || h === "Ø§Ù„ØµÙˆØ±Ø©" ? { minWidth: 50, maxWidth: 70, width: 60 } : {}),
                        }}
                        className="tableHeaderSticky"
                      >
                        {h}
                      </th>
                    ))
                  : displayedHeaders.map((h, i) => (
                      <th
                        key={i}
                        style={{
                          ...styles.tableHeader,
                          ...(h === "#" ? { width: 36, minWidth: 24, maxWidth: 40 } : {}),
                          ...(h === "Photo" || h === "Ø§Ù„ØµÙˆØ±Ø©" ? { minWidth: 50, maxWidth: 70, width: 60 } : {}),
                        }}
                        className="tableHeaderSticky"
                      >
                        {h}
                      </th>
                    ))}
              </tr>
            </thead>
            <tbody>
              {filteredClients.map((client, idx) => (
                <tr
                  key={client.id || (client.nom + idx)}
                  style={{
                    ...styles.tableRow,
                    background:
                      idx === selectedIndex
                        ? "#cce5ff"
                        : idx % 2 === 0
                        ? "#fff"
                        : "#f4f8ff"
                  }}
                  onClick={() => setSelectedIndex(idx)}
                >
                  {isRtl ? (
                    <>
                      <td style={styles.tableCell}>{idx + 1}</td>
                      {[...tableFieldsOrder].reverse().map(field => renderTableCell(client, field))}
                    </>
                  ) : (
                    <>
                      <td style={styles.tableCell}>{idx + 1}</td>
                      {tableFieldsOrder.map(field => renderTableCell(client, field))}
                    </>
                  )}
                </tr>
              ))}
              {filteredClients.length === 0 && !isLoading && (
                <tr>
                  <td colSpan={displayedHeaders.length} style={{ padding: "1rem", textAlign: "center", color: "#666" }}>
                    {translations[locale].errorLoad}
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      )}
      {drawerLeft}
      {drawerRight}
      {/* Modal for Adding/Editing a Client */}
      {showModal && (
        <div style={styles.modalOverlay}>
          <div style={{ ...styles.modalContent, ...(isRtl ? { direction: "rtl", textAlign: "right" } : {}) }}>
            <h3>
              {modalMode === "add"
                ? translations[locale].addClient
                : translations[locale].editClient}
            </h3>
            <form onSubmit={handleSaveClient} style={styles.modalForm}>
              {modalRows.map((row, rowIdx) => (
                <div style={styles.fieldRow4} key={rowIdx}>
                  {row.map((field, i) => {
                    // For date fields, use native date input (like group js)
                    if (
                      field === "date_de_naissance" ||
                      field === "date_delivrance" ||
                      field === "date_expiration"
                    ) {
                      return (
                        <div style={styles.fieldItem4} key={i}>
                          <label style={styles.modalLabel}>
                            {translations[locale].modalLabels[field]}:
                            <input
                              type="date"
                              value={
                                clientForm[field]
                                  ? formatDateToISO(clientForm[field])
                                  : ""
                              }
                              onChange={e =>
                                setClientForm(prev => ({
                                  ...prev,
                                  [field]: e.target.value
                                    ? formatDateToFR(e.target.value)
                                    : ""
                                }))
                              }
                              style={styles.modalInput}
                            />
                          </label>
                        </div>
                      );
                    }
                    // For credit, use a number input (show in both ajouter and modifier)
                    if (field === "credit") {
                      return (
                        <div style={styles.fieldItem4} key={i}>
                          <label style={styles.modalLabel}>
                            {translations[locale].modalLabels.credit}:
                            <input
                              type="number"
                              value={clientForm.credit || ""}
                              onChange={e =>
                                setClientForm({ ...clientForm, credit: e.target.value })
                              }
                              style={styles.modalInput}
                              min={0}
                            />
                          </label>
                        </div>
                      );
                    }
                    return (
                      <div style={styles.fieldItem4} key={i}>
                        <label style={styles.modalLabel}>
                          {translations[locale].modalLabels[field]}:
                          {field === "nationalite" ? (
                            <select
                              value={clientForm.nationalite}
                              onChange={e =>
                                setClientForm({ ...clientForm, nationalite: e.target.value })
                              }
                              style={styles.modalInput}
                            >
                              {NATIONALITIES.map(nat => (
                                <option value={nat} key={nat}>{nat}</option>
                              ))}
                            </select>
                          ) : field.endsWith("_ar") ? (
                            <>
                              <input
                                type="text"
                                value={clientForm[field] || ""}
                                onChange={e => handleArInput(field, e.target.value)}
                                style={styles.modalInput}
                                dir="rtl"
                                lang="ar"
                                pattern="[\u0600-\u06FF\s]+"
                                title={translations[locale].saisieArabe}
                                placeholder="..."
                              />
                              {arErrors[field] && (
                                <span style={styles.arError}>{arErrors[field]}</span>
                              )}
                            </>
                          ) : (
                            <input
                              type="text"
                              value={clientForm[field] || ""}
                              onChange={e =>
                                setClientForm({ ...clientForm, [field]: e.target.value })
                              }
                              style={styles.modalInput}
                              required={
                                ["nom", "prenom", "passport_number"].includes(field)
                              }
                            />
                          )}
                        </label>
                      </div>
                    );
                  })}
                  {row.length < 4 &&
                    [...Array(4 - row.length)].map((_, idx) => (
                      <div style={styles.fieldItem4} key={"empty" + idx}></div>
                    ))}
                </div>
              ))}
              <div style={styles.fieldRow4}>
                <div style={{ ...styles.fieldItem4, flex: 1 }}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.photo}:
                    <input
                      type="file"
                      accept="image/*"
                      onChange={(e) => handlePhotoChange(e.target.files[0])}
                      style={{ marginTop: 6 }}
                    />
                    {clientForm.photo ? (
                      <div style={{ marginTop: 8 }}>
                        <img
                          src={photoSrc(clientForm.photo)}
                          alt="preview"
                          style={{ width: 80, height: 60, objectFit: "cover", borderRadius: 6 }}
                        />
                      </div>
                    ) : null}
                  </label>
                </div>
                <div style={styles.fieldItem4}></div>
                <div style={styles.fieldItem4}></div>
                <div style={styles.fieldItem4}></div>
              </div>
              <div style={styles.modalButtons}>
                <button type="submit" style={styles.modalButton}>
                  {translations[locale].save}
                </button>
                <button type="button" style={styles.modalButton} onClick={handleCloseModal}>
                  {translations[locale].cancel}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

