function formatDateToFR(dateString) {
  if (!dateString) return "";
  if (/^\d{4}-\d{2}-\d{2}$/.test(dateString)) {
    const [yyyy, mm, dd] = dateString.split("-");
    return `${dd}/${mm}/${yyyy}`;
  }
  if (/^\d{2}\/\d{2}\/\d{4}$/.test(dateString)) return dateString;
  return dateString;
}
function formatDateToISO(dateString) {
  if (!dateString) return "";
  if (/^\d{2}\/\d{2}\/\d{4}$/.test(dateString)) {
    const [dd, mm, yyyy] = dateString.split("/");
    return `${yyyy}-${mm}-${dd}`;
  } else if (/^\d{4}-\d{2}-\d{2}$/.test(dateString)) return dateString;
  return dateString;
}

async function generateVolPDF(vol, locale) {
  const doc = new jsPDF();
  let y = 12;
  doc.setFontSize(18);
  doc.text(translations[locale].ficheButton, 12, y);
  y += 10;
  doc.setFontSize(12);
  const L = translations[locale].modalLabels;
  const printField = (label, value) => {
    doc.text(`${label}: ${value ?? ""}`, 12, y);
    y += 8;
  };
  printField(L.flight_number, vol.flight_number);
  printField(L.compagnie, vol.compagnie);
  printField(L.depart_aeroport, vol.depart_aeroport);
  printField(L.arrivee_aeroport, vol.arrivee_aeroport);
  printField(L.date_depart, vol.date_depart);
  printField(L.heure_depart, vol.heure_depart);
  printField(L.duree, vol.duree);
  printField(L.classe, vol.classe);
  printField(L.prix, vol.prix);
  printField(L.places_disponibles, vol.places_disponibles);
  printField(L.statut, vol.statut);
  const pdfBlob = doc.output("blob");
  const arrayBuffer = await pdfBlob.arrayBuffer();
  const uint8Array = new Uint8Array(arrayBuffer);
  let binary = '';
  for (let i = 0; i < uint8Array.length; i++) binary += String.fromCharCode(uint8Array[i]);
  const base64PDF = window.btoa(binary);
  if (window.pywebview && window.pywebview.api && window.pywebview.api.open_pdf_base64) {
    await window.pywebview.api.open_pdf_base64(base64PDF);
  } else {
    alert("PDF preview not supported in this environment.");
  }
}

function VolsPage({ locale = "fr" }) {
  const [vols, setVols] = useState([]);
  const [selectedIndex, setSelectedIndex] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [modalMode, setModalMode] = useState("add");
  const emptyForm = {
    id: null,
    flight_number: "",
    compagnie: "",
    depart_aeroport: "",
    arrivee_aeroport: "",
    date_depart: "",
    heure_depart: "",
    duree: "",
    classe: "",
    prix: "",
    places_disponibles: "",
    statut: "OK"
  };
  const [volForm, setVolForm] = useState({ ...emptyForm });
  const [isLoading, setIsLoading] = useState(false);
  const [isDeleting, setIsDeleting] = useState(false);

  const isRtl = locale === "ar";
  const displayedHeaders = isRtl ? [...translations[locale].tableHeaders].reverse() : translations[locale].tableHeaders;
  const tableFieldsOrder = isRtl ? [...TABLE_FIELDS].reverse() : [...TABLE_FIELDS];
  const directionStyle = isRtl ? { direction: "rtl" } : { direction: "ltr" };

  const fetchVols = async () => {
    setIsLoading(true);
    try {
      const res = await window.pywebview.api.get_vols();
      setVols((res || []).map(v => ({
        ...v,
        prix: v.prix !== undefined ? String(v.prix) : "",
        places_disponibles: v.places_disponibles !== undefined ? String(v.places_disponibles) : ""
      })));
    } catch (e) {
      setVols([]);
    }
    setIsLoading(false);
  };

  useEffect(() => {
    fetchVols();
    // eslint-disable-next-line
  }, [locale]);

  const filteredVols = vols.filter(v => {
    const q = searchQuery.trim().toLowerCase();
    if (!q) return true;
    return (
      (v.flight_number || "").toLowerCase().includes(q) ||
      (v.compagnie || "").toLowerCase().includes(q) ||
      (v.depart_aeroport || "").toLowerCase().includes(q) ||
      (v.arrivee_aeroport || "").toLowerCase().includes(q)
    );
  });

  const totalVols = filteredVols.length;
  const totalSeats = filteredVols.reduce((s, v) => s + (parseInt(v.places_disponibles || "0", 10) || 0), 0);

  const handleAjouter = () => {
    setModalMode("add");
    setVolForm({ ...emptyForm });
    setShowModal(true);
  };
  const handleModifier = () => {
    if (selectedIndex === null || !filteredVols[selectedIndex]) {
      alert(translations[locale].selectRowAlert);
      return;
    }
    const sel = filteredVols[selectedIndex];
    setModalMode("edit");
    setVolForm({
      ...sel,
      date_depart: formatDateToFR(sel.date_depart)
    });
    setShowModal(true);
  };
  const handleSupprimer = async () => {
    if (selectedIndex === null || !filteredVols[selectedIndex]) {
      alert(translations[locale].selectRowAlert);
      return;
    }
    if (isDeleting) return;
    const id = filteredVols[selectedIndex].id;
    const name = filteredVols[selectedIndex].flight_number || "";
    if (!window.confirm(translations[locale].confirmDelete(name))) return;
    try {
      setIsDeleting(true);
      await window.pywebview.api.delete_vol(id);
      setVols(prev => prev.filter(v => v.id !== id));
      setSelectedIndex(null);
    } catch (e) {
      alert(translations[locale].errorDelete);
    } finally {
      setIsDeleting(false);
    }
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setVolForm({ ...emptyForm });
  };

  const handleSave = async (e) => {
    e.preventDefault();
    try {
      const payload = {
        ...volForm,
        date_depart: formatDateToISO(volForm.date_depart)
      };
      if (modalMode === "add") {
        const newV = await window.pywebview.api.add_vol(payload);
        if (newV && newV.id !== undefined) {
          setVols(prev => [...prev, { ...newV, prix: newV.prix !== undefined ? String(newV.prix) : "", places_disponibles: newV.places_disponibles !== undefined ? String(newV.places_disponibles) : "" }]);
        } else {
          await fetchVols();
        }
      } else {
        const updated = await window.pywebview.api.update_vol(payload);
        if (updated && updated.id !== undefined) {
          setVols(prev => prev.map(v => v.id === updated.id ? { ...updated, prix: updated.prix !== undefined ? String(updated.prix) : "", places_disponibles: updated.places_disponibles !== undefined ? String(updated.places_disponibles) : "" } : v));
        } else {
          await fetchVols();
        }
      }
      handleCloseModal();
    } catch (err) {
      alert(translations[locale].errorSave);
    }
  };

  const handleImpression = () => window.print();
  const handleFiche = () => {
    if (selectedIndex === null || !filteredVols[selectedIndex]) {
      alert(translations[locale].selectRowAlert);
      return;
    }
    generateVolPDF(filteredVols[selectedIndex], locale);
  };

  // small style reuse from your caisse layout (keeps same look & feel)
  const styles = {
    pageContainer: { padding: "1.25rem", position: "relative", minHeight: "100vh", boxSizing: "border-box" },
    searchContainer: { marginBottom: "1rem", display: "flex", alignItems: "center", maxWidth: "420px", gap: 8, flex: 1 },
    searchIcon: { fontSize: "1.2rem", marginRight: "0.5rem" },
    searchInput: { width: "100%", padding: "0.5rem", fontSize: "1rem", borderRadius: "4px", border: "1px solid #ccc" },
    table: { width: "100%", borderCollapse: "collapse", marginTop: 0, tableLayout: "fixed" },
    tableHeader: { padding: "0.5rem", border: "1px solid #c2c2c2", fontWeight: "bold", fontSize: "0.98rem", background: "#e8f0fe" },
    tableCell: { padding: "0.35rem 0.45rem", border: "1px solid #e0e0e0", fontSize: "0.96rem", wordBreak: "break-word", verticalAlign: "middle" },
    drawer: { position: "fixed", background: "transparent", padding: "1rem", borderRadius: "8px", boxShadow: "0 2px 8px rgba(0,0,0,0.15)", display: "flex", gap: "0.5rem", zIndex: 8 },
    drawerButton: { display: "flex", alignItems: "center", gap: "0.3rem", padding: "0.5rem 1rem", fontSize: "1rem", color: "#3477eb", background: "#eaf3ff", border: "none", borderRadius: "4px", cursor: "pointer" },
    modalOverlay: { position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh", background: "rgba(0,0,0,0.5)", display: "flex", alignItems: "center", justifyContent: "center", zIndex: 1000 },
    modalContent: { background: "#fff", padding: "2rem", borderRadius: "8px", width: "96%", maxWidth: "820px", boxShadow: "0 2px 16px rgba(0,0,0,0.25)" },
    modalForm: { display: "flex", flexDirection: "column", gap: "1rem" },
    modalLabel: { display: "flex", flexDirection: "column", fontSize: "0.9rem", color: "#333" },
    modalInput: { padding: "0.5rem", fontSize: "1rem", borderRadius: "4px", border: "1px solid #ccc", marginTop: "0.3rem" },
    modalButtons: { display: "flex", justifyContent: "flex-end", gap: "1rem", marginTop: "1rem" },
    modalButton: { padding: "0.5rem 1.2rem", fontSize: "1rem", borderRadius: "4px", border: "none", cursor: "pointer", background: "#3477eb", color: "#fff", fontWeight: "bold" },
    summaryBadge: { padding: "0.4rem 0.6rem", background: "rgba(255,255,255,0.9)", borderRadius: 8, border: "1px solid #e6eefb", display: "flex", alignItems: "center", gap: 8, fontWeight: 700, color: "#14355f" }
  };

  return (
    <div style={{ ...styles.pageContainer, ...directionStyle }}>
      <h2 style={{ textAlign: "center" }}>{translations[locale].title}</h2>

      <div style={{ display: "flex", gap: 12, marginBottom: 12, alignItems: "center" }}>
        <div style={{ ...styles.searchContainer, maxWidth: 420, flexDirection: isRtl ? "row-reverse" : "row" }}>
          <span style={styles.searchIcon}>üîç</span>
          <input
            type="text"
            placeholder={translations[locale].searchPlaceholder}
            value={searchQuery}
            onChange={e => { setSearchQuery(e.target.value); setSelectedIndex(null); }}
            style={styles.searchInput}
          />
        </div>
        <div style={{ marginLeft: "auto", display: "flex", gap: 8 }}>
          <div style={styles.summaryBadge}>
            ‚úàÔ∏è {locale === "fr" ? "Total Vols" : "ÿ•ÿ¨ŸÖÿßŸÑŸä ÿßŸÑÿ±ÿ≠ŸÑÿßÿ™"}:
            <strong style={{ marginLeft: 6 }}>{totalVols}</strong>
          </div>
          <div style={styles.summaryBadge}>
            ü™ë {locale === "fr" ? "Total Places" : "ÿ•ÿ¨ŸÖÿßŸÑŸä ÿßŸÑŸÖŸÇÿßÿπÿØ"}:
            <strong style={{ marginLeft: 6 }}>{totalSeats}</strong>
          </div>
        </div>
      </div>

      <div style={{ maxHeight: "60vh", overflowY: "auto", border: "1px solid #e0e0e0", borderRadius: 6 }}>
        <table style={styles.table}>
          <thead>
            <tr style={{ background: "#e8f0fe" }}>
              {displayedHeaders.map((h, i) => (
                <th key={i} style={{ ...styles.tableHeader, ...(i === 0 ? { width: 36 } : {}) }}>{h}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {filteredVols.map((vol, idx) => (
              <tr
                key={vol.id || (vol.flight_number + idx)}
                style={{ background: idx === selectedIndex ? "#cce5ff" : idx % 2 === 0 ? "#fff" : "#f4f8ff", cursor: "pointer" }}
                onClick={() => setSelectedIndex(idx)}
              >
                <td style={styles.tableCell}>{idx + 1}</td>
                {tableFieldsOrder.map(field => (
                  <td key={field} style={styles.tableCell}>
                    {field === "date_depart" ? formatDateToFR(vol[field]) : (vol[field] ?? "-")}
                  </td>
                ))}
              </tr>
            ))}
            {filteredVols.length === 0 && !isLoading && (
              <tr>
                <td colSpan={displayedHeaders.length} style={{ padding: "1rem", textAlign: "center", color: "#666" }}>
                  {translations[locale].errorLoad}
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>

      {/* left drawer */}
      <div style={{ position: "fixed", left: 20, bottom: 28, display: "flex", gap: 8, zIndex: 8 }}>
        <button style={styles.drawerButton} onClick={handleAjouter}>{translations[locale].addButton}</button>
        <button style={styles.drawerButton} onClick={handleModifier}>{translations[locale].editButton}</button>
        <button style={styles.drawerButton} onClick={handleSupprimer}>{translations[locale].deleteButton}</button>
      </div>
      {/* right drawer */}
      <div style={{ position: "fixed", right: 20, bottom: 28, display: "flex", gap: 8, zIndex: 8 }}>
        <button style={{ ...styles.drawerButton, background: "#1b9e5c", color: "#fff" }} onClick={handleFiche}>{translations[locale].ficheButton}</button>
        <button style={{ ...styles.drawerButton, background: "#3477eb", color: "#fff" }} onClick={handleImpression}>{translations[locale].printButton}</button>
      </div>

      {showModal && (
        <div style={styles.modalOverlay}>
          <div style={{ ...styles.modalContent, ...(isRtl ? { direction: "rtl", textAlign: "right" } : {}) }}>
            <h3>{modalMode === "add" ? translations[locale].addVol : translations[locale].editVol}</h3>
            <form onSubmit={handleSave} style={styles.modalForm}>
              <div style={{ display: "flex", gap: 8 }}>
                <label style={styles.modalLabel}>
                  {translations[locale].modalLabels.flight_number}:
                  <input style={styles.modalInput} value={volForm.flight_number} onChange={e => setVolForm({ ...volForm, flight_number: e.target.value })} required />
                </label>
                <label style={styles.modalLabel}>
                  {translations[locale].modalLabels.compagnie}:
                  <input style={styles.modalInput} value={volForm.compagnie} onChange={e => setVolForm({ ...volForm, compagnie: e.target.value })} />
                </label>
              </div>

              <div style={{ display: "flex", gap: 8 }}>
                <label style={styles.modalLabel}>
                  {translations[locale].modalLabels.depart_aeroport}:
                  <input style={styles.modalInput} value={volForm.depart_aeroport} onChange={e => setVolForm({ ...volForm, depart_aeroport: e.target.value })} />
                </label>
                <label style={styles.modalLabel}>
                  {translations[locale].modalLabels.arrivee_aeroport}:
                  <input style={styles.modalInput} value={volForm.arrivee_aeroport} onChange={e => setVolForm({ ...volForm, arrivee_aeroport: e.target.value })} />
                </label>
              </div>

              <div style={{ display: "flex", gap: 8 }}>
                <label style={styles.modalLabel}>
                  {translations[locale].modalLabels.date_depart}:
                  <input type="date" style={styles.modalInput} value={volForm.date_depart ? formatDateToISO(volForm.date_depart) : ""} onChange={e => setVolForm({ ...volForm, date_depart: e.target.value ? formatDateToFR(e.target.value) : "" })} />
                </label>
                <label style={styles.modalLabel}>
                  {translations[locale].modalLabels.heure_depart}:
                  <input type="time" style={styles.modalInput} value={volForm.heure_depart} onChange={e => setVolForm({ ...volForm, heure_depart: e.target.value })} />
                </label>
              </div>

              <div style={{ display: "flex", gap: 8 }}>
                <label style={styles.modalLabel}>
                  {translations[locale].modalLabels.duree}:
                  <input style={styles.modalInput} value={volForm.duree} onChange={e => setVolForm({ ...volForm, duree: e.target.value })} />
                </label>
                <label style={styles.modalLabel}>
                  {translations[locale].modalLabels.classe}:
                  <input style={styles.modalInput} value={volForm.classe} onChange={e => setVolForm({ ...volForm, classe: e.target.value })} />
                </label>
              </div>

              <div style={{ display: "flex", gap: 8 }}>
                <label style={styles.modalLabel}>
                  {translations[locale].modalLabels.prix}:
                  <input type="number" step="0.01" style={styles.modalInput} value={volForm.prix} onChange={e => setVolForm({ ...volForm, prix: e.target.value })} />
                </label>
                <label style={styles.modalLabel}>
                  {translations[locale].modalLabels.places_disponibles}:
                  <input type="number" style={styles.modalInput} value={volForm.places_disponibles} onChange={e => setVolForm({ ...volForm, places_disponibles: e.target.value })} />
                </label>
              </div>

              <div>
                <label style={styles.modalLabel}>
                  {translations[locale].modalLabels.statut}:
                  <select style={styles.modalInput} value={volForm.statut} onChange={e => setVolForm({ ...volForm, statut: e.target.value })}>
                    <option value="OK">OK</option>
                    <option value="Annul√©">Annul√©</option>
                  </select>
                </label>
              </div>

              <div style={styles.modalButtons}>
                <button type="submit" style={styles.modalButton}>{translations[locale].save}</button>
                <button type="button" style={{ ...styles.modalButton, background: "#999" }} onClick={handleCloseModal}>{translations[locale].cancel}</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default VolsPage;
