import React, { useEffect, useState } from "react";

function ClientPage() {
  // Client data state
  const [clients, setClients] = useState([]);
  const [selectedIndex, setSelectedIndex] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");

  // Modal state: whether modal is visible and which mode ("add" or "edit") is active.
  const [showModal, setShowModal] = useState(false);
  const [modalMode, setModalMode] = useState("add"); // "add" or "edit"
  const [clientForm, setClientForm] = useState({
    id: null,
    passport: "",
    full_name: "",
    birthdate: "",
    phone: "",
    email: "",
    address: "",
  });

  // Fetch clients from the backend
  const fetchClients = async () => {
    try {
      const result = await window.pywebview.api.get_clients();
      setClients(result);
    } catch (err) {
      alert("Erreur lors du chargement des clients");
    }
  };

  useEffect(() => {
    fetchClients();
  }, []);

  // Button Handlers
  const handleAjouter = () => {
    setModalMode("add");
    setClientForm({
      id: null,
      passport: "",
      full_name: "",
      birthdate: "",
      phone: "",
      email: "",
      address: "",
    });
    setShowModal(true);
  };

  const handleModifier = () => {
    if (selectedIndex !== null && filteredClients[selectedIndex]) {
      setModalMode("edit");
      setClientForm({ ...filteredClients[selectedIndex] });
      setShowModal(true);
    } else {
      alert("Veuillez s√©lectionner une ligne client.");
    }
  };

  const handleSupprimer = async () => {
    if (selectedIndex !== null && filteredClients[selectedIndex]) {
      if (
        window.confirm(
          `Supprimer le client ${filteredClients[selectedIndex].full_name} ?`
        )
      ) {
        try {
          await window.pywebview.api.delete_client(
            filteredClients[selectedIndex].id
          );
          setSelectedIndex(null);
          fetchClients();
        } catch (err) {
          alert("Erreur lors de la suppression !");
        }
      }
    } else {
      alert("Veuillez s√©lectionner une ligne client.");
    }
  };

  // Modal Handlers
  const handleCloseModal = () => {
    setShowModal(false);
    setClientForm({
      id: null,
      passport: "",
      full_name: "",
      birthdate: "",
      phone: "",
      email: "",
      address: "",
    });
  };

  const handleSaveClient = async (e) => {
    e.preventDefault();
    try {
      if (modalMode === "add") {
        await window.pywebview.api.add_client(clientForm);
      } else if (modalMode === "edit") {
        await window.pywebview.api.update_client(clientForm);
      }
      handleCloseModal();
      fetchClients();
    } catch (err) {
      alert("Erreur lors de l'enregistrement !");
    }
  };

  // Filtered clients based on search query (searching passport, full_name, or email)
  const filteredClients = clients.filter((client) => {
    const query = searchQuery.toLowerCase();
    return (
      client.passport.toLowerCase().includes(query) ||
      client.full_name.toLowerCase().includes(query) ||
      client.email.toLowerCase().includes(query)
    );
  });

  return (
    <div style={styles.pageContainer}>
      <h2 style={styles.title}>Liste des Clients</h2>
      {/* Sticky Search Bar */}
      <div style={styles.stickyHeader}>
        <div style={styles.searchContainer}>
          <span style={styles.searchIcon}>üîç</span>
          <input
            type="text"
            placeholder="Rechercher..."
            value={searchQuery}
            onChange={(e) => {
              setSearchQuery(e.target.value);
              setSelectedIndex(null);
            }}
            style={styles.searchInput}
          />
        </div>
        <table style={styles.table}>
          <thead style={styles.stickyTableHeader}>
            <tr>
              <th style={styles.tableHeader}>#</th>
              <th style={styles.tableHeader}>N¬∞ Passeport</th>
              <th style={styles.tableHeader}>Nom complet</th>
              <th style={styles.tableHeader}>Date de naissance</th>
              <th style={styles.tableHeader}>T√©l√©phone</th>
              <th style={styles.tableHeader}>Email</th>
              <th style={styles.tableHeader}>Adresse</th>
            </tr>
          </thead>
        </table>
      </div>
      {/* Scrollable Table Body */}
      <div style={styles.scrollableTableContainer}>
        <table style={styles.table}>
          <tbody>
            {filteredClients.map((client, idx) => (
              <tr
                key={client.id || client.passport + idx}
                style={{
                  ...styles.tableRow,
                  background:
                    idx === selectedIndex
                      ? "#cce5ff"
                      : idx % 2 === 0
                      ? "#fff"
                      : "#f4f8ff",
                }}
                onClick={() => setSelectedIndex(idx)}
              >
                <td style={styles.tableCell}>{idx + 1}</td>
                <td style={styles.tableCell}>{client.passport}</td>
                <td style={styles.tableCell}>{client.full_name}</td>
                <td style={styles.tableCell}>{client.birthdate}</td>
                <td style={styles.tableCell}>{client.phone}</td>
                <td style={styles.tableCell}>{client.email}</td>
                <td style={styles.tableCell}>{client.address}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      {/* Horizontal drawer for buttons */}
      <div style={styles.drawer}>
        <button style={styles.drawerButton} onClick={handleAjouter}>
          ‚ûï Ajouter
        </button>
        <button style={styles.drawerButton} onClick={handleModifier}>
          ‚úèÔ∏è Modifier
        </button>
        <button style={styles.drawerButton} onClick={handleSupprimer}>
          üóëÔ∏è Supprimer
        </button>
      </div>
      {/* Modal for adding or modifying a client */}
      {showModal && (
        <div style={styles.modalOverlay}>
          <div style={styles.modalContent}>
            <h3>
              {modalMode === "add" ? "Ajouter Nouveau Client" : "Modifier Client"}
            </h3>
            <form onSubmit={handleSaveClient} style={styles.modalForm}>
              <label style={styles.modalLabel}>
                Passeport:
                <input
                  type="text"
                  value={clientForm.passport}
                  onChange={(e) =>
                    setClientForm({ ...clientForm, passport: e.target.value })
                  }
                  style={styles.modalInput}
                  required
                />
              </label>
              <label style={styles.modalLabel}>
                Nom complet:
                <input
                  type="text"
                  value={clientForm.full_name}
                  onChange={(e) =>
                    setClientForm({ ...clientForm, full_name: e.target.value })
                  }
                  style={styles.modalInput}
                  required
                />
              </label>
              <label style={styles.modalLabel}>
                Date de naissance:
                <input
                  type="date"
                  value={clientForm.birthdate}
                  onChange={(e) =>
                    setClientForm({ ...clientForm, birthdate: e.target.value })
                  }
                  style={styles.modalInput}
                  required
                />
              </label>
              <label style={styles.modalLabel}>
                T√©l√©phone:
                <input
                  type="text"
                  value={clientForm.phone}
                  onChange={(e) =>
                    setClientForm({ ...clientForm, phone: e.target.value })
                  }
                  style={styles.modalInput}
                  required
                />
              </label>
              <label style={styles.modalLabel}>
                Email:
                <input
                  type="email"
                  value={clientForm.email}
                  onChange={(e) =>
                    setClientForm({ ...clientForm, email: e.target.value })
                  }
                  style={styles.modalInput}
                  required
                />
              </label>
              <label style={styles.modalLabel}>
                Adresse:
                <input
                  type="text"
                  value={clientForm.address}
                  onChange={(e) =>
                    setClientForm({ ...clientForm, address: e.target.value })
                  }
                  style={styles.modalInput}
                  required
                />
              </label>
              <div style={styles.modalButtons}>
                <button type="submit" style={styles.modalButton}>
                  Enregistrer
                </button>
                <button
                  type="button"
                  style={styles.modalButton}
                  onClick={handleCloseModal}
                >
                  Annuler
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

const styles = {
  pageContainer: {
    padding: "60px 2rem 2rem", // top padding to leave room for the sticky header & center the title
    maxWidth: "1200px",
    margin: "0 auto",
    position: "relative",
  },
  title: {
    textAlign: "center",
    marginBottom: "30px",
  },
  stickyHeader: {
    position: "sticky",
    top: 0,
    background: "#f0f6ff",
    zIndex: 100,
    paddingBottom: "10px",
  },
  searchContainer: {
    display: "flex",
    alignItems: "center",
    maxWidth: "400px",
    margin: "0 auto 10px auto",
    padding: "10px 0",
  },
  searchIcon: {
    fontSize: "1.2rem",
    marginRight: "0.5rem",
  },
  searchInput: {
    flex: 1,
    padding: "0.5rem",
    fontSize: "1rem",
    borderRadius: "4px",
    border: "1px solid #ccc",
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
  },
  stickyTableHeader: {
    position: "sticky",
    top: "60px", // height of the search bar area; adjust as needed
    background: "#e8f0fe",
    zIndex: 90,
  },
  tableHeader: {
    padding: "0.7rem",
    border: "1px solid #c2c2c2",
    fontWeight: "bold",
  },
  tableRow: {
    cursor: "pointer",
  },
  tableCell: {
    padding: "0.6rem",
    border: "1px solid #e0e0e0",
  },
  scrollableTableContainer: {
    maxHeight: "600px", // Adjust this value to set a fixed scrollable area for the table body
    overflowY: "auto",
  },
  // Drawer styles for CRUD buttons
  drawer: {
    position: "fixed",
    bottom: "20px",
    left: "20px",
    background: "#ffffff",
    padding: "1rem",
    borderRadius: "8px",
    boxShadow: "0 2px 8px rgba(0,0,0,0.15)",
    display: "flex",
    flexDirection: "row",
    gap: "0.5rem",
  },
  drawerButton: {
    display: "flex",
    alignItems: "center",
    gap: "0.3rem",
    padding: "0.5rem 1rem",
    fontSize: "1rem",
    color: "#3477eb",
    background: "#e8f0fe",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
    transition: "background 0.2s",
  },
  // Modal styles
  modalOverlay: {
    position: "fixed",
    top: 0,
    left: 0,
    width: "100vw",
    height: "100vh",
    background: "rgba(0, 0, 0, 0.5)",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    zIndex: 1000,
  },
  modalContent: {
    background: "#fff",
    padding: "2rem",
    borderRadius: "8px",
    width: "90%",
    maxWidth: "400px",
    boxShadow: "0 2px 16px rgba(0,0,0,0.25)",
  },
  modalForm: {
    display: "flex",
    flexDirection: "column",
    gap: "1rem",
  },
  modalLabel: {
    display: "flex",
    flexDirection: "column",
    fontSize: "0.9rem",
    color: "#333",
  },
  modalInput: {
    padding: "0.5rem",
    fontSize: "1rem",
    borderRadius: "4px",
    border: "1px solid #ccc",
    marginTop: "0.3rem",
  },
  modalButtons: {
    display: "flex",
    justifyContent: "flex-end",
    gap: "1rem",
    marginTop: "1rem",
  },
  modalButton: {
    padding: "0.5rem 1rem",
    fontSize: "1rem",
    borderRadius: "4px",
    border: "none",
    cursor: "pointer",
    background: "#3477eb",
    color: "#fff",
    fontWeight: "bold",
    transition: "background 0.2s",
  },
};

export default ClientPage;
