import React, { useEffect, useState } from "react";

/*
  caisse.js

  Cash ledger (Caisse) page ‚Äî table UI similar to personel.js.

  Features:
  - Table columns: Date, Heure, Op√©ration, Nature, Montant, Solde
  - Add / Edit / Delete buttons (drawer on left)
  - Impression (print) and Export (simple print) button (drawer on right)
  - Modal for adding/editing entries with validation
  - Fetch/save/delete via window.pywebview.api:
      - get_caisse() -> returns list of records
      - add_caisse(payload) -> adds record, returns saved record
      - update_caisse(payload) -> updates record, returns saved record
      - delete_caisse(id) -> deletes record
    (If your backend uses different function names, adapt accordingly.)
  - Simple LTR/RTL support via locale prop (fr / ar)
  - Numeric formatting for montants (space thousands, dot decimals)
*/

function formatNumberSpaceDot(value) {
  if (value === null || value === undefined || value === "") return "";
  const cleaned = String(value).replace(/\s+/g, "").replace(",", ".");
  const n = Number(cleaned);
  if (!Number.isFinite(n)) return String(value);
  const parts = n.toFixed(2).split(".");
  parts[0] = parts[0].replace(/\B(?=(\d{3})+(?!\d))/g, " ");
  return parts.join(".");
}

function parseNumber(value) {
  if (value === null || value === undefined || value === "") return 0;
  const s = String(value).replace(/\s+/g, "").replace(",", ".");
  const n = Number(s);
  return Number.isFinite(n) ? n : 0;
}

function formatDateToDisplay(iso) {
  if (!iso) return "";
  // accept ISO "YYYY-MM-DD" or with time; display DD/MM/YYYY
  const m = iso.match(/^(\d{4})-(\d{2})-(\d{2})/);
  if (m) return `${m[3]}/${m[2]}/${m[1]}`;
  return iso;
}

function toISODate(input) {
  // input may be "DD/MM/YYYY" or "YYYY-MM-DD" or Date object
  if (!input) return "";
  if (input instanceof Date) {
    const yyyy = input.getFullYear();
    const mm = String(input.getMonth() + 1).padStart(2, "0");
    const dd = String(input.getDate()).padStart(2, "0");
    return `${yyyy}-${mm}-${dd}`;
  }
  if (/^\d{2}\/\d{2}\/\d{4}$/.test(input)) {
    const [dd, mm, yyyy] = input.split("/");
    return `${yyyy}-${mm}-${dd}`;
  }
  if (/^\d{4}-\d{2}-\d{2}$/.test(input)) return input;
  return input;
}

function photoSrc(val) {
  if (!val) return null;
  if (typeof val !== "string") return null;
  if (val.startsWith("data:")) return val;
  return `data:image/png;base64,${val}`;
}

export default function CaissePage({ locale = "fr" }) {
  const isRtl = locale === "ar";
  const [entries, setEntries] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedIndex, setSelectedIndex] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [modalMode, setModalMode] = useState("add"); // add | edit
  const [form, setForm] = useState({
    id: null,
    date: "",    // YYYY-MM-DD
    heure: "",   // HH:MM
    operation: "",
    nature: "",
    montant: "", // numeric string
    solde: ""    // numeric string (optional ‚Äî could be computed server-side)
  });
  const [error, setError] = useState("");

  const TABLE_FIELDS = ["date", "heure", "operation", "nature", "montant", "solde"];
  const headersFr = ["#", "Date", "Heure", "Op√©ration", "Nature", "Montant", "Solde"];
  const headersAr = ["#", "ÿßŸÑÿ™ÿßÿ±ŸäÿÆ", "ÿßŸÑÿ≥ÿßÿπÿ©", "ÿßŸÑÿπŸÖŸÑŸäÿ©", "ÿßŸÑŸÜŸàÿπ", "ÿßŸÑŸÖÿ®ŸÑÿ∫", "ÿßŸÑÿ±ÿµŸäÿØ"];
  const headers = locale === "ar" ? headersAr : headersFr;

  const directionStyle = isRtl ? { direction: "rtl" } : { direction: "ltr" };

  async function fetchEntries() {
    setLoading(true);
    try {
      if (window.pywebview && window.pywebview.api && window.pywebview.api.get_caisse) {
        const res = await window.pywebview.api.get_caisse();
        // normalize amounts to strings
        const normalized = (res || []).map(r => ({
          ...r,
          montant: r.montant !== undefined && r.montant !== null ? String(r.montant) : "",
          solde: r.solde !== undefined && r.solde !== null ? String(r.solde) : "",
          date: r.date || r.date_iso || ""
        }));
        setEntries(normalized);
      } else {
        setEntries([]);
      }
    } catch (e) {
      console.error("Failed to load caisse entries", e);
      setEntries([]);
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => {
    fetchEntries();
    // eslint-disable-next-line
  }, [locale]);

  const filtered = entries.filter((e) => {
    const q = searchQuery.trim().toLowerCase();
    if (!q) return true;
    return (
      (e.date && e.date.toLowerCase().includes(q)) ||
      (e.heure && e.heure.toLowerCase().includes(q)) ||
      (e.operation && String(e.operation).toLowerCase().includes(q)) ||
      (e.nature && String(e.nature).toLowerCase().includes(q)) ||
      (String(e.montant || "").toLowerCase().includes(q)) ||
      (String(e.solde || "").toLowerCase().includes(q))
    );
  });

  function openAdd() {
    setModalMode("add");
    setForm({
      id: null,
      date: toISODate(new Date()),
      heure: `${String(new Date().getHours()).padStart(2,"0")}:${String(new Date().getMinutes()).padStart(2,"0")}`,
      operation: "",
      nature: "",
      montant: "",
      solde: ""
    });
    setError("");
    setShowModal(true);
  }

  function openEdit() {
    if (selectedIndex === null || filtered[selectedIndex] === undefined) {
      alert(locale === "ar" ? "ÿßŸÑÿ±ÿ¨ÿßÿ° ÿ™ÿ≠ÿØŸäÿØ ÿ≥ÿ∑ÿ±." : "Please select a row.");
      return;
    }
    const row = filtered[selectedIndex];
    setModalMode("edit");
    setForm({
      id: row.id ?? null,
      date: row.date ? toISODate(row.date) : "",
      heure: row.heure || "",
      operation: row.operation || "",
      nature: row.nature || "",
      montant: row.montant !== undefined && row.montant !== null ? String(row.montant) : "",
      solde: row.solde !== undefined && row.solde !== null ? String(row.solde) : ""
    });
    setError("");
    setShowModal(true);
  }

  async function handleDelete() {
    if (selectedIndex === null || filtered[selectedIndex] === undefined) {
      alert(locale === "ar" ? "ÿßŸÑÿ±ÿ¨ÿßÿ° ÿ™ÿ≠ÿØŸäÿØ ÿ≥ÿ∑ÿ±." : "Please select a row.");
      return;
    }
    const row = filtered[selectedIndex];
    const name = `${row.date || ""} ${row.operation || ""}`;
    if (!window.confirm((locale === "ar" ? "ÿ≠ÿ∞ŸÅ ÿßŸÑÿ≥ÿ¨ŸÑ " : "Delete entry ") + name + "?")) return;
    try {
      if (window.pywebview && window.pywebview.api && window.pywebview.api.delete_caisse) {
        await window.pywebview.api.delete_caisse(row.id);
        // refresh
        await fetchEntries();
        setSelectedIndex(null);
      } else {
        // fallback: remove locally by id
        setEntries(prev => prev.filter(x => x.id !== row.id));
        setSelectedIndex(null);
      }
    } catch (e) {
      console.error("delete failed", e);
      alert(locale === "ar" ? "ŸÅÿ¥ŸÑ ÿßŸÑÿ≠ÿ∞ŸÅ" : "Delete failed");
    }
  }

  async function handleSave(e) {
    e?.preventDefault?.();
    setError("");
    // basic validation
    if (!form.date || !form.heure || !form.operation || !form.nature || form.montant === "") {
      setError(locale === "ar" ? "ÿ™ÿ£ŸÉÿØ ŸÖŸÜ ŸÖŸÑÿ° ÿßŸÑÿ≠ŸÇŸàŸÑ ÿßŸÑŸÖÿ∑ŸÑŸàÿ®ÿ©" : "Please fill required fields");
      return;
    }

    const payload = {
      ...form,
      date: toISODate(form.date),
      montant: parseNumber(form.montant),
      solde: form.solde === "" ? null : parseNumber(form.solde)
    };

    try {
      if (modalMode === "add") {
        if (window.pywebview && window.pywebview.api && window.pywebview.api.add_caisse) {
          const saved = await window.pywebview.api.add_caisse(payload);
          // refresh from server for canonical state
          await fetchEntries();
        } else {
          // fallback: local insert with temporary id
          const tmpId = Date.now();
          setEntries(prev => [...prev, { ...payload, id: tmpId }]);
        }
      } else {
        if (window.pywebview && window.pywebview.api && window.pywebview.api.update_caisse) {
          const saved = await window.pywebview.api.update_caisse(payload);
          await fetchEntries();
        } else {
          setEntries(prev => prev.map(x => (x.id === payload.id ? { ...x, ...payload } : x)));
        }
      }
      setShowModal(false);
    } catch (err) {
      console.error("save error", err);
      setError(locale === "ar" ? "ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿ≠ŸÅÿ∏" : "Save error");
    }
  }

  function handlePrint() {
    window.print();
  }

  // Render helpers
  function renderCell(row, field, idx) {
    const isLast = idx === TABLE_FIELDS.length - 1;
    const baseStyle = { ...styles.tableCell, borderRight: isLast ? "none" : "1px solid #e6eefb" };
    if (field === "date") {
      return <td key={field} style={baseStyle}>{formatDateToDisplay(row.date) || "-"}</td>;
    }
    if (field === "montant" || field === "solde") {
      return <td key={field} style={baseStyle}><span style={styles.ltr}>{formatNumberSpaceDot(row[field])}</span></td>;
    }
    return <td key={field} style={baseStyle}>{row[field] ?? "-"}</td>;
  }

  return (
    <div style={{ ...styles.container, ...directionStyle }}>
      <h2 style={{ textAlign: "center" }}>{locale === "ar" ? "ÿßŸÑÿÆÿ≤ŸäŸÜÿ©" : "Caisse"}</h2>

      <div style={{ display: "flex", gap: 12, marginBottom: 12, alignItems: "center" }}>
        <div style={{ ...styles.searchWrap, maxWidth: 420 }}>
          <span style={{ marginRight: 8 }}>üîç</span>
          <input
            placeholder={locale === "ar" ? "ÿ®ÿ≠ÿ´..." : "Search..."}
            value={searchQuery}
            onChange={e => { setSearchQuery(e.target.value); setSelectedIndex(null); }}
            style={styles.searchInput}
          />
        </div>

        <div style={{ marginLeft: "auto", display: "flex", gap: 8 }}>
          <div style={styles.summaryBadge}>
            {locale === "ar" ? "ÿßŸÑÿ≥ÿ¨ŸÑÿßÿ™" : "Entries"}: <strong style={{ marginLeft: 6 }}>{filtered.length}</strong>
          </div>
        </div>
      </div>

      <div style={styles.tableWrap}>
        <table style={styles.table}>
          <thead>
            <tr style={styles.tableHeaderRow}>
              {/* numbering header */}
              <th style={{ ...styles.tableHeader, borderRight: "1px solid #e6eefb", width: 36 }}>#</th>
              {TABLE_FIELDS.map((f, idx) => (
                <th key={f} style={{ ...styles.tableHeader, borderRight: idx === TABLE_FIELDS.length - 1 ? "none" : "1px solid #e6eefb" }}>
                  {headers[idx + 1]}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {loading ? (
              <tr><td colSpan={1 + TABLE_FIELDS.length} style={{ padding: 20, textAlign: "center" }}>{locale === "ar" ? "ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ..." : "Loading..."}</td></tr>
            ) : filtered.length === 0 ? (
              <tr><td colSpan={1 + TABLE_FIELDS.length} style={{ padding: 20, textAlign: "center" }}>{locale === "ar" ? "ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ≥ÿ¨ŸÑÿßÿ™" : "No entries"}</td></tr>
            ) : (
              filtered.map((row, i) => (
                <tr
                  key={row.id ?? i}
                  onClick={() => setSelectedIndex(i)}
                  style={{
                    ...styles.tableRow,
                    background: i === selectedIndex ? "#cce5ff" : i % 2 === 0 ? "#fff" : "#f4f8ff",
                    cursor: "pointer"
                  }}
                >
                  <td style={{ ...styles.tableCell, width: 36 }}>{i + 1}</td>
                  {TABLE_FIELDS.map((f, idx) => renderCell(row, f, idx))}
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      {/* Drawers */}
      <div style={{ ...styles.drawer, left: 20, right: "unset", bottom: 28 }}>
        <button style={styles.drawerButton} onClick={openAdd}>{locale === "ar" ? "‚ûï ÿ•ÿ∂ÿßŸÅÿ©" : "‚ûï Ajouter"}</button>
        <button style={styles.drawerButton} onClick={openEdit}>{locale === "ar" ? "‚úèÔ∏è ÿ™ÿπÿØŸäŸÑ" : "‚úèÔ∏è Modifier"}</button>
        <button style={styles.drawerButton} onClick={handleDelete}>{locale === "ar" ? "üóëÔ∏è ÿ≠ÿ∞ŸÅ" : "üóëÔ∏è Supprimer"}</button>
      </div>

      <div style={{ ...styles.drawer, right: 20, left: "unset", bottom: 28 }}>
        <button style={{ ...styles.drawerButton, background: "#3477eb", color: "#fff" }} onClick={handlePrint}>
          {locale === "ar" ? "üñ®Ô∏è ÿ∑ÿ®ÿßÿπÿ©" : "üñ®Ô∏è Impression"}
        </button>
      </div>

      {/* Modal */}
      {showModal && (
        <div style={styles.modalOverlay}>
          <div style={{ ...styles.modal, ...(isRtl ? { direction: "rtl", textAlign: "right" } : {}) }}>
            <h3>{modalMode === "add" ? (locale === "ar" ? "ÿ•ÿ∂ÿßŸÅÿ© ÿ≥ÿ¨ŸÑ" : "Ajouter entr√©e") : (locale === "ar" ? "ÿ™ÿπÿØŸäŸÑ ÿ≥ÿ¨ŸÑ" : "Modifier entr√©e")}</h3>

            <form onSubmit={handleSave} style={styles.form}>
              <div style={styles.row}>
                <label style={styles.label}>
                  {locale === "ar" ? "ÿßŸÑÿ™ÿßÿ±ŸäÿÆ" : "Date"}
                  <input type="date" value={form.date ? toISODate(form.date) : ""} onChange={e => setForm(s => ({ ...s, date: e.target.value }))} style={styles.input} required />
                </label>
                <label style={styles.label}>
                  {locale === "ar" ? "ÿßŸÑÿ≥ÿßÿπÿ©" : "Heure"}
                  <input type="time" value={form.heure || ""} onChange={e => setForm(s => ({ ...s, heure: e.target.value }))} style={styles.input} required />
                </label>
              </div>

              <div style={styles.row}>
                <label style={styles.label}>
                  {locale === "ar" ? "ÿßŸÑÿπŸÖŸÑŸäÿ©" : "Op√©ration"}
                  <input value={form.operation || ""} onChange={e => setForm(s => ({ ...s, operation: e.target.value }))} style={styles.input} required />
                </label>
                <label style={styles.label}>
                  {locale === "ar" ? "ÿßŸÑŸÜŸàÿπ" : "Nature"}
                  <input value={form.nature || ""} onChange={e => setForm(s => ({ ...s, nature: e.target.value }))} style={styles.input} required />
                </label>
              </div>

              <div style={styles.row}>
                <label style={styles.label}>
                  {locale === "ar" ? "ÿßŸÑŸÖÿ®ŸÑÿ∫" : "Montant"}
                  <input value={form.montant || ""} onChange={e => setForm(s => ({ ...s, montant: e.target.value }))} style={styles.input} type="number" step="0.01" required />
                </label>
                <label style={styles.label}>
                  {locale === "ar" ? "ÿßŸÑÿ±ÿµŸäÿØ (ÿßÿÆÿ™Ÿäÿßÿ±Ÿä)" : "Solde (optionnel)"}
                  <input value={form.solde || ""} onChange={e => setForm(s => ({ ...s, solde: e.target.value }))} style={styles.input} type="number" step="0.01" />
                </label>
              </div>

              {error && <div style={{ color: "red" }}>{error}</div>}

              <div style={{ display: "flex", justifyContent: "flex-end", gap: 8, marginTop: 12 }}>
                <button type="submit" style={styles.saveButton}>{locale === "ar" ? "ÿ≠ŸÅÿ∏" : "Enregistrer"}</button>
                <button type="button" style={styles.cancelButton} onClick={() => setShowModal(false)}>{locale === "ar" ? "ÿ•ŸÑÿ∫ÿßÿ°" : "Annuler"}</button>
              </div>
            </form>
          </div>
        </div>
      )}

    </div>
  );
}

const styles = {
  container: {
    padding: "1.25rem",
    position: "relative",
    minHeight: "100vh",
    boxSizing: "border-box"
  },
  searchWrap: {
    flex: 1,
    maxWidth: 420,
    display: "flex",
    alignItems: "center",
    gap: 8
  },
  searchInput: {
    width: "100%",
    padding: "0.5rem",
    borderRadius: 6,
    border: "1px solid #ccc",
    fontSize: "1rem"
  },
  tableWrap: {
    maxHeight: "60vh",
    overflowY: "auto",
    border: "1px solid #e0e0e0",
    borderRadius: 6,
    background: "#fff"
  },
  table: {
    width: "100%",
    borderCollapse: "separate",
    tableLayout: "fixed",
    borderSpacing: 0
  },
  tableHeaderRow: {
    background: "#f1f6ff"
  },
  tableHeader: {
    textAlign: "center",
    padding: "0.6rem",
    borderBottom: "1px solid #e2e8f0",
    background: "#f1f6ff",
    fontWeight: 700
  },
  tableRow: {
    cursor: "pointer"
  },
  tableCell: {
    padding: "0.45rem 0.6rem",
    borderBottom: "1px solid #f0f4fb",
    verticalAlign: "middle",
    wordBreak: "break-word"
  },
  drawer: {
    position: "fixed",
    background: "transparent",
    padding: "1rem",
    borderRadius: "8px",
    boxShadow: "0 2px 8px rgba(0,0,0,0.15)",
    display: "flex",
    flexDirection: "row",
    gap: "0.5rem",
    zIndex: 8,
    minWidth: 0,
    width: "auto",
    alignItems: "center"
  },
  drawerButton: {
    display: "flex",
    alignItems: "center",
    gap: "0.3rem",
    padding: "0.5rem 1rem",
    fontSize: "1rem",
    color: "#3477eb",
    background: "#eaf3ff",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer"
  },
  summaryBadge: {
    padding: "0.4rem 0.6rem",
    background: "rgba(255,255,255,0.9)",
    borderRadius: 8,
    border: "1px solid #e6eefb",
    display: "flex",
    alignItems: "center",
    gap: 8,
    fontWeight: 700,
    color: "#14355f"
  },
  modalOverlay: {
    position: "fixed",
    inset: 0,
    background: "rgba(0,0,0,0.45)",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    zIndex: 999
  },
  modal: {
    background: "#fff",
    padding: 20,
    borderRadius: 10,
    width: "92%",
    maxWidth: 820,
    boxShadow: "0 8px 30px rgba(0,0,0,0.2)"
  },
  form: {
    display: "flex",
    flexDirection: "column",
    gap: 12
  },
  row: {
    display: "flex",
    gap: 12
  },
  label: {
    flex: 1,
    display: "flex",
    flexDirection: "column",
    fontSize: 14,
    color: "#333"
  },
  input: {
    marginTop: 6,
    padding: "0.5rem",
    borderRadius: 6,
    border: "1px solid #ccc",
    fontSize: 14
  },
  saveButton: {
    padding: "0.55rem 1rem",
    background: "#3477eb",
    color: "#fff",
    border: "none",
    borderRadius: 6,
    cursor: "pointer",
    fontWeight: 700
  },
  cancelButton: {
    padding: "0.45rem 0.9rem",
    background: "#f1f4f8",
    color: "#333",
    border: "none",
    borderRadius: 6,
    cursor: "pointer",
    fontWeight: 700
  },
  ltr: {
    direction: "ltr",
    unicodeBidi: "isolate"
  }
};
