import React, { useState } from "react";
import ClientPage from "./client";
import GroupPage from "./group";
import PersonelPage from "./personel";
// src/index.js (or src/App.js)
import './pointage'; // ensure the pointage module is executed and registers the openPointage listener

// Define translations in French and Arabic including updated pages
const translations = {
  fr: {
    loginTitle: "Connexion Agence de Voyage",
    usernamePlaceholder: "Nom d'utilisateur",
    passwordPlaceholder: "Mot de passe",
    loginButton: "Se connecter",
    dashboardTitle: "Tableau de bord",
    retourDashboard: "‚Üê Retour au dashboard",
    pages: [
      { label: "Clients", page: "client" },
      { label: "Groupes", page: "groupe" },
      { label: "Caisse", page: "caisse" },
      { label: "Personnel", page: "personel" },
      { label: "Vols & H√¥tels", page: "vols_hotels" },
      { label: "Param√®tres", page: "parametres" }
    ],
    loginError: "Nom d'utilisateur ou mot de passe incorrect.",
    backendError: "Erreur de connexion au backend."
  },
  ar: {
    loginTitle: "ÿ™ÿ≥ÿ¨ŸäŸÑ ÿØÿÆŸàŸÑ ŸàŸÉÿßŸÑÿ© ÿßŸÑÿ≥ŸÅÿ±",
    usernamePlaceholder: "ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ",
    passwordPlaceholder: "ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±",
    loginButton: "ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ",
    dashboardTitle: "ŸÑŸàÿ≠ÿ© ÿßŸÑŸÖÿπŸÑŸàŸÖÿßÿ™",
    retourDashboard: "‚Üê ÿßŸÑÿπŸàÿØÿ© ŸÑŸÑŸàÿ≠ÿ©",
    pages: [
      { label: "ÿßŸÑÿπŸÖŸÑÿßÿ°", page: "client" },
      { label: "ÿßŸÑŸÖÿ¨ŸÖŸàÿπÿßÿ™", page: "groupe" },
      { label: "ÿßŸÑÿÆÿ≤ŸäŸÜÿ©", page: "caisse" },
      { label: "ÿßŸÑŸÖŸàÿ∏ŸÅŸàŸÜ", page: "personel" },
      { label: "ÿ±ÿ≠ŸÑÿßÿ™ Ÿàÿ•ŸÇÿßŸÖÿßÿ™", page: "vols_hotels" },
      { label: "ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™", page: "parametres" }
    ],
    loginError: "ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿ£Ÿà ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ± ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠ÿ©.",
    backendError: "ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆŸÑŸÅŸäÿ©."
  }
};

function Tabs({ currentPage, onNavigate, locale, setLocale, pages }) {
  const icons = {
    client: "üë•",
    groupe: "üß≥",
    caisse: "üßæ",
    personel: "üëî",
    vols_hotels: "üõ´",
    parametres: "‚öôÔ∏è"
  };
  return (
    <div style={styles.tabs}>
      {pages.map(({ label, page }) => (
        <button
          key={page}
          className={`button-hover tab-button ${currentPage === page ? "active-tab-button" : ""}`}
          onClick={() => onNavigate(page)}
        >
          <span className="tab-icon" aria-hidden="true">{icons[page]}</span>
          <span className="tab-label">{label}</span>
        </button>
      ))}
      <div style={styles.languageSwitch}>
        <button
          onClick={() => setLocale("fr")}
          className="button-hover small-lang-button tab-button"
          aria-label="Switch to French"
        >
          FR
        </button>
        <button
          onClick={() => setLocale("ar")}
          className="button-hover small-lang-button tab-button"
          aria-label="Switch to Arabic"
        >
          AR
        </button>
      </div>
    </div>
  );
}

function Dashboard({ onNavigate, locale }) {
  const icons = {
    client: "üë•",
    groupe: "üß≥",
    caisse: "üßæ",
    personel: "üëî",
    vols_hotels: "üõ´",
    parametres: "‚öôÔ∏è"
  };
  return (
    <div style={styles.dashboardContainer}>
      <h2>{translations[locale].dashboardTitle}</h2>
      <div style={styles.grid}>
        {translations[locale].pages.map(({ label, page }) => (
          <button
            key={page}
            className="dashboard-button button-hover tab-button"
            style={styles.tile}
            onClick={() => onNavigate(page)}
          >
            <span className="tab-icon" aria-hidden="true">{icons[page]}</span>
            <span className="tab-label">{label}</span>
          </button>
        ))}
      </div>
      {/* Footer Bar */}
      <div style={styles.footerBar}>
        <div style={styles.footerLeft}>¬© 2025 R-TECH Inc.</div>
        <div style={styles.footerCenter}>Contactez nous:</div>
        <div style={styles.footerRight}>
          <button
            className="button-hover social-button tab-button"
            onClick={() =>
              window.open("https://www.facebook.com/yourpage", "_blank")
            }
          >
            <img
              src="https://upload.wikimedia.org/wikipedia/commons/5/51/Facebook_f_logo_%282019%29.svg"
              alt="Facebook"
              style={styles.socialIcon}
            />
          </button>
          <button
            className="button-hover social-button tab-button"
            onClick={() =>
              window.open("https://www.instagram.com/yourpage", "_blank")
            }
          >
            <img
              src="https://upload.wikimedia.org/wikipedia/commons/e/e7/Instagram_logo_2016.svg"
              alt="Instagram"
              style={styles.socialIcon}
            />
          </button>
          <button
            className="button-hover social-button tab-button"
            onClick={() =>
              window.open("https://www.tiktok.com/@yourpage", "_blank")
            }
          >
            <img
              src="https://upload.wikimedia.org/wikipedia/en/a/a9/TikTok_logo.svg"
              alt="TikTok"
              style={styles.socialIcon}
            />
          </button>
          <button
            className="button-hover social-button tab-button"
            onClick={() =>
              window.open("https://www.youtube.com/yourchannel", "_blank")
            }
          >
            <img
              src="https://upload.wikimedia.org/wikipedia/commons/b/b8/YouTube_Logo_2017.svg"
              alt="YouTube"
              style={styles.socialIcon}
            />
          </button>
        </div>
      </div>
    </div>
  );
}

function App() {
  const [locale, setLocale] = useState("fr");
  const [loggedIn, setLoggedIn] = useState(false);
  const [currentPage, setCurrentPage] = useState("dashboard");
  const [loginData, setLoginData] = useState({ username: "", password: "" });
  const [error, setError] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const exists = await window.pywebview.api.check_user_login({
        username: loginData.username,
        password: loginData.password
      });
      if (exists) {
        setLoggedIn(true);
        setError("");
        setCurrentPage("dashboard");
      } else {
        setError(translations[locale].loginError);
      }
    } catch (err) {
      setError(translations[locale].backendError);
    }
  };

  let pageContent;
  if (!loggedIn) {
    pageContent = (
      <div style={styles.loginContainer}>
        <div style={styles.loginCard} className="login-card-hover">
          <h2>{translations[locale].loginTitle}</h2>
          <form onSubmit={handleLogin} style={styles.form}>
            <input
              type="text"
              placeholder={translations[locale].usernamePlaceholder}
              value={loginData.username}
              onChange={(e) =>
                setLoginData({ ...loginData, username: e.target.value })
              }
              style={styles.input}
            />
            <input
              type="password"
              placeholder={translations[locale].passwordPlaceholder}
              value={loginData.password}
              onChange={(e) =>
                setLoginData({ ...loginData, password: e.target.value })
              }
              style={styles.input}
            />
            <button type="submit" className="button-hover" style={styles.button}>
              {translations[locale].loginButton}
            </button>
            {error && <p style={styles.error}>{error}</p>}
          </form>
        </div>
      </div>
    );
  } else {
    switch (currentPage) {
      case "client":
        pageContent = <ClientPage locale={locale} />;
        break;
      case "groupe":
        pageContent = <GroupPage locale={locale} />;
        break;
      case "caisse":
        pageContent = <div style={styles.page}>Caisse page</div>;
        break;
      case "personel":
        pageContent = <PersonelPage locale={locale} />;
        break;
      case "vols_hotels":
        pageContent = <div style={styles.page}>üõ´ Vols & H√¥tels page</div>;
        break;
      case "parametres":
        pageContent = <div style={styles.page}>Param√®tres page</div>;
        break;
      case "dashboard":
      default:
        pageContent = <Dashboard onNavigate={setCurrentPage} locale={locale} />;
        break;
    }
  }

  return (
    <div style={styles.appContainer}>
      <style>{`
        .button-hover {
          transition: background 0.28s ease, box-shadow 0.28s ease, transform 0.18s ease;
          cursor: pointer;
          border: none;
          outline: none;
        }

        .button-hover:hover {
          box-shadow: 0px 10px 30px rgba(13, 40, 78, 0.22);
          transform: translateY(-4px);
        }
        /* Tab and dashboard buttons: all white, same look */
        .tab-button {
          display: inline-flex;
          align-items: center;
          gap: 0.6rem;
          padding: 0.7rem 1.1rem;
          font-size: 1rem;
          font-weight: 700;
          border-radius: 12px;
          margin-right: 0.5rem;
          background: #ffffff;                /* <- white background */
          color: #14355f;                     /* <- dark blue text */
          border: 1.5px solid #e0e8f5;        /* <- light border */
          box-shadow: 0 2.5px 10px rgba(11, 30, 60, 0.05);
          transition: background 0.22s ease, color 0.22s ease, box-shadow 0.22s ease, transform 0.18s ease;
        }
        .tab-button:hover,
        .dashboard-button.tab-button:hover {
          background: linear-gradient(90deg, #f4faff 0%, #fff0f0 100%);
          color: #071a36;
          box-shadow: 0 8px 28px rgba(11, 30, 60, 0.10);
        }
        .active-tab-button {
          border-bottom: 3px solid #eb3434;
          background: linear-gradient(90deg, #f7fbff 0%, #ffecec 100%);
          color: #071a36;
        }
        .tab-icon {
          font-size: 1.6rem;
          line-height: 1;
          display: inline-flex;
          align-items: center;
        }
        .tab-label {
          display: inline-block;
          transform: translateY(1px);
        }
        .dashboard-button {
          position: relative;
          overflow: hidden;
          border-radius: 14px;
        }
        .dashboard-button::before {
          content: "";
          position: absolute;
          top: 0;
          left: -100%;
          width: 100%;
          height: 100%;
          background: linear-gradient(90deg, #3477eb22 0%, #eb343499 100%);
          transition: left 0.35s ease;
          z-index: -1;
        }
        .dashboard-button:hover::before {
          left: 0;
        }
        .dashboard-button.tab-button {
          transition: transform 0.25s ease, box-shadow 0.25s ease;
          transform-style: preserve-3d;
        }

        .dashboard-button.tab-button:hover {
          transform: translateY(-8px) scale(1.03); /* slight lift and zoom */
          box-shadow: 0 16px 32px rgba(11, 30, 60, 0.15);
        }  
        .dashboard-button.tab-button::after {
          content: "";
          position: absolute;
          inset: 0;
          border-radius: 14px;
          background: radial-gradient(circle at top left, rgba(255,255,255,0.25), transparent 70%);
          opacity: 0;
          transition: opacity 0.25s ease;
        }

        .dashboard-button.tab-button:hover::after {
          opacity: 1;
        }

        .small-lang-button {
          padding: 0.35rem 0.6rem;
          border-radius: 8px;
          background: #fff;
          color: #153e7a;
          border: 1.5px solid #e0e8f5;
          margin-left: 0.5rem;
          font-weight: 700;
        }
        .small-lang-button:hover {
          background: linear-gradient(90deg, #f4faff, #fff0f0);
        }
        .social-button {
          padding: 0.35rem;
          border-radius: 8px;
          background: #fff;
        }
        .social-button:hover {
          background: #f4faff;
        }
        .login-card-hover {
          transition: transform 0.25s ease, box-shadow 0.25s ease;
        }

        .login-card-hover:hover {
          transform: translateY(-6px);
          box-shadow: 0 16px 40px rgba(0, 0, 0, 0.35);
        }
      `}</style>

      <div style={styles.bgAnimation} />

      {loggedIn && (
        <Tabs
          currentPage={currentPage}
          onNavigate={setCurrentPage}
          locale={locale}
          setLocale={setLocale}
          pages={translations[locale].pages}
        />
      )}
      {loggedIn && currentPage !== "dashboard" && (
        <button
          className="button-hover tab-button"
          style={styles.backButton}
          onClick={() => setCurrentPage("dashboard")}
        >
          {translations[locale].retourDashboard}
        </button>
      )}
      {pageContent}
    </div>
  );
}

