import React, { useEffect, useState } from "react";

// Personnel management page (Personnel / Staff)
// Fields: id, nom, prenom, role, telephone, email, adresse, photo

const translations = {
  fr: {
    title: "Personnel",
    addButton: "âž• Ajouter",
    editButton: "âœï¸ Modifier",
    deleteButton: "ðŸ—‘ï¸ Supprimer",
    save: "Enregistrer",
    cancel: "Annuler",
    searchPlaceholder: "Rechercher...",
    selectRowAlert: "Veuillez sÃ©lectionner un membre du personnel.",
    confirmDelete: (name) => `Supprimer ${name} ?`,
    tableHeaders: ["#", "Nom", "PrÃ©nom", "RÃ´le", "TÃ©lÃ©phone", "Email", "Adresse", "Photo"],
    modalLabels: {
      nom: "Nom",
      prenom: "PrÃ©nom",
      role: "RÃ´le",
      telephone: "TÃ©lÃ©phone",
      email: "Email",
      adresse: "Adresse",
      photo: "Photo (jpg/png)"
    },
    errorLoad: "Erreur lors du chargement du personnel",
    errorSave: "Erreur lors de l'enregistrement",
    errorDelete: "Erreur lors de la suppression"
  },
  ar: {
    title: "Ø§Ù„Ù…ÙˆØ¸ÙÙˆÙ†",
    addButton: "âž• Ø¥Ø¶Ø§ÙØ©",
    editButton: "âœï¸ ØªØ¹Ø¯ÙŠÙ„",
    deleteButton: "ðŸ—‘ï¸ Ø­Ø°Ù",
    save: "Ø­ÙØ¸",
    cancel: "Ø¥Ù„ØºØ§Ø¡",
    searchPlaceholder: "Ø¨Ø­Ø«...",
    selectRowAlert: "Ø§Ù„Ø±Ø¬Ø§Ø¡ ØªØ­Ø¯ÙŠØ¯ Ù…ÙˆØ¸Ù.",
    confirmDelete: (name) => `Ù‡Ù„ ØªØ±ÙŠØ¯ Ø­Ø°Ù ${name}ØŸ`,
    tableHeaders: ["#", "Ø§Ù„Ù„Ù‚Ø¨", "Ø§Ù„Ø§Ø³Ù…", "Ø§Ù„Ø¯ÙˆØ±", "Ø§Ù„Ù‡Ø§ØªÙ", "Ø§Ù„Ø¨Ø±ÙŠØ¯", "Ø§Ù„Ø¹Ù†ÙˆØ§Ù†", "Ø§Ù„ØµÙˆØ±Ø©"],
    modalLabels: {
      nom: "Ø§Ù„Ù„Ù‚Ø¨",
      prenom: "Ø§Ù„Ø§Ø³Ù…",
      role: "Ø§Ù„Ø¯ÙˆØ±",
      telephone: "Ø§Ù„Ù‡Ø§ØªÙ",
      email: "Ø§Ù„Ø¨Ø±ÙŠØ¯",
      adresse: "Ø§Ù„Ø¹Ù†ÙˆØ§Ù†",
      photo: "Ø§Ù„ØµÙˆØ±Ø© (jpg/png)"
    },
    errorLoad: "Ø®Ø·Ø£ Ø£Ø«Ù†Ø§Ø¡ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ù…ÙˆØ¸ÙÙŠÙ†",
    errorSave: "Ø®Ø·Ø£ Ø£Ø«Ù†Ø§Ø¡ Ø§Ù„Ø­ÙØ¸",
    errorDelete: "Ø®Ø·Ø£ Ø£Ø«Ù†Ø§Ø¡ Ø§Ù„Ø­Ø°Ù"
  }
};

function photoSrc(val) {
  if (!val) return null;
  if (typeof val !== "string") return null;
  if (val.startsWith("data:")) return val;
  return `data:image/png;base64,${val}`;
}

function PersonelPage({ locale = "fr" }) {
  const [personel, setPersonel] = useState([]);
  const [selectedIndex, setSelectedIndex] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [modalMode, setModalMode] = useState("add");
  const [form, setForm] = useState({
    id: null,
    nom: "",
    prenom: "",
    role: "",
    telephone: "",
    email: "",
    adresse: "",
    photo: ""
  });
  const [isLoading, setIsLoading] = useState(false);
  const [isDeleting, setIsDeleting] = useState(false);

  const T = translations[locale] || translations.fr;

  const fetchPersonel = async () => {
    setIsLoading(true);
    try {
      const res = await window.pywebview.api.get_personnel();
      setPersonel(res || []);
    } catch (e) {
      setPersonel([]);
      // console.error(e);
    }
    setIsLoading(false);
  };

  useEffect(() => {
    fetchPersonel();
    // eslint-disable-next-line
  }, [locale]);

  const filtered = personel.filter((p) => {
    const q = searchQuery.trim().toLowerCase();
    if (!q) return true;
    return (
      (p.nom || "").toLowerCase().includes(q) ||
      (p.prenom || "").toLowerCase().includes(q) ||
      (p.role || "").toLowerCase().includes(q) ||
      (p.telephone || "").toLowerCase().includes(q) ||
      (p.email || "").toLowerCase().includes(q)
    );
  });

  const emptyForm = {
    id: null,
    nom: "",
    prenom: "",
    role: "",
    telephone: "",
    email: "",
    adresse: "",
    photo: ""
  };

  const handleAdd = () => {
    setModalMode("add");
    setForm({ ...emptyForm });
    setShowModal(true);
  };

  const handleEdit = () => {
    if (selectedIndex === null || filtered[selectedIndex] === undefined) {
      alert(T.selectRowAlert);
      return;
    }
    const sel = filtered[selectedIndex];
    setModalMode("edit");
    setForm({
      id: sel.id ?? null,
      nom: sel.nom ?? "",
      prenom: sel.prenom ?? "",
      role: sel.role ?? "",
      telephone: sel.telephone ?? "",
      email: sel.email ?? "",
      adresse: sel.adresse ?? "",
      photo: sel.photo ?? ""
    });
    setShowModal(true);
  };

  const handleDelete = async () => {
    if (selectedIndex === null || filtered[selectedIndex] === undefined) {
      alert(T.selectRowAlert);
      return;
    }
    const sel = filtered[selectedIndex];
    const name = `${sel.nom || ""} ${sel.prenom || ""}`.trim() || sel.id || "";
    if (!window.confirm(T.confirmDelete(name))) return;
    try {
      setIsDeleting(true);
      await window.pywebview.api.delete_personnel(sel.id);
      setSelectedIndex(null);
      fetchPersonel();
    } catch (e) {
      alert(T.errorDelete);
    } finally {
      setIsDeleting(false);
    }
  };

  const handleSave = async (e) => {
    e.preventDefault();
    try {
      const payload = { ...form };
      if (modalMode === "add") {
        await window.pywebview.api.add_personnel(payload);
      } else {
        await window.pywebview.api.update_personnel(payload);
      }
      setShowModal(false);
      fetchPersonel();
    } catch (e) {
      alert(T.errorSave);
    }
  };

  const handlePhotoChange = (file) => {
    if (!file) {
      setForm((s) => ({ ...s, photo: "" }));
      return;
    }
    const reader = new FileReader();
    reader.onload = () => {
      setForm((s) => ({ ...s, photo: reader.result }));
    };
    reader.readAsDataURL(file);
  };

  const renderRow = (p, idx) => {
    const selected = idx === selectedIndex;
    return (
      <tr
        key={p.id ?? idx}
        onClick={() => setSelectedIndex(idx)}
        style={{
          background: selected ? "#cce5ff" : idx % 2 === 0 ? "#fff" : "#f4f8ff",
          cursor: "pointer"
        }}
      >
        <td style={styles.tableCell}>{idx + 1}</td>
        <td style={styles.tableCell}>{p.nom ?? "-"}</td>
        <td style={styles.tableCell}>{p.prenom ?? "-"}</td>
        <td style={styles.tableCell}>{p.role ?? "-"}</td>
        <td style={styles.tableCell}>{p.telephone ?? "-"}</td>
        <td style={styles.tableCell}>{p.email ?? "-"}</td>
        <td style={styles.tableCell}>{p.adresse ?? "-"}</td>
        <td style={{ ...styles.tableCell, textAlign: "center" }}>
          {p.photo ? (
            <img
              src={photoSrc(p.photo)}
              alt="photo"
              style={{ width: 50, height: 36, objectFit: "cover", borderRadius: 6 }}
            />
          ) : (
            "-"
          )}
        </td>
      </tr>
    );
  };

  return (
    <div style={styles.container}>
      <h2 style={{ textAlign: "center" }}>{T.title}</h2>

      <div style={styles.controls}>
        <div style={styles.searchWrap}>
          <input
            placeholder={T.searchPlaceholder}
            value={searchQuery}
            onChange={(e) => {
              setSearchQuery(e.target.value);
              setSelectedIndex(null);
            }}
            style={styles.searchInput}
          />
        </div>
        <div style={styles.actions}>
          <button className="button-hover" style={styles.actionButton} onClick={handleAdd}>
            {T.addButton}
          </button>
          <button className="button-hover" style={styles.actionButton} onClick={handleEdit}>
            {T.editButton}
          </button>
          <button className="button-hover" style={styles.actionButton} onClick={handleDelete}>
            {T.deleteButton}
          </button>
        </div>
      </div>

      {isLoading ? (
        <div style={{ padding: 40, textAlign: "center" }}>Loading...</div>
      ) : (
        <div style={styles.tableWrap}>
          <table style={styles.table}>
            <thead>
              <tr>
                {T.tableHeaders.map((h, i) => (
                  <th key={i} style={styles.tableHeader}>{h}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {filtered.length ? (
                filtered.map((p, i) => renderRow(p, i))
              ) : (
                <tr>
                  <td colSpan={8} style={{ padding: "1rem", textAlign: "center", color: "#666" }}>
                    {T.errorLoad}
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      )}

      {/* Modal */}
      {showModal && (
        <div style={styles.modalOverlay}>
          <div style={styles.modal}>
            <h3>{modalMode === "add" ? T.addButton : T.editButton}</h3>
            <form onSubmit={handleSave} style={styles.form}>
              <div style={styles.row}>
                <label style={styles.label}>
                  {T.modalLabels.nom}
                  <input
                    style={styles.input}
                    value={form.nom}
                    onChange={(e) => setForm({ ...form, nom: e.target.value })}
                    required
                  />
                </label>
                <label style={styles.label}>
                  {T.modalLabels.prenom}
                  <input
                    style={styles.input}
                    value={form.prenom}
                    onChange={(e) => setForm({ ...form, prenom: e.target.value })}
                    required
                  />
                </label>
              </div>

              <div style={styles.row}>
                <label style={styles.label}>
                  {T.modalLabels.role}
                  <input
                    style={styles.input}
                    value={form.role}
                    onChange={(e) => setForm({ ...form, role: e.target.value })}
                  />
                </label>
                <label style={styles.label}>
                  {T.modalLabels.telephone}
                  <input
                    style={styles.input}
                    value={form.telephone}
                    onChange={(e) => setForm({ ...form, telephone: e.target.value })}
                  />
                </label>
              </div>

              <div style={styles.row}>
                <label style={styles.label}>
                  {T.modalLabels.email}
                  <input
                    style={styles.input}
                    value={form.email}
                    onChange={(e) => setForm({ ...form, email: e.target.value })}
                    type="email"
                  />
                </label>
                <label style={styles.label}>
                  {T.modalLabels.adresse}
                  <input
                    style={styles.input}
                    value={form.adresse}
                    onChange={(e) => setForm({ ...form, adresse: e.target.value })}
                  />
                </label>
              </div>

              <div style={{ display: "flex", gap: 12, alignItems: "flex-start", marginTop: 6 }}>
                <div style={{ flex: 1 }}>
                  <label style={styles.label}>
                    {T.modalLabels.photo}
                    <input
                      type="file"
                      accept="image/*"
                      onChange={(e) => handlePhotoChange(e.target.files[0])}
                      style={{ marginTop: 6 }}
                    />
                  </label>
                </div>
                <div style={{ width: 110 }}>
                  {form.photo ? (
                    <img src={photoSrc(form.photo)} alt="preview" style={{ width: 100, height: 72, objectFit: "cover", borderRadius: 6 }} />
                  ) : null}
                </div>
              </div>

              <div style={{ display: "flex", justifyContent: "flex-end", gap: 8, marginTop: 12 }}>
                <button type="submit" style={styles.saveButton}>{T.save}</button>
                <button type="button" style={styles.cancelButton} onClick={() => setShowModal(false)}>{T.cancel}</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

