import React, { useEffect, useState } from "react";

// Define translations for French (fr) and Arabic (ar) including modal and deletion texts
const translations = {
  fr: {
    title: "Liste des Clients",
    searchPlaceholder: "Rechercher...",
    selectRowAlert: "Veuillez s√©lectionner une ligne client.",
    addClient: "Ajouter Nouveau Client",
    editClient: "Modifier Client",
    confirmDelete: (name) => `Supprimer le client ${name} ?`,
    save: "Enregistrer",
    cancel: "Annuler",
    addButton: "‚ûï Ajouter",
    editButton: "‚úèÔ∏è Modifier",
    deleteButton: "üóëÔ∏è Supprimer",
    errorLoad: "Erreur lors du chargement des clients",
    errorSave: "Erreur lors de l'enregistrement !",
    errorDelete: "Erreur lors de la suppression !",
    tableHeaders: [
      "#",
      "N¬∞ Passeport",
      "Nom complet",
      "Date de naissance",
      "T√©l√©phone",
      "Email",
      "Adresse"
    ]
  },
  ar: {
    title: "ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿπŸÖŸÑÿßÿ°",
    searchPlaceholder: "ÿ®ÿ≠ÿ´...",
    selectRowAlert: "ÿßŸÑÿ±ÿ¨ÿßÿ° ÿ™ÿ≠ÿØŸäÿØ ÿµŸÅ ÿßŸÑÿπŸÖŸäŸÑ.",
    addClient: "ÿ•ÿ∂ÿßŸÅÿ© ÿπŸÖŸäŸÑ ÿ¨ÿØŸäÿØ",
    editClient: "ÿ™ÿπÿØŸäŸÑ ÿßŸÑÿπŸÖŸäŸÑ",
    confirmDelete: (name) => `ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ≠ÿ∞ŸÅ ÿßŸÑÿπŸÖŸäŸÑ ${name}ÿü`,
    save: "ÿ≠ŸÅÿ∏",
    cancel: "ÿ•ŸÑÿ∫ÿßÿ°",
    addButton: "‚ûï ÿ•ÿ∂ÿßŸÅÿ©",
    editButton: "‚úèÔ∏è ÿ™ÿπÿØŸäŸÑ",
    deleteButton: "üóëÔ∏è ÿ≠ÿ∞ŸÅ",
    errorLoad: "ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿπŸÖŸÑÿßÿ°",
    errorSave: "ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿ≠ŸÅÿ∏!",
    errorDelete: "ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿ≠ÿ∞ŸÅ!",
    tableHeaders: [
      "#",
      "ÿ±ŸÇŸÖ ÿßŸÑÿ¨Ÿàÿßÿ≤",
      "ÿßŸÑÿßÿ≥ŸÖ ÿßŸÑŸÉÿßŸÖŸÑ",
      "ÿ™ÿßÿ±ŸäÿÆ ÿßŸÑŸÖŸäŸÑÿßÿØ",
      "ÿßŸÑŸáÿßÿ™ŸÅ",
      "ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä",
      "ÿßŸÑÿπŸÜŸàÿßŸÜ"
    ]
  }
};

function ClientPage({ locale = "fr" }) {
  // Client data state
  const [clients, setClients] = useState([]);
  const [selectedIndex, setSelectedIndex] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");

  // Modal state: whether modal is visible and which mode ("add" or "edit") is active.
  const [showModal, setShowModal] = useState(false);
  const [modalMode, setModalMode] = useState("add"); // "add" or "edit"
  const [clientForm, setClientForm] = useState({
    id: null,
    passport: "",
    full_name: "",
    birthdate: "",
    phone: "",
    email: "",
    address: ""
  });

  // Busy flag to prevent multiple delete calls
  const [isDeleting, setIsDeleting] = useState(false);

  // Fetch clients from the backend using the utils methods (via pywebview API)
  const fetchClients = async () => {
    try {
      const result = await window.pywebview.api.get_clients();
      setClients(result);
    } catch (err) {
      alert(translations[locale].errorLoad);
    }
  };

  useEffect(() => {
    fetchClients();
  }, []);

  // Button Handlers
  const handleAjouter = () => {
    setModalMode("add");
    setClientForm({
      id: null,
      passport: "",
      full_name: "",
      birthdate: "",
      phone: "",
      email: "",
      address: ""
    });
    setShowModal(true);
  };

  const handleModifier = () => {
    if (selectedIndex !== null && filteredClients[selectedIndex]) {
      setModalMode("edit");
      setClientForm({ ...filteredClients[selectedIndex] });
      setShowModal(true);
    } else {
      alert(translations[locale].selectRowAlert);
    }
  };

  const handleSupprimer = async () => {
    // Check if a row is selected
    if (selectedIndex === null || !filteredClients[selectedIndex]) {
      alert(translations[locale].selectRowAlert);
      return;
    }
    // If a deletion is already in progress, do not trigger another one.
    if (isDeleting) return;

    const clientId = filteredClients[selectedIndex].id;
    const clientName = filteredClients[selectedIndex].full_name;
    const confirmDelete = window.confirm(
      translations[locale].confirmDelete(clientName)
    );
    if (!confirmDelete) return;

    try {
      setIsDeleting(true); // Start busy flag
      await window.pywebview.api.delete_client(clientId);
      // Clear selected row to prevent further deletion calls for the same row
      setSelectedIndex(null);
      fetchClients();
    } catch (err) {
      alert(translations[locale].errorDelete);
    } finally {
      setIsDeleting(false); // End busy flag
    }
  };

  // Modal Handlers
  const handleCloseModal = () => {
    setShowModal(false);
    setClientForm({
      id: null,
      passport: "",
      full_name: "",
      birthdate: "",
      phone: "",
      email: "",
      address: ""
    });
  };

  const handleSaveClient = async (e) => {
    e.preventDefault();
    try {
      if (modalMode === "add") {
        await window.pywebview.api.add_client(clientForm);
      } else if (modalMode === "edit") {
        await window.pywebview.api.update_client(clientForm);
      }
      handleCloseModal();
      fetchClients();
    } catch (err) {
      alert(translations[locale].errorSave);
    }
  };

  // Filtered clients based on search query (searching passport, full_name, or email)
  const filteredClients = clients.filter((client) => {
    const query = searchQuery.toLowerCase();
    return (
      client.passport.toLowerCase().includes(query) ||
      client.full_name.toLowerCase().includes(query) ||
      client.email.toLowerCase().includes(query)
    );
  });

  return (
    <div style={styles.pageContainer}>
      <h2 style={{ textAlign: "center" }}>{translations[locale].title}</h2>
      {/* Search Bar */}
      <div style={styles.searchContainer}>
        <span style={styles.searchIcon}>üîç</span>
        <input
          type="text"
          placeholder={translations[locale].searchPlaceholder}
          value={searchQuery}
          onChange={(e) => {
            setSearchQuery(e.target.value);
            setSelectedIndex(null);
          }}
          style={styles.searchInput}
        />
      </div>
      <table style={styles.table}>
        <thead>
          <tr style={styles.tableHeaderRow}>
            {translations[locale].tableHeaders.map((header, i) => (
              <th key={i} style={styles.tableHeader}>
                {header}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {filteredClients.map((client, idx) => (
            <tr
              key={client.id || client.passport + idx}
              style={{
                ...styles.tableRow,
                background:
                  idx === selectedIndex
                    ? "#cce5ff"
                    : idx % 2 === 0
                    ? "#fff"
                    : "#f4f8ff"
              }}
              onClick={() => setSelectedIndex(idx)}
            >
              <td style={styles.tableCell}>{idx + 1}</td>
              <td style={styles.tableCell}>{client.passport}</td>
              <td style={styles.tableCell}>{client.full_name}</td>
              <td style={styles.tableCell}>{client.birthdate}</td>
              <td style={styles.tableCell}>{client.phone}</td>
              <td style={styles.tableCell}>{client.email}</td>
              <td style={styles.tableCell}>{client.address}</td>
            </tr>
          ))}
        </tbody>
      </table>
      {/* Horizontal drawer for CRUD buttons */}
      <div style={styles.drawer}>
        <button style={styles.drawerButton} onClick={handleAjouter}>
          {translations[locale].addButton}
        </button>
        <button style={styles.drawerButton} onClick={handleModifier}>
          {translations[locale].editButton}
        </button>
        <button style={styles.drawerButton} onClick={handleSupprimer}>
          {translations[locale].deleteButton}
        </button>
      </div>
      {/* Modal for Adding/Editing a Client */}
      {showModal && (
        <div style={styles.modalOverlay}>
          <div style={styles.modalContent}>
            <h3>
              {modalMode === "add"
                ? translations[locale].addClient
                : translations[locale].editClient}
            </h3>
            <form onSubmit={handleSaveClient} style={styles.modalForm}>
              <label style={styles.modalLabel}>
                Passeport:
                <input
                  type="text"
                  value={clientForm.passport}
                  onChange={(e) =>
                    setClientForm({ ...clientForm, passport: e.target.value })
                  }
                  style={styles.modalInput}
                  required
                />
              </label>
              <label style={styles.modalLabel}>
                Nom complet:
                <input
                  type="text"
                  value={clientForm.full_name}
                  onChange={(e) =>
                    setClientForm({ ...clientForm, full_name: e.target.value })
                  }
                  style={styles.modalInput}
                  required
                />
              </label>
              <label style={styles.modalLabel}>
                Date de naissance:
                <input
                  type="date"
                  value={clientForm.birthdate}
                  onChange={(e) =>
                    setClientForm({ ...clientForm, birthdate: e.target.value })
                  }
                  style={styles.modalInput}
                  required
                />
              </label>
              <label style={styles.modalLabel}>
                T√©l√©phone:
                <input
                  type="text"
                  value={clientForm.phone}
                  onChange={(e) =>
                    setClientForm({ ...clientForm, phone: e.target.value })
                  }
                  style={styles.modalInput}
                  required
                />
              </label>
              <label style={styles.modalLabel}>
                Email:
                <input
                  type="email"
                  value={clientForm.email}
                  onChange={(e) =>
                    setClientForm({ ...clientForm, email: e.target.value })
                  }
                  style={styles.modalInput}
                  required
                />
              </label>
              <label style={styles.modalLabel}>
                Adresse:
                <input
                  type="text"
                  value={clientForm.address}
                  onChange={(e) =>
                    setClientForm({ ...clientForm, address: e.target.value })
                  }
                  style={styles.modalInput}
                  required
                />
              </label>
              <div style={styles.modalButtons}>
                <button type="submit" style={styles.modalButton}>
                  {translations[locale].save}
                </button>
                <button
                  type="button"
                  style={styles.modalButton}
                  onClick={handleCloseModal}
                >
                  {translations[locale].cancel}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

const styles = {
  pageContainer: {
    padding: "2rem",
    position: "relative",
    minHeight: "100vh"
  },
  searchContainer: {
    marginBottom: "1rem",
    display: "flex",
    alignItems: "center",
    maxWidth: "400px"
  },
  searchIcon: {
    fontSize: "1.2rem",
    marginRight: "0.5rem"
  },
  searchInput: {
    flex: 1,
    padding: "0.5rem",
    fontSize: "1rem",
    borderRadius: "4px",
    border: "1px solid #ccc"
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
    marginTop: "1rem"
  },
  tableHeaderRow: {
    background: "#e8f0fe"
  },
  tableHeader: {
    padding: "0.7rem",
    border: "1px solid #c2c2c2",
    fontWeight: "bold"
  },
  tableRow: {
    cursor: "pointer"
  },
  tableCell: {
    padding: "0.6rem",
    border: "1px solid #e0e0e0"
  },
  // Drawer styles for CRUD buttons
  drawer: {
    position: "fixed",
    bottom: "20px",
    left: "20px",
    background: "#ffffff",
    padding: "1rem",
    borderRadius: "8px",
    boxShadow: "0 2px 8px rgba(0,0,0,0.15)",
    display: "flex",
    flexDirection: "row",
    gap: "0.5rem"
  },
  drawerButton: {
    display: "flex",
    alignItems: "center",
    gap: "0.3rem",
    padding: "0.5rem 1rem",
    fontSize: "1rem",
    color: "#3477eb",
    background: "#e8f0fe",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
    transition: "background 0.2s"
  },
  // Modal styles
  modalOverlay: {
    position: "fixed",
    top: 0,
    left: 0,
    width: "100vw",
    height: "100vh",
    background: "rgba(0, 0, 0, 0.5)",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    zIndex: 1000
  },
  modalContent: {
    background: "#fff",
    padding: "2rem",
    borderRadius: "8px",
    width: "90%",
    maxWidth: "400px",
    boxShadow: "0 2px 16px rgba(0,0,0,0.25)"
  },
  modalForm: {
    display: "flex",
    flexDirection: "column",
    gap: "1rem"
  },
  modalLabel: {
    display: "flex",
    flexDirection: "column",
    fontSize: "0.9rem",
    color: "#333"
  },
  modalInput: {
    padding: "0.5rem",
    fontSize: "1rem",
    borderRadius: "4px",
    border: "1px solid #ccc",
    marginTop: "0.3rem"
  },
  modalButtons: {
    display: "flex",
    justifyContent: "flex-end",
    gap: "1rem",
    marginTop: "1rem"
  },
  modalButton: {
    padding: "0.5rem 1rem",
    fontSize: "1rem",
    borderRadius: "4px",
    border: "none",
    cursor: "pointer",
    background: "#3477eb",
    color: "#fff",
    fontWeight: "bold",
    transition: "background 0.2s"
  }
};

export default ClientPage;
