import React, { useEffect, useState } from "react";

function GroupPage({ locale = "fr" }) {
  // New group state now includes groupLeader and totalPeople
  const [groups, setGroups] = useState([]);
  const [selectedIndex, setSelectedIndex] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [modalMode, setModalMode] = useState("add"); // "add" or "edit"

  // Updated group form includes new fields.
  const [groupForm, setGroupForm] = useState({
    id: null,
    groupName: "",
    groupLeader: "",
    totalPeople: 0,
    departureDate: "",
    returnDate: "",
    hotel: "",
    flight: ""
  });

  // Updated translations for the Group page
  const translations = {
    fr: {
      title: "Groupes (Omra / Hadj Campaigns)",
      searchPlaceholder: "Rechercher un groupe...",
      selectRowAlert: "Veuillez s√©lectionner un groupe.",
      addGroup: "Ajouter Groupe",
      editGroup: "Modifier Groupe",
      confirmDelete: (name) => `Supprimer le groupe "${name}" ?`,
      save: "Enregistrer",
      cancel: "Annuler",
      addButton: "‚ûï Ajouter",
      editButton: "‚úèÔ∏è Modifier",
      deleteButton: "üóëÔ∏è Supprimer",
      errorLoad: "Erreur lors du chargement des groupes",
      errorSave: "Erreur lors de l'enregistrement !",
      errorDelete: "Erreur lors de la suppression !",
      // Updated table headers
      tableHeaders: [
        "#",
        "Nom du Groupe",
        "Chef de Groupe",
        "Nombre de personnes",
        "Date de d√©part",
        "Date de retour",
        "H√¥tel",
        "Vol"
      ]
    },
    ar: {
      title: "ÿßŸÑŸÖÿ¨ŸÖŸàÿπÿßÿ™ (ÿ≠ŸÖŸÑÿßÿ™ ÿßŸÑÿπŸÖÿ±ÿ©/ÿßŸÑÿ≠ÿ¨)",
      searchPlaceholder: "ÿßÿ®ÿ≠ÿ´ ÿπŸÜ ŸÖÿ¨ŸÖŸàÿπÿ©...",
      selectRowAlert: "ÿßŸÑÿ±ÿ¨ÿßÿ° ÿ™ÿ≠ÿØŸäÿØ ŸÖÿ¨ŸÖŸàÿπÿ©.",
      addGroup: "ÿ•ÿ∂ÿßŸÅÿ© ŸÖÿ¨ŸÖŸàÿπÿ©",
      editGroup: "ÿ™ÿπÿØŸäŸÑ ÿßŸÑŸÖÿ¨ŸÖŸàÿπÿ©",
      confirmDelete: (name) => `ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ≠ÿ∞ŸÅ ÿßŸÑŸÖÿ¨ŸÖŸàÿπÿ© "${name}"ÿü`,
      save: "ÿ≠ŸÅÿ∏",
      cancel: "ÿ•ŸÑÿ∫ÿßÿ°",
      addButton: "‚ûï ÿ•ÿ∂ÿßŸÅÿ©",
      editButton: "‚úèÔ∏è ÿ™ÿπÿØŸäŸÑ",
      deleteButton: "üóëÔ∏è ÿ≠ÿ∞ŸÅ",
      errorLoad: "ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÖÿ¨ŸÖŸàÿπÿßÿ™",
      errorSave: "ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿ≠ŸÅÿ∏!",
      errorDelete: "ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿ≠ÿ∞ŸÅ!",
      // Arabic translations for table headers (order as displayed in RTL)
      tableHeaders: [
        "#",
        "ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ¨ŸÖŸàÿπÿ©",
        "ŸÇÿßÿ¶ÿØ ÿßŸÑŸÖÿ¨ŸÖŸàÿπÿ©",
        "ÿπÿØÿØ ÿßŸÑÿ£ÿ¥ÿÆÿßÿµ",
        "ÿ™ÿßÿ±ŸäÿÆ ÿßŸÑŸÖÿ∫ÿßÿØÿ±ÿ©",
        "ÿ™ÿßÿ±ŸäÿÆ ÿßŸÑÿπŸàÿØÿ©",
        "ÿßŸÑŸÅŸÜÿØŸÇ",
        "ÿßŸÑÿ±ÿ≠ŸÑÿ©"
      ]
    }
  };

  // Determine direction style for RTL if necessary
  const directionStyle = locale === "ar" ? { direction: "rtl" } : { direction: "ltr" };

  // --- Backend calls update ---
  // Fetch groups from your backend
  const fetchGroups = async () => {
    try {
      // Calling the backend API to load groups from the "groupe" table.
      const result = await window.pywebview.api.get_groups();
      setGroups(result);
    } catch (error) {
      alert(translations[locale].errorLoad);
    }
  };

  useEffect(() => {
    fetchGroups();
  }, []);

  const handleAddGroup = () => {
    setModalMode("add");
    setGroupForm({
      id: null,
      groupName: "",
      groupLeader: "",
      totalPeople: 0,
      departureDate: "",
      returnDate: "",
      hotel: "",
      flight: ""
    });
    setShowModal(true);
  };

  const handleEditGroup = () => {
    if (selectedIndex === null || !filteredGroups[selectedIndex]) {
      alert(translations[locale].selectRowAlert);
      return;
    }
    setModalMode("edit");
    setGroupForm({ ...filteredGroups[selectedIndex] });
    setShowModal(true);
  };

  const handleDeleteGroup = async () => {
    if (selectedIndex === null || !filteredGroups[selectedIndex]) {
      alert(translations[locale].selectRowAlert);
      return;
    }
    const groupId = filteredGroups[selectedIndex].id;
    const groupName = filteredGroups[selectedIndex].groupName;
    const confirmDelete = window.confirm(
      translations[locale].confirmDelete(groupName)
    );
    if (!confirmDelete) return;
    try {
      // Call backend API to delete a group.
      await window.pywebview.api.delete_group(groupId);
      setSelectedIndex(null);
      fetchGroups();
    } catch (error) {
      alert(translations[locale].errorDelete);
    }
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setGroupForm({
      id: null,
      groupName: "",
      groupLeader: "",
      totalPeople: 0,
      departureDate: "",
      returnDate: "",
      hotel: "",
      flight: ""
    });
  };

  const handleSaveGroup = async (e) => {
    e.preventDefault();
    try {
      if (modalMode === "add") {
        // Call backend API to insert a new group.
        await window.pywebview.api.add_group(groupForm);
      } else if (modalMode === "edit") {
        // Call backend API to update the group.
        await window.pywebview.api.update_group(groupForm);
      }
      handleCloseModal();
      fetchGroups();
    } catch (error) {
      alert(translations[locale].errorSave);
    }
  };

  // Filtering groups based on searchQuery
  const filteredGroups = groups.filter((group) => {
    const query = searchQuery.toLowerCase();
    return group.groupName.toLowerCase().includes(query);
  });

  return (
    <div style={{ ...styles.pageContainer, ...directionStyle }}>
      <h2 style={{ textAlign: "center" }}>{translations[locale].title}</h2>
      {/* Search bar */}
      <div style={styles.searchContainer}>
        <span style={styles.searchIcon}>üîç</span>
        <input
          type="text"
          placeholder={translations[locale].searchPlaceholder}
          value={searchQuery}
          onChange={(e) => {
            setSearchQuery(e.target.value);
            setSelectedIndex(null);
          }}
          style={styles.searchInput}
        />
      </div>
      {/* Groups Table */}
      <table style={styles.table}>
        <thead>
          <tr style={styles.tableHeaderRow}>
            {translations[locale].tableHeaders.map((header, idx) => (
              <th key={idx} style={styles.tableHeader}>
                {header}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {filteredGroups.map((group, idx) => (
            <tr
              key={group.id || group.groupName + idx}
              style={{
                ...styles.tableRow,
                background:
                  idx === selectedIndex
                    ? "#cce5ff"
                    : idx % 2 === 0
                    ? "#fff"
                    : "#f4f8ff"
              }}
              onClick={() => setSelectedIndex(idx)}
            >
              <td style={styles.tableCell}>{idx + 1}</td>
              <td style={styles.tableCell}>{group.groupName}</td>
              <td style={styles.tableCell}>{group.groupLeader}</td>
              <td style={styles.tableCell}>{group.totalPeople}</td>
              <td style={styles.tableCell}>{group.departureDate}</td>
              <td style={styles.tableCell}>{group.returnDate}</td>
              <td style={styles.tableCell}>{group.hotel}</td>
              <td style={styles.tableCell}>{group.flight}</td>
            </tr>
          ))}
        </tbody>
      </table>
      {/* Drawer for CRUD buttons */}
      <div style={styles.drawer}>
        <button className="button-hover" style={styles.drawerButton} onClick={handleAddGroup}>
          {translations[locale].addButton}
        </button>
        <button className="button-hover" style={styles.drawerButton} onClick={handleEditGroup}>
          {translations[locale].editButton}
        </button>
        <button className="button-hover" style={styles.drawerButton} onClick={handleDeleteGroup}>
          {translations[locale].deleteButton}
        </button>
      </div>
      {/* Modal for Adding/Editing Group */}
      {showModal && (
        <div style={styles.modalOverlay}>
          <div style={{ ...styles.modalContent, ...directionStyle }}>
            <h3>
              {modalMode === "add"
                ? translations[locale].addGroup
                : translations[locale].editGroup}
            </h3>
            <form onSubmit={handleSaveGroup} style={styles.modalForm}>
              <label style={styles.modalLabel}>
                {locale === "fr" ? "Nom du groupe" : "ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ¨ŸÖŸàÿπÿ©"}:
                <input
                  type="text"
                  value={groupForm.groupName}
                  onChange={(e) => setGroupForm({ ...groupForm, groupName: e.target.value })}
                  style={styles.modalInput}
                  required
                />
              </label>
              <label style={styles.modalLabel}>
                {locale === "fr" ? "Chef de groupe" : "ŸÇÿßÿ¶ÿØ ÿßŸÑŸÖÿ¨ŸÖŸàÿπÿ©"}:
                <input
                  type="text"
                  value={groupForm.groupLeader}
                  onChange={(e) => setGroupForm({ ...groupForm, groupLeader: e.target.value })}
                  style={styles.modalInput}
                  required
                />
              </label>
              <label style={styles.modalLabel}>
                {locale === "fr" ? "Nombre de personnes" : "ÿπÿØÿØ ÿßŸÑÿ£ÿ¥ÿÆÿßÿµ"}:
                <input
                  type="number"
                  value={groupForm.totalPeople}
                  onChange={(e) =>
                    setGroupForm({
                      ...groupForm,
                      totalPeople: parseInt(e.target.value, 10)
                    })
                  }
                  style={styles.modalInput}
                  required
                />
              </label>
              <label style={styles.modalLabel}>
                {locale === "fr" ? "Date de d√©part" : "ÿ™ÿßÿ±ŸäÿÆ ÿßŸÑŸÖÿ∫ÿßÿØÿ±ÿ©"}:
                <input
                  type="date"
                  value={groupForm.departureDate}
                  onChange={(e) =>
                    setGroupForm({ ...groupForm, departureDate: e.target.value })
                  }
                  style={styles.modalInput}
                  required
                />
              </label>
              <label style={styles.modalLabel}>
                {locale === "fr" ? "Date de retour" : "ÿ™ÿßÿ±ŸäÿÆ ÿßŸÑÿπŸàÿØÿ©"}:
                <input
                  type="date"
                  value={groupForm.returnDate}
                  onChange={(e) =>
                    setGroupForm({ ...groupForm, returnDate: e.target.value })
                  }
                  style={styles.modalInput}
                  required
                />
              </label>
              <label style={styles.modalLabel}>
                {locale === "fr" ? "H√¥tel" : "ÿßŸÑŸÅŸÜÿØŸÇ"}:
                <input
                  type="text"
                  value={groupForm.hotel}
                  onChange={(e) =>
                    setGroupForm({ ...groupForm, hotel: e.target.value })
                  }
                  style={styles.modalInput}
                />
              </label>
              <label style={styles.modalLabel}>
                {locale === "fr" ? "Vol" : "ÿßŸÑÿ±ÿ≠ŸÑÿ©"}:
                <input
                  type="text"
                  value={groupForm.flight}
                  onChange={(e) =>
                    setGroupForm({ ...groupForm, flight: e.target.value })
                  }
                  style={styles.modalInput}
                />
              </label>
              <div style={styles.modalButtons}>
                <button type="submit" className="button-hover" style={styles.modalButton}>
                  {translations[locale].save}
                </button>
                <button type="button" className="button-hover" style={styles.modalButton} onClick={handleCloseModal}>
                  {translations[locale].cancel}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

const styles = {
  pageContainer: {
    padding: "2rem",
    position: "relative",
    minHeight: "100vh"
  },
  searchContainer: {
    marginBottom: "1rem",
    display: "flex",
    alignItems: "center",
    maxWidth: "400px"
  },
  searchIcon: {
    fontSize: "1.2rem",
    marginRight: "0.5rem"
  },
  searchInput: {
    flex: 1,
    padding: "0.5rem",
    fontSize: "1rem",
    borderRadius: "4px",
    border: "1px solid #ccc"
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
    marginTop: "1rem"
  },
  tableHeaderRow: {
    background: "#e8f0fe"
  },
  tableHeader: {
    padding: "0.7rem",
    border: "1px solid #c2c2c2",
    fontWeight: "bold"
  },
  tableRow: {
    cursor: "pointer"
  },
  tableCell: {
    padding: "0.6rem",
    border: "1px solid #e0e0e0"
  },
  drawer: {
    position: "fixed",
    bottom: "20px",
    left: "20px",
    background: "#ffffff",
    padding: "1rem",
    borderRadius: "8px",
    boxShadow: "0 2px 8px rgba(0,0,0,0.15)",
    display: "flex",
    flexDirection: "row",
    gap: "0.5rem"
  },
  drawerButton: {
    display: "flex",
    alignItems: "center",
    gap: "0.3rem",
    padding: "0.5rem 1rem",
    fontSize: "1rem",
    color: "#3477eb",
    background: "#e8f0fe",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
    transition: "background 0.2s"
  },
  modalOverlay: {
    position: "fixed",
    top: 0,
    left: 0,
    width: "100vw",
    height: "100vh",
    background: "rgba(0, 0, 0, 0.5)",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    zIndex: 1000
  },
  modalContent: {
    background: "#fff",
    padding: "2rem",
    borderRadius: "8px",
    width: "90%",
    maxWidth: "400px",
    boxShadow: "0 2px 16px rgba(0,0,0,0.25)"
  },
  modalForm: {
    display: "flex",
    flexDirection: "column",
    gap: "1rem"
  },
  modalLabel: {
    display: "flex",
    flexDirection: "column",
    fontSize: "0.9rem",
    color: "#333"
  },
  modalInput: {
    padding: "0.5rem",
    fontSize: "1rem",
    borderRadius: "4px",
    border: "1px solid #ccc",
    marginTop: "0.3rem"
  },
  modalButtons: {
    display: "flex",
    justifyContent: "flex-end",
    gap: "1rem",
    marginTop: "1rem"
  },
  modalButton: {
    padding: "0.5rem 1rem",
    fontSize: "1rem",
    borderRadius: "4px",
    border: "none",
    cursor: "pointer",
    background: "#3477eb",
    color: "#fff",
    fontWeight: "bold",
    transition: "background 0.2s"
  }
};

export default GroupPage;
