// Helpers
function photoSrc(val) {
  if (!val) return null;
  if (typeof val !== "string") return null;
  if (val.startsWith("data:")) return val;
  return `data:image/png;base64,${val}`;
}

function formatCurrency(value) {
  if (value === undefined || value === null || value === "") return "";
  const cleaned = String(value).replace(/\s+/g, "").replace(/,/g, "");
  const num = parseFloat(cleaned);
  if (Number.isNaN(num)) return String(value);
  return num.toLocaleString("fr-FR", { minimumFractionDigits: 2, maximumFractionDigits: 2 }).replace(/\u202f/g, " ");
}

function unformatNumber(value) {
  if (value === undefined || value === null) return "";
  return String(value).replace(/\s+/g, "").replace(/,/g, "").trim();
}

function formatDateToFR(dateString) {
  if (!dateString) return "";
  if (/^\d{4}-\d{2}-\d{2}$/.test(dateString)) {
    const [yyyy, mm, dd] = dateString.split("-");
    return `${dd}/${mm}/${yyyy}`;
  } else if (/^\d{2}-\d{2}-\d{4}$/.test(dateString)) {
    const [dd, mm, yyyy] = dateString.split("-");
    return `${dd}/${mm}/${yyyy}`;
  } else if (/^\d{2}\/\d{2}\/\d{4}$/.test(dateString)) {
    return dateString;
  }
  return dateString;
}

function formatDateToISO(dateString) {
  if (!dateString) return "";
  let dd, mm, yyyy;
  if (/^\d{2}\/\d{2}\/\d{4}$/.test(dateString)) {
    [dd, mm, yyyy] = dateString.split("/");
  } else if (/^\d{2}-\d{2}-\d{4}$/.test(dateString)) {
    [dd, mm, yyyy] = dateString.split("-");
  } else if (/^\d{4}-\d{2}-\d{2}$/.test(dateString)) {
    return dateString;
  }
  if (yyyy && mm && dd) {
    return `${yyyy}-${mm}-${dd}`;
  }
  return dateString;
}

function isArabic(str) {
  return /^[\u0600-\u06FF\s]+$/.test(str);
}

function PersonelPage({ locale = "fr" }) {
  const T = translations[locale] || translations.fr;

  const [personel, setPersonel] = useState([]);
  const [selectedIndex, setSelectedIndex] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [modalMode, setModalMode] = useState("add"); // add | edit
  const [form, setForm] = useState({
    id: null,
    nom: "",
    prenom: "",
    poste: "",
    telephone: "",
    adresse: "",
    date_dentree: "",
    date_sortie: "",
    numero_assurance: "",
    salaire: "",
    avance: "",
    photo: ""
  });
  const [arErrors, setArErrors] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [isDeleting, setIsDeleting] = useState(false);

  const isRtl = locale === "ar";
  const displayedHeaders = isRtl ? [...T.tableHeaders].reverse() : T.tableHeaders;
  const tableFieldsOrder = isRtl ? [...TABLE_FIELDS].reverse() : [...TABLE_FIELDS];
  const directionStyle = isRtl ? { direction: "rtl" } : { direction: "ltr" };

  // Fetch personnel
  const fetchPersonel = async () => {
    setIsLoading(true);
    try {
      console.log("Fetching personnel from backend...");
      const res = await window.pywebview.api.get_personnel();
      const normalized = (res || []).map(r => ({
        ...r,
        salaire: r.salaire !== undefined && r.salaire !== null ? String(r.salaire) : "",
        avance: r.avance !== undefined && r.avance !== null ? String(r.avance) : "",
        date_dentree: r.date_dentree || "",
        date_sortie: r.date_sortie || "",
        numero_assurance: r.numero_assurance || r.num_assurance || "",
        photo: r.photo || ""
      }));
      setPersonel(normalized);
      console.log("Personnel loaded:", normalized.length);
    } catch (err) {
      console.error("Failed to fetch personnel:", err);
      setPersonel([]);
      alert(T.errorLoad + (err?.message ? `: ${err.message}` : ""));
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchPersonel();
    // eslint-disable-next-line
  }, [locale]);

  // Filtering
  const filtered = personel.filter((p) => {
    const q = searchQuery.trim().toLowerCase();
    if (!q) return true;
    return (
      (p.nom || "").toLowerCase().includes(q) ||
      (p.prenom || "").toLowerCase().includes(q) ||
      (p.poste || "").toLowerCase().includes(q) ||
      (p.telephone || "").toLowerCase().includes(q) ||
      (p.adresse || "").toLowerCase().includes(q)
    );
  });

  // Totals
  const totalStaff = filtered.length;
  const totalSalaire = filtered.reduce((s, p) => {
    const val = parseFloat(String(p.salaire || "0").replace(/\s+/g, "").replace(",", "."));
    return s + (isNaN(val) ? 0 : val);
  }, 0);
  const totalAvance = filtered.reduce((s, p) => {
    const val = parseFloat(String(p.avance || "0").replace(/\s+/g, "").replace(",", "."));
    return s + (isNaN(val) ? 0 : val);
  }, 0);

  const emptyForm = {
    id: null,
    nom: "",
    prenom: "",
    poste: "",
    telephone: "",
    adresse: "",
    date_dentree: "",
    date_sortie: "",
    numero_assurance: "",
    salaire: "",
    avance: "",
    photo: ""
  };

  const handleAdd = () => {
    setModalMode("add");
    setForm({ ...emptyForm });
    setArErrors({});
    setShowModal(true);
  };

  const handleEdit = () => {
    if (selectedIndex === null || filtered[selectedIndex] === undefined) {
      alert(T.selectRowAlert);
      return;
    }
    const sel = filtered[selectedIndex];
    setModalMode("edit");
    setForm({
      id: sel.id ?? null,
      nom: sel.nom ?? "",
      prenom: sel.prenom ?? "",
      poste: sel.poste ?? "",
      telephone: sel.telephone ?? "",
      adresse: sel.adresse ?? "",
      date_dentree: sel.date_dentree ? formatDateToFR(sel.date_dentree) : "",
      date_sortie: sel.date_sortie ? formatDateToFR(sel.date_sortie) : "",
      numero_assurance: sel.numero_assurance ?? "",
      salaire: sel.salaire !== undefined && sel.salaire !== null ? String(sel.salaire) : "",
      avance: sel.avance !== undefined && sel.avance !== null ? String(sel.avance) : "",
      photo: sel.photo ?? ""
    });
    setShowModal(true);
  };

  const handleDelete = async () => {
    if (selectedIndex === null || filtered[selectedIndex] === undefined) {
      alert(T.selectRowAlert);
      return;
    }
    const sel = filtered[selectedIndex];
    const name = `${sel.nom || ""} ${sel.prenom || ""}`.trim() || sel.id || "";
    if (!window.confirm(T.confirmDelete(name))) return;
    try {
      setIsDeleting(true);
      console.log("Deleting personnel id=", sel.id);
      const resp = await window.pywebview.api.delete_personnel(sel.id);
      console.log("delete_personnel response:", resp);
      if (resp === 0 || resp === false) {
        alert(T.errorDelete);
      } else {
        setPersonel(prev => prev.filter(p => p.id !== sel.id));
        setSelectedIndex(null);
      }
    } catch (err) {
      console.error("Delete error:", err);
      alert(T.errorDelete + (err?.message ? `: ${err.message}` : ""));
    } finally {
      setIsDeleting(false);
    }
  };

  const handleSave = async (e) => {
    e?.preventDefault?.();
    let newErrors = {};
    for (const f of Object.keys(form)) {
      if (f.endsWith("_ar") && form[f] && !isArabic(form[f])) {
        newErrors[f] = translations[locale].saisieArabe || "Saisie arabe requise";
      }
    }
    setArErrors(newErrors);
    if (Object.values(newErrors).some(Boolean)) return;

    try {
      const payload = {
        ...form,
        salaire: unformatNumber(form.salaire),
        avance: unformatNumber(form.avance),
        date_dentree: formatDateToISO(form.date_dentree),
        date_sortie: formatDateToISO(form.date_sortie)
      };
      console.log("personel: calling backend with payload:", payload);

      let result;
      if (modalMode === "add") {
        result = await window.pywebview.api.add_personnel(payload);
      } else {
        result = await window.pywebview.api.update_personnel(payload);
      }

      console.log("personel: backend result:", result);

      if (!result) {
        console.error("personel: backend returned falsy value:", result);
        alert(T.errorSave + (result && result.error ? `: ${result.error}` : " (voir console)"));
        return;
      }

      if (modalMode === "add") {
        setPersonel(prev => [...prev, {
          ...result,
          salaire: result.salaire !== undefined && result.salaire !== null ? String(result.salaire) : "",
          avance: result.avance !== undefined && result.avance !== null ? String(result.avance) : "",
          photo: result.photo || ""
        }]);
      } else {
        setPersonel(prev => prev.map(p => p.id === result.id ? {
          ...result,
          salaire: result.salaire !== undefined && result.salaire !== null ? String(result.salaire) : "",
          avance: result.avance !== undefined && result.avance !== null ? String(result.avance) : "",
          photo: result.photo || ""
        } : p));
      }

      setShowModal(false);
      setForm({ ...emptyForm });
    } catch (err) {
      console.error("Save error:", err);
      alert(T.errorSave + (err?.message ? `: ${err.message}` : ""));
    }
  };

  const handlePhotoChange = (file) => {
    if (!file) {
      setForm((s) => ({ ...s, photo: "" }));
      return;
    }
    const reader = new FileReader();
    reader.onload = () => {
      setForm((s) => ({ ...s, photo: reader.result }));
    };
    reader.readAsDataURL(file);
  };

  const handleOpenPointage = async () => {
    const detail = {
      locale,
      translations,
      personnel: null
    };
    if (selectedIndex !== null && filtered[selectedIndex]) {
      detail.personnel = filtered[selectedIndex];
    }
    try {
      // Try direct import/call first (lazy load)
      try {
        const mod = await import("./pointage");
        if (mod && typeof mod.openPointage === "function") {
          mod.openPointage(detail);
          return;
        }
      } catch (impErr) {
        // ignore, we'll dispatch event as fallback
        console.warn("Could not dynamically import ./pointage (will dispatch event).", impErr);
      }

      // Fallback: dispatch the event which pointage module listens to
      window.dispatchEvent(new CustomEvent("openPointage", { detail }));
    } catch (err) {
      console.error("Failed to open pointage:", err);
      alert("Erreur lors de l'ouverture du pointage (voir console).");
    }
  };

  // Render cell helper (now accepts index to control column separators)
  function renderTableCell(item, field, idx) {
    const isLast = idx === tableFieldsOrder.length - 1;
    const baseCellStyle = {
      ...styles.tableCell,
      borderRight: isLast ? "none" : "1px solid #e6eefb"
    };

    if (field === "photo") {
      const src = photoSrc(item[field]);
      return (
        <td
          key={`${item.id}-${field}`}
          style={{
            ...baseCellStyle,
            padding: 6,
            textAlign: "center",
            minWidth: 50,
            maxWidth: 70,
            width: 60,
            verticalAlign: "middle",
            background: "#fff"
          }}
        >
          {src ? (
            <img
              src={src}
              alt="photo"
              style={{
                width: 50,
                height: 36,
                objectFit: "cover",
                borderRadius: 6,
                display: "block",
                margin: "0 auto"
              }}
            />
          ) : (
            "-"
          )}
        </td>
      );
    }

    if (field === "salaire" || field === "avance") {
      const formatted = formatCurrency(item[field]);
      return (
        <td key={`${item.id}-${field}`} style={baseCellStyle}>
          {formatted || "-"}
        </td>
      );
    }

    if (field === "date_dentree" || field === "date_sortie") {
      return (
        <td key={`${item.id}-${field}`} style={baseCellStyle}>
          {formatDateToFR(item[field]) || "-"}
        </td>
      );
    }

    return (
      <td key={`${item.id}-${field}`} style={baseCellStyle}>
        {item[field] ?? "-"}
      </td>
    );
  }

  // Drawer buttons
  const drawerLeft = (
    <div style={{ ...styles.drawer, left: 20, right: "unset", bottom: 28 }}>
      <button style={styles.drawerButton} onClick={handleAdd}>{T.addButton}</button>
      <button style={styles.drawerButton} onClick={handleEdit}>{T.editButton}</button>
      <button style={styles.drawerButton} onClick={handleDelete}>{T.deleteButton}</button>
    </div>
  );

  const drawerRight = (
    <div style={{ ...styles.drawer, right: 20, left: "unset", bottom: 28 }}>
      <button style={{ ...styles.drawerButton, background: "#3477eb", color: "#fff" }} onClick={() => window.print()}>
        {T.printLabel}
      </button>
      <button
        style={{ ...styles.drawerButton, background: "#fff", border: "1px solid #e6eefb", color: "#14355f", fontWeight: 700 }}
        onClick={handleOpenPointage}
      >
        {T.pointageButton}
      </button>
    </div>
  );

  return (
    <div style={{ ...styles.container, ...directionStyle }}>
      <h2 style={{ textAlign: "center" }}>{T.title}</h2>

      <div style={{ display: "flex", gap: 12, marginBottom: 12, alignItems: "center" }}>
        <div style={{ ...styles.searchWrap, maxWidth: 420 }}>
          <span style={{ marginRight: 8 }}>üîç</span>
          <input
            placeholder={T.searchPlaceholder}
            value={searchQuery}
            onChange={(e) => { setSearchQuery(e.target.value); setSelectedIndex(null); }}
            style={styles.searchInput}
          />
        </div>

        <div style={{ marginLeft: "auto", display: "flex", gap: 8 }}>
          <div style={styles.summaryBadge}>
            üë• {T.totalStaff}: <strong style={{ marginLeft: 6 }}>{totalStaff}</strong>
          </div>
          <div style={styles.summaryBadge}>
            üí∞ {T.totalSalaire}: <strong style={{ marginLeft: 6 }}>{formatCurrency(totalSalaire)}</strong>
          </div>
          <div style={styles.summaryBadge}>
            üí∏ {T.totalAvance}: <strong style={{ marginLeft: 6 }}>{formatCurrency(totalAvance)}</strong>
          </div>
        </div>
      </div>

      {isLoading ? (
        <div style={{ padding: 40, textAlign: "center" }}>
          <div style={styles.spinner}></div>
        </div>
      ) : (
        <div style={styles.tableWrap}>
          <table style={styles.table}>
            <thead>
              <tr style={styles.tableHeaderRow}>
                {displayedHeaders.map((h, i) => {
                  const isLastHeader = i === displayedHeaders.length - 1;
                  return (
                    <th
                      key={i}
                      style={{
                        ...styles.tableHeader,
                        borderRight: isLastHeader ? "none" : "1px solid #e6eefb",
                        ...(h === "#" ? { width: 36, minWidth: 24, maxWidth: 40 } : {}),
                        ...(h === "Photo" || h === "ÿßŸÑÿµŸàÿ±ÿ©" ? { minWidth: 50, maxWidth: 70, width: 60 } : {})
                      }}
                    >
                      {h}
                    </th>
                  );
                })}
              </tr>
            </thead>
            <tbody>
              {filtered.length ? (
                filtered.map((p, i) => (
                  <tr
                    key={p.id ?? i}
                    onClick={() => setSelectedIndex(i)}
                    style={{
                      ...styles.tableRow,
                      background: i === selectedIndex ? "#cce5ff" : i % 2 === 0 ? "#fff" : "#f4f8ff",
                      cursor: "pointer"
                    }}
                  >
                    <td style={{ ...styles.tableCell, width: 36, minWidth: 24, maxWidth: 40, textAlign: "left", borderRight: "1px solid #e6eefb" }}>{i + 1}</td>
                    {tableFieldsOrder.map((field, idx) => renderTableCell(p, field, idx))}
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan={displayedHeaders.length} style={{ padding: "1rem", textAlign: "center", color: "#666" }}>
                    {T.errorLoad}
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      )}

      {drawerLeft}
      {drawerRight}

      {/* Modal */}
      {showModal && (
        <div style={styles.modalOverlay}>
          <div style={{ ...styles.modal, ...(isRtl ? { direction: "rtl", textAlign: "right" } : {}) }}>
            <h3>{modalMode === "add" ? T.addButton : T.editButton}</h3>
            <form onSubmit={handleSave} style={styles.form}>
              <div style={styles.row}>
                <label style={styles.label}>
                  {T.modalLabels.nom}
                  <input style={styles.input} value={form.nom} onChange={(e) => setForm({ ...form, nom: e.target.value })} required />
                </label>
                <label style={styles.label}>
                  {T.modalLabels.prenom}
                  <input style={styles.input} value={form.prenom} onChange={(e) => setForm({ ...form, prenom: e.target.value })} required />
                </label>
              </div>

              <div style={styles.row}>
                <label style={styles.label}>
                  {T.modalLabels.poste}
                  <input style={styles.input} value={form.poste} onChange={(e) => setForm({ ...form, poste: e.target.value })} />
                </label>
                <label style={styles.label}>
                  {T.modalLabels.telephone}
                  <input style={styles.input} value={form.telephone} onChange={(e) => setForm({ ...form, telephone: e.target.value })} />
                </label>
              </div>

              <div style={styles.row}>
                <label style={styles.label}>
                  {T.modalLabels.adresse}
                  <input style={styles.input} value={form.adresse} onChange={(e) => setForm({ ...form, adresse: e.target.value })} />
                </label>
                <label style={styles.label}>
                  {T.modalLabels.date_dentree}
                  <input
                    type="date"
                    value={form.date_dentree ? formatDateToISO(form.date_dentree) : ""}
                    onChange={(e) => setForm(prev => ({ ...prev, date_dentree: e.target.value ? formatDateToFR(e.target.value) : "" }))}
                    style={styles.input}
                  />
                </label>
              </div>

              <div style={styles.row}>
                <label style={styles.label}>
                  {T.modalLabels.date_sortie}
