import React, { useState } from "react";
import ClientPage from "./client"; // Import your ClientPage component

// Define translation dictionaries for French (FR) and Arabic (AR)
const translations = {
  FR: {
    dashboard: "Tableau de bord",
    clients: "Clients",
    booking: "Booking",
    payments: "Paiements",
    reports: "Rapports",
    settings: "Paramètres",
    loginTitle: "Connexion Agence de Voyage",
    usernamePlaceholder: "Nom d'utilisateur",
    passwordPlaceholder: "Mot de passe",
    loginButton: "Se connecter",
    incorrectLogin: "Nom d'utilisateur ou mot de passe incorrect.",
    retourDashboard: "← Retour au dashboard",
    language: "Langue",
  },
  AR: {
    dashboard: "لوحة القيادة",
    clients: "العملاء",
    booking: "الحجوزات",
    payments: "المدفوعات",
    reports: "التقارير",
    settings: "الإعدادات",
    loginTitle: "تسجيل الدخول وكالة السفر",
    usernamePlaceholder: "اسم المستخدم",
    passwordPlaceholder: "كلمة المرور",
    loginButton: "تسجيل الدخول",
    incorrectLogin: "اسم المستخدم أو كلمة المرور غير صحيحة.",
    retourDashboard: "← العودة إلى لوحة القيادة",
    language: "اللغة",
  },
};

// Array that defines all available pages (their keys match the translation keys)
const pages = [
  { labelKey: "clients", page: "client" },
  { labelKey: "booking", page: "booking" },
  { labelKey: "payments", page: "payments" },
  { labelKey: "reports", page: "reports" },
  { labelKey: "settings", page: "settings" },
];

// Tab bar component with language switch button included in the right side
function Tabs({ currentPage, onNavigate, language, setLanguage }) {
  // Function to toggle language between FR and AR
  const toggleLanguage = () => {
    setLanguage(prev => (prev === "FR" ? "AR" : "FR"));
  };

  return (
    <div style={styles.tabs}>
      {pages.map(({ labelKey, page }) => (
        <button
          key={page}
          style={{
            ...styles.tab,
            ...(currentPage === page ? styles.activeTab : {}),
          }}
          onClick={() => onNavigate(page)}
        >
          {translations[language][labelKey]}
        </button>
      ))}
      {/* Language Switcher */}
      <button style={styles.langButton} onClick={toggleLanguage}>
        {translations[language].language}: {language}
      </button>
    </div>
  );
}

// Dashboard component that displays all buttons in a bento grid style
function Dashboard({ onNavigate, language }) {
  return (
    <div style={styles.dashboardContainer}>
      <h2>{translations[language].dashboard}</h2>
      <div style={styles.grid}>
        {pages.map(({ labelKey, page }) => (
          <button
            key={page}
            style={styles.tile}
            onClick={() => onNavigate(page)}
          >
            {translations[language][labelKey]}
          </button>
        ))}
      </div>
    </div>
  );
}

function App() {
  // Manage login and navigation states
  const [loggedIn, setLoggedIn] = useState(false);
  const [currentPage, setCurrentPage] = useState("dashboard");
  const [loginData, setLoginData] = useState({ username: "", password: "" });
  const [error, setError] = useState("");
  // Manage active language state: "FR" or "AR"
  const [language, setLanguage] = useState("FR");

  // Adjust overall text alignment based on language:
  const direction = language === "AR" ? "rtl" : "ltr";

  // Handler for login form submission
  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      // Call the backend via pywebview API to check the user
      const exists = await window.pywebview.api.check_user_login({
        username: loginData.username,
        password: loginData.password,
      });
      if (exists) {
        setLoggedIn(true);
        setError("");
        setCurrentPage("dashboard");
      } else {
        setError(translations[language].incorrectLogin);
      }
    } catch (err) {
      setError("Erreur de connexion au backend.");
    }
  };

  // Determine which page to display based on currentPage state
  let pageContent;
  if (!loggedIn) {
    pageContent = (
      <div style={{ ...styles.container, direction }}>
        <h2>{translations[language].loginTitle}</h2>
        <form onSubmit={handleLogin} style={styles.form}>
          <input
            type="text"
            placeholder={translations[language].usernamePlaceholder}
            value={loginData.username}
            onChange={(e) =>
              setLoginData({ ...loginData, username: e.target.value })
            }
            style={styles.input}
          />
          <input
            type="password"
            placeholder={translations[language].passwordPlaceholder}
            value={loginData.password}
            onChange={(e) =>
              setLoginData({ ...loginData, password: e.target.value })
            }
            style={styles.input}
          />
          <button type="submit" style={styles.button}>
            {translations[language].loginButton}
          </button>
          {error && <p style={styles.error}>{error}</p>}
        </form>
      </div>
    );
  } else {
    // When logged in, display the Tab bar with a language switcher and the selected page's content
    switch (currentPage) {
      case "client":
        pageContent = <ClientPage />;
        break;
      case "booking":
        pageContent = <div style={styles.pageContainer}>Booking Page</div>;
        break;
      case "payments":
        pageContent = <div style={styles.pageContainer}>Payments Page</div>;
        break;
      case "reports":
        pageContent = <div style={styles.pageContainer}>Reports Page</div>;
        break;
      case "settings":
        pageContent = <div style={styles.pageContainer}>Settings Page</div>;
        break;
      case "dashboard":
      default:
        pageContent = <Dashboard onNavigate={setCurrentPage} language={language} />;
        break;
    }
  }

  return (
    <div style={{ ...styles.appContainer, direction }}>
      {loggedIn && (
        <Tabs
          currentPage={currentPage}
          onNavigate={setCurrentPage}
          language={language}
          setLanguage={setLanguage}
        />
      )}
      {loggedIn && currentPage !== "dashboard" && (
        <button
          style={styles.backButton}
          onClick={() => setCurrentPage("dashboard")}
        >
          {translations[language].retourDashboard}
        </button>
      )}
      {pageContent}
    </div>
  );
}

const styles = {
  appContainer: {
    minHeight: "100vh",
    background: "#f0f6ff",
    paddingTop: "70px",
    position: "relative",
  },
  container: {
    minHeight: "100vh",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    background: "#f0f6ff",
  },
  form: {
    display: "flex",
    flexDirection: "column",
    gap: "1rem",
    width: "300px",
    padding: "2rem",
    background: "#fff",
    borderRadius: "10px",
    boxShadow: "0 4px 24px rgba(0,0,0,0.06)",
  },
  input: {
    padding: "0.5rem",
    fontSize: "1rem",
    borderRadius: "4px",
    border: "1px solid #c2c2c2",
  },
  button: {
    padding: "0.7rem",
    fontSize: "1rem",
    borderRadius: "4px",
    border: "none",
    background: "#3477eb",
    color: "#fff",
    fontWeight: "bold",
    cursor: "pointer",
  },
  error: {
    color: "red",
    fontWeight: "bold",
    margin: 0,
  },
  tabs: {
    display: "flex",
    position: "fixed",
    top: 0,
    left: 0,
    width: "100%",
    background: "#fff",
    boxShadow: "0 2px 8px rgba(52,119,235,0.05)",
    zIndex: 10,
    justifyContent: "space-between",
    alignItems: "center",
    padding: "0.5rem 1rem",
  },
  tab: {
    padding: "0.7rem 1rem",
    fontSize: "1rem",
    border: "none",
    borderBottom: "3px solid transparent",
    background: "none",
    color: "#3477eb",
    fontWeight: "bold",
    cursor: "pointer",
    outline: "none",
    transition: "border-bottom 0.2s, color 0.2s",
  },
  activeTab: {
    color: "#222",
    borderBottom: "3px solid #3477eb",
    background: "#e8f0fe",
    borderRadius: "8px 8px 0 0",
  },
  langButton: {
    marginLeft: "auto",
    padding: "0.5rem 1rem",
    fontSize: "0.9rem",
    background: "#3477eb",
    color: "#fff",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  },
  backButton: {
    position: "absolute",
    top: "70px",
    left: "24px",
    background: "#e1e8f5",
    border: "none",
    borderRadius: "8px",
    padding: "0.5rem 1rem",
    color: "#3477eb",
    fontWeight: "bold",
    cursor: "pointer",
    zIndex: 2,
  },
  dashboardContainer: {
    textAlign: "center",
    margin: "0 auto",
    maxWidth: "700px",
  },
  grid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(180px, 1fr))",
    gap: "1.5rem",
    marginTop: "2rem",
  },
  pageContainer: {
    padding: "2rem",
    textAlign: "center",
  },
};

export default App;
