import React, { useEffect, useState } from "react";

/*
  hotels.js
  - Hôtels page component.
  - Features:
    * Table listing hotels with columns: Hôtel, Ville, Check-in, Check-out, Chambres, Prix/nuit, Statut
    * Search bar, add / edit / delete buttons, print
    * Add/Edit modal with validation
    * Loads/saves via window.pywebview.api functions if available:
        - load_hotels() | loadHotels() | get_hotels()
        - add_hotel(payload) | addHotel(payload)
        - update_hotel(id, payload) | updateHotel(id, payload)
        - delete_hotel(id) | deleteHotel(id)
      Falls back to local state if backend missing.
    * Locale aware (fr / ar) and RTL support.
*/

function formatNumber(v) {
  if (v === null || v === undefined || v === "") return "";
  return Number(v).toLocaleString(undefined, { minimumFractionDigits: 0, maximumFractionDigits: 2 });
}
function toISODate(input) {
  if (!input) return "";
  if (input instanceof Date) {
    const yyyy = input.getFullYear();
    const mm = String(input.getMonth() + 1).padStart(2, "0");
    const dd = String(input.getDate()).padStart(2, "0");
    return `${yyyy}-${mm}-${dd}`;
  }
  if (/^\d{4}-\d{2}-\d{2}$/.test(input)) return input;
  if (/^\d{2}\/\d{2}\/\d{4}$/.test(input)) {
    const [dd, mm, yyyy] = input.split("/");
    return `${yyyy}-${mm}-${dd}`;
  }
  return input;
}

export default function HotelsPage({ locale = "fr" }) {
  const isRtl = locale === "ar";
  const [hotels, setHotels] = useState([]);
  const [loading, setLoading] = useState(false);
  const [search, setSearch] = useState("");
  const [selectedIndex, setSelectedIndex] = useState(null);

  const [showModal, setShowModal] = useState(false);
  const [mode, setMode] = useState("add"); // add | edit
  const [form, setForm] = useState({
    id: null,
    name: "",
    city: "",
    checkin: "",
    checkout: "",
    rooms: "",
    pricePerNight: "",
    statut: "Available"
  });
  const [error, setError] = useState("");

  const texts = {
    fr: {
      title: "Hôtels",
      add: "Ajouter",
      edit: "Modifier",
      delete: "Supprimer",
      print: "Impression",
      searchPlaceholder: "Rechercher hôtel...",
      noItems: "Aucun hôtel",
      save: "Enregistrer",
      cancel: "Annuler",
      required: "Veuillez remplir tous les champs requis."
    },
    ar: {
      title: "الفنادق",
      add: "إضافة",
      edit: "تعديل",
      delete: "حذف",
      print: "طباعة",
      searchPlaceholder: "بحث عن فندق...",
      noItems: "لا توجد فنادق",
      save: "حفظ",
      cancel: "إلغاء",
      required: "يرجى تعبئة الحقول المطلوبة."
    }
  }[locale];

  async function loadHotels() {
    setLoading(true);
    try {
      const loader = window.pywebview?.api?.load_hotels || window.pywebview?.api?.loadHotels || window.pywebview?.api?.get_hotels;
      if (typeof loader === "function") {
        const res = await loader();
        setHotels(Array.isArray(res) ? res : []);
      } else {
        setHotels([]);
      }
    } catch (e) {
      console.error("loadHotels error", e);
      setHotels([]);
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => { loadHotels(); }, [locale]);

  const filtered = hotels.filter(h => {
    const q = search.trim().toLowerCase();
    if (!q) return true;
    return (
      (h.name || "").toLowerCase().includes(q) ||
      (h.city || "").toLowerCase().includes(q)
    );
  });

  function openAdd() {
    setMode("add");
    setForm({
      id: null,
      name: "",
      city: "",
      checkin: toISODate(new Date()),
      checkout: toISODate(new Date()),
      rooms: "",
      pricePerNight: "",
      statut: "Available"
    });
    setError("");
    setShowModal(true);
  }

  function openEdit() {
    if (selectedIndex === null || !filtered[selectedIndex]) {
      alert(locale === "ar" ? "الرجاء تحديد سطر." : "Please select a row.");
      return;
    }
    const h = filtered[selectedIndex];
    setMode("edit");
    setForm({
      id: h.id ?? null,
      name: h.name || "",
      city: h.city || "",
      checkin: h.checkin ? toISODate(h.checkin) : "",
      checkout: h.checkout ? toISODate(h.checkout) : "",
      rooms: h.rooms !== undefined ? String(h.rooms) : "",
      pricePerNight: h.pricePerNight !== undefined ? String(h.pricePerNight) : "",
      statut: h.statut || "Available"
    });
    setError("");
    setShowModal(true);
  }

  async function handleDelete() {
    if (selectedIndex === null || !filtered[selectedIndex]) {
      alert(locale === "ar" ? "الرجاء تحديد سطر." : "Please select a row.");
      return;
    }
    const h = filtered[selectedIndex];
    if (!window.confirm((locale === "ar" ? "حذف الفندق " : "Delete hotel ") + (h.name || "") + " ?")) return;
    try {
      const fn = window.pywebview?.api?.delete_hotel || window.pywebview?.api?.deleteHotel;
      if (typeof fn === "function") {
        await fn(h.id);
        await loadHotels();
        setSelectedIndex(null);
      } else {
        setHotels(prev => prev.filter(x => x.id !== h.id));
        setSelectedIndex(null);
        alert(locale === "ar" ? "تم الحذف محليًا." : "Deleted locally (no backend).");
      }
    } catch (e) {
      console.error("delete hotel failed", e);
      alert(locale === "ar" ? "فشل الحذف" : "Delete failed");
    }
  }

  async function handleSave(e) {
    e?.preventDefault?.();
    setError("");
    if (!form.name || !form.city || form.rooms === "" || form.pricePerNight === "") {
      setError(texts.required);
      return;
    }

    const payload = {
      name: form.name,
      city: form.city,
      checkin: toISODate(form.checkin),
      checkout: toISODate(form.checkout),
      rooms: Number(form.rooms),
      pricePerNight: Number(form.pricePerNight),
      statut: form.statut
    };

    try {
      if (mode === "add") {
        const fn = window.pywebview?.api?.add_hotel || window.pywebview?.api?.addHotel;
        if (typeof fn === "function") {
          await fn(payload);
          await loadHotels();
        } else {
          const tmpId = Date.now();
          setHotels(prev => [{ ...payload, id: tmpId }, ...prev]);
        }
      } else {
        if (!form.id) {
          setError("Missing id");
          return;
        }
        const fn = window.pywebview?.api?.update_hotel || window.pywebview?.api?.updateHotel;
        if (typeof fn === "function") {
          await fn(form.id, payload);
          await loadHotels();
        } else {
          setHotels(prev => prev.map(x => (x.id === form.id ? { ...x, ...payload } : x)));
        }
      }
      setShowModal(false);
    } catch (err) {
      console.error("save hotel error", err);
      setError(locale === "ar" ? "خطأ أثناء الحفظ" : "Save error");
    }
  }

  function handlePrint() { window.print(); }

  return (
    <div style={{ padding: 20, direction: isRtl ? "rtl" : "ltr" }}>
      <h2 style={{ textAlign: "center" }}>{texts.title}</h2>

      <div style={{ display: "flex", gap: 12, marginBottom: 12, alignItems: "center" }}>
        <input
          placeholder={texts.searchPlaceholder}
          value={search}
          onChange={e => { setSearch(e.target.value); setSelectedIndex(null); }}
          style={{ padding: 8, borderRadius: 6, border: "1px solid #ccc", flex: 1 }}
        />
        <div style={{ marginLeft: "auto", display: "flex", gap: 8 }}>
          <button onClick={openAdd} style={styles.actionButton}>{texts.add}</button>
          <button onClick={openEdit} style={styles.actionButton}>{texts.edit}</button>
          <button onClick={handleDelete} style={styles.actionButton}>{texts.delete}</button>
        </div>
      </div>

      <div style={{ border: "1px solid #e0e0e0", borderRadius: 8, overflow: "auto" }}>
        <table style={{ width: "100%", borderCollapse: "collapse" }}>
          <thead style={{ background: "#f1f6ff", fontWeight: 700 }}>
            <tr>
              <th style={styles.th}>#</th>
              <th style={styles.th}>{locale === "ar" ? "الفندق" : "Hôtel"}</th>
              <th style={styles.th}>{locale === "ar" ? "المدينة" : "Ville"}</th>
              <th style={styles.th}>{locale === "ar" ? "تسجيل الوصول" : "Check-in"}</th>
              <th style={styles.th}>{locale === "ar" ? "تسجيل المغادرة" : "Check-out"}</th>
              <th style={styles.th}>{locale === "ar" ? "الغرف" : "Chambres"}</th>
              <th style={styles.th}>{locale === "ar" ? "السعر/ليلة" : "Prix/nuit"}</th>
              <th style={styles.th}>{locale === "ar" ? "الحالة" : "Statut"}</th>
            </tr>
          </thead>
          <tbody>
            {loading ? (
              <tr><td colSpan={8} style={{ padding: 20, textAlign: "center" }}>{locale === "ar" ? "جاري التحميل..." : "Loading..."}</td></tr>
            ) : filtered.length === 0 ? (
              <tr><td colSpan={8} style={{ padding: 20, textAlign: "center" }}>{texts.noItems}</td></tr>
            ) : (
              filtered.map((h, i) => (
                <tr
                  key={h.id ?? i}
                  onClick={() => setSelectedIndex(i)}
                  style={{ background: i === selectedIndex ? "#e6f0ff" : (i % 2 === 0 ? "#fff" : "#fbfdff"), cursor: "pointer" }}
                >
                  <td style={styles.td}>{i + 1}</td>
                  <td style={styles.td}>{h.name}</td>
                  <td style={styles.td}>{h.city}</td>
                  <td style={styles.td}>{h.checkin}</td>
                  <td style={styles.td}>{h.checkout}</td>
                  <td style={styles.td}>{h.rooms}</td>
                  <td style={styles.td}>{formatNumber(h.pricePerNight)}</td>
                  <td style={styles.td}>{h.statut}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      <div style={{ position: "fixed", right: 20, bottom: 20 }}>
        <button onClick={handlePrint} style={{ ...styles.actionButton, background: "#3477eb", color: "#fff" }}>{texts.print}</button>
      </div>

      {/* Modal */}
      {showModal && (
        <div style={styles.modalOverlay}>
          <div style={{ ...styles.modal, ...(isRtl ? { direction: "rtl", textAlign: "right" } : {}) }}>
            <h3>{mode === "add" ? texts.add : texts.edit}</h3>
            <form onSubmit={handleSave} style={{ display: "flex", flexDirection: "column", gap: 12 }}>
              <div style={{ display: "flex", gap: 12 }}>
                <label style={styles.label}>
                  Hôtel
                  <input value={form.name} onChange={e => setForm(s => ({ ...s, name: e.target.value }))} style={styles.input} />
                </label>
                <label style={styles.label}>
                  Ville
                  <input value={form.city} onChange={e => setForm(s => ({ ...s, city: e.target.value }))} style={styles.input} />
                </label>
              </div>

              <div style={{ display: "flex", gap: 12 }}>
                <label style={styles.label}>
                  Check-in
                  <input type="date" value={form.checkin} onChange={e => setForm(s => ({ ...s, checkin: e.target.value }))} style={styles.input} />
                </label>
                <label style={styles.label}>
                  Check-out
                  <input type="date" value={form.checkout} onChange={e => setForm(s => ({ ...s, checkout: e.target.value }))} style={styles.input} />
                </label>
              </div>

              <div style={{ display: "flex", gap: 12 }}>
                <label style={styles.label}>
                  Chambres
                  <input value={form.rooms} onChange={e => setForm(s => ({ ...s, rooms: e.target.value }))} style={styles.input} type="number" />
                </label>
                <label style={styles.label}>
                  Prix / nuit
                  <input value={form.pricePerNight} onChange={e => setForm(s => ({ ...s, pricePerNight: e.target.value }))} style={styles.input} type="number" step="0.01" />
                </label>
              </div>

              <label style={{ display: "flex", flexDirection: "column" }}>
                Statut
                <select value={form.statut} onChange={e => setForm(s => ({ ...s, statut: e.target.value }))} style={styles.input}>
                  <option>Available</option>
                  <option>Full</option>
                  <option>Maintenance</option>
                </select>
              </label>

              {error && <div style={{ color: "red" }}>{error}</div>}

              <div style={{ display: "flex", justifyContent: "flex-end", gap: 8 }}>
                <button type="submit" style={styles.saveBtn}>{texts.save}</button>
                <button type="button" onClick={() => setShowModal(false)} style={styles.cancelBtn}>{texts.cancel}</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

const styles = {
  th: { padding: "0.75rem", textAlign: "left", borderBottom: "1px solid #e6eefb" },
  td: { padding: "0.6rem", borderBottom: "1px solid #f2f6ff", verticalAlign: "middle" },
  actionButton: { padding: "0.5rem 0.9rem", borderRadius: 6, border: "none", background: "#eaf3ff", color: "#3477eb", cursor: "pointer", fontWeight: 700 },
  modalOverlay: { position: "fixed", inset: 0, background: "rgba(0,0,0,0.45)", display: "flex", alignItems: "center", justifyContent: "center", zIndex: 999 },
  modal: { background: "#fff", padding: 18, borderRadius: 10, width: "92%", maxWidth: 820, boxShadow: "0 8px 30px rgba(0,0,0,0.2)" },
  label: { flex: 1, display: "flex", flexDirection: "column", fontSize: 14 },
  input: { marginTop: 6, padding: "0.5rem", borderRadius: 6, border: "1px solid #ccc" },
  saveBtn: { padding: "0.55rem 1rem", background: "#3477eb", color: "#fff", border: "none", borderRadius: 6, cursor: "pointer", fontWeight: 700 },
  cancelBtn: { padding: "0.45rem 0.9rem", background: "#f1f4f8", color: "#333", border: "none", borderRadius: 6, cursor: "pointer", fontWeight: 700 }
};
