// --- Animated Electronic SVG BG ---
function ElectronicBG({ nightMode }) {
  // Helper: glowing nodes
  function Glow({ x, y }) {
    return (
      <circle
        cx={x}
        cy={y}
        r="7.5"
        fill={nightMode ? "#eb3434" : "#ffe57f"}
        filter={
          nightMode
            ? "drop-shadow(0 0 18px #eb3434cc) drop-shadow(0 0 22px #7cdfefcc)"
            : "drop-shadow(0 0 13px #ffe57fcc) drop-shadow(0 0 28px #fffbe7cc)"
        }
        opacity={0.86}
      />
    );
  }
  // Helper: animated line
  function Line({ x1, y1, x2, y2, delay }) {
    const color = nightMode ? "#7cdfef" : "#f7d16e";
    return (
      <line
        x1={x1} y1={y1} x2={x2} y2={y2}
        stroke={color} strokeWidth="2"
        strokeDasharray="6 18"
        style={{
          animation: `circuit-move 2.2s ${delay}s linear infinite`
        }}
      />
    );
  }
  // Helper: moving dot
  function MovingDot({ pathId, path, duration, delay }) {
    return (
      <>
        <path id={pathId} d={path} fill="none" stroke="none"/>
        <circle r="5" fill={nightMode ? "#7cdfef" : "#eb3434"} opacity="0.95">
          <animateMotion
            dur={`${duration}s`}
            repeatCount="indefinite"
            keyPoints="0;1"
            keyTimes="0;1"
            begin={`${delay}s`}
          >
            <mpath xlinkHref={`#${pathId}`} />
          </animateMotion>
          <animate attributeName="opacity"
            values="0.2;1;0.2"
            dur={`${duration * 0.8}s`}
            repeatCount="indefinite"
            begin={`${delay + 0.2}s`}
          />
        </circle>
      </>
    );
  }
  return (
    <svg
      width="600"
      height="350"
      viewBox="0 0 600 350"
      style={{
        position: "absolute",
        left: "50%",
        top: "50%",
        transform: "translate(-50%,-48%) scale(1.1)",
        zIndex: 1,
        pointerEvents: "none",
        opacity: nightMode ? 0.22 : 0.17,
        filter: nightMode
          ? "drop-shadow(0 4px 44px #7cdfef77) drop-shadow(0 0px 55px #eb3434bb)"
          : "drop-shadow(0 2px 42px #fff9c977)"
      }}
    >
      {/* Animated circuit lines */}
      <g>
        <Line x1={70} y1={70} x2={540} y2={70} delay={0.1} />
        <Line x1={70} y1={150} x2={540} y2={150} delay={0.2} />
        <Line x1={70} y1={230} x2={540} y2={230} delay={0.32} />
        <Line x1={160} y1={70} x2={160} y2={230} delay={0.18} />
        <Line x1={330} y1={70} x2={330} y2={230} delay={0.26} />
        {/* Moving dots */}
        <MovingDot path="M70,70 L540,70" pathId="h1" duration={3.5} delay={0.1}/>
        <MovingDot path="M70,150 L540,150" pathId="h2" duration={3.8} delay={0.2}/>
        <MovingDot path="M70,230 L540,230" pathId="h3" duration={4.2} delay={0.32}/>
        <MovingDot path="M160,70 L160,230" pathId="v1" duration={3.9} delay={0.18}/>
        <MovingDot path="M330,70 L330,230" pathId="v2" duration={4.1} delay={0.26}/>
      </g>
      {/* Glowing nodes */}
      <Glow x={70} y={70} /><Glow x={160} y={70} /><Glow x={330} y={70} /><Glow x={540} y={70} />
      <Glow x={70} y={150} /><Glow x={160} y={150} /><Glow x={330} y={150} /><Glow x={540} y={150} />
      <Glow x={70} y={230} /><Glow x={160} y={230} /><Glow x={330} y={230} /><Glow x={540} y={230} />
    </svg>
  );
}

