import React, { useState } from "react";
import ClientPage from "./client"; // Import your ClientPage from client.js

// Define placeholder components for the remaining pages
function BookingPage() {
  return (
    <div style={pageStyles.container}>
      <h2>Page Booking</h2>
      <p>Booking details and management will appear here.</p>
    </div>
  );
}

function PaymentsPage() {
  return (
    <div style={pageStyles.container}>
      <h2>Page Paiements</h2>
      <p>Payment information and processing will be displayed here.</p>
    </div>
  );
}

function ReportsPage() {
  return (
    <div style={pageStyles.container}>
      <h2>Page Rapports</h2>
      <p>Reports and analytics are shown here.</p>
    </div>
  );
}

function SettingsPage() {
  return (
    <div style={pageStyles.container}>
      <h2>Page Paramètres</h2>
      <p>Application settings and configurations appear here.</p>
    </div>
  );
}

// Array that defines all available pages
const pages = [
  { label: "Clients", page: "client" },
  { label: "Booking", page: "booking" },
  { label: "Paiements", page: "payments" },
  { label: "Rapports", page: "reports" },
  { label: "Paramètres", page: "settings" },
];

// Tab bar component
function Tabs({ currentPage, onNavigate }) {
  return (
    <div style={styles.tabs}>
      {pages.map(({ label, page }) => (
        <button
          key={page}
          style={{
            ...styles.tab,
            ...(currentPage === page ? styles.activeTab : {}),
          }}
          onClick={() => onNavigate(page)}
        >
          {label}
        </button>
      ))}
    </div>
  );
}

// Dashboard component that displays all buttons in a bento grid style
function Dashboard({ onNavigate }) {
  return (
    <div style={styles.dashboardContainer}>
      <h2>Tableau de bord</h2>
      <div style={styles.grid}>
        {pages.map(({ label, page }) => (
          <button
            key={page}
            style={styles.tile}
            onClick={() => onNavigate(page)}
          >
            {label}
          </button>
        ))}
      </div>
    </div>
  );
}

function App() {
  const [loggedIn, setLoggedIn] = useState(false);
  const [currentPage, setCurrentPage] = useState("dashboard");
  const [loginData, setLoginData] = useState({ username: "", password: "" });
  const [error, setError] = useState("");

  const handleLogin = (e) => {
    e.preventDefault();
    // Demo credentials: admin / password
    if (loginData.username === "admin" && loginData.password === "password") {
      setLoggedIn(true);
      setError("");
      setCurrentPage("dashboard");
    } else {
      setError("Nom d'utilisateur ou mot de passe incorrect.");
    }
  };

  // Determine which page to display based on currentPage state
  let pageContent;
  if (!loggedIn) {
    pageContent = (
      <div style={styles.container}>
        <h2>Connexion Agence de Voyage</h2>
        <form onSubmit={handleLogin} style={styles.form}>
          <input
            type="text"
            placeholder="Nom d'utilisateur"
            value={loginData.username}
            onChange={(e) =>
              setLoginData({ ...loginData, username: e.target.value })
            }
            style={styles.input}
          />
          <input
            type="password"
            placeholder="Mot de passe"
            value={loginData.password}
            onChange={(e) =>
              setLoginData({ ...loginData, password: e.target.value })
            }
            style={styles.input}
          />
          <button type="submit" style={styles.button}>
            Se connecter
          </button>
          {error && <p style={styles.error}>{error}</p>}
        </form>
      </div>
    );
  } else {
    // When logged in, display the Tab bar and the current page's content
    switch (currentPage) {
      case "client":
        pageContent = <ClientPage />;
        break;
      case "booking":
        pageContent = <BookingPage />;
        break;
      case "payments":
        pageContent = <PaymentsPage />;
        break;
      case "reports":
        pageContent = <ReportsPage />;
        break;
      case "settings":
        pageContent = <SettingsPage />;
        break;
      case "dashboard":
      default:
        pageContent = <Dashboard onNavigate={setCurrentPage} />;
        break;
    }
  }

  return (
    <div style={styles.appContainer}>
      {loggedIn && <Tabs currentPage={currentPage} onNavigate={setCurrentPage} />}
      {loggedIn && currentPage !== "dashboard" && (
        <button
          style={styles.backButton}
          onClick={() => setCurrentPage("dashboard")}
        >
          ← Retour au dashboard
        </button>
      )}
      {pageContent}
    </div>
  );
}

const styles = {
  appContainer: {
    minHeight: "100vh",
    background: "#f0f6ff",
    paddingTop: "70px",
    position: "relative",
  },
  container: {
    minHeight: "100vh",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    background: "#f0f6ff",
  },
  form: {
    display: "flex",
    flexDirection: "column",
    gap: "1rem",
    width: "300px",
    padding: "2rem",
    background: "#fff",
    borderRadius: "10px",
    boxShadow: "0 4px 24px rgba(0,0,0,0.06)",
  },
  input: {
    padding: "0.5rem",
    fontSize: "1rem",
    borderRadius: "4px",
    border: "1px solid #c2c2c2",
  },
  button: {
    padding: "0.7rem",
    fontSize: "1rem",
    borderRadius: "4px",
    border: "none",
    background: "#3477eb",
    color: "#fff",
    fontWeight: "bold",
    cursor: "pointer",
  },
  error: {
    color: "red",
    fontWeight: "bold",
    margin: 0,
  },
  tabs: {
    display: "flex",
    position: "fixed",
    top: 0,
    left: 0,
    width: "100%",
    background: "#fff",
    boxShadow: "0 2px 8px rgba(52,119,235,0.05)",
    zIndex: 10,
    justifyContent: "center",
    padding: "0.5rem 0",
  },
  tab: {
    padding: "0.7rem 2rem",
    fontSize: "1rem",
    border: "none",
    borderBottom: "3px solid transparent",
    background: "none",
    color: "#3477eb",
    fontWeight: "bold",
    cursor: "pointer",
    outline: "none",
    transition: "border-bottom 0.2s, color 0.2s",
  },
  activeTab: {
    color: "#222",
    borderBottom: "3px solid #3477eb",
    background: "#e8f0fe",
    borderRadius: "8px 8px 0 0",
  },
  dashboardContainer: {
    textAlign: "center",
    margin: "0 auto",
    maxWidth: "700px",
  },
  grid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(180px, 1fr))",
    gap: "1.5rem",
    marginTop: "2rem",
  },
  tile: {
    padding: "2rem",
    fontSize: "1.25rem",
    borderRadius: "8px",
    border: "none",
    background: "#fff",
    boxShadow: "0 2px 16px rgba(52,119,235,0.07)",
    color: "#3477eb",
    cursor: "pointer",
    transition: "transform 0.1s",
  },
  backButton: {
    position: "absolute",
    top: "70px",
    left: "24px",
    background: "#e1e8f5",
    border: "none",
    borderRadius: "8px",
    padding: "0.5rem 1rem",
    color: "#3477eb",
    fontWeight: "bold",
    cursor: "pointer",
    zIndex: 2,
  },
};

const pageStyles = {
  container: {
    padding: "2rem",
    textAlign: "center",
  },
};

export default App;
