function formatDateToFR(dateString) {
  if (!dateString) return "";
  if (/^\d{4}-\d{2}-\d{2}$/.test(dateString)) {
    const [yyyy, mm, dd] = dateString.split("-");
    return `${dd}/${mm}/${yyyy}`;
  } else if (/^\d{2}-\d{2}-\d{4}$/.test(dateString)) {
    const [dd, mm, yyyy] = dateString.split("-");
    return `${dd}/${mm}/${yyyy}`;
  } else if (/^\d{2}\/\d{2}\/\d{4}$/.test(dateString)) {
    return dateString;
  }
  return dateString;
}

function formatDateToISO(dateString) {
  if (!dateString) return "";
  let dd, mm, yyyy;
  if (/^\d{2}\/\d{2}\/\d{4}$/.test(dateString)) {
    [dd, mm, yyyy] = dateString.split("/");
  } else if (/^\d{2}-\d{2}-\d{4}$/.test(dateString)) {
    [dd, mm, yyyy] = dateString.split("-");
  } else if (/^\d{4}-\d{2}-\d{2}$/.test(dateString)) {
    return dateString;
  }
  if (yyyy && mm && dd) {
    return `${yyyy}-${mm}-${dd}`;
  }
  return dateString;
}

function isArabic(str) {
  return /^[\u0600-\u06FF\s]+$/.test(str);
}

// PDF generator for Fiche client, sends to backend for system preview
async function generateClientPDF(client, locale, translations) {
  const doc = new jsPDF();
  let y = 12;
  doc.setFontSize(18);
  doc.text(translations[locale].ficheButton, 12, y);
  y += 10;
  doc.setFontSize(12);

  const L = translations[locale].modalLabels;
  const printField = (label, value) => {
    doc.text(`${label}: ${value ?? ""}`, 12, y);
    y += 8;
  };

  printField(L.nom, client.nom);
  printField(L.prenom, client.prenom);
  printField(L.nom_ar, client.nom_ar);
  printField(L.prenom_ar, client.prenom_ar);
  printField(L.prenom_pere, client.prenom_pere);
  printField(L.prenom_pere_ar, client.prenom_pere_ar);
  printField(L.nom_mere, client.nom_mere);
  printField(L.prenom_mere, client.prenom_mere);
  printField(L.nom_mere_ar, client.nom_mere_ar);
  printField(L.prenom_mere_ar, client.prenom_mere_ar);
  printField(L.date_de_naissance, client.date_de_naissance);
  printField(L.lieu_de_naissance, client.lieu_de_naissance);
  printField(L.nationalite, client.nationalite);
  printField(L.passport_number, client.passport_number);
  printField(L.date_delivrance, client.date_delivrance);
  printField(L.date_expiration, client.date_expiration);
  printField(L.addresse, client.addresse);
  printField(L.telephone, client.telephone);
  printField(L.credit, client.credit);

  if (client.photo && client.photo.startsWith("data:")) {
    try {
      doc.addImage(client.photo, "JPEG", 140, 12, 40, 32);
    } catch (e) {}
  }

  // Get PDF as Uint8Array and then to base64
  const pdfBlob = doc.output("blob");
  const arrayBuffer = await pdfBlob.arrayBuffer();
  const uint8Array = new Uint8Array(arrayBuffer);
  let binary = '';
  for (let i = 0; i < uint8Array.length; i++) {
    binary += String.fromCharCode(uint8Array[i]);
  }
  const base64PDF = window.btoa(binary);

  // Send to backend for system PDF preview
  if (window.pywebview && window.pywebview.api && window.pywebview.api.open_pdf_base64) {
    await window.pywebview.api.open_pdf_base64(base64PDF);
  } else {
    alert("PDF preview not supported in this environment.");
  }
}

function ClientPage({ locale = "fr", nightMode = false }) {
  const [clients, setClients] = useState([]);
  const [selectedIndex, setSelectedIndex] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [modalMode, setModalMode] = useState("add");
  const [clientForm, setClientForm] = useState({
    id: null,
    nom: "",
    prenom: "",
    nom_ar: "",
    prenom_ar: "",
    prenom_pere: "",
    prenom_pere_ar: "",
    nom_mere: "",
    prenom_mere: "",
    nom_mere_ar: "",
    prenom_mere_ar: "",
    date_de_naissance: "",
    lieu_de_naissance: "",
    nationalite: "Alg√©rienne",
    passport_number: "",
    date_delivrance: "",
    date_expiration: "",
    addresse: "",
    telephone: "",
    credit: "",
    photo: ""
  });
  const [arErrors, setArErrors] = useState({});
  const [isDeleting, setIsDeleting] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const isRtl = locale === "ar";
  const displayedHeaders = isRtl
    ? [...translations[locale].tableHeaders].reverse()
    : translations[locale].tableHeaders;
  const tableFieldsOrder = isRtl
    ? [...TABLE_FIELDS].reverse()
    : [...TABLE_FIELDS];
  const directionStyle = isRtl
    ? { direction: "rtl" }
    : { direction: "ltr" };

  const fetchClients = async () => {
    setIsLoading(true);
    try {
      const result = await window.pywebview.api.get_clients();
      setClients(result || []);
    } catch (err) {
      setClients([]);
    }
    setIsLoading(false);
  };

  useEffect(() => {
    fetchClients();
    // eslint-disable-next-line
  }, [locale]);

  const filteredClients = clients.filter((c) => {
    const q = searchQuery.trim().toLowerCase();
    if (!q) return true;
    return (
      (c.nom || "").toLowerCase().includes(q) ||
      (c.prenom || "").toLowerCase().includes(q) ||
      (c.date_de_naissance || "").toLowerCase().includes(q) ||
      (c.lieu_de_naissance || "").toLowerCase().includes(q) ||
      (c.nationalite || "").toLowerCase().includes(q) ||
      (c.passport_number || "").toString().toLowerCase().includes(q) ||
      (c.telephone || "").toLowerCase().includes(q)
    );
  });

  const emptyForm = {
    id: null,
    nom: "",
    prenom: "",
    nom_ar: "",
    prenom_ar: "",
    prenom_pere: "",
    prenom_pere_ar: "",
    nom_mere: "",
    prenom_mere: "",
    nom_mere_ar: "",
    prenom_mere_ar: "",
    date_de_naissance: "",
    lieu_de_naissance: "",
    nationalite: "Alg√©rienne",
    passport_number: "",
    date_delivrance: "",
    date_expiration: "",
    addresse: "",
    telephone: "",
    credit: "",
    photo: ""
  };

  const handleAjouter = () => {
    setModalMode("add");
    setClientForm({ ...emptyForm });
    setArErrors({});
    setShowModal(true);
  };

  const handleModifier = () => {
    if (selectedIndex !== null && filteredClients[selectedIndex]) {
      setModalMode("edit");
      setClientForm({
        ...filteredClients[selectedIndex],
        date_de_naissance: formatDateToFR(filteredClients[selectedIndex].date_de_naissance),
        date_delivrance: formatDateToFR(filteredClients[selectedIndex].date_delivrance),
        date_expiration: formatDateToFR(filteredClients[selectedIndex].date_expiration)
      });
      setArErrors({});
      setShowModal(true);
    } else {
      alert(translations[locale].selectRowAlert);
    }
  };

  const handleSupprimer = async () => {
    if (selectedIndex === null || !filteredClients[selectedIndex]) {
      alert(translations[locale].selectRowAlert);
      return;
    }
    if (isDeleting) return;
    const clientId = filteredClients[selectedIndex].id;
    const clientName =
      filteredClients[selectedIndex].nom ||
      filteredClients[selectedIndex].prenom ||
      filteredClients[selectedIndex].passport_number ||
      "";
    const confirmDelete = window.confirm(
      translations[locale].confirmDelete(clientName)
    );
    if (!confirmDelete) return;
    try {
      setIsDeleting(true);
      await window.pywebview.api.delete_client(clientId);
      setSelectedIndex(null);
      fetchClients();
    } catch (err) {
      alert(translations[locale].errorDelete);
    } finally {
      setIsDeleting(false);
    }
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setArErrors({});
    setClientForm({ ...emptyForm });
  };

  const handleArInput = (field, value) => {
    let err = "";
    if (value && !isArabic(value)) {
      err = translations[locale].saisieArabe;
    }
    setClientForm((prev) => ({ ...prev, [field]: value }));
    setArErrors((prev) => ({ ...prev, [field]: err }));
  };

  const handleSaveClient = async (e) => {
    e.preventDefault();
    let newErrors = {};
    for (const f of Object.keys(clientForm)) {
      if (f.endsWith("_ar") && clientForm[f] && !isArabic(clientForm[f])) {
        newErrors[f] = translations[locale].saisieArabe;
      }
    }
    setArErrors(newErrors);
    if (Object.values(newErrors).some(Boolean)) return;

    try {
      const payload = {
        ...clientForm,
        date_de_naissance: formatDateToISO(clientForm.date_de_naissance),
        date_delivrance: formatDateToISO(clientForm.date_delivrance),
        date_expiration: formatDateToISO(clientForm.date_expiration)
      };
      if (modalMode === "add") {
        await window.pywebview.api.add_client(payload);
      } else {
        await window.pywebview.api.update_client(payload);
      }
      handleCloseModal();
      fetchClients();
    } catch (err) {
      alert(translations[locale].errorSave);
    }
  };

  const handlePhotoChange = (file) => {
    if (!file) {
      setClientForm((s) => ({ ...s, photo: "" }));
      return;
    }
    const reader = new FileReader();
    reader.onload = () => {
      setClientForm((s) => ({ ...s, photo: reader.result }));
    };
    reader.readAsDataURL(file);
  };

  const photoSrc = (val) => {
    if (!val) return null;
    if (typeof val !== "string") return null;
    if (val.startsWith("data:")) return val;
    return `data:image/png;base64,${val}`;
  };

  // NIGHT MODE STYLES
  const tableContainerStyle = nightMode
    ? {
        maxHeight: "60vh",
        overflowY: "auto",
        border: "1.5px solid #29334b",
        borderRadius: "8px",
        background: "#212736",
        boxShadow: "0 4px 32px #151926cc",
      }
    : {
