        overflowY: "auto",
        border: "1px solid #e0e0e0",
        borderRadius: "6px"
      };

  const tableStyle = nightMode
    ? {
        width: "100%",
        borderCollapse: "collapse",
        marginTop: "0",
        tableLayout: "fixed",
        background: "#232939"
      }
    : {
        width: "100%",
        borderCollapse: "collapse",
        marginTop: "0",
        tableLayout: "fixed"
      };

  const tableHeaderStyle = nightMode
    ? {
        padding: "0.5rem",
        border: "1px solid #27304b",
        fontWeight: "bold",
        fontSize: "0.98rem",
        background: "#232c44",
        color: "#aad5ff"
      }
    : {
        padding: "0.5rem",
        border: "1px solid #c2c2c2",
        fontWeight: "bold",
        fontSize: "0.98rem",
        background: "#e8f0fe"
      };

  const tableRowStyle = (idx) =>
    nightMode
      ? {
          cursor: "pointer",
          background:
            idx % 2 === 0 ? "#232939" : "#262c3c",
          transition: "background 0.15s"
        }
      : {
          cursor: "pointer",
          background: idx % 2 === 0 ? "#fff" : "#f4f8ff"
        };

  const tableCellStyle = nightMode
    ? {
        padding: "0.35rem 0.45rem",
        border: "1px solid #27304b",
        fontSize: "0.96rem",
        wordBreak: "break-word",
        verticalAlign: "middle",
        color: "#e5eaff",
        background: "inherit"
      }
    : {
        padding: "0.35rem 0.45rem",
        border: "1px solid #e0e0e0",
        fontSize: "0.96rem",
        wordBreak: "break-word",
        verticalAlign: "middle"
      };

  const highlightedRow = nightMode
    ? { background: "#183b57", color: "#ffe57f" }
    : { background: "#cce5ff" };

  const spinnerStyle = nightMode
    ? {
        margin: "60px auto",
        border: "8px solid #232939",
        borderTop: "8px solid #3477eb",
