  };

  // Two drawers: left and right, slightly lower (bottom: 28px)
  const drawerLeft = (
    <div
      style={{
        position: "fixed",
        left: 20,
        right: "unset",
        bottom: 28,
        justifyContent: "flex-start",
        width: "auto",
        minWidth: 0,
        background: nightMode ? "#192032ee" : "#ffffff",
        padding: "1rem",
        borderRadius: "8px",
        boxShadow: nightMode
          ? "0 2px 8px #0e1528cc"
          : "0 2px 8px rgba(0,0,0,0.15)",
        display: "flex",
        flexDirection: "row",
        gap: "0.5rem",
        zIndex: 8,
        minWidth: 0,
        alignItems: "center"
      }}
    >
      <button style={{
        display: "flex",
        alignItems: "center",
        gap: "0.3rem",
        padding: "0.5rem 1rem",
        fontSize: "1rem",
        color: nightMode ? "#aad7ff" : "#3477eb",
        background: nightMode ? "#1b2536" : "#eaf3ff",
        border: "none",
        borderRadius: "4px",
        cursor: "pointer",
        transition: "background 0.2s"
      }} onClick={handleAjouter}>
        {translations[locale].addButton}
      </button>
      <button style={{
        display: "flex",
        alignItems: "center",
        gap: "0.3rem",
        padding: "0.5rem 1rem",
        fontSize: "1rem",
        color: nightMode ? "#aad7ff" : "#3477eb",
        background: nightMode ? "#1b2536" : "#eaf3ff",
        border: "none",
        borderRadius: "4px",
        cursor: "pointer",
        transition: "background 0.2s"
      }} onClick={handleModifier}>
        {translations[locale].editButton}
      </button>
      <button style={{
        display: "flex",
        alignItems: "center",
        gap: "0.3rem",
        padding: "0.5rem 1rem",
        fontSize: "1rem",
        color: nightMode ? "#aad7ff" : "#3477eb",
        background: nightMode ? "#1b2536" : "#eaf3ff",
        border: "none",
        borderRadius: "4px",
        cursor: "pointer",
        transition: "background 0.2s"
      }} onClick={handleSupprimer}>
        {translations[locale].deleteButton}
      </button>
    </div>
  );
  const drawerRight = (
    <div
      style={{
        position: "fixed",
        right: 20,
        left: "unset",
        bottom: 28,
        justifyContent: "flex-end",
        width: "auto",
        minWidth: 0,
        background: nightMode ? "#192032ee" : "#ffffff",
        padding: "1rem",
        borderRadius: "8px",
        boxShadow: nightMode
          ? "0 2px 8px #0e1528cc"
          : "0 2px 8px rgba(0,0,0,0.15)",
        display: "flex",
        flexDirection: "row",
        gap: "0.5rem",
        zIndex: 8,
        minWidth: 0,
        alignItems: "center"
      }}
    >
      <button style={{ background: "#1b9e5c", color: "#fff", border: "none", borderRadius: 4, padding: "0.5rem 1rem", fontWeight: 500, cursor: "pointer" }} onClick={handleFicheClient}>
        {translations[locale].ficheButton}
      </button>
      <button style={{ background: "#3477eb", color: "#fff", border: "none", borderRadius: 4, padding: "0.5rem 1rem", fontWeight: 500, cursor: "pointer" }} onClick={handleImpression}>
        {translations[locale].printButton}
      </button>
    </div>
  );

  return (
    <div style={{ ...directionStyle, background: nightMode ? "#181c24" : "#fff", minHeight: "100vh", padding: "1.25rem" }}>
      <h2 style={{ textAlign: "center", color: nightMode ? "#aad7ff" : "#232d40" }}>{translations[locale].title}</h2>
      {/* Search bar */}
      <div style={{
        marginBottom: "1rem",
        display: "flex",
        alignItems: "center",
        maxWidth: 340,
        flexDirection: isRtl ? "row-reverse" : "row"
      }}>
        <span style={{ fontSize: "1.2rem", marginRight: "0.5rem", color: nightMode ? "#aad7ff" : "#232d40" }}>üîç</span>
        <input
          type="text"
          placeholder={translations[locale].searchPlaceholder}
          value={searchQuery}
          onChange={(e) => {
            setSearchQuery(e.target.value);
            setSelectedIndex(null);
          }}
          style={{
            flex: 1,
            padding: "0.5rem",
            fontSize: "1rem",
            borderRadius: "4px",
            border: nightMode ? "1px solid #27304b" : "1px solid #ccc",
            background: nightMode ? "#212736" : "#fff",
            color: nightMode ? "#e5eaff" : "#232d40"
          }}
        />
      </div>
      {isLoading ? (
        <div style={{ display: "flex", justifyContent: "center" }}>
          <div style={spinnerStyle}></div>
        </div>
      ) : (
        <div style={tableContainerStyle}>
          <table style={tableStyle}>
            <thead>
              <tr>
                {isRtl
                  ? [...displayedHeaders].reverse().map((h, i) => (
                      <th
                        key={i}
                        style={{
                          ...tableHeaderStyle,
                          ...(h === "#" ? { width: 36, minWidth: 24, maxWidth: 40 } : {}),
                          ...(h === "Photo" || h === "ÿßŸÑÿµŸàÿ±ÿ©" ? { minWidth: 50, maxWidth: 70, width: 60 } : {}),
                        }}
                        className="tableHeaderSticky"
                      >
                        {h}
                      </th>
                    ))
                  : displayedHeaders.map((h, i) => (
                      <th
                        key={i}
                        style={{
                          ...tableHeaderStyle,
                          ...(h === "#" ? { width: 36, minWidth: 24, maxWidth: 40 } : {}),
                          ...(h === "Photo" || h === "ÿßŸÑÿµŸàÿ±ÿ©" ? { minWidth: 50, maxWidth: 70, width: 60 } : {}),
                        }}
                        className="tableHeaderSticky"
                      >
                        {h}
                      </th>
                    ))}
              </tr>
            </thead>
            <tbody>
              {filteredClients.map((client, idx) => (
                <tr
                  key={client.id || (client.nom + idx)}
                  style={{
                    ...tableRowStyle(idx),
                    ...(idx === selectedIndex ? highlightedRow : {})
                  }}
                  onClick={() => setSelectedIndex(idx)}
                >
                  {isRtl ? (
                    <>
                      <td style={tableCellStyle}>{idx + 1}</td>
                      {[...tableFieldsOrder].reverse().map(field => renderTableCell(client, field))}
                    </>
                  ) : (
                    <>
                      <td style={tableCellStyle}>{idx + 1}</td>
                      {tableFieldsOrder.map(field => renderTableCell(client, field))}
                    </>
                  )}
                </tr>
              ))}
              {filteredClients.length === 0 && !isLoading && (
                <tr>
                  <td colSpan={displayedHeaders.length} style={{ padding: "1rem", textAlign: "center", color: nightMode ? "#7ea6c7" : "#666" }}>
                    {translations[locale].errorLoad}
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      )}
      {drawerLeft}
      {drawerRight}
      {/* Modal for Adding/Editing a Client */}
      {/* ...modal code remains unchanged, but you can apply similar nightMode styles if desired... */}
    </div>
  );
}

export default ClientPage;
