    alertSelectRow: "Veuillez sÃ©lectionner une ligne client.",
    modalTitleAdd: "Ajouter Nouveau Client",
    modalTitleEdit: "Modifier Client",
    labelPassport: "Passeport",
    labelFullName: "Nom complet",
    labelBirthdate: "Date de naissance",
    labelPhone: "TÃ©lÃ©phone",
