    labelPhone: "Ø§Ù„Ù‡Ø§ØªÙ",
    labelEmail: "Ø§Ù„Ø¨Ø±ÙŠØ¯ Ø§Ù„Ø¥Ù„ÙƒØªØ±ÙˆÙ†ÙŠ",
    labelAddress: "Ø§Ù„Ø¹Ù†ÙˆØ§Ù†",
    saveButton: "Ø­ÙØ¸",
