function ClientPage({ locale = "fr" }) {
  // Client data state
  const [clients, setClients] = useState([]);
  const [selectedIndex, setSelectedIndex] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");

  // Modal state: whether modal is visible and which mode ("add" or "edit") is active.
  const [showModal, setShowModal] = useState(false);
  const [modalMode, setModalMode] = useState("add"); // "add" or "edit"
  const [clientForm, setClientForm] = useState({
    id: null,
    passport: "",
    full_name: "",
    birthdate: "",
    phone: "",
    email: "",
    address: "",
  });

  // Adjust container direction based on locale for RTL support
  const directionStyle = locale === "ar" ? { direction: "rtl", textAlign: "right" } : {};

  // Fetch clients from the backend
  const fetchClients = async () => {
    try {
      const result = await window.pywebview.api.get_clients();
      setClients(result);
    } catch (err) {
      alert("Erreur lors du chargement des clients");
    }
  };

  useEffect(() => {
    fetchClients();
  }, []);

  // Button Handlers
  const handleAdd = () => {
    setModalMode("add");
    setClientForm({
      id: null,
      passport: "",
      full_name: "",
      birthdate: "",
      phone: "",
      email: "",
      address: "",
    });
    setShowModal(true);
  };

  const handleEdit = () => {
    if (selectedIndex !== null && filteredClients[selectedIndex]) {
      setModalMode("edit");
      setClientForm({ ...filteredClients[selectedIndex] });
      setShowModal(true);
    } else {
      alert(translations[locale].alertSelectRow);
    }
  };

  const handleDelete = async () => {
    if (selectedIndex !== null && filteredClients[selectedIndex]) {
      if (
        window.confirm(
          translations[locale].confirmDelete(filteredClients[selectedIndex].full_name)
        )
      ) {
        try {
          await window.pywebview.api.delete_client(filteredClients[selectedIndex].id);
          setSelectedIndex(null);
          fetchClients();
        } catch (err) {
          alert("Erreur lors de la suppression !");
        }
      }
    } else {
      alert(translations[locale].alertSelectRow);
    }
  };

  // Modal Handlers
  const handleCloseModal = () => {
    setShowModal(false);
    setClientForm({
      id: null,
      passport: "",
      full_name: "",
      birthdate: "",
      phone: "",
      email: "",
      address: "",
    });
  };

  const handleSaveClient = async (e) => {
    e.preventDefault();
    try {
      if (modalMode === "add") {
        await window.pywebview.api.add_client(clientForm);
      } else if (modalMode === "edit") {
        await window.pywebview.api.update_client(clientForm);
      }
      handleCloseModal();
      fetchClients();
    } catch (err) {
      alert("Erreur lors de l'enregistrement !");
    }
  };

  // Filtered clients based on search query (searching passport, full_name, or email)
  const filteredClients = clients.filter((client) => {
    const query = searchQuery.toLowerCase();
    return (
      client.passport.toLowerCase().includes(query) ||
      client.full_name.toLowerCase().includes(query) ||
      client.email.toLowerCase().includes(query)
    );
  });

  return (
    <div style={{ ...styles.pageContainer, ...directionStyle }}>
      {/* Header: Title and search bar */}
      <div style={styles.headerContainer}>
        <h2 style={{ margin: 0 }}>{translations[locale].pageTitle}</h2>
        <input
          type="text"
          placeholder={translations[locale].searchPlaceholder}
          value={searchQuery}
          onChange={(e) => {
            setSearchQuery(e.target.value);
            setSelectedIndex(null);
          }}
          style={styles.searchInput}
        />
      </div>
      {/* Scrollable table container with fixed header */}
      <div style={styles.tableContainer}>
        <table style={styles.table}>
          <thead style={styles.tableHeaderGroup}>
            <tr style={styles.tableHeaderRow}>
              <th style={styles.tableHeader}>#</th>
              <th style={styles.tableHeader}>{translations[locale].labelPassport}</th>
              <th style={styles.tableHeader}>{translations[locale].labelFullName}</th>
              <th style={styles.tableHeader}>{translations[locale].labelBirthdate}</th>
              <th style={styles.tableHeader}>{translations[locale].labelPhone}</th>
              <th style={styles.tableHeader}>{translations[locale].labelEmail}</th>
              <th style={styles.tableHeader}>{translations[locale].labelAddress}</th>
            </tr>
          </thead>
          <tbody>
            {filteredClients.map((client, idx) => (
              <tr
                key={client.id || client.passport + idx}
                style={{
                  ...styles.tableRow,
                  background:
                    idx === selectedIndex
                      ? "#cce5ff"
                      : idx % 2 === 0
                      ? "#fff"
                      : "#f4f8ff",
                }}
                onClick={() => setSelectedIndex(idx)}
              >
                <td style={styles.tableCell}>{idx + 1}</td>
                <td style={styles.tableCell}>{client.passport}</td>
                <td style={styles.tableCell}>{client.full_name}</td>
                <td style={styles.tableCell}>{client.birthdate}</td>
                <td style={styles.tableCell}>{client.phone}</td>
                <td style={styles.tableCell}>{client.email}</td>
                <td style={styles.tableCell}>{client.address}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      {/* Horizontal drawer for button actions */}
      <div style={styles.drawer}>
        <button style={styles.drawerButton} onClick={handleAdd}>
          {translations[locale].addButton}
        </button>
        <button style={styles.drawerButton} onClick={handleEdit}>
          {translations[locale].editButton}
        </button>
        <button style={styles.drawerButton} onClick={handleDelete}>
          {translations[locale].deleteButton}
        </button>
      </div>
      {/* Modal for adding/editing a client */}
      {showModal && (
        <div style={styles.modalOverlay}>
          <div style={styles.modalContent}>
            <h3>
              {modalMode === "add"
                ? translations[locale].modalTitleAdd
                : translations[locale].modalTitleEdit}
            </h3>
            <form onSubmit={handleSaveClient} style={styles.modalForm}>
              <label style={styles.modalLabel}>
                {translations[locale].labelPassport}:
                <input
                  type="text"
                  value={clientForm.passport}
                  onChange={(e) =>
                    setClientForm({ ...clientForm, passport: e.target.value })
                  }
                  style={styles.modalInput}
                  required
                />
              </label>
              <label style={styles.modalLabel}>
                {translations[locale].labelFullName}:
                <input
                  type="text"
                  value={clientForm.full_name}
                  onChange={(e) =>
                    setClientForm({ ...clientForm, full_name: e.target.value })
                  }
                  style={styles.modalInput}
                  required
                />
              </label>
              <label style={styles.modalLabel}>
                {translations[locale].labelBirthdate}:
                <input
                  type="date"
                  value={clientForm.birthdate}
                  onChange={(e) =>
                    setClientForm({ ...clientForm, birthdate: e.target.value })
                  }
                  style={styles.modalInput}
                  required
                />
              </label>
              <label style={styles.modalLabel}>
                {translations[locale].labelPhone}:
                <input
                  type="text"
                  value={clientForm.phone}
                  onChange={(e) =>
                    setClientForm({ ...clientForm, phone: e.target.value })
                  }
                  style={styles.modalInput}
                  required
                />
              </label>
              <label style={styles.modalLabel}>
                {translations[locale].labelEmail}:
                <input
                  type="email"
                  value={clientForm.email}
                  onChange={(e) =>
                    setClientForm({ ...clientForm, email: e.target.value })
                  }
                  style={styles.modalInput}
                  required
                />
              </label>
              <label style={styles.modalLabel}>
                {translations[locale].labelAddress}:
                <input
                  type="text"
                  value={clientForm.address}
                  onChange={(e) =>
                    setClientForm({ ...clientForm, address: e.target.value })
                  }
                  style={styles.modalInput}
                  required
                />
              </label>
              <div style={styles.modalButtons}>
                <button type="submit" style={styles.modalButton}>
                  {translations[locale].saveButton}
                </button>
                <button
                  type="button"
                  style={styles.modalButton}
                  onClick={handleCloseModal}
                >
                  {translations[locale].cancelButton}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

const styles = {
  pageContainer: {
    padding: "1rem",
    position: "relative",
    minHeight: "100vh",
    background: "#f0f6ff"
  },
  headerContainer: {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    marginBottom: "1rem"
  },
  searchInput: {
    width: "40%",
    padding: "0.5rem",
    fontSize: "1rem",
    borderRadius: "4px",
    border: "1px solid #ccc"
  },
  tableContainer: {
    maxHeight: "400px",
    overflowY: "auto",
    border: "1px solid #ccc"
  },
  table: {
    width: "100%",
    borderCollapse: "collapse"
  },
  tableHeaderGroup: {
    position: "sticky",
    top: 0,
    background: "#e8f0fe",
    zIndex: 1
  },
  tableHeaderRow: {
    background: "#e8f0fe"
  },
  tableHeader: {
    padding: "0.7rem",
    border: "1px solid #c2c2c2",
    fontWeight: "bold",
    position: "sticky",
    top: 0,
    background: "#e8f0fe"
  },
  tableRow: {
    cursor: "pointer"
  },
  tableCell: {
    padding: "0.6rem",
    border: "1px solid #e0e0e0"
  },
  drawer: {
    position: "fixed",
    bottom: "20px",
    left: "20px",
    background: "#ffffff",
    padding: "1rem",
    borderRadius: "8px",
    boxShadow: "0 2px 8px rgba(0,0,0,0.15)",
    display: "flex",
    flexDirection: "row",
    gap: "0.5rem"
  },
  drawerButton: {
    display: "flex",
    alignItems: "center",
    gap: "0.3rem",
    padding: "0.5rem 1rem",
    fontSize: "1rem",
    color: "#3477eb",
    background: "#e8f0fe",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
    transition: "background 0.2s"
  },
  modalOverlay: {
    position: "fixed",
    top: 0,
    left: 0,
    width: "100vw",
    height: "100vh",
    background: "rgba(0, 0, 0, 0.5)",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    zIndex: 1000
  },
  modalContent: {
    background: "#fff",
    padding: "2rem",
    borderRadius: "8px",
    width: "90%",
    maxWidth: "400px",
    boxShadow: "0 2px 16px rgba(0,0,0,0.25)"
  },
  modalForm: {
    display: "flex",
    flexDirection: "column",
    gap: "1rem"
  },
  modalLabel: {
    display: "flex",
    flexDirection: "column",
    fontSize: "0.9rem",
    color: "#333"
  },
  modalInput: {
    padding: "0.5rem",
    fontSize: "1rem",
    borderRadius: "4px",
    border: "1px solid #ccc",
    marginTop: "0.3rem"
  },
  modalButtons: {
    display: "flex",
    justifyContent: "flex-end",
    gap: "1rem",
    marginTop: "1rem"
  },
  modalButton: {
    padding: "0.5rem 1rem",
    fontSize: "1rem",
    borderRadius: "4px",
    border: "none",
    cursor: "pointer",
    background: "#3477eb",
    color: "#fff",
    fontWeight: "bold",
    transition: "background 0.2s"
  }
};

export default ClientPage;
