import React, { useEffect, useRef, useState } from "react";

/*
Updated ClientPage with:
- double-click on photo thumbnail in the table to open a centered large preview overlay
- close preview by clicking overlay, clicking close button, or pressing Escape
- preserves previous behaviors (loading spinner, sticky header, resizable/toggleable columns)
*/

const translations = {
