      addresse: "ÿßŸÑÿπŸÜŸàÿßŸÜ",
      telephone: "ÿßŸÑŸáÿßÿ™ŸÅ",
      email: "ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä",
      photo: "ÿßŸÑÿµŸàÿ±ÿ© (jpg/png)"
    }
  }
};

// Field order mapping (matches translations table order, excluding the index "#")
const FIELDS = [
  "passport_number",
  "nom_et_prenom",
  "prenom_pere",
  "nom_prenom_mere",
  "date_de_naissance",
  "lieu_de_naissance",
  "date_delivrance",
  "date_expiration",
  "addresse",
  "telephone",
  "email",
  "photo"
];

function ClientPage({ locale = "fr" }) {
  const [clients, setClients] = useState([]);
  const [selectedIndex, setSelectedIndex] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");

  // modal states and form
  const [showModal, setShowModal] = useState(false);
  const [modalMode, setModalMode] = useState("add");
  const [clientForm, setClientForm] = useState({
    id: null,
    passport_number: "",
    nom_et_prenom: "",
    prenom_pere: "",
    nom_prenom_mere: "",
    date_de_naissance: "",
    lieu_de_naissance: "",
    date_delivrance: "",
    date_expiration: "",
    addresse: "",
    telephone: "",
    email: "",
    photo: ""
  });

  const [isDeleting, setIsDeleting] = useState(false);

  // loading & error
  const [isLoading, setIsLoading] = useState(false);
  const [loadError, setLoadError] = useState(null);

  // resizing refs / state
  const tableRef = useRef(null);
  const [colWidths, setColWidths] = useState([]); // px per column (includes index column)
  const [colCollapsed, setColCollapsed] = useState([]); // boolean per column: true => collapsed (fit header)
  const resizingRef = useRef({
    isResizing: false,
    colIndex: null,
    startX: 0,
    startWidth: 0
  });

  // big photo preview state (opened by double-click)
  const [bigPhoto, setBigPhoto] = useState(null);

  // --- Utility: measure text width using canvas for accurate sizing ---
  const measureTextWidth = (text, font = "14px Arial") => {
    if (!text) return 0;
    try {
      const canvas = measureTextWidth.canvas || (measureTextWidth.canvas = document.createElement("canvas"));
      const ctx = canvas.getContext("2d");
      ctx.font = font;
      const metrics = ctx.measureText(text);
      return metrics.width;
    } catch {
      return (text || "").length * 8;
    }
  };

  const headerFitWidth = (label) => {
    const textWidth = measureTextWidth(label, "600 14px 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif");
    const padding = 24;
    return Math.ceil(textWidth + padding);
  };

  const contentFitWidth = (field, rows) => {
    if (field === "photo") return 80;
    let max = 0;
    rows.forEach((r) => {
      const val = r[field] != null ? String(r[field]) : "";
      const w = measureTextWidth(val, "14px 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif");
      if (w > max) max = w;
    });
    const padding = 24;
    const minimum = 80;
    return Math.max(minimum, Math.ceil(max + padding));
  };

  // Fetch clients from backend
  const fetchClients = async () => {
    setIsLoading(true);
    setLoadError(null);
    try {
      const result = await window.pywebview.api.get_clients();
      setClients(result || []);
      requestAnimationFrame(() => initColumnWidths(result || []));
    } catch (err) {
      console.error(err);
      setLoadError(translations[locale].errorLoad);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchClients();
    return () => {
      removeMouseListeners();
      removeKeyListeners();
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [locale]);

  const initColumnWidths = (rows) => {
    const headers = translations[locale].tableHeaders;
    const widths = headers.map((h) => headerFitWidth(h));
    const collapsed = headers.map(() => true);
    if (widths.length > 0) {
      widths[0] = Math.max(48, widths[0]);
      collapsed[0] = true;
    }
    setColWidths(widths);
    setColCollapsed(collapsed);
  };

  // Resizing logic
  const addMouseListeners = () => {
    window.addEventListener("mousemove", onMouseMove);
    window.addEventListener("mouseup", onMouseUp);
  };
  const removeMouseListeners = () => {
    window.removeEventListener("mousemove", onMouseMove);
    window.removeEventListener("mouseup", onMouseUp);
  };

  const onMouseMove = (e) => {
    const state = resizingRef.current;
    if (!state.isResizing) return;
    const dx = e.clientX - state.startX;
    const newWidth = Math.max(40, state.startWidth + dx);
    setColWidths((prev) => {
      const next = [...prev];
      next[state.colIndex] = newWidth;
      return next;
    });
  };

  const onMouseUp = (e) => {
    const state = resizingRef.current;
    if (!state.isResizing) return;
    const dx = e.clientX - state.startX;
    const clickThreshold = 4;
    const colIndex = state.colIndex;
    state.isResizing = false;
    state.colIndex = null;
    removeMouseListeners();
    if (Math.abs(dx) < clickThreshold) {
      toggleColumnCollapse(colIndex);
    }
  };

  const startResize = (colIndex, e) => {
    e.preventDefault();
    resizingRef.current.isResizing = true;
    resizingRef.current.colIndex = colIndex;
    resizingRef.current.startX = e.clientX;
    resizingRef.current.startWidth = colWidths[colIndex] ?? 120;
    addMouseListeners();
  };

  const toggleColumnCollapse = (colIndex) => {
    const headers = translations[locale].tableHeaders;
    const newCollapsed = [...colCollapsed];
    newCollapsed[colIndex] = !newCollapsed[colIndex];
    const rows = clients;
    let newWidth = colWidths[colIndex] || headerFitWidth(headers[colIndex] || "");
    if (newCollapsed[colIndex]) {
      newWidth = headerFitWidth(headers[colIndex] || "");
    } else {
      if (colIndex === 0) {
        newWidth = 64;
      } else {
        const field = FIELDS[colIndex - 1];
        newWidth = contentFitWidth(field, rows);
        newWidth = Math.max(newWidth, headerFitWidth(headers[colIndex] || ""));
        const maxCap = Math.min(800, Math.max(300, newWidth));
        newWidth = Math.min(newWidth, maxCap);
      }
    }
    setColCollapsed(newCollapsed);
    setColWidths((prev) => {
      const next = [...prev];
      next[colIndex] = newWidth;
      return next;
    });
  };

  // --- Filtered clients ---
  const filteredClients = clients.filter((c) => {
    const q = searchQuery.trim().toLowerCase();
    if (!q) return true;
    return (
      (c.passport_number || "").toString().toLowerCase().includes(q) ||
      (c.nom_et_prenom || "").toLowerCase().includes(q) ||
      (c.telephone || "").toLowerCase().includes(q) ||
      (c.email || "").toLowerCase().includes(q)
    );
  });

  // RTL support
  const isRtl = locale === "ar";
  const directionStyle = isRtl ? { direction: "rtl" } : { direction: "ltr" };

  // CRUD handlers
  const handleAjouter = () => {
    setModalMode("add");
    setClientForm({
      id: null,
      passport_number: "",
      nom_et_prenom: "",
      prenom_pere: "",
      nom_prenom_mere: "",
      date_de_naissance: "",
      lieu_de_naissance: "",
      date_delivrance: "",
      date_expiration: "",
      addresse: "",
      telephone: "",
      email: "",
      photo: ""
    });
    setShowModal(true);
  };

  const handleModifier = () => {
    if (selectedIndex !== null && filteredClients[selectedIndex]) {
      setModalMode("edit");
      const client = filteredClients[selectedIndex];
      setClientForm({ ...client });
      setShowModal(true);
    } else {
      alert(translations[locale].selectRowAlert);
    }
  };

  const handleSupprimer = async () => {
    if (selectedIndex === null || !filteredClients[selectedIndex]) {
      alert(translations[locale].selectRowAlert);
      return;
    }
    if (isDeleting) return;
    const clientId = filteredClients[selectedIndex].id;
    const clientName = filteredClients[selectedIndex].nom_et_prenom || filteredClients[selectedIndex].passport_number || "";
    const confirmDelete = window.confirm(translations[locale].confirmDelete(clientName));
    if (!confirmDelete) return;
    try {
      setIsDeleting(true);
      await window.pywebview.api.delete_client(clientId);
      setSelectedIndex(null);
      fetchClients();
    } catch (err) {
      console.error(err);
      alert(translations[locale].errorDelete);
    } finally {
      setIsDeleting(false);
    }
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setClientForm({
      id: null,
      passport_number: "",
      nom_et_prenom: "",
      prenom_pere: "",
      nom_prenom_mere: "",
      date_de_naissance: "",
      lieu_de_naissance: "",
      date_delivrance: "",
      date_expiration: "",
      addresse: "",
      telephone: "",
      email: "",
      photo: ""
    });
  };

  const handleSaveClient = async (e) => {
    e.preventDefault();
    try {
      const payload = { ...clientForm };
      if (modalMode === "add") {
        await window.pywebview.api.add_client(payload);
      } else {
        await window.pywebview.api.update_client(payload);
      }
      handleCloseModal();
      fetchClients();
    } catch (err) {
      console.error(err);
      alert(translations[locale].errorSave);
    }
  };

  const handlePhotoChange = (file) => {
    if (!file) {
      setClientForm((s) => ({ ...s, photo: "" }));
      return;
    }
    const reader = new FileReader();
    reader.onload = () => {
      setClientForm((s) => ({ ...s, photo: reader.result }));
    };
    reader.readAsDataURL(file);
  };

  const photoSrc = (val) => {
    if (!val) return null;
    if (typeof val !== "string") return null;
    if (val.startsWith("data:")) return val;
    return `data:image/png;base64,${val}`;
  };

  // big photo preview handlers
  const openBigPhoto = (val) => {
    const src = photoSrc(val);
    if (src) {
      setBigPhoto(src);
    }
  };
  const closeBigPhoto = () => setBigPhoto(null);

  // close big photo on Escape
  useEffect(() => {
    const onKey = (e) => {
      if (e.key === "Escape") closeBigPhoto();
    };
    if (bigPhoto) {
      window.addEventListener("keydown", onKey);
    }
    return () => window.removeEventListener("keydown", onKey);
  }, [bigPhoto]);

  // helper for column style
  const getColStyle = (colIndex) => {
    if (colWidths && colWidths[colIndex] != null) {
      return {
        width: `${colWidths[colIndex]}px`,
        minWidth: `${Math.max(40, colWidths[colIndex])}px`,
        boxSizing: "border-box"
      };
    }
    return {};
  };

  // table container style (scrollable)
  const tableContainerStyle = {
    maxHeight: "64vh",
    overflow: "auto",
    border: "1px solid #e0e0e0",
    borderRadius: 6
  };

  // Render
  const headers = translations[locale].tableHeaders;
  const fieldsOrder = isRtl ? [...FIELDS].reverse() : [...FIELDS];

  return (
    <div style={{ ...styles.pageContainer, ...directionStyle }}>
      <h2 style={{ textAlign: "center" }}>{translations[locale].title}</h2>

      {/* Search and controls */}
      <div style={styles.searchContainer}>
        <span style={styles.searchIcon}>üîç</span>
        <input
          type="text"
          placeholder={translations[locale].searchPlaceholder}
          value={searchQuery}
          onChange={(e) => {
            setSearchQuery(e.target.value);
            setSelectedIndex(null);
          }}
          style={styles.searchInput}
        />
      </div>

      {/* Loading spinner overlay */}
      {isLoading && (
        <div style={styles.loadingOverlay}>
          <div style={styles.spinner} aria-hidden />
        </div>
      )}

      {/* Error message (only shown when load failed) */}
      {loadError && !isLoading && (
        <div style={styles.errorBox}>{loadError}</div>
      )}

      {/* Scrollable table with sticky header */}
      <div style={tableContainerStyle} ref={tableRef}>
        <table style={styles.table}>
          <thead style={{ position: "sticky", top: 0, zIndex: 5 }}>
            <tr style={styles.tableHeaderRow}>
              {headers.map((h, i) => (
                <th
                  key={i}
                  style={{
                    ...styles.tableHeader,
                    ...getColStyle(i),
                    position: "relative",
                    background: "#f6f9ff"
                  }}
                >
                  <div style={{ display: "flex", alignItems: "center", justifyContent: isRtl ? "flex-end" : "flex-start" }}>
                    <span style={{ overflow: "hidden", textOverflow: "ellipsis", whiteSpace: "nowrap" }}>{h}</span>
                    {i < headers.length - 1 && (
                      <div
                        onMouseDown={(e) => startResize(i, e)}
                        style={styles.resizer}
                        title="Cliquer pour r√©duire/√©tendre; glisser pour redimensionner"
                      />
                    )}
                  </div>
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {filteredClients.map((client, idx) => {
              const cells = fieldsOrder.map((field, ci) => {
                const colIndex = 1 + ci;
                if (field === "photo") {
                  const src = photoSrc(client[field]);
                  return (
                    <td key={`${client.id}-${field}`} style={{ ...styles.tableCell, ...getColStyle(colIndex) }}>
                      {src ? (
                        <img
                          src={src}
                          alt="photo"
                          style={{ width: 48, height: 48, objectFit: "cover", borderRadius: 4, cursor: "zoom-in" }}
                          onDoubleClick={() => openBigPhoto(client[field])}
                        />
                      ) : (
                        "-"
                      )}
                    </td>
                  );
                }
                return <td key={`${client.id}-${field}`} style={{ ...styles.tableCell, ...getColStyle(colIndex) }}>{client[field] ?? "-"}</td>;
              });

              return (
                <tr
                  key={client.id || (client.passport_number + idx)}
                  style={{
                    ...styles.tableRow,
                    background: idx === selectedIndex ? "#e6f3ff" : idx % 2 === 0 ? "#fff" : "#fbfdff"
                  }}
                  onClick={() => setSelectedIndex(idx)}
                >
                  <td style={{ ...styles.tableCell, ...getColStyle(0) }}>{idx + 1}</td>
                  {cells}
                </tr>
              );
            })}

            {filteredClients.length === 0 && !isLoading && (
              <tr>
                <td colSpan={headers.length} style={{ padding: "1rem", textAlign: "center", color: "#666" }}>
                  {clients.length === 0 ? "Aucune donn√©e." : "Aucun r√©sultat pour la recherche."}
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>

      {/* Drawer buttons */}
      <div style={styles.drawer}>
        <button style={styles.drawerButton} onClick={handleAjouter}>{translations[locale].addButton}</button>
        <button style={styles.drawerButton} onClick={handleModifier}>{translations[locale].editButton}</button>
        <button style={styles.drawerButton} onClick={handleSupprimer}>{translations[locale].deleteButton}</button>
      </div>

      {/* Modal - unchanged layout (two inputs per row) */}
      {showModal && (
        <div style={styles.modalOverlay}>
          <div style={{ ...styles.modalContent, ...(isRtl ? { direction: "rtl", textAlign: "right" } : {}) }}>
            <h3>{modalMode === "add" ? translations[locale].addClient : translations[locale].editClient}</h3>
            <form onSubmit={handleSaveClient} style={styles.modalForm}>
              <div style={styles.fieldRow}>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.passport_number}:
                    <input type="text" value={clientForm.passport_number || ""} onChange={(e) => setClientForm({ ...clientForm, passport_number: e.target.value })} style={styles.modalInput} required />
                  </label>
                </div>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.nom_et_prenom}:
                    <input type="text" value={clientForm.nom_et_prenom || ""} onChange={(e) => setClientForm({ ...clientForm, nom_et_prenom: e.target.value })} style={styles.modalInput} required />
                  </label>
                </div>
              </div>

              <div style={styles.fieldRow}>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.prenom_pere}:
                    <input type="text" value={clientForm.prenom_pere || ""} onChange={(e) => setClientForm({ ...clientForm, prenom_pere: e.target.value })} style={styles.modalInput} />
                  </label>
                </div>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.nom_prenom_mere}:
                    <input type="text" value={clientForm.nom_prenom_mere || ""} onChange={(e) => setClientForm({ ...clientForm, nom_prenom_mere: e.target.value })} style={styles.modalInput} />
                  </label>
                </div>
              </div>

              <div style={styles.fieldRow}>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.date_de_naissance}:
                    <input type="date" value={clientForm.date_de_naissance || ""} onChange={(e) => setClientForm({ ...clientForm, date_de_naissance: e.target.value })} style={styles.modalInput} />
                  </label>
                </div>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.lieu_de_naissance}:
                    <input type="text" value={clientForm.lieu_de_naissance || ""} onChange={(e) => setClientForm({ ...clientForm, lieu_de_naissance: e.target.value })} style={styles.modalInput} />
                  </label>
                </div>
              </div>

              <div style={styles.fieldRow}>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.date_delivrance}:
                    <input type="date" value={clientForm.date_delivrance || ""} onChange={(e) => setClientForm({ ...clientForm, date_delivrance: e.target.value })} style={styles.modalInput} />
                  </label>
                </div>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.date_expiration}:
                    <input type="date" value={clientForm.date_expiration || ""} onChange={(e) => setClientForm({ ...clientForm, date_expiration: e.target.value })} style={styles.modalInput} />
                  </label>
                </div>
              </div>

              <div style={styles.fieldRow}>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.addresse}:
                    <input type="text" value={clientForm.addresse || ""} onChange={(e) => setClientForm({ ...clientForm, addresse: e.target.value })} style={styles.modalInput} />
                  </label>
                </div>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.telephone}:
                    <input type="text" value={clientForm.telephone || ""} onChange={(e) => setClientForm({ ...clientForm, telephone: e.target.value })} style={styles.modalInput} />
                  </label>
                </div>
              </div>

              <div style={styles.fieldRow}>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.email}:
                    <input type="email" value={clientForm.email || ""} onChange={(e) => setClientForm({ ...clientForm, email: e.target.value })} style={styles.modalInput} />
                  </label>
                </div>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.photo}:
                    <input type="file" accept="image/*" onChange={(e) => handlePhotoChange(e.target.files[0])} style={{ marginTop: 6 }} />
                    {clientForm.photo ? <div style={{ marginTop: 8 }}><img src={photoSrc(clientForm.photo)} alt="preview" style={{ width: 100, height: 80, objectFit: "cover", borderRadius: 6 }} /></div> : null}
                  </label>
                </div>
              </div>

              <div style={styles.modalButtons}>
                <button type="submit" style={styles.modalButton}>{translations[locale].save}</button>
                <button type="button" style={styles.modalButton} onClick={handleCloseModal}>{translations[locale].cancel}</button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Big photo overlay (centered) opened by double-clicking a table thumbnail */}
      {bigPhoto && (
        <div style={styles.bigPhotoOverlay} onClick={closeBigPhoto}>
          <div style={styles.bigPhotoContainer} onClick={(e) => e.stopPropagation()}>
            <button style={styles.bigPhotoClose} onClick={closeBigPhoto} aria-label="Close">‚úï</button>
            <img src={bigPhoto} alt="preview large" style={styles.bigPhotoImage} />
          </div>
        </div>
      )}
    </div>
  );
}

