import React, { useState } from "react";
import ClientPage from "./client";
import GroupPage from "./group";

// Sun/Moon SVG icon component with animation
function NightModeToggle({ nightMode, setNightMode }) {
  return (
    <div
      style={{
        position: "relative",
        width: 48,
        height: 28,
        display: "inline-block",
        margin: "0 0.5rem",
        verticalAlign: "middle",
        cursor: "pointer",
        userSelect: "none"
      }}
      onClick={() => setNightMode(!nightMode)}
      title={nightMode ? "Mode jour" : "Mode nuit"}
    >
      <div
        style={{
          position: "absolute",
          top: 6,
          left: 7,
          width: 34,
          height: 16,
          borderRadius: 12,
          background: nightMode
            ? "linear-gradient(90deg,#1a253b 60%,#2d3e5e 100%)"
            : "linear-gradient(90deg,#ffe57f 60%,#ffc400 100%)",
          transition: "background 0.4s"
        }}
      ></div>
      {/* Sun/Moon */}
      <div
        style={{
          position: "absolute",
          top: nightMode ? 9 : 4,
          left: nightMode ? 26 : 8,
          width: 17,
          height: 17,
          borderRadius: "50%",
          background: nightMode
            ? "radial-gradient(circle at 60% 40%,#e3e3e3 70%,#b6b6c6 100%)"
            : "radial-gradient(circle at 60% 40%,#fffde7 70%,#ffd600 100%)",
          boxShadow: nightMode
            ? "0 0 14px 4px #eaeef6a2"
            : "0 0 20px 5px #fffde7",
          border: nightMode
            ? "1.5px solid #d8d8e8"
            : "1.5px solid #ffe57f",
          zIndex: 2,
          transition:
            "left 0.42s cubic-bezier(.7,-0.25,.5,1.25), top 0.42s, background 0.35s, box-shadow 0.35s"
        }}
      >
        {/* Craters on moon */}
        {nightMode && (
          <>
            <div
              style={{
                position: "absolute",
                width: 3,
                height: 3,
                borderRadius: "50%",
                background: "#bfc0d6",
                opacity: 0.5,
                left: 6,
                top: 4
              }}
            />
            <div
              style={{
                position: "absolute",
                width: 2,
                height: 2,
                borderRadius: "50%",
                background: "#bfc0d6",
                opacity: 0.28,
                left: 11,
                top: 9
              }}
            />
          </>
        )}
        {/* Sun rays */}
        {!nightMode &&
          Array.from({ length: 6 }).map((_, i) => (
            <div
              key={i}
              style={{
                position: "absolute",
                left: 7,
                top: 7,
                width: 2,
                height: 7,
                background: "#fffde7aa",
                borderRadius: "1.5px",
                transform: `rotate(${i * 60}deg) translateY(-9px)`,
                opacity: 0.6
              }}
            />
          ))}
      </div>
    </div>
  );
}

// Animated electronic background SVG for login
function ElectronicBG({ nightMode }) {
  return (
    <svg
      width="600"
      height="350"
      viewBox="0 0 600 350"
      style={{
        position: "absolute",
        left: "50%",
        top: "50%",
        transform: "translate(-50%,-48%) scale(1.1)",
        zIndex: 1,
        pointerEvents: "none",
        opacity: nightMode ? 0.22 : 0.17,
        filter: nightMode
          ? "drop-shadow(0 4px 44px #7cdfef77) drop-shadow(0 0px 55px #eb3434bb)"
          : "drop-shadow(0 2px 42px #fff9c977)"
      }}
    >
      {/* Animated circuit lines */}
      <g>
        <AnimatedLine x1={70} y1={70} x2={540} y2={70} delay={0.1} nightMode={nightMode} />
        <AnimatedLine x1={70} y1={150} x2={540} y2={150} delay={0.2} nightMode={nightMode} />
        <AnimatedLine x1={70} y1={230} x2={540} y2={230} delay={0.32} nightMode={nightMode} />
        <AnimatedLine x1={160} y1={70} x2={160} y2={230} delay={0.18} nightMode={nightMode} />
        <AnimatedLine x1={330} y1={70} x2={330} y2={230} delay={0.26} nightMode={nightMode} />
        {/* Moving dots */}
        <MovingDot path="M70,70 L540,70" duration={3.5} delay={0.1} nightMode={nightMode} />
        <MovingDot path="M70,150 L540,150" duration={3.8} delay={0.2} nightMode={nightMode} />
        <MovingDot path="M70,230 L540,230" duration={4.2} delay={0.32} nightMode={nightMode} />
        <MovingDot path="M160,70 L160,230" duration={3.9} delay={0.18} nightMode={nightMode} />
        <MovingDot path="M330,70 L330,230" duration={4.1} delay={0.26} nightMode={nightMode} />
      </g>
      {/* Glowing nodes */}
      <glowNode x={70} y={70} nightMode={nightMode} />
      <glowNode x={160} y={70} nightMode={nightMode} />
      <glowNode x={330} y={70} nightMode={nightMode} />
      <glowNode x={540} y={70} nightMode={nightMode} />
      <glowNode x={70} y={150} nightMode={nightMode} />
      <glowNode x={160} y={150} nightMode={nightMode} />
      <glowNode x={330} y={150} nightMode={nightMode} />
      <glowNode x={540} y={150} nightMode={nightMode} />
      <glowNode x={70} y={230} nightMode={nightMode} />
      <glowNode x={160} y={230} nightMode={nightMode} />
      <glowNode x={330} y={230} nightMode={nightMode} />
      <glowNode x={540} y={230} nightMode={nightMode} />
    </svg>
  );
}

// Helper animated line (simulated with strokeDasharray animation)
function AnimatedLine({ x1, y1, x2, y2, delay, nightMode }) {
  const color = nightMode ? "#7cdfef" : "#f7d16e";
  return (
    <line
      x1={x1}
      y1={y1}
      x2={x2}
      y2={y2}
      stroke={color}
      strokeWidth="2"
      strokeDasharray="6 18"
      style={{
        animation: `circuit-move 2.2s ${delay}s linear infinite`
      }}
    />
  );
}
// Helper for glowing node
function glowNode({ x, y, nightMode }) {
  return (
    <circle
      cx={x}
      cy={y}
      r="7.5"
      fill={nightMode ? "#eb3434" : "#ffe57f"}
      filter={
        nightMode
          ? "drop-shadow(0 0 18px #eb3434cc) drop-shadow(0 0 22px #7cdfefcc)"
          : "drop-shadow(0 0 13px #ffe57fcc) drop-shadow(0 0 28px #fffbe7cc)"
      }
      opacity={0.86}
    />
  );
}
// Helper for animated moving dots
function MovingDot({ path, duration, delay, nightMode }) {
  // Animate dot along path using SMIL (supported in Electron/Chromium/webview)
  return (
    <circle
      r="5"
      fill={nightMode ? "#7cdfef" : "#eb3434"}
      opacity="0.95"
    >
      <animateMotion
        dur={`${duration}s`}
        repeatCount="indefinite"
        keyPoints="0;1"
        keyTimes="0;1"
        begin={`${delay}s`}
      >
        <mpath xlinkHref={`#${path.replace(/[ ,]/g, "")}`} />
      </animateMotion>
      <animate
        attributeName="opacity"
        values="0.2;1;0.2"
        dur={`${duration * 0.8}s`}
        repeatCount="indefinite"
        begin={`${delay + 0.2}s`}
      />
      {/* Hidden path for motion */}
      <path
        id={path.replace(/[ ,]/g, "")}
        d={path}
        fill="none"
        stroke="none"
      />
    </circle>
  );
}

// Main translations
const translations = {
  fr: {
    // ... as before ...
    loginTitle: "Connexion Agence de Voyage",
    usernamePlaceholder: "Nom d'utilisateur",
    passwordPlaceholder: "Mot de passe",
    loginButton: "Se connecter",
    dashboardTitle: "Tableau de bord",
    retourDashboard: "â† Retour au dashboard",
    // Updated pages:
    pages: [
      { label: "Clients", page: "client" },
      { label: "Groupes", page: "groupe" },
      { label: "Caisse", page: "caisse" },
      { label: "Personnel", page: "personel" },
      { label: "Vols & HÃ´tels", page: "vols_hotels" },
      { label: "ParamÃ¨tres", page: "parametres" }
    ],
    loginError: "Nom d'utilisateur ou mot de passe incorrect.",
    backendError: "Erreur de connexion au backend.",
    nightMode: "Mode nuit"
  },
  ar: {
    // ... as before ...
    loginTitle: "ØªØ³Ø¬ÙŠÙ„ Ø¯Ø®ÙˆÙ„ ÙˆÙƒØ§Ù„Ø© Ø§Ù„Ø³ÙØ±",
    usernamePlaceholder: "Ø§Ø³Ù… Ø§Ù„Ù…Ø³ØªØ®Ø¯Ù…",
    passwordPlaceholder: "ÙƒÙ„Ù…Ø© Ø§Ù„Ù…Ø±ÙˆØ±",
    loginButton: "ØªØ³Ø¬ÙŠÙ„ Ø§Ù„Ø¯Ø®ÙˆÙ„",
    dashboardTitle: "Ù„ÙˆØ­Ø© Ø§Ù„Ù…Ø¹Ù„ÙˆÙ…Ø§Øª",
    retourDashboard: "â† Ø§Ù„Ø¹ÙˆØ¯Ø© Ù„Ù„ÙˆØ­Ø©",
    // Updated pages (Arabic translations; adjust text as needed)
    pages: [
      { label: "Ø§Ù„Ø¹Ù…Ù„Ø§Ø¡", page: "client" },
      { label: "Ø§Ù„Ù…Ø¬Ù…ÙˆØ¹Ø§Øª", page: "groupe" },
