// Field order mapping (no n_passport and no email)
const FIELDS = [
  "passport_number",
  "nom_et_prenom",
  "prenom_pere",
  "nom_prenom_mere",
  "date_de_naissance",
  "lieu_de_naissance",
  "date_delivrance",
  "date_expiration",
  "addresse",
  "telephone",
  "photo"
];

function ClientPage({ locale = "fr" }) {
  const [clients, setClients] = useState([]);
  const [selectedIndex, setSelectedIndex] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [modalMode, setModalMode] = useState("add");
  const [clientForm, setClientForm] = useState({
    id: null,
    passport_number: "",
    nom_et_prenom: "",
    prenom_pere: "",
    nom_prenom_mere: "",
    date_de_naissance: "",
    lieu_de_naissance: "",
    date_delivrance: "",
    date_expiration: "",
    addresse: "",
    telephone: "",
    photo: ""
  });
  const [isDeleting, setIsDeleting] = useState(false);

  // Compute displayed headers and fields order for RTL/LTR
  const isRtl = locale === "ar";
  const displayedHeaders = isRtl
    ? [...translations[locale].tableHeaders].slice().reverse()
    : translations[locale].tableHeaders;
  const fieldsOrder = isRtl
    ? [...FIELDS].slice().reverse()
    : [...FIELDS];
  const directionStyle = isRtl ? { direction: "rtl" } : { direction: "ltr" };

  const fetchClients = async () => {
    try {
      const result = await window.pywebview.api.get_clients();
      setClients(result || []);
    } catch (err) {
      console.error(err);
      alert(translations[locale].errorLoad);
    }
  };

  useEffect(() => {
    fetchClients();
  }, [locale]);

  const filteredClients = clients.filter((c) => {
    const q = searchQuery.trim().toLowerCase();
    if (!q) return true;
    return (
      (c.passport_number || "").toString().toLowerCase().includes(q) ||
      (c.nom_et_prenom || "").toLowerCase().includes(q) ||
      (c.telephone || "").toLowerCase().includes(q)
    );
  });

  const handleAjouter = () => {
    setModalMode("add");
    setClientForm({
      id: null,
      passport_number: "",
      nom_et_prenom: "",
      prenom_pere: "",
      nom_prenom_mere: "",
      date_de_naissance: "",
      lieu_de_naissance: "",
      date_delivrance: "",
      date_expiration: "",
      addresse: "",
      telephone: "",
      photo: ""
    });
    setShowModal(true);
  };

  const handleModifier = () => {
    if (selectedIndex !== null && filteredClients[selectedIndex]) {
      setModalMode("edit");
      const client = filteredClients[selectedIndex];
      setClientForm({ ...client });
      setShowModal(true);
    } else {
      alert(translations[locale].selectRowAlert);
    }
  };

  const handleSupprimer = async () => {
    if (selectedIndex === null || !filteredClients[selectedIndex]) {
      alert(translations[locale].selectRowAlert);
      return;
    }
    if (isDeleting) return;
    const clientId = filteredClients[selectedIndex].id;
    const clientName = filteredClients[selectedIndex].nom_et_prenom || filteredClients[selectedIndex].passport_number || "";
    const confirmDelete = window.confirm(translations[locale].confirmDelete(clientName));
    if (!confirmDelete) return;
    try {
      setIsDeleting(true);
      await window.pywebview.api.delete_client(clientId);
      setSelectedIndex(null);
      fetchClients();
    } catch (err) {
      console.error(err);
      alert(translations[locale].errorDelete);
    } finally {
      setIsDeleting(false);
    }
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setClientForm({
      id: null,
      passport_number: "",
      nom_et_prenom: "",
      prenom_pere: "",
      nom_prenom_mere: "",
      date_de_naissance: "",
      lieu_de_naissance: "",
      date_delivrance: "",
      date_expiration: "",
      addresse: "",
      telephone: "",
      photo: ""
    });
  };

  const handleSaveClient = async (e) => {
    e.preventDefault();
    try {
      const payload = { ...clientForm };
      if (modalMode === "add") {
        await window.pywebview.api.add_client(payload);
      } else {
        await window.pywebview.api.update_client(payload);
      }
      handleCloseModal();
      fetchClients();
    } catch (err) {
      console.error(err);
      alert(translations[locale].errorSave);
    }
  };

  const handlePhotoChange = (file) => {
    if (!file) {
      setClientForm((s) => ({ ...s, photo: "" }));
      return;
    }
    const reader = new FileReader();
    reader.onload = () => {
      setClientForm((s) => ({ ...s, photo: reader.result }));
    };
    reader.readAsDataURL(file);
  };

  const photoSrc = (val) => {
    if (!val) return null;
    if (typeof val !== "string") return null;
    if (val.startsWith("data:")) return val;
    return `data:image/png;base64,${val}`;
  };

  const tableContainerStyle = {
    maxHeight: "60vh",
    overflowY: "auto",
    border: "1px solid #e0e0e0",
    borderRadius: "6px"
  };

  return (
    <div style={{ ...styles.pageContainer, ...directionStyle }}>
      <h2 style={{ textAlign: "center" }}>{translations[locale].title}</h2>
      {/* Search Bar */}
      <div style={styles.searchContainer}>
        <span style={styles.searchIcon}>üîç</span>
        <input
          type="text"
          placeholder={translations[locale].searchPlaceholder}
          value={searchQuery}
          onChange={(e) => {
            setSearchQuery(e.target.value);
            setSelectedIndex(null);
          }}
          style={styles.searchInput}
        />
      </div>
      {/* Scrollable Table */}
      <div style={tableContainerStyle}>
        <table style={styles.table}>
          <thead>
            <tr style={styles.tableHeaderRow}>
              {displayedHeaders.map((h, i) => (
                <th key={i} style={styles.tableHeader}>
                  {h}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {filteredClients.map((client, idx) => {
              // Build cells in the same order as fieldsOrder
              const cells = fieldsOrder.map((field, colIdx) => {
                if (field === "photo") {
                  const src = photoSrc(client[field]);
                  return (
                    <td key={`${client.id}-${field}`} style={styles.tableCell}>
                      {src ? (
                        <img
                          src={src}
                          alt="photo"
                          style={{ width: 48, height: 48, objectFit: "cover", borderRadius: 4 }}
                        />
                      ) : (
                        "-"
                      )}
                    </td>
                  );
                }
                return <td key={`${client.id}-${field}`} style={styles.tableCell}>{client[field] ?? "-"}</td>;
              });

              return (
                <tr
                  key={client.id || (client.passport_number + idx)}
                  style={{
                    ...styles.tableRow,
                    background:
                      idx === selectedIndex
                        ? "#cce5ff"
                        : idx % 2 === 0
                        ? "#fff"
                        : "#f4f8ff"
                  }}
                  onClick={() => setSelectedIndex(idx)}
                >
                  <td style={styles.tableCell}>{idx + 1}</td>
                  {cells}
                </tr>
              );
            })}
            {filteredClients.length === 0 && (
              <tr>
                <td colSpan={displayedHeaders.length} style={{ padding: "1rem", textAlign: "center", color: "#666" }}>
                  {translations[locale].errorLoad}
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
      {/* Drawer for CRUD buttons */}
      <div style={styles.drawer}>
        <button style={styles.drawerButton} onClick={handleAjouter}>
          {translations[locale].addButton}
        </button>
        <button style={styles.drawerButton} onClick={handleModifier}>
          {translations[locale].editButton}
        </button>
        <button style={styles.drawerButton} onClick={handleSupprimer}>
          {translations[locale].deleteButton}
        </button>
      </div>
      {/* Modal for Adding/Editing a Client */}
      {showModal && (
        <div style={styles.modalOverlay}>
          <div style={{ ...styles.modalContent, ...(isRtl ? { direction: "rtl", textAlign: "right" } : {}) }}>
            <h3>
              {modalMode === "add"
                ? translations[locale].addClient
                : translations[locale].editClient}
            </h3>
            <form onSubmit={handleSaveClient} style={styles.modalForm}>
              {/* Two inputs per line rows */}
              <div style={styles.fieldRow}>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.passport_number}:
                    <input
                      type="text"
                      value={clientForm.passport_number || ""}
                      onChange={(e) => setClientForm({ ...clientForm, passport_number: e.target.value })}
                      style={styles.modalInput}
                      required
                    />
                  </label>
                </div>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.nom_et_prenom}:
                    <input
                      type="text"
                      value={clientForm.nom_et_prenom || ""}
                      onChange={(e) => setClientForm({ ...clientForm, nom_et_prenom: e.target.value })}
                      style={styles.modalInput}
                      required
                    />
                  </label>
                </div>
              </div>

              <div style={styles.fieldRow}>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.prenom_pere}:
                    <input
                      type="text"
                      value={clientForm.prenom_pere || ""}
                      onChange={(e) => setClientForm({ ...clientForm, prenom_pere: e.target.value })}
                      style={styles.modalInput}
                    />
                  </label>
                </div>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.nom_prenom_mere}:
                    <input
                      type="text"
                      value={clientForm.nom_prenom_mere || ""}
                      onChange={(e) => setClientForm({ ...clientForm, nom_prenom_mere: e.target.value })}
                      style={styles.modalInput}
                    />
                  </label>
                </div>
              </div>

              <div style={styles.fieldRow}>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.date_de_naissance}:
                    <input
                      type="date"
                      value={clientForm.date_de_naissance || ""}
                      onChange={(e) => setClientForm({ ...clientForm, date_de_naissance: e.target.value })}
                      style={styles.modalInput}
                    />
                  </label>
                </div>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.lieu_de_naissance}:
                    <input
                      type="text"
                      value={clientForm.lieu_de_naissance || ""}
                      onChange={(e) => setClientForm({ ...clientForm, lieu_de_naissance: e.target.value })}
                      style={styles.modalInput}
                    />
                  </label>
                </div>
              </div>

              <div style={styles.fieldRow}>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.date_delivrance}:
                    <input
                      type="date"
                      value={clientForm.date_delivrance || ""}
                      onChange={(e) => setClientForm({ ...clientForm, date_delivrance: e.target.value })}
                      style={styles.modalInput}
                    />
                  </label>
                </div>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.date_expiration}:
                    <input
                      type="date"
                      value={clientForm.date_expiration || ""}
                      onChange={(e) => setClientForm({ ...clientForm, date_expiration: e.target.value })}
                      style={styles.modalInput}
                    />
                  </label>
                </div>
              </div>

              <div style={styles.fieldRow}>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.addresse}:
                    <input
                      type="text"
                      value={clientForm.addresse || ""}
                      onChange={(e) => setClientForm({ ...clientForm, addresse: e.target.value })}
                      style={styles.modalInput}
                    />
                  </label>
                </div>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.telephone}:
                    <input
                      type="text"
                      value={clientForm.telephone || ""}
                      onChange={(e) => setClientForm({ ...clientForm, telephone: e.target.value })}
                      style={styles.modalInput}
                    />
                  </label>
                </div>
              </div>

              <div style={styles.fieldRow}>
                <div style={{ ...styles.fieldItem, flex: 1 }}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.photo}:
                    <input
                      type="file"
                      accept="image/*"
                      onChange={(e) => handlePhotoChange(e.target.files[0])}
                      style={{ marginTop: 6 }}
                    />
                    {clientForm.photo ? (
                      <div style={{ marginTop: 8 }}>
                        <img
                          src={photoSrc(clientForm.photo)}
                          alt="preview"
                          style={{ width: 100, height: 80, objectFit: "cover", borderRadius: 6 }}
                        />
                      </div>
                    ) : null}
                  </label>
                </div>
                <div style={{ width: 1 }} />
              </div>

              <div style={styles.modalButtons}>
                <button type="submit" style={styles.modalButton}>
                  {translations[locale].save}
                </button>
                <button type="button" style={styles.modalButton} onClick={handleCloseModal}>
                  {translations[locale].cancel}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

const styles = {
  pageContainer: {
    padding: "1.25rem",
    position: "relative",
    minHeight: "100vh",
    boxSizing: "border-box"
  },
  searchContainer: {
    marginBottom: "1rem",
    display: "flex",
    alignItems: "center",
    maxWidth: "600px"
  },
  searchIcon: {
    fontSize: "1.2rem",
    marginRight: "0.5rem"
  },
  searchInput: {
    flex: 1,
    padding: "0.5rem",
    fontSize: "1rem",
    borderRadius: "4px",
    border: "1px solid #ccc"
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
    marginTop: "0",
    tableLayout: "fixed"
  },
  tableHeaderRow: {
    background: "#e8f0fe"
  },
  tableHeader: {
    padding: "0.7rem",
    border: "1px solid #c2c2c2",
    fontWeight: "bold"
  },
  tableRow: {
    cursor: "pointer"
  },
  tableCell: {
    padding: "0.6rem",
    border: "1px solid #e0e0e0"
  },
  drawer: {
    position: "fixed",
    bottom: "20px",
    left: "20px",
    background: "#ffffff",
    padding: "1rem",
    borderRadius: "8px",
    boxShadow: "0 2px 8px rgba(0,0,0,0.15)",
    display: "flex",
    flexDirection: "row",
    gap: "0.5rem"
  },
  drawerButton: {
    display: "flex",
    alignItems: "center",
    gap: "0.3rem",
    padding: "0.5rem 1rem",
    fontSize: "1rem",
    color: "#3477eb",
    background: "#e8f0fe",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
    transition: "background 0.2s"
  },
  modalOverlay: {
    position: "fixed",
    top: 0,
    left: 0,
    width: "100vw",
    height: "100vh",
    background: "rgba(0, 0, 0, 0.5)",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    zIndex: 1000
  },
  modalContent: {
    background: "#fff",
    padding: "2rem",
    borderRadius: "8px",
    width: "90%",
    maxWidth: "400px",
    boxShadow: "0 2px 16px rgba(0,0,0,0.25)"
  },
  modalForm: {
    display: "flex",
    flexDirection: "column",
    gap: "1rem"
  },
  modalLabel: {
    display: "flex",
    flexDirection: "column",
    fontSize: "0.9rem",
    color: "#333"
  },
  modalInput: {
    padding: "0.5rem",
    fontSize: "1rem",
    borderRadius: "4px",
    border: "1px solid #ccc",
    marginTop: "0.3rem"
  },
  modalButtons: {
    display: "flex",
    justifyContent: "flex-end",
    gap: "1rem",
    marginTop: "1rem"
  },
  modalButton: {
    padding: "0.5rem 1rem",
    fontSize: "1rem",
    borderRadius: "4px",
    border: "none",
    cursor: "pointer",
    background: "#3477eb",
    color: "#fff",
    fontWeight: "bold",
    transition: "background 0.2s"
  },
  fieldRow: {
    display: "flex",
    gap: "0.75rem",
    alignItems: "flex-start"
  },
  fieldItem: {
    flex: 1,
    minWidth: 0
  }
};

export default ClientPage;
