import React, { useEffect, useState } from "react";

/*
  vols.js
  - Vols (Flights) page component.
  - Features:
    * Table listing flights with columns: Vol #, Date départ, Origine, Destination, Places, Prix, Statut
    * Search bar, add / edit / delete buttons, print
    * Add/Edit modal with validation
    * Loads/saves via window.pywebview.api functions if available:
        - load_vols() | loadVols() | get_vols()
        - add_vol(payload) | addVol(payload)
        - update_vol(id, payload) | updateVol(id, payload)
        - delete_vol(id) | deleteVol(id)
      If backend functions are missing, a local fallback is used so UI remains usable.
    * Locale aware (fr / ar) for labels and RTL support.
*/

function formatNumber(v) {
  if (v === null || v === undefined || v === "") return "";
  return Number(v).toLocaleString(undefined, { minimumFractionDigits: 0, maximumFractionDigits: 2 });
}
function toISODate(input) {
  if (!input) return "";
  if (input instanceof Date) {
    const yyyy = input.getFullYear();
    const mm = String(input.getMonth() + 1).padStart(2, "0");
    const dd = String(input.getDate()).padStart(2, "0");
    return `${yyyy}-${mm}-${dd}`;
  }
  if (/^\d{4}-\d{2}-\d{2}$/.test(input)) return input;
  if (/^\d{2}\/\d{2}\/\d{4}$/.test(input)) {
    const [dd, mm, yyyy] = input.split("/");
    return `${yyyy}-${mm}-${dd}`;
  }
  return input;
}

export default function VolsPage({ locale = "fr" }) {
  const isRtl = locale === "ar";
  const [vols, setVols] = useState([]);
  const [loading, setLoading] = useState(false);
  const [search, setSearch] = useState("");
  const [selectedIndex, setSelectedIndex] = useState(null);

  const [showModal, setShowModal] = useState(false);
  const [mode, setMode] = useState("add"); // add | edit
  const [form, setForm] = useState({
    id: null,
    volNumber: "",
    dateDepart: "",
    origine: "",
    destination: "",
    places: "",
    prix: "",
    statut: "Scheduled"
  });
  const [error, setError] = useState("");

  const texts = {
    fr: {
      title: "Vols",
      add: "Ajouter",
      edit: "Modifier",
      delete: "Supprimer",
      print: "Impression",
      searchPlaceholder: "Rechercher vol...",
      noItems: "Aucun vol",
      save: "Enregistrer",
      cancel: "Annuler",
      required: "Veuillez remplir tous les champs requis."
    },
    ar: {
      title: "الرحلات",
      add: "إضافة",
      edit: "تعديل",
      delete: "حذف",
      print: "طباعة",
      searchPlaceholder: "بحث عن رحلة...",
      noItems: "لا توجد رحلات",
      save: "حفظ",
      cancel: "إلغاء",
      required: "يرجى تعبئة الحقول المطلوبة."
    }
  }[locale];

  async function loadVols() {
    setLoading(true);
    try {
      const loader = window.pywebview?.api?.load_vols || window.pywebview?.api?.loadVols || window.pywebview?.api?.get_vols;
      if (typeof loader === "function") {
        const res = await loader();
        setVols(Array.isArray(res) ? res : []);
      } else {
        setVols([]); // no backend: empty
      }
    } catch (e) {
      console.error("loadVols error", e);
      setVols([]);
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => {
    loadVols();
  }, [locale]);

  const filtered = vols.filter(v => {
    const q = search.trim().toLowerCase();
    if (!q) return true;
    return (
      (v.volNumber || "").toString().toLowerCase().includes(q) ||
      (v.origine || "").toLowerCase().includes(q) ||
      (v.destination || "").toLowerCase().includes(q)
    );
  });

  function openAdd() {
    setMode("add");
    setForm({
      id: null,
      volNumber: "",
      dateDepart: toISODate(new Date()),
      origine: "",
      destination: "",
      places: "",
      prix: "",
      statut: "Scheduled"
    });
    setError("");
    setShowModal(true);
  }

  function openEdit() {
    if (selectedIndex === null || !filtered[selectedIndex]) {
      alert(locale === "ar" ? "الرجاء تحديد سطر." : "Please select a row.");
      return;
    }
    const v = filtered[selectedIndex];
    setMode("edit");
    setForm({
      id: v.id ?? null,
      volNumber: v.volNumber || "",
      dateDepart: v.dateDepart ? toISODate(v.dateDepart) : "",
      origine: v.origine || "",
      destination: v.destination || "",
      places: v.places !== undefined ? String(v.places) : "",
      prix: v.prix !== undefined ? String(v.prix) : "",
      statut: v.statut || "Scheduled"
    });
    setError("");
    setShowModal(true);
  }

  async function handleDelete() {
    if (selectedIndex === null || !filtered[selectedIndex]) {
      alert(locale === "ar" ? "الرجاء تحديد سطر." : "Please select a row.");
      return;
    }
    const v = filtered[selectedIndex];
    if (!window.confirm((locale === "ar" ? "حذف الرحلة " : "Delete flight ") + (v.volNumber || "") + " ?")) return;
    try {
      const fn = window.pywebview?.api?.delete_vol || window.pywebview?.api?.deleteVol;
      if (typeof fn === "function") {
        await fn(v.id);
        await loadVols();
        setSelectedIndex(null);
      } else {
        setVols(prev => prev.filter(x => x.id !== v.id));
        setSelectedIndex(null);
        alert(locale === "ar" ? "تم الحذف محليًا." : "Deleted locally (no backend).");
      }
    } catch (e) {
      console.error("delete vol failed", e);
      alert(locale === "ar" ? "فشل الحذف" : "Delete failed");
    }
  }

  async function handleSave(e) {
    e?.preventDefault?.();
    setError("");
    if (!form.volNumber || !form.dateDepart || !form.origine || !form.destination || form.places === "" || form.prix === "") {
      setError(texts.required);
      return;
    }

    const payload = {
      volNumber: form.volNumber,
      dateDepart: toISODate(form.dateDepart),
      origine: form.origine,
      destination: form.destination,
      places: Number(form.places),
      prix: Number(form.prix),
      statut: form.statut
    };

    try {
      if (mode === "add") {
        const fn = window.pywebview?.api?.add_vol || window.pywebview?.api?.addVol;
        if (typeof fn === "function") {
          await fn(payload);
          await loadVols();
        } else {
          // fallback local
          const tmpId = Date.now();
          setVols(prev => [{ ...payload, id: tmpId }, ...prev]);
        }
      } else {
        if (!form.id) {
          setError("Missing id");
          return;
        }
        const fn = window.pywebview?.api?.update_vol || window.pywebview?.api?.updateVol;
        if (typeof fn === "function") {
          await fn(form.id, payload);
          await loadVols();
        } else {
          setVols(prev => prev.map(x => (x.id === form.id ? { ...x, ...payload } : x)));
        }
      }
      setShowModal(false);
    } catch (err) {
      console.error("save vol error", err);
      setError(locale === "ar" ? "خطأ أثناء الحفظ" : "Save error");
    }
  }

  function handlePrint() {
    window.print();
  }

  return (
    <div style={{ padding: 20, direction: isRtl ? "rtl" : "ltr" }}>
      <h2 style={{ textAlign: "center" }}>{texts.title}</h2>

      <div style={{ display: "flex", gap: 12, marginBottom: 12, alignItems: "center" }}>
        <input
          placeholder={texts.searchPlaceholder}
          value={search}
          onChange={e => { setSearch(e.target.value); setSelectedIndex(null); }}
          style={{ padding: 8, borderRadius: 6, border: "1px solid #ccc", flex: 1 }}
        />
        <div style={{ marginLeft: "auto", display: "flex", gap: 8 }}>
          <button onClick={openAdd} style={styles.actionButton}>{texts.add}</button>
          <button onClick={openEdit} style={styles.actionButton}>{texts.edit}</button>
          <button onClick={handleDelete} style={styles.actionButton}>{texts.delete}</button>
        </div>
      </div>

      <div style={{ border: "1px solid #e0e0e0", borderRadius: 8, overflow: "auto" }}>
        <table style={{ width: "100%", borderCollapse: "collapse" }}>
          <thead style={{ background: "#f1f6ff", fontWeight: 700 }}>
            <tr>
              <th style={styles.th}>#</th>
              <th style={styles.th}>Vol #</th>
              <th style={styles.th}>{locale === "ar" ? "تاريخ المغادرة" : "Date départ"}</th>
              <th style={styles.th}>{locale === "ar" ? "المنبع" : "Origine"}</th>
              <th style={styles.th}>{locale === "ar" ? "الوجهة" : "Destination"}</th>
              <th style={styles.th}>{locale === "ar" ? "الاماكن" : "Places"}</th>
              <th style={styles.th}>{locale === "ar" ? "السعر" : "Prix"}</th>
              <th style={styles.th}>{locale === "ar" ? "الحالة" : "Statut"}</th>
            </tr>
          </thead>
          <tbody>
            {loading ? (
              <tr><td colSpan={8} style={{ padding: 20, textAlign: "center" }}>{locale === "ar" ? "جاري التحميل..." : "Loading..."}</td></tr>
            ) : filtered.length === 0 ? (
              <tr><td colSpan={8} style={{ padding: 20, textAlign: "center" }}>{texts.noItems}</td></tr>
            ) : (
              filtered.map((v, i) => (
                <tr
                  key={v.id ?? i}
                  onClick={() => setSelectedIndex(i)}
                  style={{ background: i === selectedIndex ? "#e6f0ff" : (i % 2 === 0 ? "#fff" : "#fbfdff"), cursor: "pointer" }}
                >
                  <td style={styles.td}>{i + 1}</td>
                  <td style={styles.td}>{v.volNumber}</td>
                  <td style={styles.td}>{v.dateDepart}</td>
                  <td style={styles.td}>{v.origine}</td>
                  <td style={styles.td}>{v.destination}</td>
                  <td style={styles.td}>{v.places}</td>
                  <td style={styles.td}>{formatNumber(v.prix)}</td>
                  <td style={styles.td}>{v.statut}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      <div style={{ position: "fixed", right: 20, bottom: 20 }}>
        <button onClick={handlePrint} style={{ ...styles.actionButton, background: "#3477eb", color: "#fff" }}>{texts.print}</button>
      </div>

      {/* Modal */}
      {showModal && (
        <div style={styles.modalOverlay}>
          <div style={{ ...styles.modal, ...(isRtl ? { direction: "rtl", textAlign: "right" } : {}) }}>
            <h3>{mode === "add" ? texts.add : texts.edit}</h3>
            <form onSubmit={handleSave} style={{ display: "flex", flexDirection: "column", gap: 12 }}>
              <div style={{ display: "flex", gap: 12 }}>
                <label style={styles.label}>
                  Vol #
                  <input value={form.volNumber} onChange={e => setForm(s => ({ ...s, volNumber: e.target.value }))} style={styles.input} />
                </label>
                <label style={styles.label}>
                  {locale === "ar" ? "تاريخ المغادرة" : "Date départ"}
                  <input type="date" value={form.dateDepart} onChange={e => setForm(s => ({ ...s, dateDepart: e.target.value }))} style={styles.input} />
                </label>
              </div>

              <div style={{ display: "flex", gap: 12 }}>
                <label style={styles.label}>
                  Origine
                  <input value={form.origine} onChange={e => setForm(s => ({ ...s, origine: e.target.value }))} style={styles.input} />
                </label>
                <label style={styles.label}>
                  Destination
                  <input value={form.destination} onChange={e => setForm(s => ({ ...s, destination: e.target.value }))} style={styles.input} />
                </label>
              </div>

              <div style={{ display: "flex", gap: 12 }}>
                <label style={styles.label}>
                  Places
                  <input value={form.places} onChange={e => setForm(s => ({ ...s, places: e.target.value }))} style={styles.input} type="number" />
                </label>
                <label style={styles.label}>
                  Prix
                  <input value={form.prix} onChange={e => setForm(s => ({ ...s, prix: e.target.value }))} style={styles.input} type="number" step="0.01" />
                </label>
              </div>

              <label style={{ display: "flex", flexDirection: "column" }}>
                Statut
                <select value={form.statut} onChange={e => setForm(s => ({ ...s, statut: e.target.value }))} style={styles.input}>
                  <option>Scheduled</option>
                  <option>Delayed</option>
                  <option>Cancelled</option>
                  <option>Departed</option>
                </select>
              </label>

              {error && <div style={{ color: "red" }}>{error}</div>}

              <div style={{ display: "flex", justifyContent: "flex-end", gap: 8 }}>
                <button type="submit" style={styles.saveBtn}>{texts.save}</button>
                <button type="button" onClick={() => setShowModal(false)} style={styles.cancelBtn}>{texts.cancel}</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

const styles = {
  th: { padding: "0.75rem", textAlign: "left", borderBottom: "1px solid #e6eefb" },
  td: { padding: "0.6rem", borderBottom: "1px solid #f2f6ff", verticalAlign: "middle" },
  actionButton: { padding: "0.5rem 0.9rem", borderRadius: 6, border: "none", background: "#eaf3ff", color: "#3477eb", cursor: "pointer", fontWeight: 700 },
  modalOverlay: { position: "fixed", inset: 0, background: "rgba(0,0,0,0.45)", display: "flex", alignItems: "center", justifyContent: "center", zIndex: 999 },
  modal: { background: "#fff", padding: 18, borderRadius: 10, width: "92%", maxWidth: 820, boxShadow: "0 8px 30px rgba(0,0,0,0.2)" },
  label: { flex: 1, display: "flex", flexDirection: "column", fontSize: 14 },
  input: { marginTop: 6, padding: "0.5rem", borderRadius: 6, border: "1px solid #ccc" },
  saveBtn: { padding: "0.55rem 1rem", background: "#3477eb", color: "#fff", border: "none", borderRadius: 6, cursor: "pointer", fontWeight: 700 },
  cancelBtn: { padding: "0.45rem 0.9rem", background: "#f1f4f8", color: "#333", border: "none", borderRadius: 6, cursor: "pointer", fontWeight: 700 }
};
