  ar: {
    title: "ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿπŸÖŸÑÿßÿ°",
    searchPlaceholder: "ÿ®ÿ≠ÿ´...",
    selectRowAlert: "ÿßŸÑÿ±ÿ¨ÿßÿ° ÿ™ÿ≠ÿØŸäÿØ ÿµŸÅ ÿßŸÑÿπŸÖŸäŸÑ.",
    addClient: "ÿ•ÿ∂ÿßŸÅÿ© ÿπŸÖŸäŸÑ ÿ¨ÿØŸäÿØ",
    editClient: "ÿ™ÿπÿØŸäŸÑ ÿßŸÑÿπŸÖŸäŸÑ",
    confirmDelete: (name) => `ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ≠ÿ∞ŸÅ ÿßŸÑÿπŸÖŸäŸÑ ${name}ÿü`,
    save: "ÿ≠ŸÅÿ∏",
    cancel: "ÿ•ŸÑÿ∫ÿßÿ°",
    addButton: "‚ûï ÿ•ÿ∂ÿßŸÅÿ©",
    editButton: "‚úèÔ∏è ÿ™ÿπÿØŸäŸÑ",
    deleteButton: "üóëÔ∏è ÿ≠ÿ∞ŸÅ",
    errorLoad: "ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿπŸÖŸÑÿßÿ°",
    errorSave: "ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿ≠ŸÅÿ∏!",
    errorDelete: "ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿ≠ÿ∞ŸÅ!",
    // Arabic header texts (kept same logical order; we'll reverse rendering for RTL)
    tableHeaders: [
      "#",
      "ÿ±ŸÇŸÖ ÿßŸÑÿ¨Ÿàÿßÿ≤",
      "ÿßŸÑÿßÿ≥ŸÖ ÿßŸÑŸÉÿßŸÖŸÑ",
      "ÿßÿ≥ŸÖ ÿßŸÑÿ£ÿ®",
      "ÿßÿ≥ŸÖ ŸàŸÑŸÇÿ® ÿßŸÑÿ£ŸÖ",
      "ÿ™ÿßÿ±ŸäÿÆ ÿßŸÑŸÖŸäŸÑÿßÿØ",
      "ŸÖŸÉÿßŸÜ ÿßŸÑŸÖŸäŸÑÿßÿØ",
      "ÿ™ÿßÿ±ŸäÿÆ ÿßŸÑÿ•ÿµÿØÿßÿ±",
      "ÿ™ÿßÿ±ŸäÿÆ ÿßŸÑÿßŸÜÿ™Ÿáÿßÿ°",
      "ÿßŸÑÿπŸÜŸàÿßŸÜ",
      "ÿßŸÑŸáÿßÿ™ŸÅ",
      "ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä",
      "ÿßŸÑÿµŸàÿ±ÿ©"
    ],
    modalLabels: {
      passport_number: "ÿ±ŸÇŸÖ ÿßŸÑÿ¨Ÿàÿßÿ≤",
      nom_et_prenom: "ÿßŸÑÿßÿ≥ŸÖ ÿßŸÑŸÉÿßŸÖŸÑ",
      prenom_pere: "ÿßÿ≥ŸÖ ÿßŸÑÿ£ÿ®",
      nom_prenom_mere: "ÿßÿ≥ŸÖ ŸàŸÑŸÇÿ® ÿßŸÑÿ£ŸÖ",
      date_de_naissance: "ÿ™ÿßÿ±ŸäÿÆ ÿßŸÑŸÖŸäŸÑÿßÿØ",
      lieu_de_naissance: "ŸÖŸÉÿßŸÜ ÿßŸÑŸÖŸäŸÑÿßÿØ",
      date_delivrance: "ÿ™ÿßÿ±ŸäÿÆ ÿßŸÑÿ•ÿµÿØÿßÿ±",
      date_expiration: "ÿ™ÿßÿ±ŸäÿÆ ÿßŸÑÿßŸÜÿ™Ÿáÿßÿ°",
      addresse: "ÿßŸÑÿπŸÜŸàÿßŸÜ",
      telephone: "ÿßŸÑŸáÿßÿ™ŸÅ",
      email: "ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä",
      photo: "ÿßŸÑÿµŸàÿ±ÿ© (jpg/png)"
    }
  }
};

// Field order mapping (matches translations table order)
const FIELDS = [
  "passport_number",
  "nom_et_prenom",
  "prenom_pere",
  "nom_prenom_mere",
  "date_de_naissance",
  "lieu_de_naissance",
  "date_delivrance",
  "date_expiration",
  "addresse",
  "telephone",
  "email",
  "photo"
];

function ClientPage({ locale = "fr" }) {
  // Client data state
  const [clients, setClients] = useState([]);
  const [selectedIndex, setSelectedIndex] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");

  // Modal state
  const [showModal, setShowModal] = useState(false);
  const [modalMode, setModalMode] = useState("add"); // "add" or "edit"
  const [clientForm, setClientForm] = useState({
    id: null,
    passport_number: "",
    nom_et_prenom: "",
    prenom_pere: "",
    nom_prenom_mere: "",
    date_de_naissance: "",
    lieu_de_naissance: "",
    date_delivrance: "",
    date_expiration: "",
    addresse: "",
    telephone: "",
    email: "",
    photo: "" // data URI (preferred) or base64 string
  });

  const [isDeleting, setIsDeleting] = useState(false);

  // For resizable columns
  const tableRef = useRef(null);
  const [colWidths, setColWidths] = useState([]); // px widths for each header (including "#" column)
  const resizingRef = useRef({
    isResizing: false,
    colIndex: null,
    startX: 0,
    startWidth: 0
  });

  // Fetch clients from backend via pywebview API
  const fetchClients = async () => {
    try {
      const result = await window.pywebview.api.get_clients();
      setClients(result || []);
      // Initialize col widths after we have data/render
      requestAnimationFrame(() => initColWidths());
    } catch (err) {
      console.error(err);
      alert(translations[locale].errorLoad);
    }
  };

  useEffect(() => {
    fetchClients();
    // cleanup listeners on unmount
    return () => removeMouseListeners();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // Initialize column widths to equal division or based on current table width
  const initColWidths = () => {
    const table = tableRef.current;
    if (!table) return;
    const headers = translations[locale].tableHeaders;
    const total = table.offsetWidth || table.clientWidth || 800;
    const defaultWidth = Math.max(80, Math.floor(total / headers.length));
    setColWidths(headers.map(() => defaultWidth));
  };

  // Mouse handlers for resizing
  const onMouseMove = (e) => {
    const state = resizingRef.current;
    if (!state.isResizing) return;
    const dx = e.clientX - state.startX;
    const newWidth = Math.max(40, state.startWidth + dx);
    setColWidths((prev) => {
      const next = [...prev];
      next[state.colIndex] = newWidth;
      return next;
    });
  };

  const onMouseUp = () => {
    if (resizingRef.current.isResizing) {
      resizingRef.current.isResizing = false;
      resizingRef.current.colIndex = null;
      removeMouseListeners();
    }
  };

  const addMouseListeners = () => {
    window.addEventListener("mousemove", onMouseMove);
    window.addEventListener("mouseup", onMouseUp);
  };

  const removeMouseListeners = () => {
    window.removeEventListener("mousemove", onMouseMove);
    window.removeEventListener("mouseup", onMouseUp);
  };

  const startResize = (colIndex, e) => {
    e.preventDefault();
    resizingRef.current.isResizing = true;
    resizingRef.current.colIndex = colIndex;
    resizingRef.current.startX = e.clientX;
    resizingRef.current.startWidth = colWidths[colIndex] || 120;
    addMouseListeners();
  };

  // Search / filter
  const filteredClients = clients.filter((c) => {
    const q = searchQuery.trim().toLowerCase();
    if (!q) return true;
    // Search in passport_number, nom_et_prenom, telephone, email
    return (
      (c.passport_number || "").toString().toLowerCase().includes(q) ||
      (c.nom_et_prenom || "").toLowerCase().includes(q) ||
      (c.telephone || "").toLowerCase().includes(q) ||
      (c.email || "").toLowerCase().includes(q)
    );
  });

  // Direction/RTL: apply rtl and also reverse column rendering if arabic
  const isRtl = locale === "ar";
  const directionStyle = isRtl ? { direction: "rtl" } : { direction: "ltr" };

  // Handlers
  const handleAjouter = () => {
    setModalMode("add");
    setClientForm({
      id: null,
      passport_number: "",
      nom_et_prenom: "",
      prenom_pere: "",
      nom_prenom_mere: "",
      date_de_naissance: "",
      lieu_de_naissance: "",
      date_delivrance: "",
      date_expiration: "",
      addresse: "",
      telephone: "",
      email: "",
      photo: ""
    });
    setShowModal(true);
  };

  const handleModifier = () => {
    if (selectedIndex !== null && filteredClients[selectedIndex]) {
      setModalMode("edit");
      // populate form with the DB keys
      const client = filteredClients[selectedIndex];
      setClientForm({
        ...client
      });
      setShowModal(true);
    } else {
      alert(translations[locale].selectRowAlert);
    }
  };

  const handleSupprimer = async () => {
    if (selectedIndex === null || !filteredClients[selectedIndex]) {
      alert(translations[locale].selectRowAlert);
      return;
    }
    if (isDeleting) return;
    const clientId = filteredClients[selectedIndex].id;
    const clientName = filteredClients[selectedIndex].nom_et_prenom || filteredClients[selectedIndex].passport_number || "";
    const confirmDelete = window.confirm(translations[locale].confirmDelete(clientName));
    if (!confirmDelete) return;
    try {
      setIsDeleting(true);
      await window.pywebview.api.delete_client(clientId);
      setSelectedIndex(null);
      fetchClients();
    } catch (err) {
      console.error(err);
      alert(translations[locale].errorDelete);
    } finally {
      setIsDeleting(false);
    }
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setClientForm({
      id: null,
      passport_number: "",
      nom_et_prenom: "",
      prenom_pere: "",
      nom_prenom_mere: "",
      date_de_naissance: "",
      lieu_de_naissance: "",
      date_delivrance: "",
      date_expiration: "",
      addresse: "",
      telephone: "",
      email: "",
      photo: ""
    });
  };

  const handleSaveClient = async (e) => {
    e.preventDefault();
    try {
      // Build payload matching DB columns
      const payload = { ...clientForm };
      // Remove any extra keys (none in this version)
      if (modalMode === "add") {
        await window.pywebview.api.add_client(payload);
      } else {
        await window.pywebview.api.update_client(payload);
      }
      handleCloseModal();
      fetchClients();
    } catch (err) {
      console.error(err);
      alert(translations[locale].errorSave);
    }
  };

  // File -> dataURL for photo
  const handlePhotoChange = (file) => {
    if (!file) {
      setClientForm((s) => ({ ...s, photo: "" }));
      return;
    }
    const reader = new FileReader();
    reader.onload = () => {
      // reader.result is a data URL (data:image/..;base64,...)
      setClientForm((s) => ({ ...s, photo: reader.result }));
    };
    reader.readAsDataURL(file);
  };

  // Utility to produce image src from DB value (either data URI or raw base64)
  const photoSrc = (val) => {
    if (!val) return null;
    if (typeof val !== "string") return null;
    if (val.startsWith("data:")) return val;
    // assume raw base64 -> prefix with png mime (best-effort)
    return `data:image/png;base64,${val}`;
  };

  // For table rendering: headers and fields order; reverse if RTL to "invert"
  const headers = translations[locale].tableHeaders;
  const fieldsOrder = isRtl ? [...FIELDS].reverse() : [...FIELDS];

  // Scrollable table container style
  const tableContainerStyle = {
    maxHeight: "60vh",
    overflow: "auto",
    border: "1px solid #e0e0e0",
    borderRadius: "6px"
  };

  // Helper to get width style for a given column index (includes index column)
  const getColStyle = (colIndex) => {
    if (colWidths && colWidths[colIndex] != null) {
      return { width: `${colWidths[colIndex]}px`, minWidth: `${colWidths[colIndex]}px`, boxSizing: "border-box" };
    }
    return {};
  };

  return (
    <div style={{ ...styles.pageContainer, ...directionStyle }}>
      <h2 style={{ textAlign: "center" }}>{translations[locale].title}</h2>

      {/* Search Bar */}
      <div style={styles.searchContainer}>
        <span style={styles.searchIcon}>üîç</span>
        <input
          type="text"
          placeholder={translations[locale].searchPlaceholder}
          value={searchQuery}
          onChange={(e) => {
            setSearchQuery(e.target.value);
            setSelectedIndex(null);
          }}
          style={styles.searchInput}
        />
      </div>

      {/* Scrollable Table */}
      <div style={tableContainerStyle} ref={tableRef}>
        <table style={styles.table}>
          <thead>
            <tr style={styles.tableHeaderRow}>
              {/* Render header cells with resizer between columns */}
              {headers.map((h, i) => (
                <th key={i} style={{ ...styles.tableHeader, ...getColStyle(i), position: "relative" }}>
                  <div style={{ display: "flex", alignItems: "center", justifyContent: isRtl ? "flex-end" : "flex-start" }}>
                    <span style={{ overflow: "hidden", textOverflow: "ellipsis", whiteSpace: "nowrap" }}>{h}</span>
                    {/* Add resizer except for the last column */}
                    {i < headers.length - 1 && (
                      <div
                        onMouseDown={(e) => startResize(i, e)}
                        style={styles.resizer}
                        title="Redimensionner la colonne"
                      />
                    )}
                  </div>
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {filteredClients.map((client, idx) => {
              // Build cells in the same order as fieldsOrder
              const cells = fieldsOrder.map((field, ci) => {
                // compute actual column index: +1 because first column is index (#)
                const colIndex = 1 + ci;
                if (field === "photo") {
                  const src = photoSrc(client[field]);
                  return (
                    <td key={`${client.id}-${field}`} style={{ ...styles.tableCell, ...getColStyle(colIndex) }}>
                      {src ? (
                        <img
                          src={src}
                          alt="photo"
                          style={{ width: 48, height: 48, objectFit: "cover", borderRadius: 4 }}
                        />
                      ) : (
                        "-"
                      )}
                    </td>
                  );
                }
                return <td key={`${client.id}-${field}`} style={{ ...styles.tableCell, ...getColStyle(colIndex) }}>{client[field] ?? "-"}</td>;
              });

              return (
                <tr
                  key={client.id || (client.passport_number + idx)}
                  style={{
                    ...styles.tableRow,
                    background:
                      idx === selectedIndex
                        ? "#cce5ff"
                        : idx % 2 === 0
                        ? "#fff"
                        : "#f4f8ff"
                  }}
                  onClick={() => setSelectedIndex(idx)}
                >
                  <td style={{ ...styles.tableCell, ...getColStyle(0) }}>{idx + 1}</td>
                  {cells}
                </tr>
              );
            })}
            {filteredClients.length === 0 && (
              <tr>
                <td colSpan={headers.length} style={{ padding: "1rem", textAlign: "center", color: "#666" }}>
                  {translations[locale].errorLoad}
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>

      {/* Drawer for CRUD buttons */}
      <div style={styles.drawer}>
        <button style={styles.drawerButton} onClick={handleAjouter}>
          {translations[locale].addButton}
        </button>
        <button style={styles.drawerButton} onClick={handleModifier}>
          {translations[locale].editButton}
        </button>
        <button style={styles.drawerButton} onClick={handleSupprimer}>
          {translations[locale].deleteButton}
        </button>
      </div>

      {/* Modal for Adding/Editing a Client */}
      {showModal && (
        <div style={styles.modalOverlay}>
          <div style={{ ...styles.modalContent, ...(isRtl ? { direction: "rtl", textAlign: "right" } : {}) }}>
            <h3>
              {modalMode === "add" ? translations[locale].addClient : translations[locale].editClient}
            </h3>
            <form onSubmit={handleSaveClient} style={styles.modalForm}>
              {/* Two inputs per line rows */}
              <div style={styles.fieldRow}>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.passport_number}:
                    <input
                      type="text"
                      value={clientForm.passport_number || ""}
                      onChange={(e) => setClientForm({ ...clientForm, passport_number: e.target.value })}
                      style={styles.modalInput}
                      required
                    />
                  </label>
                </div>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.nom_et_prenom}:
                    <input
                      type="text"
                      value={clientForm.nom_et_prenom || ""}
                      onChange={(e) => setClientForm({ ...clientForm, nom_et_prenom: e.target.value })}
                      style={styles.modalInput}
                      required
                    />
                  </label>
                </div>
              </div>

              <div style={styles.fieldRow}>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.prenom_pere}:
                    <input
                      type="text"
                      value={clientForm.prenom_pere || ""}
                      onChange={(e) => setClientForm({ ...clientForm, prenom_pere: e.target.value })}
                      style={styles.modalInput}
                    />
                  </label>
                </div>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.nom_prenom_mere}:
                    <input
                      type="text"
                      value={clientForm.nom_prenom_mere || ""}
                      onChange={(e) => setClientForm({ ...clientForm, nom_prenom_mere: e.target.value })}
                      style={styles.modalInput}
                    />
                  </label>
                </div>
              </div>

              <div style={styles.fieldRow}>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.date_de_naissance}:
                    <input
                      type="date"
                      value={clientForm.date_de_naissance || ""}
                      onChange={(e) => setClientForm({ ...clientForm, date_de_naissance: e.target.value })}
                      style={styles.modalInput}
                    />
                  </label>
                </div>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.lieu_de_naissance}:
                    <input
                      type="text"
                      value={clientForm.lieu_de_naissance || ""}
                      onChange={(e) => setClientForm({ ...clientForm, lieu_de_naissance: e.target.value })}
                      style={styles.modalInput}
                    />
                  </label>
                </div>
              </div>

              <div style={styles.fieldRow}>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.date_delivrance}:
                    <input
                      type="date"
                      value={clientForm.date_delivrance || ""}
                      onChange={(e) => setClientForm({ ...clientForm, date_delivrance: e.target.value })}
                      style={styles.modalInput}
                    />
                  </label>
                </div>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.date_expiration}:
                    <input
                      type="date"
                      value={clientForm.date_expiration || ""}
                      onChange={(e) => setClientForm({ ...clientForm, date_expiration: e.target.value })}
                      style={styles.modalInput}
                    />
                  </label>
                </div>
              </div>

              <div style={styles.fieldRow}>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.addresse}:
                    <input
                      type="text"
                      value={clientForm.addresse || ""}
                      onChange={(e) => setClientForm({ ...clientForm, addresse: e.target.value })}
                      style={styles.modalInput}
                    />
                  </label>
                </div>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.telephone}:
                    <input
                      type="text"
                      value={clientForm.telephone || ""}
                      onChange={(e) => setClientForm({ ...clientForm, telephone: e.target.value })}
                      style={styles.modalInput}
                    />
                  </label>
                </div>
              </div>

              <div style={styles.fieldRow}>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.email}:
                    <input
                      type="email"
                      value={clientForm.email || ""}
                      onChange={(e) => setClientForm({ ...clientForm, email: e.target.value })}
                      style={styles.modalInput}
                    />
                  </label>
                </div>
                <div style={styles.fieldItem}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.photo}:
                    <input
                      type="file"
                      accept="image/*"
                      onChange={(e) => handlePhotoChange(e.target.files[0])}
                      style={{ marginTop: 6 }}
                    />
                    {/* show preview */}
                    {clientForm.photo ? (
                      <div style={{ marginTop: 8 }}>
                        <img
                          src={photoSrc(clientForm.photo)}
                          alt="preview"
                          style={{ width: 100, height: 80, objectFit: "cover", borderRadius: 6 }}
                        />
                      </div>
                    ) : null}
                  </label>
                </div>
              </div>

              <div style={styles.modalButtons}>
                <button type="submit" style={styles.modalButton}>
                  {translations[locale].save}
                </button>
                <button type="button" style={styles.modalButton} onClick={handleCloseModal}>
                  {translations[locale].cancel}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

