import React, { useEffect, useState } from "react";
import { jsPDF } from "jspdf";

/*
  Vols.js
  Identical layout and drawer system to Caisse.js
  Features:
  - Left drawer (Ajouter, Modifier, Supprimer)
  - Right drawer (Impression, Fiche Vol)
  - Table + Modal for CRUD
  - RTL/Arabic + FR support
  - PyWebView API integration
*/

// ðŸ§­ Drawer Hover Animation (same as caisse.js)
useEffect(() => {
