  style.id = "drawer-hover-style";
  style.innerHTML = `
    .drawer-left, .drawer-right {
      position: fixed;
      top: 50%;
      transform: translateY(-50%);
      z-index: 10;
      display: flex;
      flex-direction: column;
      gap: 10px;
      padding: 0.5rem;
      background: rgba(255,255,255,0.9);
      border-radius: 10px;
      box-shadow: 0 0 10px rgba(0,0,0,0.1);
      transition: all 0.4s ease;
      opacity: 0.3;
    }
    .drawer-left {
      left: -180px;
    }
    .drawer-right {
      right: -180px;
    }
    .drawer-left:hover {
      left: 15px;
      opacity: 1;
    }
    .drawer-right:hover {
      right: 15px;
      opacity: 1;
    }
    .drawer-left button,
    .drawer-right button {
      background: #f4f7ff;
      border: none;
      border-radius: 6px;
      padding: 0.6rem 1.1rem;
      cursor: pointer;
      font-size: 1rem;
      font-weight: 600;
      color: #2a3f79;
      display: flex;
      align-items: center;
      gap: 6px;
      transition: all 0.3s;
    }
    .drawer-left button:hover,
    .drawer-right button:hover {
      background: #3477eb;
      color: white;
    }
  `;
  document.head.appendChild(style);
  return () => document.head.removeChild(style);
}, []);

// -----------------------------------

