function formatDateToFR(dateString) {
  if (!dateString) return "";
  if (/^\d{4}-\d{2}-\d{2}$/.test(dateString)) {
    const [yyyy, mm, dd] = dateString.split("-");
    return `${dd}/${mm}/${yyyy}`;
  }
  return dateString;
}
function formatDateToISO(dateString) {
  if (!dateString) return "";
  if (/^\d{2}\/\d{2}\/\d{4}$/.test(dateString)) {
    const [dd, mm, yyyy] = dateString.split("/");
    return `${yyyy}-${mm}-${dd}`;
  }
  return dateString;
}

async function generateVolPDF(vol, locale) {
  const doc = new jsPDF();
  let y = 12;
  doc.setFontSize(18);
  doc.text(translations[locale].ficheButton, 12, y);
  y += 10;
  doc.setFontSize(12);
  const L = translations[locale].modalLabels;
  const printField = (label, value) => {
    doc.text(`${label}: ${value ?? ""}`, 12, y);
    y += 8;
  };
  for (const field of TABLE_FIELDS) {
    printField(L[field] || field, vol[field]);
  }
  const pdfBlob = doc.output("blob");
  const arrayBuffer = await pdfBlob.arrayBuffer();
  const uint8Array = new Uint8Array(arrayBuffer);
  let binary = '';
  for (let i = 0; i < uint8Array.length; i++) binary += String.fromCharCode(uint8Array[i]);
  const base64PDF = window.btoa(binary);
  if (window.pywebview?.api?.open_pdf_base64) {
    await window.pywebview.api.open_pdf_base64(base64PDF);
  } else alert("PDF preview not supported.");
}

function VolsPage({ locale = "fr" }) {
  const [vols, setVols] = useState([]);
  const [selectedIndex, setSelectedIndex] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [modalMode, setModalMode] = useState("add");
  const [volForm, setVolForm] = useState({
    id: null,
    flight_number: "",
    compagnie: "",
    depart_aeroport: "",
    arrivee_aeroport: "",
    date_depart: "",
    heure_depart: "",
    duree: "",
    classe: "",
    prix: "",
    places_disponibles: "",
    statut: "OK"
  });

  const isRtl = locale === "ar";
  const directionStyle = isRtl ? { direction: "rtl" } : {};

  const fetchVols = async () => {
    const res = await window.pywebview.api.get_vols();
    setVols(res || []);
  };

  useEffect(() => { fetchVols(); }, []);

  const filteredVols = vols.filter(v => {
    const q = searchQuery.trim().toLowerCase();
    return (
      v.flight_number?.toLowerCase().includes(q) ||
      v.compagnie?.toLowerCase().includes(q)
    );
  });

  const handleAjouter = () => { setModalMode("add"); setShowModal(true); };
  const handleModifier = () => {
    if (selectedIndex === null) return alert(translations[locale].selectRowAlert);
    setModalMode("edit");
    setVolForm(filteredVols[selectedIndex]);
    setShowModal(true);
  };
  const handleSupprimer = async () => {
    if (selectedIndex === null) return alert(translations[locale].selectRowAlert);
    const id = filteredVols[selectedIndex].id;
    if (!window.confirm(translations[locale].confirmDelete(filteredVols[selectedIndex].flight_number))) return;
    await window.pywebview.api.delete_vol(id);
    fetchVols();
  };
  const handleSave = async (e) => {
    e.preventDefault();
    const payload = { ...volForm, date_depart: formatDateToISO(volForm.date_depart) };
    if (modalMode === "add") await window.pywebview.api.add_vol(payload);
    else await window.pywebview.api.update_vol(payload);
    setShowModal(false);
    fetchVols();
  };
  const handleImpression = () => window.print();
  const handleFiche = () => {
    if (selectedIndex === null) return alert(translations[locale].selectRowAlert);
    generateVolPDF(filteredVols[selectedIndex], locale);
  };

  return (
    <div style={{ padding: "1.25rem", ...directionStyle }}>
      <h2 style={{ textAlign: "center" }}>{translations[locale].title}</h2>

      <div style={{ display: "flex", marginBottom: "1rem" }}>
        <input
          type="text"
          placeholder={translations[locale].searchPlaceholder}
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          style={{
            width: "100%",
            padding: "0.5rem",
            borderRadius: "5px",
            border: "1px solid #ccc"
          }}
        />
      </div>

      <table style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr>
            {translations[locale].tableHeaders.map((h, i) => (
              <th key={i} style={{ border: "1px solid #ddd", padding: "8px", background: "#f4f6ff" }}>
                {h}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {filteredVols.map((v, idx) => (
            <tr
              key={v.id}
              style={{
                background: idx === selectedIndex ? "#cce5ff" : idx % 2 ? "#fff" : "#f9f9f9",
                cursor: "pointer"
              }}
              onClick={() => setSelectedIndex(idx)}
            >
              <td>{idx + 1}</td>
              {TABLE_FIELDS.map(f => (
                <td key={f} style={{ border: "1px solid #ddd", padding: "8px" }}>{v[f]}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>

      {/* ðŸ§­ Left Drawer */}
      <div className="drawer-left">
        <button onClick={handleAjouter}>{translations[locale].addButton}</button>
        <button onClick={handleModifier}>{translations[locale].editButton}</button>
        <button onClick={handleSupprimer}>{translations[locale].deleteButton}</button>
      </div>

      {/* ðŸ§­ Right Drawer */}
      <div className="drawer-right">
        <button onClick={handleImpression}>{translations[locale].printButton}</button>
        <button onClick={handleFiche}>{translations[locale].ficheButton}</button>
      </div>

      {/* ðŸªŸ Modal */}
      {showModal && (
        <div style={{
          position: "fixed", top: 0, left: 0, width: "100%", height: "100%",
          background: "rgba(0,0,0,0.5)", display: "flex", alignItems: "center", justifyContent: "center"
        }}>
          <div style={{
            background: "white", padding: "2rem", borderRadius: "10px", width: "90%", maxWidth: "600px"
          }}>
            <h3>{modalMode === "add" ? translations[locale].addVol : translations[locale].editVol}</h3>
            <form onSubmit={handleSave} style={{ display: "flex", flexDirection: "column", gap: "1rem" }}>
              {Object.keys(translations[locale].modalLabels).map((key) => (
                <label key={key}>
                  {translations[locale].modalLabels[key]}:
                  <input
                    value={volForm[key] || ""}
                    onChange={(e) => setVolForm({ ...volForm, [key]: e.target.value })}
                    style={{ width: "100%", padding: "0.5rem", borderRadius: "5px", border: "1px solid #ccc" }}
                  />
                </label>
              ))}
              <div style={{ display: "flex", justifyContent: "flex-end", gap: "1rem" }}>
                <button type="submit" style={{ background: "#3477eb", color: "white", border: "none", padding: "0.6rem 1rem", borderRadius: "5px" }}>{translations[locale].save}</button>
                <button type="button" onClick={() => setShowModal(false)} style={{ background: "#999", color: "white", border: "none", padding: "0.6rem 1rem", borderRadius: "5px" }}>{translations[locale].cancel}</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default VolsPage;
