// pointage.js
// Opens a modal window to record monthly attendance ("pointage") for a personnel.
// - Registers window event listener "openPointage" and exports openPointage(detail).
// - Shows working days of chosen month (Fridays excluded), allows toggling presence per day.
// - Computes salary_per_day, days_present, salary_due and saves via window.pywebview.api.save_pointage.
// - Uses a stable number formatter that renders "5 000.00" and forces LTR rendering so Arabic pages do not reverse digits.
// - Drop this file in the same folder as App.js (or adjust import path in App.js: import './pointage';)

import React, { useEffect, useState } from "react";
import ReactDOM from "react-dom/client";

// Helpers
function monthName(monthIndex, locale = "fr") {
  const monthsFr = ["Janvier","Février","Mars","Avril","Mai","Juin","Juillet","Août","Septembre","Octobre","Novembre","Décembre"];
  const monthsAr = ["يناير","فيفري","مارس","أبريل","ماي","جوان","جويلية","أوت","سبتمبر","أكتوبر","نوفمبر","ديسمبر"];
  return (locale === "ar" ? monthsAr : monthsFr)[monthIndex];
}

function getWorkingDays(year, month) {
  // returns Date objects for all days in month except Fridays (Friday = 5)
  const days = [];
  const d = new Date(year, month, 1);
  while (d.getMonth() === month) {
    // In JS: Sunday=0, Monday=1, ... Friday=5, Saturday=6
    if (d.getDay() !== 5) {
      days.push(new Date(d));
    }
    d.setDate(d.getDate() + 1);
  }
  return days;
}

function formatDateISO(d) {
  if (!d) return "";
  const yyyy = d.getFullYear();
  const mm = String(d.getMonth() + 1).padStart(2, "0");
  const dd = String(d.getDate()).padStart(2, "0");
  return `${yyyy}-${mm}-${dd}`;
}

function parseNumber(value) {
  if (value === null || value === undefined || value === "") return 0;
  const s = String(value).replace(/\s+/g, "").replace(",", ".");
  const n = Number(s);
  return Number.isFinite(n) ? n : 0;
}

// New helper: format number with space thousands and dot decimal and force LTR rendering in the UI
function formatNumberSpaceDot(value) {
  if (value === null || value === undefined || value === "") return "";
  const cleaned = String(value).replace(/\s+/g, "").replace(",", ".");
  const n = Number(cleaned);
  if (!Number.isFinite(n)) return String(value);
  const parts = n.toFixed(2).split(".");
  parts[0] = parts[0].replace(/\B(?=(\d{3})+(?!\d))/g, " ");
  return parts.join(".");
}

// Modal component
function PointageModal({ detail = {}, onClose }) {
  const locale = detail.locale || "fr";
  const T = {
    fr: {
      title: "Pointage / Calcul salaire",
      selectPersonnel: "Sélectionner le personnel",
      monthLabel: "Mois",
      yearLabel: "Année",
      workingDaysNote: "Jours ouvrables (vendredis exclus)",
      presentLabel: "Présent",
      daysPresent: "Présences",
      salaryPerDay: "Salaire par jour",
      salaryDue: "Salaire dû",
      advance: "Avance",
      save: "Enregistrer le pointage",
      close: "Fermer",
      loading: "Chargement...",
      noPersonnel: "Aucun personnel sélectionné.",
      savedOK: "Pointage enregistré."
    },
    ar: {
      title: "تسجيل الحضور / حساب الراتب",
      selectPersonnel: "اختر الموظف",
      monthLabel: "الشهر",
      yearLabel: "السنة",
      workingDaysNote: "أيام العمل (باستثناء الجمعة)",
      presentLabel: "حاضر",
      daysPresent: "الحضور",
      salaryPerDay: "الراتب اليومي",
      salaryDue: "المبلغ المستحق",
      advance: "السلفة",
      save: "حفظ التسجيل",
      close: "إغلاق",
      loading: "جاري التحميل...",
      noPersonnel: "لم يتم اختيار موظف.",
      savedOK: "تم حفظ التسجيل."
    }
  }[locale];

  const [personnelList, setPersonnelList] = useState([]);
  const [selectedPersonnel, setSelectedPersonnel] = useState(detail.personnel || null);
  const now = new Date();
  const [year, setYear] = useState(now.getFullYear());
  const [month, setMonth] = useState(now.getMonth()); // 0-11
  const [workingDays, setWorkingDays] = useState([]);
  const [presentMap, setPresentMap] = useState({}); // isoDate -> boolean
  const [isSaving, setIsSaving] = useState(false);
  const [loadingPersonnel, setLoadingPersonnel] = useState(false);
  const [error, setError] = useState("");

  // build working days when month/year changes
  useEffect(() => {
    const days = getWorkingDays(year, month);
    setWorkingDays(days);
    // initialize presentMap: default true for each day unless previously set
    setPresentMap(prev => {
      const map = {};
      days.forEach(d => {
        const iso = formatDateISO(d);
        map[iso] = prev.hasOwnProperty(iso) ? prev[iso] : true;
      });
      return map;
    });
  }, [year, month]);

  // if no personnel passed, fetch personnel list
  useEffect(() => {
    let mounted = true;
    async function loadPersonnel() {
      if (selectedPersonnel) return;
      setLoadingPersonnel(true);
      try {
        const res = await window.pywebview?.api?.get_personnel?.();
        if (!mounted) return;
        setPersonnelList(Array.isArray(res) ? res : []);
      } catch (e) {
        console.error("pointage: failed to load personnel", e);
        setPersonnelList([]);
      } finally {
        setLoadingPersonnel(false);
      }
    }
    loadPersonnel();
    return () => { mounted = false; };
  }, [selectedPersonnel]);

  // load existing pointage for this personnel/year/month if exists
  useEffect(() => {
    let mounted = true;
    async function loadExisting() {
      if (!selectedPersonnel || !selectedPersonnel.id) return;
      try {
        const rec = await window.pywebview?.api?.get_pointage_for_person_month?.(selectedPersonnel.id, year, month + 1);
        if (!mounted) return;
        if (rec) {
          let dates = rec.dates_present || rec.dates_present_text || rec.dates_present_json || null;
          if (typeof dates === "string") {
            try { dates = JSON.parse(dates); } catch (e) {}
          }
          if (Array.isArray(dates)) {
            const map = {};
            // Ensure workingDays is up-to-date
            const days = getWorkingDays(year, month);
            days.forEach(d => {
              const iso = formatDateISO(d);
              map[iso] = dates.includes(iso);
            });
            setPresentMap(map);
          }
        }
      } catch (e) {
        console.warn("pointage: no existing record or failed to load", e);
      }
    }
    loadExisting();
    return () => { mounted = false; };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [selectedPersonnel, year, month]);

  const totalWorkingDays = workingDays.length;
  const daysPresent = Object.values(presentMap).filter(Boolean).length;

  const monthlySalary = parseNumber((selectedPersonnel && (selectedPersonnel.salaire ?? selectedPersonnel.salary)) || 0);
  const avance = parseNumber((selectedPersonnel && (selectedPersonnel.avance ?? selectedPersonnel.advance)) || 0);

  const salaryPerDay = totalWorkingDays > 0 ? (monthlySalary / totalWorkingDays) : 0;
  const salaryDue = (salaryPerDay * daysPresent) - avance;

  function toggleDay(iso) {
    setPresentMap(prev => ({ ...prev, [iso]: !prev[iso] }));
  }

  async function handleSave() {
    if (!selectedPersonnel || !selectedPersonnel.id) {
      setError(T.noPersonnel);
      return;
    }
    setError("");
    setIsSaving(true);
    const payload = {
      personnel_id: selectedPersonnel.id,
      year,
      month: month + 1, // 1..12
      days_total: totalWorkingDays,
      days_present: daysPresent,
      salary_per_day: salaryPerDay,
      salary_due: salaryDue,
      dates_present: Object.entries(presentMap).filter(([k,v]) => v).map(([k]) => k)
    };
    console.log("pointage: saving payload", payload);
    try {
      if (window.pywebview && window.pywebview.api && window.pywebview.api.save_pointage) {
        const result = await window.pywebview.api.save_pointage(payload);
        console.log("pointage: save result", result);
      } else {
        // fallback: store in localStorage for demo
        const key = `pointage_${selectedPersonnel.id}_${year}_${month+1}`;
        localStorage.setItem(key, JSON.stringify(payload));
        console.log("pointage: saved to localStorage", key);
      }
      setIsSaving(false);
      alert(T.savedOK);
      onClose();
    } catch (e) {
      console.error("pointage: save failed", e);
      setError((locale === "fr" ? "Erreur lors de l'enregistrement." : "فشل الحفظ.") + (e?.message ? ` ${e.message}` : ""));
      setIsSaving(false);
    }
  }

  function handleSelectPersonnelId(id) {
    const p = personnelList.find(x => String(x.id) === String(id));
    setSelectedPersonnel(p || null);
    // reset present map when switching person
    const days = getWorkingDays(year, month);
    const map = {};
    days.forEach(d => { map[formatDateISO(d)] = true; });
    setPresentMap(map);
  }

  // Render
  const currencySuffix = locale === "ar" ? " دج" : " DZD";

  return (
    <div style={styles.overlay}>
      <div style={{ ...styles.modal, direction: locale === "ar" ? "rtl" : "ltr", textAlign: locale === "ar" ? "right" : "left" }}>
        <div style={styles.header}>
          <h3 style={{ margin: 0 }}>{T.title}</h3>
          <button onClick={onClose} style={styles.closeButton}>✖</button>
        </div>

        <div style={styles.controlsRow}>
          <div style={{ flex: 1 }}>
            <label style={styles.labelSmall}>{T.selectPersonnel}</label>
            {selectedPersonnel ? (
              <div style={styles.personnelCard}>
                <div>
                  <strong>{selectedPersonnel.nom} {selectedPersonnel.prenom}</strong>
                  <div style={{ fontSize: 12, color: "#666" }}>{selectedPersonnel.poste || ""}</div>
                </div>
                <div style={{ textAlign: "right" }}>
                  <div style={{ fontSize: 12 }}>{locale === "fr" ? "Salaire mensuel" : "الراتب الشهري"}</div>
                  <div style={{ fontWeight: 700 }}>
                    <span style={{ direction: "ltr", unicodeBidi: "isolate" }}>
                      {formatNumberSpaceDot(monthlySalary)}{currencySuffix}
                    </span>
                  </div>
                </div>
              </div>
            ) : (
              loadingPersonnel ? <div>{T.loading}</div> : (
                <select style={styles.select} defaultValue="" onChange={(e) => handleSelectPersonnelId(e.target.value)}>
                  <option value="">{locale === "fr" ? "— Select —" : "— اختر —"}</option>
                  {personnelList.map(p => (
                    <option key={p.id} value={p.id}>{p.nom} {p.prenom} {p.poste ? ` - ${p.poste}` : ""}</option>
                  ))}
                </select>
              )
            )}
          </div>

          <div style={{ width: 260 }}>
            <label style={styles.labelSmall}>{T.monthLabel} / {T.yearLabel}</label>
            <div style={{ display: "flex", gap: 8 }}>
              <select value={month} onChange={(e) => setMonth(Number(e.target.value))} style={styles.select}>
                {Array.from({ length: 12 }).map((_, i) => <option key={i} value={i}>{monthName(i, locale)}</option>)}
              </select>
              <input type="number" value={year} onChange={(e) => setYear(Number(e.target.value))} style={{ ...styles.select, width: 100 }} />
            </div>
            <div style={{ marginTop: 6, fontSize: 12, color: "#666" }}>
              {totalWorkingDays} {locale === "fr" ? "jours ouvrables" : "أيام عمل"} ({locale === "fr" ? "vendredis exclus" : "باستثناء الجمعة"})
            </div>
          </div>
        </div>

        <div style={{ marginTop: 12 }}>
          <div style={{ fontWeight: 700, marginBottom: 8 }}>{T.workingDaysNote}</div>
          <div style={styles.daysGrid}>
            {workingDays.map(d => {
              const iso = formatDateISO(d);
              const label = `${String(d.getDate()).padStart(2,"0")}/${String(d.getMonth()+1).padStart(2,"0")}`;
              const weekday = d.toLocaleDateString(locale === "fr" ? "fr-FR" : "ar", { weekday: "short" });
              return (
                <label key={iso} style={styles.dayItem}>
                  <input type="checkbox" checked={!!presentMap[iso]} onChange={() => toggleDay(iso)} />
                  <div style={{ marginLeft: 8 }}>
                    <div style={{ fontSize: 13 }}>{label} <span style={{ color: "#666", fontSize: 12 }}>({weekday})</span></div>
                  </div>
                </label>
              );
            })}
          </div>
        </div>

        <div style={{ marginTop: 12, display: "flex", gap: 12, alignItems: "center", justifyContent: "space-between" }}>
          <div>
            <div style={{ fontSize: 14, color: "#666" }}>{T.daysPresent}</div>
            <div style={{ fontWeight: 700, fontSize: 18 }}>{daysPresent} / {totalWorkingDays}</div>
          </div>

          <div style={{ textAlign: "right" }}>
            <div style={{ fontSize: 13, color: "#666" }}>{T.salaryPerDay}</div>
            <div style={{ fontWeight: 700 }}>
              <span style={{ direction: "ltr", unicodeBidi: "isolate" }}>
                {formatNumberSpaceDot(salaryPerDay)}{currencySuffix}
              </span>
            </div>
            <div style={{ height: 6 }} />
            <div style={{ fontSize: 13, color: "#666" }}>{T.salaryDue}</div>
            <div style={{ fontWeight: 900, fontSize: 18, color: salaryDue < 0 ? "#c00" : "#14355f" }}>
              <span style={{ direction: "ltr", unicodeBidi: "isolate" }}>
                {formatNumberSpaceDot(salaryDue)}{currencySuffix}
              </span>
            </div>
            <div style={{ fontSize: 11, color: "#666" }}>{T.advance}: <span style={{ direction: "ltr", unicodeBidi: "isolate" }}>{formatNumberSpaceDot(avance)}{currencySuffix}</span></div>
          </div>
        </div>

        {error && <div style={{ marginTop: 8, color: "red" }}>{error}</div>}

        <div style={{ marginTop: 14, display: "flex", gap: 8, justifyContent: "flex-end" }}>
          <button onClick={onClose} style={styles.btnSecondary}>{T.close}</button>
          <button onClick={handleSave} style={styles.btnPrimary} disabled={isSaving || !selectedPersonnel}>
            {isSaving ? (locale === "fr" ? "Enregistrement..." : "جاري الحفظ...") : T.save}
          </button>
        </div>
      </div>
    </div>
  );
}

// Programmatic opener: mount modal into document.body
export function openPointage(detail = {}) {
  console.log("[pointage] openPointage called with detail:", detail);
  const id = "pointage-modal-root";
  let container = document.getElementById(id);
  if (!container) {
    container = document.createElement("div");
    container.id = id;
    document.body.appendChild(container);
  }

  function cleanup(root, containerEl) {
    try { root.unmount(); } catch (e) { /* ignore */ }
    try { containerEl.remove(); } catch (e) { /* ignore */ }
  }

  try {
    const root = ReactDOM.createRoot(container);
    const onClose = () => cleanup(root, container);
    root.render(<PointageModal detail={detail} onClose={onClose} />);
  } catch (err) {
    // fallback for older React
    try {
      const onClose = () => {
        try { ReactDOM.unmountComponentAtNode(container); } catch (e) {}
        try { container.remove(); } catch (e) {}
      };
      // eslint-disable-next-line
      ReactDOM.render(<PointageModal detail={detail} onClose={onClose} />, container);
    } catch (e) {
      console.error("[pointage] failed to render modal:", e);
      alert("Failed to open pointage (see console).");
    }
  }
}

// Event listener registration so dispatching the custom event works
function onOpenPointageEvent(e) {
  try {
    console.log("[pointage] openPointage event received:", e?.detail);
    openPointage(e?.detail || {});
  } catch (err) {
    console.error("[pointage] event handler error:", err);
  }
}

try {
  window.__pointageLoaded = true;
  window.addEventListener("openPointage", onOpenPointageEvent);
  window.openPointage = window.openPointage || openPointage;
  console.log("[pointage] module loaded. Listener registered. window.__pointageLoaded=true");
} catch (e) {
  console.error("[pointage] could not register openPointage listener:", e);
}

// Styles (inline simple styles)
const styles = {
  overlay: {
    position: "fixed",
    inset: 0,
    background: "rgba(0,0,0,0.45)",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    zIndex: 9999
  },
  modal: {
    width: "94%",
    maxWidth: 980,
    background: "#fff",
    borderRadius: 10,
    padding: 16,
    boxShadow: "0 12px 40px rgba(0,0,0,0.35)"
  },
  header: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center"
  },
  closeButton: {
    border: "none",
    background: "transparent",
    fontSize: 18,
    cursor: "pointer"
  },
  controlsRow: {
    display: "flex",
    gap: 12,
    alignItems: "flex-start"
  },
  labelSmall: {
    fontSize: 12,
    color: "#333",
    marginBottom: 6,
    display: "block"
  },
  select: {
    padding: "0.45rem 0.6rem",
    borderRadius: 6,
    border: "1px solid #ccc",
    background: "#fff",
    fontSize: 14,
    width: "100%"
  },
  personnelCard: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "8px 10px",
    border: "1px solid #e9eef9",
    borderRadius: 8,
    background: "#fbfdff"
  },
  daysGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(160px, 1fr))",
    gap: 8,
    maxHeight: 320,
    overflowY: "auto",
    padding: 6,
    border: "1px solid #eef3fb",
    borderRadius: 6,
    background: "#fff"
  },
  dayItem: {
    display: "flex",
    alignItems: "center",
    gap: 8,
    padding: "6px 8px",
    borderRadius: 6,
    background: "#fbfbff",
    border: "1px solid #f0f6ff",
    fontSize: 13
  },
  btnPrimary: {
    padding: "0.6rem 1rem",
    background: "#3477eb",
    color: "#fff",
    border: "none",
    borderRadius: 6,
    cursor: "pointer",
    fontWeight: 700
  },
  btnSecondary: {
    padding: "0.5rem 0.9rem",
    background: "#f4f6fb",
    border: "none",
    borderRadius: 6,
    cursor: "pointer"
  }
};

export default openPointage;
