const FIELDS = [
  "nom",
  "prenom",
  "nom_ar",
  "prenom_ar",
  "prenom_pere",
  "prenom_pere_ar",
  "nom_mere",
  "prenom_mere",
  "nom_mere_ar",
  "prenom_mere_ar",
  "date_de_naissance",
  "lieu_de_naissance",
  "nationalite",
  "passport_number",
  "date_delivrance",
  "date_expiration",
  "addresse",
  "telephone",
  "photo"
];

function ClientPage({ locale = "fr" }) {
  const [clients, setClients] = useState([]);
  const [selectedIndex, setSelectedIndex] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [modalMode, setModalMode] = useState("add");
  const [clientForm, setClientForm] = useState({
    id: null,
    nom: "",
    prenom: "",
    nom_ar: "",
    prenom_ar: "",
    prenom_pere: "",
    prenom_pere_ar: "",
    nom_mere: "",
    prenom_mere: "",
    nom_mere_ar: "",
    prenom_mere_ar: "",
    date_de_naissance: "",
    lieu_de_naissance: "",
    nationalite: "",
    passport_number: "",
    date_delivrance: "",
    date_expiration: "",
    addresse: "",
    telephone: "",
    photo: ""
  });
  const [isDeleting, setIsDeleting] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  // Header/fields order and direction
  const isRtl = locale === "ar";
  const displayedHeaders = isRtl
    ? [...translations[locale].tableHeaders].slice().reverse()
    : translations[locale].tableHeaders;
  const fieldsOrder = isRtl
    ? [...FIELDS].slice().reverse()
    : [...FIELDS];
  const directionStyle = isRtl ? { direction: "rtl" } : { direction: "ltr" };

  const fetchClients = async () => {
    setIsLoading(true);
    try {
      const result = await window.pywebview.api.get_clients();
      setClients(result || []);
    } catch (err) {
      setClients([]);
    }
    setIsLoading(false);
  };

  useEffect(() => {
    fetchClients();
    // eslint-disable-next-line
  }, [locale]);

  const filteredClients = clients.filter((c) => {
    const q = searchQuery.trim().toLowerCase();
    if (!q) return true;
    return (
      (c.nom || "").toLowerCase().includes(q) ||
      (c.prenom || "").toLowerCase().includes(q) ||
      (c.nom_ar || "").toLowerCase().includes(q) ||
      (c.prenom_ar || "").toLowerCase().includes(q) ||
      (c.prenom_pere || "").toLowerCase().includes(q) ||
      (c.nom_mere || "").toLowerCase().includes(q) ||
      (c.prenom_mere || "").toLowerCase().includes(q) ||
      (c.nationalite || "").toLowerCase().includes(q) ||
      (c.passport_number || "").toString().toLowerCase().includes(q) ||
      (c.telephone || "").toLowerCase().includes(q)
    );
  });

  const emptyForm = {
    id: null,
    nom: "",
    prenom: "",
    nom_ar: "",
    prenom_ar: "",
    prenom_pere: "",
    prenom_pere_ar: "",
    nom_mere: "",
    prenom_mere: "",
    nom_mere_ar: "",
    prenom_mere_ar: "",
    date_de_naissance: "",
    lieu_de_naissance: "",
    nationalite: "",
    passport_number: "",
    date_delivrance: "",
    date_expiration: "",
    addresse: "",
    telephone: "",
    photo: ""
  };

  const handleAjouter = () => {
    setModalMode("add");
    setClientForm({ ...emptyForm });
    setShowModal(true);
  };

  const handleModifier = () => {
    if (selectedIndex !== null && filteredClients[selectedIndex]) {
      setModalMode("edit");
      setClientForm({ ...filteredClients[selectedIndex] });
      setShowModal(true);
    } else {
      alert(translations[locale].selectRowAlert);
    }
  };

  const handleSupprimer = async () => {
    if (selectedIndex === null || !filteredClients[selectedIndex]) {
      alert(translations[locale].selectRowAlert);
      return;
    }
    if (isDeleting) return;
    const clientId = filteredClients[selectedIndex].id;
    const clientName =
      filteredClients[selectedIndex].nom ||
      filteredClients[selectedIndex].prenom ||
      filteredClients[selectedIndex].passport_number ||
      "";
    const confirmDelete = window.confirm(
      translations[locale].confirmDelete(clientName)
    );
    if (!confirmDelete) return;
    try {
      setIsDeleting(true);
      await window.pywebview.api.delete_client(clientId);
      setSelectedIndex(null);
      fetchClients();
    } catch (err) {
      alert(translations[locale].errorDelete);
    } finally {
      setIsDeleting(false);
    }
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setClientForm({ ...emptyForm });
  };

  const handleSaveClient = async (e) => {
    e.preventDefault();
    try {
      const payload = { ...clientForm };
      if (modalMode === "add") {
        await window.pywebview.api.add_client(payload);
      } else {
        await window.pywebview.api.update_client(payload);
      }
      handleCloseModal();
      fetchClients();
    } catch (err) {
      alert(translations[locale].errorSave);
    }
  };

  const handlePhotoChange = (file) => {
    if (!file) {
      setClientForm((s) => ({ ...s, photo: "" }));
      return;
    }
    const reader = new FileReader();
    reader.onload = () => {
      setClientForm((s) => ({ ...s, photo: reader.result }));
    };
    reader.readAsDataURL(file);
  };

  const photoSrc = (val) => {
    if (!val) return null;
    if (typeof val !== "string") return null;
    if (val.startsWith("data:")) return val;
    return `data:image/png;base64,${val}`;
  };

  const tableContainerStyle = {
    maxHeight: "60vh",
    overflowY: "auto",
    border: "1px solid #e0e0e0",
    borderRadius: "6px"
  };

  const spinnerStyle = {
    margin: "60px auto",
    border: "8px solid #eee",
    borderTop: "8px solid #3477eb",
    borderRadius: "50%",
    width: 48,
    height: 48,
    animation: "spin 1s linear infinite"
  };

  // Add spinner keyframes if not present
  if (typeof document !== "undefined" && !document.getElementById("client-spinner-style")) {
    const style = document.createElement("style");
    style.id = "client-spinner-style";
    style.innerHTML = `
      @keyframes spin { to { transform: rotate(360deg); } }
    `;
    document.head.appendChild(style);
  }

  return (
    <div style={{ ...styles.pageContainer, ...directionStyle }}>
      <h2 style={{ textAlign: "center" }}>{translations[locale].title}</h2>
      {/* Search Bar */}
      <div style={styles.searchContainer}>
        <span style={styles.searchIcon}>üîç</span>
        <input
          type="text"
          placeholder={translations[locale].searchPlaceholder}
          value={searchQuery}
          onChange={(e) => {
            setSearchQuery(e.target.value);
            setSelectedIndex(null);
          }}
          style={styles.searchInput}
        />
      </div>
      {/* Loading spinner */}
      {isLoading ? (
        <div style={{ display: "flex", justifyContent: "center" }}>
          <div style={spinnerStyle}></div>
        </div>
      ) : (
        <div style={tableContainerStyle}>
          <table style={styles.table}>
            <thead>
              <tr style={styles.tableHeaderRow}>
                {displayedHeaders.map((h, i) => (
                  <th key={i} style={styles.tableHeader}>
                    {h}
                  </th>
                ))}
              </tr>
            </thead>
            <tbody>
              {filteredClients.map((client, idx) => {
                const cells = fieldsOrder.map((field, colIdx) => {
                  if (field === "photo") {
                    const src = photoSrc(client[field]);
                    return (
                      <td key={`${client.id}-${field}`} style={styles.tableCell}>
                        {src ? (
                          <img
                            src={src}
                            alt="photo"
                            style={{ width: 48, height: 48, objectFit: "cover", borderRadius: 4 }}
                          />
                        ) : (
                          "-"
                        )}
                      </td>
                    );
                  }
                  return (
                    <td key={`${client.id}-${field}`} style={styles.tableCell}>
                      {client[field] ?? "-"}
                    </td>
                  );
                });

                return (
                  <tr
                    key={client.id || (client.nom + idx)}
                    style={{
                      ...styles.tableRow,
                      background:
                        idx === selectedIndex
                          ? "#cce5ff"
                          : idx % 2 === 0
                          ? "#fff"
                          : "#f4f8ff"
                    }}
                    onClick={() => setSelectedIndex(idx)}
                  >
                    <td style={styles.tableCell}>{idx + 1}</td>
                    {cells}
                  </tr>
                );
              })}
              {filteredClients.length === 0 && !isLoading && (
                <tr>
                  <td colSpan={displayedHeaders.length} style={{ padding: "1rem", textAlign: "center", color: "#666" }}>
                    {translations[locale].errorLoad}
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      )}
      {/* Drawer for CRUD buttons */}
      <div style={styles.drawer}>
        <button style={styles.drawerButton} onClick={handleAjouter}>
          {translations[locale].addButton}
        </button>
        <button style={styles.drawerButton} onClick={handleModifier}>
          {translations[locale].editButton}
        </button>
        <button style={styles.drawerButton} onClick={handleSupprimer}>
          {translations[locale].deleteButton}
        </button>
      </div>
      {/* Modal for Adding/Editing a Client */}
      {showModal && (
        <div style={styles.modalOverlay}>
          <div style={{ ...styles.modalContent, ...(isRtl ? { direction: "rtl", textAlign: "right" } : {}) }}>
            <h3>
              {modalMode === "add"
                ? translations[locale].addClient
                : translations[locale].editClient}
            </h3>
            <form onSubmit={handleSaveClient} style={styles.modalForm}>
              {/* Render inputs two per line */}
              {[
                ["nom", "prenom"],
                ["nom_ar", "prenom_ar"],
                ["prenom_pere", "prenom_pere_ar"],
                ["nom_mere", "prenom_mere"],
                ["nom_mere_ar", "prenom_mere_ar"],
                ["date_de_naissance", "lieu_de_naissance"],
                ["nationalite", "passport_number"],
                ["date_delivrance", "date_expiration"],
                ["addresse", "telephone"]
              ].map(([field1, field2], rowIdx) => (
                <div style={styles.fieldRow} key={rowIdx}>
                  <div style={styles.fieldItem}>
                    <label style={styles.modalLabel}>
                      {translations[locale].modalLabels[field1]}:
                      <input
                        type={field1.includes("date") ? "date" : "text"}
                        value={clientForm[field1] || ""}
                        onChange={(e) =>
                          setClientForm({ ...clientForm, [field1]: e.target.value })
                        }
                        style={styles.modalInput}
                        required={
                          ["nom", "prenom", "passport_number"].includes(field1)
                        }
                      />
                    </label>
                  </div>
                  <div style={styles.fieldItem}>
                    <label style={styles.modalLabel}>
                      {translations[locale].modalLabels[field2]}:
                      <input
                        type={field2 && field2.includes("date") ? "date" : "text"}
                        value={clientForm[field2] || ""}
                        onChange={(e) =>
                          setClientForm({ ...clientForm, [field2]: e.target.value })
                        }
                        style={styles.modalInput}
                      />
                    </label>
                  </div>
                </div>
              ))}

              {/* Photo */}
              <div style={styles.fieldRow}>
                <div style={{ ...styles.fieldItem, flex: 1 }}>
                  <label style={styles.modalLabel}>
                    {translations[locale].modalLabels.photo}:
                    <input
                      type="file"
                      accept="image/*"
                      onChange={(e) => handlePhotoChange(e.target.files[0])}
                      style={{ marginTop: 6 }}
                    />
                    {clientForm.photo ? (
                      <div style={{ marginTop: 8 }}>
                        <img
                          src={photoSrc(clientForm.photo)}
                          alt="preview"
                          style={{ width: 100, height: 80, objectFit: "cover", borderRadius: 6 }}
                        />
                      </div>
                    ) : null}
                  </label>
                </div>
                <div style={{ width: 1 }} />
              </div>

              <div style={styles.modalButtons}>
                <button type="submit" style={styles.modalButton}>
                  {translations[locale].save}
                </button>
                <button type="button" style={styles.modalButton} onClick={handleCloseModal}>
                  {translations[locale].cancel}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

